INSERT INTO `strophe` (`idStrophe`, `type`, `identifiant`, `texte`, `position`, `idChant`) VALUES
(1, 'Strophe', 1, 'Je veux chanter l\'amour puissant \n\r Qui sauve et qui délivre, \n\r Qui remplit mon cœur maintenant \n\r De joie et me fait vivre.', 1, 1),
(2, 'Refrain', 1, 'Je veux chanter, \n\r Chanter de tout mon cœur \n\r L\'amour de Dieu, \n\r L\'amour de mon Sauveur.', 2, 1),
(3, 'Strophe', 2, 'Je veux chanter l\'amour vainqueur, \n\r Qui, malgré ma faiblesse, \n\r Peut me donner, à moi, pécheur, \n\r La victoire sans cesse.', 3, 1),
(4, 'Refrain', 1, 'Je veux chanter, \n\r Chanter de tout mon cœur \n\r L\'amour de Dieu, \n\r L\'amour de mon Sauveur.', 4, 1),
(5, 'Strophe', 3, 'Je veux chanter le tendre amour, \n\r Qui dans les jours de peine \n\r Rends le poids du chagrin moins lourd, \n\r Console et rassérène.', 5, 1),
(6, 'Refrain', 1, 'Je veux chanter, \n\r Chanter de tout mon cœur \n\r L\'amour de Dieu, \n\r L\'amour de mon Sauveur.', 6, 1),
(7, 'Strophe', 4, 'Je veux chanter le grand amour, \n\r Immuable et fidèle, \n\r Qui me conduit de jour en jour \n\r Vers la vie éternelle.', 7, 1),
(8, 'Refrain', 1, 'Je veux chanter, \n\r Chanter de tout mon cœur \n\r L\'amour de Dieu, \n\r L\'amour de mon Sauveur.', 8, 1),
(9, 'Strophe', 1, 'Dieu tout-puissant, \n\r Quand mon cœur considère \n\r Tout l\'univers créé par ton pouvoir; \n\r Le ciel d\'azur, les éclairs, le tonnerre, \n\r Le clair matin, ou les ombres du soir.', 1, 2),
(10, 'Refrain', 1, 'De tout mon être, alors s\'élève un chant: \n\r «Dieu tout-puissant, que tu es grand!» \n\r De tout mon être, alors s\'élève un chant: \n\r «Dieu tout-puissant, que tu es grand!»', 2, 2),
(11, 'Strophe', 2, 'Quand par les bois, ou la forêt profonde, \n\r J\'erre et j\'entends tous les oiseaux chanter; \n\r Quand, sur les monts, la source avec son onde, \n\r Livre au zéphir son chant doux et léger.', 3, 2),
(12, 'Refrain', 1, 'Mon cœur heureux, s\'écrie à chaque instant: \n\r «Ô Dieu d\'amour, que tu es grand!» \n\r Mon cœur heureux, s\'écrie à chaque instant: \n\r «Ô Dieu d\'amour, que tu es grand!»', 4, 2),
(13, 'Strophe', 3, 'Mais quand je songe, ô sublime mystère! \n\r Qu\'un Dieu si grand a pu penser à moi; \n\r Que son cher Fils est devenu mon frère, \n\r Et que je suis l\'héritier du grand Roi.', 5, 2),
(14, 'Refrain', 1, 'Alors mon cœur redit, la nuit, le jour: \n\r «Que tu es bon, ô Dieu d\'amour!» \n\r Alors mon cœur redit, la nuit, le jour: \n\r «Que tu es bon, ô Dieu d\'amour!»', 6, 2),
(15, 'Strophe', 4, 'Quand mon Sauveur, éclatant de lumière, \n\r Se lèvera de son trône éternel, \n\r Et que, laissant les douleurs de la terre, \n\r Je pourrai voir les splendeurs de son ciel.', 7, 2),
(16, 'Refrain', 1, 'Je redirai, dans son divin séjour: \n\r «Rien n\'est plus grand \n\r que ton amour!» \n\r Je redirai, dans son divin séjour: \n\r «Rien n\'est plus grand \n\r que ton amour!»', 8, 2),
(17, 'Strophe', 1, 'Il est tant de beautés en ce monde, \n\r Merveilles qui s\'offrent à moi! \n\r Mais je sais, mon Seigneur, que rien n\'égalera \n\r Le grand amour qui me vient de toi.', 1, 3),
(18, 'Refrain', 1, 'Rien ne me séparera, \n\r Mon Seigneur et mon Roi, \n\r De l\'amour que tu as pour moi. \n\r Rien ne me séparera, \n\r Mon Seigneur et mon Roi, \n\r De l\'amour que tu as pour moi.', 2, 3),
(19, 'Strophe', 2, 'Ni la mort, ni la vie, ni les ombres, \n\r Non rien ne t\'éloigne de moi. \n\r Ton amour généreux qui me suit pas à pas \n\r Me libère et me montre la voie.', 3, 3),
(20, 'Refrain', 1, 'Rien ne me séparera, \n\r Mon Seigneur et mon Roi, \n\r De l\'amour que tu as pour moi. \n\r Rien ne me séparera, \n\r Mon Seigneur et mon Roi, \n\r De l\'amour que tu as pour moi.', 4, 3),
(21, 'Strophe', 1, 'L\'amour de Dieu gonfle la voile blanche \n\r Du bateau qui vogue vers l\'horizon. \n\r Il ne tient pas la coque qui s\'élance, \n\r L\'amour de Dieu n\'est pas une prison.', 1, 4),
(22, 'Refrain', 1, 'L\'amour de Dieu gonfle la voile blanche \n\r Du bateau qui vogue vers l\'horizon.', 2, 4),
(23, 'Strophe', 2, 'Nous connaissons bien cette voie royale, \n\r Celle qui mène au pays liberté. \n\r Si en chemin tu tombes ou tu défailles, \n\r La main de Dieu pourra te relever.', 3, 4),
(24, 'Refrain', 1, 'L\'amour de Dieu gonfle la voile blanche \n\r Du bateau qui vogue vers l\'horizon.', 4, 4),
(25, 'Strophe', 3, 'Viens nous aider, Seigneur, toi qui nous aimes, \n\r À désirer transformer notre vie. \n\r Nous ne saurions résoudre nos problèmes \n\r Si tu ne viens. Seigneur, je t\'ai choisi.', 5, 4),
(26, 'Refrain', 1, 'L\'amour de Dieu gonfle la voile blanche \n\r Du bateau qui vogue vers l\'horizon.', 6, 4),
(27, 'Strophe', 1, 'Quand les montagnes s\'éloigneraient, \n\r Quand les collines chancelleraient, \n\r Quand les montagnes s\'éloigneraient, \n\r Dieu fera tout comme il promet:', 1, 5),
(28, 'Refrain', 1, '«Mon amour, oui mon amour, \n\r Ne s\'éloignera pas de toi. \n\r Mon amour, oui mon amour, \n\r Ne s\'éloignera pas de toi.»', 2, 5),
(29, 'Strophe', 2, 'Quand les montagnes s\'éloigneraient, \n\r Quand les collines chancelleraient, \n\r Quand les montagnes s\'éloigneraient, \n\r Dieu fera tout comme il promet.', 3, 5),
(30, 'Refrain', 1, '«Mon amour, oui mon amour, \n\r Ne s\'éloignera pas de toi. \n\r Mon amour, oui mon amour, \n\r Ne s\'éloignera pas de toi.»', 4, 5),
(31, 'Strophe', 1, 'Tu m\'as aimé, Seigneur! que la lumière \n\r Brillât sur l\'univers que ta voix a formé, \n\r Et que l\'astre du jour, parcourant sa carrière. \n\r Versât la vie à flots sur la nature entière. \n\r Mon Dieu, tu m\'as aimé! (bis)', 1, 6),
(32, 'Strophe', 2, 'Mon Dieu! tu m\'as aimé, \n\r quand sur la croix infâme \n\r On vit de Jésus-Christ le corps inanimé, \n\r Et quand pour m\'arracher à la terrible flamme, \n\r Ton saint Fils a porté les crimes de mon âme, \n\r Mon Dieu, tu m\'as aimé! (bis)', 2, 6),
(33, 'Strophe', 3, 'Mon Dieu! tu m\'as aimé, \n\r quand par l\'Esprit de vie \n\r Le feu de ton amour, en mon cœur allumé, \n\r Ouvrit les nouveaux cieux à mon âme ravie; \n\r Quand la paix fut en moi de sainteté suivie, \n\r Mon Dieu, tu m\'as aimé! (bis)', 3, 6),
(34, 'Strophe', 4, 'Tu m\'aimeras toujours! ni la chair ni le monde, \n\r Ni la mort, de tes dons n\'arrêteront le cours. \n\r Où le mal abonda, ta grâce surabonde. \n\r À ton amour, ô Dieu! que mon amour réponde, \n\r Toi qui m\'aimes toujours. (bis)', 4, 6),
(35, 'Strophe', 1, 'Oh! qu\'il m\'est doux de savoir que Dieu m\'aime, \n\r Qu\'il a donné son Fils pour me sauver, \n\r Que par Jésus, ô charité suprême! \n\r Il me pardonne et veut toujours m\'aimer!', 1, 7),
(36, 'Strophe', 2, 'Oh! qu\'il m\'est doux de savoir que mon Père \n\r De son amour m\'accompagne en tous lieux, \n\r Qu\'il me bénit chaque jour sur la terre, \n\r Qu\'il va bientôt me rerevoir aux cieux!', 2, 7),
(37, 'Strophe', 3, 'Oh! qu\'il m\'est doux de penser à ta grâce, \n\r Seigneur Jésus, mon Rédempteur, mon Roi, \n\r Quand l\'ennemi m\'accuse et me menace, \n\r Quand mes péchés s\'élèvent contre moi!', 3, 7),
(38, 'Strophe', 4, 'Quel doux repos et quelle paix profonde \n\r Je trouve en toi, mon bien-aimé Sauveur, \n\r Quand sur toi seul tout mon espoir se fonde, \n\r Quand sur toi seul se repose mon cœur!', 4, 7),
(39, 'Strophe', 5, 'Aussi je veux, tous les jours de ma vie, \n\r Puissant ami, te suivre par la foi; \n\r Mon seul désir et ma plus chère envie, \n\r C\'est de t\'aimer et de vivre pour toi.', 5, 7),
(40, 'Strophe', 1, 'Pour allumer un feu, \n\r Il suffit d\'une flamme. \n\r Sa chaleur et son feu \n\r S\'étendent et se propagent. \n\r L\'amour de Dieu est comme un feu \n\r Quand il est avec toi. \n\r Par sa chaleur, par sa douceur, \n\r Tu veux le partager.', 1, 8),
(41, 'Strophe', 2, 'Quand le printemps est là, \n\r C\'est la vie qui s\'éveille, \n\r Dans les champs, dans les bois, \n\r Tout commence à renaître. \n\r L\'amour de Dieu est un printemps, \n\r Quand il est dans ton cœur. \n\r Par sa fraîcheur, par sa douceur, \n\r Tu veux le partager.', 2, 8),
(42, 'Strophe', 3, 'Oh, je voudrais pour toi \n\r Partager ce message. \n\r Partout où tu iras, \n\r Il sera ton partage. \n\r Je veux le crier sur les toits, \n\r Je veux chanter ma joie. \n\r Le Dieu d\'amour est avec moi, \n\r Je veux le partager.', 3, 8),
(43, 'Strophe', 1, 'Sur tes promesses, Dieu d\'amour, \n\r En paix se reposent les tiens \n\r En ta puissance est leur secours. \n\r Ta grâce est leur constant soutien.', 1, 9),
(44, 'Strophe', 2, 'Tu nous as tout donné, Seigneur, \n\r La délivrance et le pardon, \n\r La joie, la paix, le vrai bonheur, \n\r Notre âme exalte ton grand nom.', 2, 9),
(45, 'Strophe', 3, 'Ô Seigneur, fais-nous mesurer \n\r De ton amour l\'immensité, \n\r Et qu\'à jamais, sans nous lasser, \n\r Nos cœurs célèbres ta bonté.', 3, 9),
(46, 'Strophe', 1, 'Écoute Israël, le Seigneur est notre Dieu, \n\r Le Seigneur est un; \n\r Écoute, Israël, le Seigneur est notre Dieu, \n\r Le Seigneur est un; \n\r Écoute, Israël, le Seigneur est notre Dieu, \n\r Le Seigneur est un.', 1, 10),
(47, 'Strophe', 2, 'Sh\'ma Israël, Adonaï Elohenou, \n\r Adonaï erhad; \n\r Sh\'ma Israël, Adonaï Elohenou, \n\r Adonaï erhad; \n\r Sh\'ma Israël, Adonaï Elohenou, \n\r Adonaï erhad.', 2, 10),
(48, 'Strophe', 1, 'Notre Dieu est délivrance, \n\r Il se lève, il nous sauve, \n\r Il est le Dieu de notre histoire, \n\r Que les peuples lui rendent gloire.', 1, 11),
(49, 'Strophe', 1, 'Saint, saint, saint est le Seigneur, le Seigneur du monde. (2×) \n\r La terre est remplie de sa gloire, (3×) \n\r Saint est le Seigneur.', 1, 12),
(50, 'Strophe', 1, 'Comme est belle cette Terre, \n\r Notre cœur s\'en réjouit! \n\r Tout son charme nous rappelle \n\r Le bonheur du Paradis, \n\r Le bonheur du Paradis.', 1, 13),
(51, 'Strophe', 2, 'Mais ce riche et vaste monde, \n\r Si parfait dans sa beauté \n\r Pour l\'auguste Roi de gloire \n\r N\'est qu\'un simple marchepied, \n\r N\'est qu\'un simple marchepied.', 2, 13),
(52, 'Strophe', 3, 'Le soleil et les étoiles \n\r Se promènent dans les cieux, \n\r Éclairant la terre entière, \n\r Ravissant toujours nos yeux, \n\r Ravissant toujours nos yeux.', 3, 13),
(53, 'Strophe', 4, 'La splendeur du ciel en fête, \n\r Cette cour du Tout-Puissant, \n\r Fait sa robe de lumière, \n\r Son céleste vêtement, \n\r Son céleste vêtement.', 4, 13),
(54, 'Strophe', 5, 'Oui, son trône est magnifique, \n\r Ses ouvrages merveilleux! \n\r Mais, combien plus veau, plus tendre, \n\r Est le cœur de notre Dieu, \n\r Est le cœur de notre Dieu.', 5, 13),
(55, 'Strophe', 1, 'Dieu créa par sa Parole \n\r Les rochers et les torrents; \n\r Il fit toutes les étoiles \n\r Et fixa leur mouvements; \n\r Dieu forma l\'homme et la femme \n\r Et les prit pour ses enfants.', 1, 14),
(56, 'Strophe', 2, 'Autrefois, aux saint prophètes \n\r Dieu parlait par son Esprit; \n\r Maintenant il nous révèle \n\r Ses desseins par Jésus-Christ; \n\r Chaque Jour, par sa parole, \n\r Il condamne l\'ennemi.', 2, 14),
(57, 'Strophe', 3, 'Dieu vécut sur cette terre, \n\r Par le Verbe, il s\'est donné; \n\r C\'est aussi par sa Parole \n\r Qu\'il nous a sanctifiés; \n\r Il nous comble de sa vie \n\r Jusque dans l\'éternité.', 3, 14),
(58, 'Strophe', 1, 'Gloire! Gloire! Gloire! \n\r Dieu dans son temple, \n\r Exerce son règne sur terre et dans les cieux. \n\r Gloire! Gloire! Gloire! \n\r L\'univers contemple \n\r Sa majesté qui remplit le saint lieu.', 1, 15),
(59, 'Strophe', 2, 'Gloire! Gloire! Gloire! \n\r Les cœurs des anges, \n\r En chants alternés, disent: «Saint est l\'Éternel». \n\r Gloire! Gloire! Gloire! \n\r Digne de louange \n\r Est le grand Dieu adoré dans le ciel.', 2, 15),
(60, 'Strophe', 3, 'Gloire! Gloire! Gloire! \n\r Partout sur terre, \n\r Les hommes aussi célèbrent le Créateur. \n\r Gloire! Gloire! Gloire! \n\r Au Dieu trinitaire: \n\r Au Père et Fils et Saint-Esprit l\'honneur.', 3, 15),
(61, 'Strophe', 1, 'L\'air, la mer, la terre et le ciel, \n\r Rendent gloire à l\'Éternel; \n\r Qu\'on entende aussi ma voix \n\r En l\'honneur du Roi des rois!', 1, 16),
(62, 'Strophe', 2, 'Dans le ciel, sans voix, sans bruit, \n\r Comme l\'astre de la nuit, \n\r Le soleil redit toujours: \n\r «Qu\'il est grand, ce Dieu d\'amour!»', 2, 16),
(63, 'Strophe', 3, 'Les forêts, les champs, les prés, \n\r Sur la terre parsemés, \n\r Font, avec les animaux, \n\r Le bonheur du Dieu Très-Haut.', 3, 16),
(64, 'Strophe', 4, 'Les oiseaux aux chants si doux, \n\r Le tonnerre et son courroux, \n\r Sont l\'ouvrage de ses mains \n\r Et soumis à ses desseins.', 4, 16),
(65, 'Strophe', 1, 'Le monde est dans ses mains, \n\r les mains du Créateur. \n\r J\'entends les chants, \n\r les doux accents qu\'inspire sa splendeur. \n\r Le monde est dans ses mains; \n\r que j\'aime à contempler \n\r Les cieux, les mers, les arbres verts, les fleurs et les rochers!', 1, 17),
(66, 'Strophe', 2, 'Le monde est dans ses mains; \n\r les gais oiseaux chanteurs, \n\r Le jour naissant, \n\r le pur lis blanc célèbrent leur Seigneur. \n\r Le monde est dans ses mains, \n\r tout par lui resplendit \n\r Et dans le vent même je l\'entends: \n\r partout sa voix me suit.', 2, 17),
(67, 'Strophe', 3, 'Le monde est dans ses mains, \n\r je ne peux l\'oublier! \n\r Si la douleur emplit nos cœurs, \n\r Dieu reste le premier. \n\r Le monde est dans ses mains, \n\r mon cœur apaise-toi: \n\r Il tient en main notre destin; \n\r nous sommes fils du Roi.', 3, 17),
(68, 'Strophe', 1, 'Que tes œuvres sont belles, \n\r Que tes œuvres sont grandes! \n\r Seigneur, Seigneur, \n\r Tu nous combles de joie! \n\r Que tes œuvres sont belles, \n\r Que tes œuvres sont grandes! \n\r Seigneur, Seigneur, \n\r Tu nous combles de joie!', 1, 18),
(69, 'Strophe', 1, 'Qui dit au soleil sur la terre \n\r D\'éclairer tout homme en tout lieu, \n\r Et qui donne aux nuits leur mystère?', 1, 19),
(70, 'Refrain', 1, 'C\'est l\'Éternel, c\'est notre Dieu. \n\r C\'est l\'Éternel, c\'est notre Dieu.', 2, 19),
(71, 'Strophe', 2, 'Le bleuet et le ciel superbe, \n\r Qui les a teints d\'un si beau bleu? \n\r Qui verdit l\'émeraude et l\'herbe?', 3, 19),
(72, 'Refrain', 1, 'C\'est l\'Éternel, c\'est notre Dieu. \n\r C\'est l\'Éternel, c\'est notre Dieu.', 4, 19),
(73, 'Strophe', 3, 'Quand nous pleurons, qui nous console? \n\r Qui veille sur nous en tout lieu? \n\r Qui nous soutient par sa parole?', 5, 19),
(74, 'Refrain', 1, 'C\'est l\'Éternel, c\'est notre Dieu. \n\r C\'est l\'Éternel, c\'est notre Dieu.', 6, 19),
(75, 'Strophe', 1, 'C\'est toi, Jésus, que recherche mon âme, \n\r À te trouver se bornent mes souhaits. \n\r C\'est ton regard que sur moi je réclame. \n\r Rends-moi, Seigneur, rends-moi ta douce paix!', 1, 20),
(76, 'Strophe', 2, 'Jadis j\'errais dans les sentiers du monde, \n\r Ne connaissant ni ton nom, ni ta loi. \n\r Tu me cherchas dans cette nuit profonde, \n\r Et, pour toujours, m\'en tiras par la foi.', 2, 20),
(77, 'Strophe', 3, 'Ta voix d\'amour, à moi se fit entendre: \n\r J\'appris alors que tu m\'as racheté; \n\r Et ton Esprit à mon cœur fit comprendre \n\r Ce qu\'est, ô Dieu, ta grande charité.', 3, 20),
(78, 'Strophe', 4, 'Prends donc pitié de ma grande misère, \n\r Soumets mon cœur, brise sa dureté. \n\r À Golgotha, mon âme, te fut chère: \n\r Je compte, ô Dieu, sur ta fidélité.', 4, 20),
(79, 'Strophe', 1, 'Il est un nom si beau, si doux, \n\r Qu\'on ne prononce qu\'à genoux, \n\r Un nom qui fait vibrer mon âme, \n\r Un nom que l\'univers proclame: \n\r C\'est Jésus!', 1, 21),
(80, 'Strophe', 2, 'Lorsque j\'étais sans foi, ni loi, \n\r Loin du bonheur qui n\'est qu\'en toi, \n\r En proie au doute et solitaire, \n\r Sur moi resplendit ta lumière, \n\r Ô Jésus!', 2, 21),
(81, 'Strophe', 3, 'En toi j\'ai vue la sainteté, \n\r L\'amour et la félicité. \n\r Penché sur ton œuvre admirable, \n\r J\'ai béni ton nom adorable, \n\r Ô Jésus!', 3, 21),
(82, 'Strophe', 4, 'Tu m\'as donné ta douce paix \n\r En me comblant de tes bienfaits; \n\r Par toi j\'ai recouvré la joie, \n\r J\'ai trouvé la céleste voie, \n\r Ô Jésus!', 4, 21),
(83, 'Strophe', 5, 'Et depuis lors, ton nom si doux, \n\r J\'aime le redire à genoux, \n\r Le cacher au fond de mon âme, \n\r Car de toi seul je me réclame, \n\r Ô Jésus!', 5, 21),
(84, 'Strophe', 1, 'Il y avait tant d\'amour, \n\r Quand les anges dans le ciel \n\r Chantaient dans la nuit de Noël, \n\r Aux bergers d\'Israël. \n\r Gloire à Dieu, Hosannah! \n\r Paix vous soit, ne craignez pas, \n\r C\'est à Bethléhem en Judée, \n\r Qu\'un Sauveur est né!', 1, 22),
(85, 'Strophe', 2, 'Il y avait tant d\'amour, \n\r Quand sur les bords du Jourdain \n\r Jésus appelait tous les siens, \n\r À suivre son chemin. \n\r Pêcheur de Galilée, \n\r Pour Jésus ont tout quitté, \n\r Leurs cœurs ont brûlé à sa voix, \n\r Quand il disait: «Suis-moi!»', 2, 22),
(86, 'Strophe', 3, 'Il y avait tant d\'amour, \n\r Sur la croix de Golgotha. \n\r Mon Sauveur a payé pour moi, \n\r Sublime Roi des rois! \n\r Seigneur, ta vie pour moi? \n\r Ma raison n\'y suffit pas, \n\r Comment comprendre tant d\'amour, \n\r Sans ton divin secours?', 3, 22),
(87, 'Strophe', 4, 'Il y avait tant d\'amour, \n\r Jésus est ressuscité! \n\r Auprès de Dieu, il est monté, \n\r Il viendra nous chercher. \n\r Tu nous redis d\'aimer, \n\r D\'aimer et de pardonner, \n\r Trouveras-tu un peu d\'amour, \n\r Au temps de ton retour?', 4, 22),
(88, 'Strophe', 1, 'Jésus, ô nom qui surpasse \n\r Tout nom qu\'on puisse exalter, \n\r Que jamais je ne me lasse, \n\r Nom béni, de te chanter! \n\r Seule clarté qui rayonne \n\r Sur les gloires du saint lieu, \n\r Seul nom dont l\'écho résonne \n\r Dans le cœur même de Dieu!', 1, 23),
(89, 'Strophe', 2, 'Jésus, c\'est l\'amour suprême \n\r De son trône descendu, \n\r Qui ceint de son diadème \n\r Le front de l\'homme perdu. \n\r C\'est le roi qui s\'humilie \n\r Pour vaincre le révolté, \n\r C\'est la divine folie \n\r Dans la divine bonté!', 2, 23),
(90, 'Strophe', 3, 'Qui pleura sur ceux qui pleurent? \n\r C\'est lui, l\'homme méprisé! \n\r Qui mourut pour ceux qui meurent? \n\r C\'est lui, l\'homme au cœur brisé! \n\r De son sang et de ses larmes \n\r Il arrosa son chemin, \n\r Et c\'est par ces seules armes \n\r Qu\'il sauva le genre humain.', 3, 23),
(91, 'Strophe', 4, 'Jésus, par qui Dieu pardonne, \n\r Roi d\'épines couronné, \n\r Que le monde t\'abandonne, \n\r À toi mon cœur s\'est donné! \n\r Ta mort est ma délivrance, \n\r Je suis heureux sous ta loi; \n\r Ô Jésus, mon espérance, \n\r Quel autre aurais-je que toi?', 4, 23),
(92, 'Strophe', 5, 'Attire, ô Sauveur, attire \n\r Sur ton sein et dans tes bras \n\r Le cœur qui tremble et soupire \n\r Parce qu\'il ne te voit pas. \n\r Mon frère, il t\'appelle, écoute! \n\r Il t\'appelle, ne crains plus; \n\r Suis ses pas, et sur la route \n\r Chante ce beau nom, Jésus!', 5, 23),
(93, 'Strophe', 1, 'M\'aimant, quand la route est obscure, \n\r Jésus s\'approche et me rassure. \n\r Il me conduit par sa main sûre, \n\r Dans son amour.', 1, 24),
(94, 'Strophe', 2, 'M\'aimant d\'un amour insondable, \n\r Jésus de la gloire ineffable \n\r M\'apporte joie intarissable, \n\r Par son amour.', 2, 24),
(95, 'Strophe', 3, 'M\'aimant pendant les jours d\'orage, \n\r Quand mon ciel n\'est qu\'un noir nuage, \n\r Jésus me sauve du naufrage, \n\r Dans son amour.', 3, 24),
(96, 'Strophe', 4, 'M\'aimant dans ce monde où tout passe, \n\r Où tout, comme un rêve s\'efface, \n\r Dans son cœur Jésus me fait place, \n\r Dans son amour.', 4, 24),
(97, 'Strophe', 5, 'M\'aimant dans la gloire suprême, \n\r De la crèche à la croix, il m\'aime, \n\r Hier, aujourd\'hui toujours le même, \n\r Dans son amour.', 5, 24),
(98, 'Strophe', 6, 'M\'aimant, oh! Jusqu\'à ta venue, \n\r Quand tu descendras sur la nue, \n\r Nous chantons l\'hymne l\'âme émue, \n\r De ton amour.', 6, 24),
(99, 'Strophe', 1, 'Si du nid tombe l\'oisillon, \n\r Dieu répond à son cri; \n\r Et quand survient le tourbillon, \n\r Il lui fait un abri.', 1, 25),
(100, 'Refrain', 1, 'Il m\'aime aussi, il m\'aime aussi, \n\r Je sais qu\'il m\'aime aussi. \n\r Jésus m\'aime, bien que petit, \n\r La bible me le dit.', 2, 25),
(101, 'Strophe', 2, 'Il a fait le muguet des bois, \n\r Parfumé les taillis; \n\r À la source il prête une voix, \n\r Aux fleurs leur coloris.', 3, 25),
(102, 'Refrain', 1, 'Il m\'aime aussi, il m\'aime aussi, \n\r Je sais qu\'il m\'aime aussi. \n\r Jésus m\'aime, bien que petit, \n\r La bible me le dit.', 4, 25),
(103, 'Strophe', 3, 'Le Seigneur nourrit les oiseaux, \n\r Il aime la fourmi. \n\r Celui qui dit: «Pais mes agneaux» \n\r Est aussi leur ami.', 5, 25),
(104, 'Refrain', 1, 'Il m\'aime aussi, il m\'aime aussi, \n\r Je sais qu\'il m\'aime aussi. \n\r Jésus m\'aime, bien que petit, \n\r La bible me le dit.', 6, 25),
(105, 'Strophe', 1, 'Redites-moi l\'histoire \n\r De l\'amour de Jésus; \n\r Parlez-moi de la gloire \n\r Qu\'il promet aux élus. \n\r J\'ai besoin qu\'on m\'instruise, \n\r Car je suis ignorant, \n\r Qu\'à Christ on me conduise \n\r Comme un petit enfant.', 1, 26),
(106, 'Refrain', 1, 'Redites-moi l\'histoire, \n\r Redites-moi l\'histoire, \n\r Redites-moi l\'histoire de l\'amour de Jésus.', 2, 26),
(107, 'Strophe', 2, 'Redites-moi l\'histoire \n\r De la crèche à la croix; \n\r Éveillez ma mémoire, \n\r Oublieuse parfois. \n\r Cette histoire si belle, \n\r Dites-la simplement, \n\r Elle est toujours nouvelle, \n\r Répétez-la souvent.', 3, 26),
(108, 'Refrain', 1, 'Redites-moi l\'histoire, \n\r Redites-moi l\'histoire, \n\r Redites-moi l\'histoire de l\'amour de Jésus.', 4, 26),
(109, 'Strophe', 3, 'Redites-moi l\'histoire \n\r De mon divin Sauveur; \n\r C\'est lui, dont la victoire \n\r Affranchit le pécheur. \n\r Ce glorieux message, \n\r Oh! redites-le moi, \n\r Lorsque je perds courage, \n\r Lorsque faiblit ma foi.', 5, 26),
(110, 'Refrain', 1, 'Redites-moi l\'histoire, \n\r Redites-moi l\'histoire, \n\r Redites-moi l\'histoire de l\'amour de Jésus.', 6, 26),
(111, 'Strophe', 1, 'D\'un arbre séculaire, \n\r Du vieux tronc d\'Isaï, \n\r Durant l\'hiver austère \n\r Un frais rameau jaillit; \n\r Et sur le sol durci, \n\r Dans la nuit de la terre, \n\r Une rose a fleuri.', 1, 27),
(112, 'Strophe', 2, 'Ainsi prend fin l\'attente \n\r Des premiers serviteurs. \n\r Dieu, par leur voix fervente, \n\r Promettait un Sauveur. \n\r Voici, suprême honneur, \n\r Qu\'il naît de sa servante \n\r La plus humble en son cœur.', 2, 27),
(113, 'Strophe', 3, 'Pauvre et sans apparence, \n\r Ô Jésus, notre roi, \n\r Tu portes nos souffrances \n\r En mourant sur la croix. \n\r Heureux celui qui croit! \n\r Il trouve l\'espérance \n\r Et l\'amour dans la foi.', 3, 27),
(114, 'Strophe', 1, 'D\'un cœur joyeux, chantez! \n\r Un fils nous est donné: \n\r Il est l\'Admirable, \n\r Né dans la pauvreté, \n\r Trésor incomparable, \n\r Salut immérité!', 1, 28),
(115, 'Refrain', 1, 'Gloire au Dieu très-haut! \n\r Gloire au Dieu très-haut!', 2, 28),
(116, 'Strophe', 2, 'Bergers soyez heureux, \n\r Devant le fils de Dieu! \n\r Il vient sur la terre: \n\r Il veut nous délivrer. \n\r Devant ce grand mystère, \n\r Venez pour adorer!', 3, 28),
(117, 'Refrain', 1, 'Gloire au Dieu très-haut! \n\r Gloire au Dieu très-haut!', 4, 28),
(118, 'Strophe', 3, 'Ô mages d\'Orient, \n\r Guidés divinement \n\r Sous l\'étoile ardente, \n\r Voyez l\'enfant qui dort! \n\r Il comble votre attente: \n\r Offrez-lui vos trésors!', 5, 28),
(119, 'Refrain', 1, 'Gloire au Dieu très-haut! \n\r Gloire au Dieu très-haut!', 6, 28),
(120, 'Strophe', 4, 'Lumière et vérité. \n\r Vivante charité, \n\r Clair soleil du monde, \n\r Jésus, soit exalté! \n\r Par toi la grâce abonde \n\r Sur notre humanité.', 7, 28),
(121, 'Refrain', 1, 'Gloire au Dieu très-haut! \n\r Gloire au Dieu très-haut!', 8, 28),
(122, 'Strophe', 1, 'Aujourd\'hui le roi des cieux, \n\r au milieu de la nuit, \n\r Voulut naître chez nous de la Vierge Marie \n\r Pour sauver le genre humain, \n\r l\'arracher au péché, \n\r Ramener au Seigneur ses enfants égarés.', 1, 29),
(123, 'Refrain', 1, 'Noël! Noël! Noël! Noël! \n\r Jésus est né! Emmanuel!', 2, 29),
(124, 'Strophe', 2, 'En ces lieux, durant la nuit, \n\r demeuraient des bergers \n\r Qui gardaient leurs troupeaux dans les champs de Judée. \n\r Tout à coup un messager apparut dans les cieux \n\r Et la gloire de Dieu resplendit autour d\'eux.', 3, 29),
(125, 'Refrain', 1, 'Noël! Noël! Noël! Noël! \n\r Jésus est né! Emmanuel!', 4, 29),
(126, 'Strophe', 3, 'L\'ange dit: Ne craignez pas, \n\r soyez tous dans la joie, \n\r Un Sauveur vous est né, \n\r c\'est le Christ votre roi. \n\r Près d\'ici vous trouverez \n\r un enfant nouveau né, \n\r Dans l\'étable couché, d\'un lange emmailloté.', 5, 29),
(127, 'Refrain', 1, 'Noël! Noël! Noël! Noël! \n\r Jésus est né! Emmanuel!', 6, 29),
(128, 'Strophe', 4, 'Aussitôt s\'emplit le ciel de lumière \n\r et de chants, \n\r D\'une armée d\'anges blancs louant Dieu \n\r et disant: Gloire à Dieu dans les hauteurs et sur terre la paix \n\r Pour les humains sur qui Dieu répand ses bienfaits!', 7, 29),
(129, 'Refrain', 1, 'Noël! Noël! Noël! Noël! \n\r Jésus est né! Emmanuel!', 8, 29),
(130, 'Strophe', 1, 'Devant ta crèche, tu me vois, \n\r Penché sur ton visage. \n\r Tout ce que j\'ai, je le dois, \n\r Je veux t\'en faire hommage, \n\r Prends-moi tout entier, prends mon cœur, \n\r À toi, Jésus, divin Sauveur, \n\r J\'appartiens sans partage.', 1, 30),
(131, 'Strophe', 2, 'Perdu dans l\'ombre de la mort, \n\r Je pleurais de détresse; \n\r Mais ton amour change mon sort, \n\r Dissipe ma tristesse. \n\r Tu viens pour m\'attacher à toi, \n\r Et dès ce jour tu mets en moi \n\r Ta joie et ta richesse.', 2, 30),
(132, 'Strophe', 3, 'Jésus, jette un regard sur moi! \n\r Accorde-moi la grâce \n\r De vivre et de mourir en toi, \n\r De contempler ta face. \n\r Choisi mon cœur pour ton séjour; \n\r Qu\'il t\'appartienne et, chaque jour, \n\r Reviens y prendre place.', 3, 30),
(133, 'Strophe', 1, 'Gloire au Seigneur! Il est venu. \n\r Ô terre soit en paix! \n\r Que notre cœur, lui fasse place. \n\r Nature et cieux, chantez, \n\r Nature et cieux, chantez, \n\r Nature et cieux, dansez, chantez.', 1, 31),
(134, 'Strophe', 2, 'Gloire au Seigneur! Il règne en Roi! \n\r Mon chant s\'élève à lui. \n\r Vers tes campagnes, collines et plaines, \n\r Joignez-vous au concert, \n\r Joignez-vous au concert, \n\r Joignez-vous tous à ce concert.', 2, 31),
(135, 'Strophe', 3, 'Grâce et droiture sont unies \n\r Pour prendre le relais. \n\r De sa justice toujours bienfaisante. \n\r Goûtons tous son amour, \n\r Goûtons tous son amour, \n\r Goûtons ensemble son amour.', 3, 31),
(136, 'Strophe', 1, 'Le Fils de Dieu, le roi de gloire \n\r À voulu naître parmi nous. \n\r Il est venu sur notre terre \n\r Au temps marqué par son amour.', 1, 32),
(137, 'Refrain', 1, 'Dès aujourd\'hui ton royaume est proche, \n\r Viens parmi nous, Seigneur Jésus.', 2, 32),
(138, 'Strophe', 2, 'Ceux qui marchaient dans les ténèbres \n\r Ont vu s\'illuminer leur nuit, \n\r Et sur les peuples dans l\'angoisse \n\r Une lumière a resplendi.', 3, 32),
(139, 'Refrain', 1, 'Dès aujourd\'hui ton royaume est proche, \n\r Viens parmi nous, Seigneur Jésus.', 4, 32),
(140, 'Strophe', 3, 'Dans notre nuit surgit l\'aurore \n\r De sa justice et de sa paix: \n\r Dieu enverra sa délivrance, \n\r Il ne nous quittera jamais.', 5, 32),
(141, 'Refrain', 1, 'Dès aujourd\'hui ton royaume est proche, \n\r Viens parmi nous, Seigneur Jésus.', 6, 32),
(142, 'Strophe', 4, 'Il a tracé pour nous la route, \n\r Il s\'est chargé de nos douleurs; \n\r Et nous marchons vers sa promesse, \n\r Le front marqué de sa splendeur.', 7, 32),
(143, 'Refrain', 1, 'Dès aujourd\'hui ton royaume est proche, \n\r Viens parmi nous, Seigneur Jésus.', 8, 32),
(144, 'Strophe', 5, 'Quand paraîtra la ville sainte, \n\r Quand s\'ouvriront les cieux nouveaux, \n\r Nous goûterons sans fin la vie \n\r Devant le trône de l\'Agneau.', 9, 32),
(145, 'Refrain', 1, 'Dès aujourd\'hui ton royaume est proche, \n\r Viens parmi nous, Seigneur Jésus.', 10, 32),
(146, 'Strophe', 1, 'L\'enfant est né à Bethléhem, \n\r Alléluia! \n\r Réjouis-toi, Jérusalem, \n\r Alléluia, Alléluia!', 1, 33),
(147, 'Strophe', 2, 'Au ciel les anges vont chantant, \n\r Alléluia! \n\r Pour annoncer le saint enfant, \n\r Alléluia, Alléluia!', 2, 33),
(148, 'Strophe', 3, 'Le Sauveur est né aujourd\'hui, \n\r Alléluia! \n\r Et les bergers s\'en vont à lui, \n\r Alléluia, Alléluia!', 3, 33),
(149, 'Strophe', 4, 'Dans l\'humble étable ils sont entrés, \n\r Alléluia! \n\r Avec leur seule pauvreté, \n\r Alléluia, Alléluia!', 4, 33),
(150, 'Strophe', 1, 'Mon âme exalte le grand Dieu, \n\r Magnifiant sa sainteté. \n\r Malgré ma grande humilité, \n\r Il a sur moi jeté les yeux.', 1, 34),
(151, 'Strophe', 2, 'Voici, les siècles désormais \n\r De mon bonheur iront chantant: \n\r Pour elle, Dieu, le Tout-Puissant \n\r A mis le comble à ses bienfaits!', 2, 34),
(152, 'Strophe', 3, 'Saint est son Nom, de sa bonté \n\r Au cours des temps on parlera. \n\r Oui, par la force de son bras, \n\r Dieu s\'est partout manifesté.', 3, 34),
(153, 'Strophe', 4, 'S\'il a déçu les orgueilleux, \n\r Anéanti leurs fiers desseins, \n\r De l\'humble, il a nourri la faim, \n\r Et relevé le cœur pieux.', 4, 34),
(154, 'Strophe', 5, 'Il se souvient du peuple élu, \n\r Comme il l\'avait toujours promis. \n\r En nous donnant enfin son Fils, \n\r Dieu vers les siens est descendu.', 5, 34),
(155, 'Strophe', 6, 'Louange et gloire au Dieu parfait, \n\r Gloire à Jésus, Seigneur et Christ, \n\r Louange et gloire au Saint-Esprit, \n\r Dès maintenant à tout jamais.', 6, 34),
(156, 'Strophe', 1, 'Nuit lumineuse, Nuit bienheureuse, \n\r Nuit où Christ descend des cieux! \n\r Ô saint mystère: Il vient sur terre, \n\r Jésus-Christ, le Fils de Dieu! \n\r Chante mon âme, Chante et proclame', 1, 35),
(157, 'Strophe', 1, '(Suite) L\'immense joie Que Dieu t\'envoie: \n\r Christ est ton maître. \n\r Alléluia! \n\r Plus de tristesse, Plus de détresse, \n\r Sois sans alarmes Sèche tes larmes: \n\r Vois-le paraître! Alléluia!', 2, 35),
(158, 'Strophe', 2, 'Paix sur la terre: \n\r Dieu notre Père \n\r Donne aux siens l\'enfant Jésus. \n\r Gloire et louange, Chantent les anges, \n\r Gloire, honneur au Fils élu!', 3, 35),
(159, 'Strophe', 2, '(Suite) Nuit mémorable, Dans une étable, \n\r Pâtres et mages, Pauvres et sages \n\r Sont là qui prient: Alléluia! \n\r Pleins de tendresse, Pleins d\'allégresse, \n\r Peuple fidèle, Quand Dieu t\'appelle, \n\r Jubile et crie: Alléluia!', 4, 35),
(160, 'Strophe', 1, 'Ô nuit bienveillante, \n\r Ô nuit rassurante, \n\r Douce nuit du premier Noël! \n\r Jésus, ô mystère, \n\r Naît sur cette terre. \n\r Chantons, chantons à l\'Éternel.', 1, 36),
(161, 'Strophe', 2, 'Ô nuit bienveillante, \n\r Ô nuit rassurante, \n\r Douce nuit du premier Noël! \n\r Jésus nous délivre, \n\r Jésus nous fait vivre. \n\r Chantons, chantons à l\'Éternel.', 2, 36),
(162, 'Strophe', 3, 'Ô nuit bienveillante, \n\r Ô nuit rassurante, \n\r Douce nuit du premier Noël! \n\r Qu\'au saint Roi des anges \n\r Montent nos louanges. \n\r Chantons, chantons à l\'Éternel.', 3, 36),
(163, 'Strophe', 4, 'Ô nuit bienveillante, \n\r Ô nuit rassurante, \n\r Douce nuit du premier Noël! \n\r Jésus faible et pauvre, \n\r Nous aime et nous sauve. \n\r Chantons, chantons à l\'Éternel.', 4, 36),
(164, 'Strophe', 1, 'Ô peuple fidèle, Jésus vous appelle, \n\r Venez, triomphants, joyeux, \n\r Venez en ces lieux. \n\r Ô peuple fidèle, Venez voir le roi des cieux. \n\r Que votre amour l\'implore, \n\r Que votre foi l\'adore, \n\r Et qu\'elle chante encore ce don précieux.', 1, 37),
(165, 'Strophe', 2, 'Là, dans l\'humble étable, Froide et misérable, \n\r Des bergers le grand amour \n\r Lui forme une cour. \n\r Dans cette humble étable accourez à votre tour. \n\r Que votre amour l\'implore, \n\r Que votre foi l\'adore, \n\r Et qu\'elle chante encore sa gloire en ce jour.', 2, 37),
(166, 'Strophe', 3, 'Esprits de lumière, Messagers du Père, \n\r Et vous, peuples, en tous lieux \n\r Entonnez, joyeux: Paix sur cette terre, \n\r Et gloire à Dieu dans les cieux. \n\r Que votre amour l\'implore, \n\r Que votre foi l\'adore, \n\r Et qu\'elle chante encore ce don précieux.', 3, 37),
(167, 'Strophe', 1, 'Ouvrez les portes du saint lieu! \n\r Voici venir le Fils de Dieu! \n\r Son front, de grâce est couronné, \n\r Son sceptre, c\'est la sainteté. \n\r Il vient porter la guérison, \n\r Comme un soleil dans ses rayons. \n\r Voici, divin Sauveur, \n\r Jésus le Rédempteur.', 1, 38),
(168, 'Strophe', 2, 'Il est le Fils, le Bien-aimé, \n\r Que Dieu choisit pour nous sauver. \n\r Heureux le peuple et le pays \n\r Par qui ce prince est accueilli! \n\r À tous il veut donner la paix. \n\r Son règne vienne pour jamais! \n\r Voici, divin Sauveur, \n\r Jésus le Rédempteur.', 2, 38),
(169, 'Strophe', 3, 'Ouvrez les portes du saint lieu! \n\r Voici venir le fils de Dieu! \n\r Pour temple saint, il veut nos cœurs: \n\r Il doit en être seul Seigneur! \n\r Sa grâce nous délivrera, \n\r Et son Esprit nous conduira, \n\r L\'Esprit du Créateur, \n\r L\'Esprit du Dieu Sauveur!', 3, 38),
(170, 'Strophe', 1, 'Sur la paille fraîche dort l\'enfant si beau; \n\r Une pauvre crèche lui sert de berceau. \n\r De l\'azur céleste, l\'étoile a souri \n\r À l\'enfant qui reste si tard endormi.', 1, 39),
(171, 'Strophe', 2, 'Du trône de gloire dans la sombre nuit, \n\r Pour notre victoire, il est descendu. \n\r Cher Jésus, je t\'aime, \n\r Oh! Viens dans mon cœur, \n\r Et veille toi-même Sur moi, doux Sauveur.', 2, 39),
(172, 'Strophe', 3, 'Près, plus près encore \n\r De moi, pour toujours, \n\r Jésus, je t\'implore, J\'attends ton secours. \n\r Écoute ma prière, tu me l\'as promis, \n\r Et viens pour nous prendre dans ton paradis.', 3, 39),
(173, 'Strophe', 1, 'Un bel enfant s\'est endormi \n\r Dans les bras de Marie, \n\r Des anges puissants près de lui, \n\r De simples bergers veillent.', 1, 40),
(174, 'Refrain', 1, 'C\'est bien lui, le Christ, le Roi \n\r Chanté par tous les anges saints. \n\r En lui, placez votre foi, \n\r À vos douleurs, il met fin.', 2, 40),
(175, 'Strophe', 2, 'Pourquoi ce manque de confort, \n\r Autour de lui ces bêtes? \n\r Croyez que, malgré ce décor, \n\r C\'est un grand jour de fête.', 3, 40),
(176, 'Refrain', 1, 'C\'est bien lui, le Christ, le Roi \n\r Chanté par tous les anges saints. \n\r En lui, placez votre foi, \n\r À vos douleurs, il met fin. \n\r nos peines.', 4, 40),
(177, 'Strophe', 3, 'Portez-lui myrrhe, encens et or, \n\r Car ces biens lui reviennent. \n\r Ce Roi partage notre sort, \n\r Il enlève', 5, 40),
(178, 'Refrain', 1, 'C\'est bien lui, le Christ, le Roi \n\r Chanté par tous les anges saints. \n\r En lui, placez votre foi, \n\r À vos douleurs, il met fin. \n\r nos peines.', 6, 40),
(179, 'Strophe', 1, 'Un enfant est né Qui n\'a pas même un toit. \n\r Un enfant est né, sans tapage et sans bruit. \n\r Tout au bout de la nuit, un enfant est né: \n\r La fleur de notre joie!', 1, 41),
(180, 'Refrain', 1, 'Un enfant est né, \n\r Bergers, réveillez vos bêtes. \n\r Un enfant est né, \n\r Bergers, venez à la fête.', 2, 41),
(181, 'Strophe', 2, 'Un enfant est né, une étoile en ses yeux. \n\r Un enfant est né, il n\'a pas de berceau: \n\r L\'étable est son château. \n\r Un enfant est né: \n\r Le fils de notre Dieu.', 3, 41),
(182, 'Refrain', 1, 'Un enfant est né, \n\r Bergers, réveillez vos bêtes. \n\r Un enfant est né, \n\r Bergers, venez à la fête.', 4, 41),
(183, 'Strophe', 3, 'Un enfant est né, Dieu habite avec nous. \n\r Un enfant est né, Dieu a pris froid et faim. \n\r Pour nous tendre la main, \n\r Un enfant est né: \n\r Un pauvre sans le sou.', 5, 41),
(184, 'Refrain', 1, 'Un enfant est né, \n\r Bergers, réveillez vos bêtes. \n\r Un enfant est né, \n\r Bergers, venez à la fête.', 6, 41),
(185, 'Strophe', 1, 'Venez, c\'est Noël, accourez vers l\'Enfant! \n\r Lumière espérée, le Seigneur nous attend. \n\r Voyez dans la crèche le Fils nouveau né; \n\r Le Dieu de tendresse aujourd\'hui l\'a donné.', 1, 42),
(186, 'Strophe', 2, 'Il vient comme un pauvre au milieu de la nuit, \n\r Lumière de joie pour Joseph et Marie. \n\r Sa mère en silence bénit le très haut: \n\r Le Verbe fait chair dans ce monde est éclos.', 2, 42),
(187, 'Strophe', 3, 'Les anges pour lui font sonner \n\r «Gloire à Dieu». \n\r Lumière de paix sur la terre et aux cieux! \n\r Voici que s\'éveille le chant des bergers; \n\r L\'étable est remplie de nouvelles clartés.', 3, 42),
(188, 'Strophe', 4, 'Jésus, Fils de Dieu, tu reviens parmi nous, \n\r Lumière d\'espoir quand nos yeux voient ton jour. \n\r Que brille l\'étoile au profond de nos cœurs! \n\r L\'amour y naîtra comme un fruit du Sauveur.', 4, 42),
(189, 'Strophe', 1, 'Au matin dans la clarté, \n\r Jésus est ressuscité. \n\r Au matin dans la clarté, \n\r De la tombe il s\'est levé. \n\r Chantons Alléluia! \n\r Christ est vainqueur de la mort, \n\r Christ, notre espoir est en toi!', 1, 43),
(190, 'Strophe', 2, 'Il nous a montré ses mains, \n\r On l\'a vu dans le jardin. \n\r Il nous a montré ses mains, \n\r Il a partagé le pain. \n\r Chantons Alléluia! \n\r Christ est vainqueur de la mort, \n\r Christ, fait nous vivre de toi!', 2, 43),
(191, 'Strophe', 3, 'Il nous trace le chemin, \n\r Il nous donne l\'Esprit Saint. \n\r Il nous trace le chemin, \n\r Et nous ne serons plus qu\'un. \n\r Chantons Alléluia! \n\r Christ est vainqueur de la mort, \n\r Christ, emplis-nous de ta joie!', 3, 43),
(192, 'Refrain', 1, 'Aujourd\'hui, Dieu te fait prophète, \n\r Aujourd\'hui tu es son témoin. \n\r L\'Esprit saint sur toi repose, \n\r Va porter la Bonne Nouvelle, \n\r Va porter la Bonne Nouvelle!', 1, 44),
(193, 'Strophe', 1, 'Parole pour l\'aveugle: \n\r Dieu guérit les yeux blessés. \n\r Il donne à tous les peuples \n\r de marcher vers sa clarté. \n\r Par Jésus l\'aurore luit, la lumière en nous fleurit.', 2, 44),
(194, 'Refrain', 1, 'Aujourd\'hui, Dieu te fait prophète, \n\r Aujourd\'hui tu es son témoin. \n\r L\'Esprit saint sur toi repose, \n\r Va porter la Bonne Nouvelle, \n\r Va porter la Bonne Nouvelle!', 3, 44),
(195, 'Strophe', 2, 'Parole de confiance: \n\r Dieu nous aime et nous connaît. \n\r Quel sourd en sa présence n\'entendrait des mots de paix? \n\r Par Jésus qui nous conduit, \n\r le Royaume se construit.', 4, 44),
(196, 'Refrain', 1, 'Aujourd\'hui, Dieu te fait prophète, \n\r Aujourd\'hui tu es son témoin. \n\r L\'Esprit saint sur toi repose, \n\r Va porter la Bonne Nouvelle, \n\r Va porter la Bonne Nouvelle!', 5, 44),
(197, 'Strophe', 3, 'Paroles pour les pauvres: \n\r Dieu libère nos prisons. \n\r Sauveur de tous les hommes, \n\r il relève nos maisons. \n\r Par Jésus brisant la nuit, \n\r notre Pâque s\'accomplit.', 6, 44),
(198, 'Refrain', 1, 'Aujourd\'hui, Dieu te fait prophète, \n\r Aujourd\'hui tu es son témoin. \n\r L\'Esprit saint sur toi repose, \n\r Va porter la Bonne Nouvelle, \n\r Va porter la Bonne Nouvelle!', 7, 44),
(199, 'Strophe', 4, 'Parole pour le monde: \n\r Dieu prépare son festin. \n\r Sa table surabonde, \n\r accourez sur vos chemins. \n\r Par Jésus, Soleil de vie, \n\r notre terre porte fruit!', 8, 44),
(200, 'Refrain', 1, 'Aujourd\'hui, Dieu te fait prophète, \n\r Aujourd\'hui tu es son témoin. \n\r L\'Esprit saint sur toi repose, \n\r Va porter la Bonne Nouvelle, \n\r Va porter la Bonne Nouvelle!', 9, 44),
(201, 'Strophe', 1, 'Christ est venu, Christ est né. \n\r Christ a souffert, Christ est mort. \n\r Christ est ressuscité, Christ est vivant. \n\r Christ reviendra, Christ est là. \n\r Christ reviendra, Christ est là.', 1, 45),
(202, 'Strophe', 1, 'Comment te reconnaître, \n\r Comment te recevoir, \n\r Toi qui seul est le Maître \n\r Mais que nul ne peut voir? \n\r Seigneur qui fit le monde, \n\r Tu vins un jour vers nous, \n\r Tu guides notre histoire \n\r Vers ton règne sans fin.', 1, 46),
(203, 'Strophe', 2, 'Tu fus un enfant pauvre \n\r Naissant à Bethléhem, \n\r Tu fus un roi sans gloire \n\r Devant Jérusalem. \n\r Nos yeux sont ouverts dans la nuit \n\r Guettant ton arrivée; \n\r Nos chants seront des branches \n\r De tant de joie chargées.', 2, 46),
(204, 'Strophe', 3, 'Tu donnes pour le monde \n\r Un avenir si beau; \n\r Et ta promesse fonde \n\r L\'espoir d\'un renouveau. \n\r Dans la vie de tout homme, \n\r Tu es le compagnon, \n\r Et la joie la plus vive \n\r Demeure dans ton nom.', 3, 46),
(205, 'Strophe', 4, 'Nous tous pour te connaître \n\r Et pour te saluer, \n\r Nous gardons ta promesse, \n\r Chantons ta royauté. \n\r Elle est cachée en ton amour, \n\r Nos noms sont tous écrits \n\r Au cœur de l\'Évangile \n\r Où parle ton Esprit.', 4, 46),
(206, 'Strophe', 1, 'Dans les cieux et sur la terre, \n\r Il n\'est aucun nom plus doux, \n\r Aucun que mon cœur préfère, \n\r Au nom de Christ mort pour nous.', 1, 47),
(207, 'Refrain', 1, 'Quel beau nom! \n\r Quel beau nom \n\r Porte l\'Oint de l\'Éternel; \n\r Quel beau nom! \n\r Quel beau nom \n\r Que celui d\'Emmanuel!', 2, 47),
(208, 'Strophe', 2, 'Quelque grand que soit un homme, \n\r Qu\'il soit prince ou qu\'il soit roi. \n\r De quelque nom qu\'on le nomme, \n\r Jésus est plus grand pour moi.', 3, 47),
(209, 'Refrain', 1, 'Quel beau nom! \n\r Quel beau nom \n\r Porte l\'Oint de l\'Éternel; \n\r Quel beau nom! \n\r Quel beau nom \n\r Que celui d\'Emmanuel!', 4, 47),
(210, 'Strophe', 3, 'Les séraphins, les archanges \n\r Portent des noms glorieux, \n\r Mais le plus beau nom des anges \n\r Pourrait-il me rendre heureux?', 5, 47),
(211, 'Refrain', 1, 'Quel beau nom! \n\r Quel beau nom \n\r Porte l\'Oint de l\'Éternel; \n\r Quel beau nom! \n\r Quel beau nom \n\r Que celui d\'Emmanuel!', 6, 47),
(212, 'Strophe', 4, 'Dans les maux, Jésus soulage, \n\r Il guérit l\'esprit froissé, \n\r Il ranime le courage \n\r Du cœur le plus oppressé.', 7, 47),
(213, 'Refrain', 1, 'Quel beau nom! \n\r Quel beau nom \n\r Porte l\'Oint de l\'Éternel; \n\r Quel beau nom! \n\r Quel beau nom \n\r Que celui d\'Emmanuel!', 8, 47),
(214, 'Strophe', 5, 'De quelle joie il inonde \n\r Le cœur du pauvre pécheur! \n\r Non, il n\'est rien en ce monde \n\r De pareil à ce bonheur!', 9, 47),
(215, 'Refrain', 1, 'Quel beau nom! \n\r Quel beau nom \n\r Porte l\'Oint de l\'Éternel; \n\r Quel beau nom! \n\r Quel beau nom \n\r Que celui d\'Emmanuel!', 10, 47),
(216, 'Strophe', 1, 'Jésus, Jésus, Jésus, Jésus, \n\r Seul nom que mon cœur aime, \n\r Je voudrais ne prononcer plus \n\r Qu\'un seul mot, ce nom même.', 1, 48),
(217, 'Strophe', 2, 'Jésus, Jésus, ce nom si beau, \n\r C\'est celui dont les anges, \n\r Prosternés autour de l\'Agneau, \n\r Remplissent leurs louanges.', 2, 48),
(218, 'Strophe', 3, 'Jésus, c\'est le nom de Celui \n\r Qui descendit du Père, \n\r Dont l\'amour sur la croix a lui, \n\r En qui la terre espère.', 3, 48),
(219, 'Strophe', 4, 'Jésus, c\'est le nom du Sauveur \n\r Favorable à toute âme, \n\r Et qu\'aujourd\'hui plus d\'un pécheur \n\r De tout son cœur acclame.', 4, 48),
(220, 'Strophe', 5, 'Jésus, Jésus, Jésus, Jésus, \n\r Bientôt mon cœur qui t\'aime, \n\r Auprès de toi ne dira plus \n\r Qu\'un seul mot: ton nom même.', 5, 48),
(221, 'Strophe', 1, 'Les cieux et la terre Célèbrent en chœur \n\r La gloire du Père, Du Dieu créateur. \n\r Qu\'il est redoutable Dans sa majesté! \n\r Qu\'il est admirable Dans sa charité!', 1, 49),
(222, 'Strophe', 2, 'C\'est lui qui nous donne \n\r Le printemps joyeux, \n\r Les fruits de l\'automne, L\'été radieux. \n\r Largesse infinie Que rien ne tarit, \n\r Sa main rassasie Tout être qui vit.', 2, 49),
(223, 'Strophe', 3, 'La vive lumière Des cieux enflammés, \n\r L\'ombre et le mystère \n\r Des bois embaumés, Le flot qui murmure, \n\r Le jour et la nuit, Tout dans la nature \n\r Nous parle de lui.', 3, 49),
(224, 'Strophe', 4, 'Mais, ô Dieu suprême, \n\r Plus que tous tes dons, \n\r C\'est ton amour même Que nous adorons. \n\r Ô source éternelle De grâce et de paix, \n\r Ton peuple fidèle Te loue à jamais.', 4, 49),
(225, 'Refrain', 1, 'Laissez venir à moi tous les petits enfants, \n\r Car le royaume des cieux est pour ceux qui leur ressemblent. \n\r Laissez venir à moi tous les petits enfants, \n\r Car le royaume des cieux est pour ceux qui leur ressemblent.', 1, 50),
(226, 'Strophe', 1, 'Non, Naomi, Tu ne peux pas t\'approcher, \n\r Ton enfant est trop petit, \n\r Tu vas le déranger. \n\r Mais Simon Pierre, Mon enfant est endormi, \n\r Quel bruit peut-il faire? \n\r Ne peut-il être béni?', 2, 50),
(227, 'Refrain', 1, 'Laissez venir à moi tous les petits enfants, \n\r Car le royaume des cieux est pour ceux qui leur ressemblent. \n\r Laissez venir à moi tous les petits enfants, \n\r Car le royaume des cieux est pour ceux qui leur ressemblent.', 3, 50),
(228, 'Strophe', 2, 'Femme approche-toi, Dit Jésus, tendant les mains. \n\r Tu as eu beaucoup de foi, \n\r C\'est pourquoi je te dis: Viens! \n\r Enfants de toute la terre, Vous qui êtes humbles et doux, \n\r Vous êtes bénis de mon Père, \n\r Mon Royaume est pour vous.', 4, 50),
(229, 'Refrain', 1, 'Laissez venir à moi tous les petits enfants, \n\r Car le royaume des cieux est pour ceux qui leur ressemblent. \n\r Laissez venir à moi tous les petits enfants, \n\r Car le royaume des cieux est pour ceux qui leur ressemblent.', 5, 50),
(230, 'Strophe', 1, 'Ma richesse, ma gloire \n\r Et ma félicité, \n\r Ô Jésus, c\'est de croire \n\r Que tu m\'as racheté. \n\r Rempli de confiance \n\r En toi, divin Sauveur, \n\r Je vis dans l\'espérance \n\r Et je marche sans peur.', 1, 51),
(231, 'Strophe', 2, 'Rien n\'ôte les souillures, \n\r Rien ne guérit le cœur, \n\r Sinon les meurtrissures \n\r Et le sang du Sauveur. \n\r Oui, Jésus seul nous lave \n\r De toute iniquité, \n\r Et procure à l\'esclave \n\r L\'heureuse liberté.', 2, 51),
(232, 'Strophe', 3, 'Oh! Que toute la terre \n\r Apporte à ce Sauveur \n\r Ses péchés, sa misère! \n\r Il fait grâce au pécheur. \n\r Son amour insondable \n\r Lui fit quitter le ciel, \n\r Pour donner au coupable \n\r Un salut éternel.', 3, 51),
(233, 'Strophe', 1, 'Nous avons vu les pas de notre Dieu \n\r Tracer sur notre terre \n\r Pour les enfants, les jeunes et les vieux, \n\r Une route de lumière. \n\r Reviendra-t-il marcher sur nos chemins, \n\r Changer nos cœurs de pierre? \n\r Reviendra-t-il semer au creux des mains \n\r L\'amour et la lumière?', 1, 52),
(234, 'Strophe', 2, 'Nous avons vu danser les malheureux \n\r Comme aux jours de la fête, \n\r Et le bonheur qui passait dans leur yeux \n\r Leur faisait lever la tête. \n\r Reviendra-t-il marcher sur nos chemins, \n\r Changer nos cœurs de pierre? \n\r Reviendra-t-il semer au creux des mains \n\r L\'amour et la lumière?', 2, 52),
(235, 'Strophe', 3, 'Nous avons vu les riches s\'en aller \n\r Le cœur et les mains vides, \n\r Et les mendiants venir se régaler \n\r Au retour de l\'enfant prodigue. \n\r Reviendra-t-il marcher sur nos chemins, \n\r Changer nos cœurs de pierre? \n\r Reviendra-t-il semer au creux des mains \n\r L\'amour et la lumière?', 3, 52),
(236, 'Strophe', 4, 'Nous avons vu fleurir en plein hiver \n\r Les fleurs de la tendresse; \n\r Nous avons vu jaillir en plein désert \n\r Une source d\'allégresse. \n\r Tu reviendras marcher sur nos chemins, \n\r Changer nos cœurs de pierre. \n\r Tu reviendras semer au creux des mains \n\r L\'amour et la lumière.', 4, 52),
(237, 'Strophe', 1, 'Nous voulons par nos cantiques, \n\r Te rendre gloire, ô Fils unique, \n\r Fils éternel du Dieu des cieux. \n\r De toi vient la délivrance; \n\r Ta mort nous donne l\'assurance \n\r De notre salut glorieux.', 1, 53),
(238, 'Refrain', 1, 'Louange, empire, honneur, \n\r À toi puissant Sauveur, alléluia! \n\r À toi, Jésus, le roi des rois, \n\r Qui pour nous mourus sur la croix!', 2, 53),
(239, 'Strophe', 2, 'Jésus, prince de la vie, \n\r Que tout en nous te glorifie, \n\r Que tout proclame ton amour! \n\r C\'est dans ta grâce éternelle \n\r Que notre foi se renouvelle \n\r Et s\'affermit de jour en jour.', 3, 53),
(240, 'Refrain', 1, 'Louange, empire, honneur, \n\r À toi puissant Sauveur, alléluia! \n\r À toi, Jésus, le roi des rois, \n\r Qui pour nous mourus sur la croix!', 4, 53),
(241, 'Strophe', 3, 'Seigneur, nous te rendons gloire, \n\r Toi dont la mort et la victoire \n\r Ont fait de nous des rachetés. \n\r Délivrés par tes blessures, \n\r Et guéris par tes meurtrissures, \n\r Nous adorons ta charité.', 5, 53),
(242, 'Refrain', 1, 'Louange, empire, honneur, \n\r À toi puissant Sauveur, alléluia! \n\r À toi, Jésus, le roi des rois, \n\r Qui pour nous mourus sur la croix!', 6, 53),
(243, 'Strophe', 1, 'Ô Dieu, tout-puissant créateur, \n\r Tu deviens homme et serviteur, \n\r Et pour nous sauver de la mort, \n\r Tu viens partager notre sort.', 1, 54),
(244, 'Strophe', 2, 'Pour compagnons tu a choisi \n\r Les pauvres de tous les pays, \n\r Pour serviteurs tu recevras \n\r De tous les peuples, tous les rois.', 2, 54),
(245, 'Strophe', 3, 'Heureux nous sommes en ce jour \n\r D\'être au pouvoir de ton amour, \n\r Heureux serons-nous à jamais \n\r D\'être au royaume de la paix.', 3, 54),
(246, 'Strophe', 1, 'Oh! Donnez-moi Jésus, \n\r Le Sauveur des perdus, \n\r L\'ami tant désiré, \n\r De mon cœur altéré! \n\r Il n\'est point de bonheur, \n\r Sans ce divin Sauveur, \n\r Aussi, je ne veux plus \n\r Que le nom de Jésus!', 1, 55),
(247, 'Refrain', 1, 'Nom si beau, si pur, \n\r Nom si merveilleux! \n\r Son éclat remplit les cieux! \n\r J\'ai trouvé le bonheur, \n\r J\'ai trouvé le salut, \n\r Par le seul nom de Jésus!', 2, 55),
(248, 'Strophe', 2, 'Qu\'est tout l\'or et l\'argent, \n\r Qu\'est le plus beau présent? \n\r Que sont tous les honneurs, \n\r Si non des biens trompeurs? \n\r Dans l\'immense univers, \n\r Rien ne m\'est aussi cher, \n\r Rien n\'apaise mon cœur, \n\r Que Jésus, mon Sauveur!', 3, 55),
(249, 'Strophe', 3, 'Le plus beau lys des champs \n\r N\'est pas si ravissant; \n\r Du miel, de sa douceur, \n\r Il passe la saveur! \n\r Tel est mon bien-aimé, \n\r Qui pour moi s\'est donné, \n\r Je ne puis un seul jour, \n\r Vivre sans son amour!', 4, 55),
(250, 'Strophe', 4, 'Les discours du prochain, \n\r Les assauts du malin, \n\r Ne sauraient m\'ébranler, \n\r Christ est mon bouclier! \n\r Il est mon chef, mon roi, \n\r Et je puis, par la foi, \n\r Du monde et de l\'erreur, \n\r Être plus que vainqueur!', 5, 55),
(251, 'Strophe', 1, 'Quel ami fidèle et tendre! \n\r Nous avons en Jésus-Christ, \n\r Toujours prêt à nous entendre \n\r À répondre à notre cri! \n\r Il connaît nos défaillances, \n\r Nos chutes de chaque jour. \n\r Sévère en ses exigences, \n\r Il est riche en son amour.', 1, 56);
INSERT INTO `strophe` (`idStrophe`, `type`, `identifiant`, `texte`, `position`, `idChant`) VALUES
(252, 'Strophe', 2, 'Quel ami fidèle et tendre! \n\r Nous avons en Jésus-Christ, \n\r Toujours prêt à nous comprendre \n\r Quand nous sommes en souci! \n\r Disons-lui toutes nos craintes, \n\r Ouvrons-lui tout notre cœur. \n\r Bientôt ses paroles saintes \n\r Nous rendront le vrai bonheur.', 2, 56),
(253, 'Strophe', 3, 'Quel ami fidèle et tendre! \n\r Nous avons en Jésus-Christ, \n\r Toujours prêt à nous défendre \n\r Quand nous presse l\'ennemi! \n\r Il nous suit dans la mélée, \n\r Nous entoure de ses bras, \n\r Et c\'est lui qui tient l\'épée \n\r Qui décide des combats.', 3, 56),
(254, 'Strophe', 4, 'Quel ami fidèle et tendre! \n\r Nous avons en Jésus-Christ, \n\r Toujours prêt à nous apprendre \n\r À vaincre en comptant sur lui. \n\r S\'il nous voit vrais et sincères \n\r À chercher la sainteté, \n\r Il écoute nos prières \n\r Et nous met en liberté.', 4, 56),
(255, 'Strophe', 5, 'Quel ami fidèle et tendre! \n\r Nous avons en Jésus-Christ, \n\r Bientôt il viendra nous prendre \n\r Pour être au ciel avec lui. \n\r Suivons donc l\'étroite voie \n\r En comptant sur son secours. \n\r Bientôt nous aurons la joie \n\r De vivre avec lui toujours!', 5, 56),
(256, 'Strophe', 1, 'Jésus-Christ est ma sagesse, \n\r Il éclaire mon chemin, \n\r Et je marche, en ma faiblesse, \n\r Conduit par sa sûre main. \n\r Tu éclaires mon chemin, \n\r Tu éclaires mon chemin, \n\r Et je marche, en ma faiblesse, \n\r Conduit par ta sûre main.', 1, 57),
(257, 'Strophe', 2, 'Jésus-Christ est ma justice; \n\r Son sang a coulé pour moi; \n\r Je trouve en son sacrifice \n\r Paix et pardon par la foi. \n\r Ton sang a coulé pour moi, \n\r Ton sang a coulé pour moi, \n\r Je trouve en ton sacrifice \n\r Paix et pardon par la foi.', 2, 57),
(258, 'Strophe', 3, 'Jésus-Christ me sanctifie; \n\r Au divin Cep attaché, \n\r Je reçois de lui la vie \n\r Qui m\'affranchit du péché. \n\r À toi, Jésus, attaché, \n\r À toi, Jésus, attaché, \n\r Je reçois de toi la vie \n\r Qui m\'affranchit du péché.', 3, 57),
(259, 'Strophe', 4, 'Jésus, en payant ma dette, \n\r À grand prix m\'a racheté, \n\r Et déjà ma place est prête \n\r Vers lui, pour l\'éternité. \n\r Jésus, tu m\'as racheté, \n\r Jésus, tu m\'as racheté, \n\r Et déjà ma place est prête \n\r Vers toi, pour l\'éternité.', 4, 57),
(260, 'Strophe', 1, 'En Jésus j\'ai placé ma confiance, \n\r Nul ami n\'est meilleur que lui; \n\r Il a mis dans mon cœur l\'espérance \n\r Que je veux chanter aujourd\'hui.', 1, 58),
(261, 'Refrain', 1, 'Attaché à la croix pour moi, \n\r Attaché à la croix pour moi, \n\r Il a pris mon péché, il m\'a délivré, \n\r Attaché à la croix pour moi.', 2, 58),
(262, 'Strophe', 2, 'Dans les cris, les clameurs, la violence \n\r Rugissant autour de la croix, \n\r Son regard vient me dire en silence \n\r Tout l\'amour du Seigneur pour moi.', 3, 58),
(263, 'Refrain', 1, 'Attaché à la croix pour moi, \n\r Attaché à la croix pour moi, \n\r Il a pris mon péché, il m\'a délivré, \n\r Attaché à la croix pour moi.', 4, 58),
(264, 'Strophe', 3, 'Aujourd\'hui quand on nie l\'existence \n\r De mon Dieu, à cause des guerres, \n\r Je ne peux expliquer la souffrance \n\r Mais je dis son propre calvaire.', 5, 58),
(265, 'Refrain', 1, 'Attaché à la croix pour moi, \n\r Attaché à la croix pour moi, \n\r Il a pris mon péché, il m\'a délivré, \n\r Attaché à la croix pour moi.', 6, 58),
(266, 'Strophe', 1, 'Rédempteur adorable, \n\r Sur la croix attaché, \n\r Traité comme un coupable, \n\r Brisé pour mon péché, \n\r Ton angoisse suprême, \n\r Ta honte, ta douleur \n\r Me disent: «Vois, je t\'aime, \n\r Je suis ton rédempteur.»', 1, 59),
(267, 'Strophe', 2, 'Abandonné du Père, \n\r En ton âme troublé, \n\r Buvant la coupe amère \n\r Pour mon iniquité, \n\r De l\'éternelle flamme \n\r Ton amour me sauva, \n\r Tu subis pour mon âme, \n\r La mort à Golgotha!', 2, 59),
(268, 'Strophe', 3, 'Ton amour me réclame, \n\r Me voici, cher Sauveur! \n\r Prends mon corps et mon âme \n\r Pour prix de ta douleur. \n\r Oui, mon âme ravie \n\r Désormais ne veut plus \n\r Que vivre de ta vie, \n\r À ta gloire, ô Jésus!', 3, 59),
(269, 'Strophe', 1, 'Gethsémané, lieu de prière, \n\r Où le Sauveur lutte à genoux: \n\r Tremblant, il prend la coupe amère. \n\r Il va souffrir et c\'est pour nous.', 1, 60),
(270, 'Refrain', 1, 'Que sa victoire nous apprenne \n\r Comment en lui vivre en vainqueur, \n\r Avoir pour le péché la haine \n\r Et de l\'amour pour le pécheur.', 2, 60),
(271, 'Strophe', 2, 'Portant sa croix jusqu\'au Calvaire, \n\r C\'est le Seigneur roué de coups. \n\r Pourquoi sa mort? Pour qui ce drame? \n\r Pour nous, pécheurs, rien que pour nous!', 3, 60),
(272, 'Refrain', 1, 'Que sa victoire nous apprenne \n\r Comment en lui vivre en vainqueur, \n\r Avoir pour le péché la haine \n\r Et de l\'amour pour le pécheur.', 4, 60),
(273, 'Strophe', 3, 'Regardons ces soldats infâmes \n\r Percer sa chair avec leurs clous. \n\r Pourquoi sa mort? Pour qui ce drame? \n\r Pour nous, pécheurs, rien que pour nous!', 5, 60),
(274, 'Refrain', 1, 'Que sa victoire nous apprenne \n\r Comment en lui vivre en vainqueur, \n\r Avoir pour le péché la haine \n\r Et de l\'amour pour le pécheur.', 6, 60),
(275, 'Strophe', 1, 'Loin d\'ici, tout en haut est plantée une croix, \n\r Instrument de torture et d\'effroi; \n\r Et pourtant, savez-vous? Elle est chère à mon cœur: \n\r Jésus y souffrit pour moi, pécheur.', 1, 61),
(276, 'Refrain', 1, 'Je reçois par Jésus le pardon. \n\r Mon Sauveur a payé ma rançon. \n\r Je veux donc me saisir par la foi \n\r Du salut obtenu à la croix.', 2, 61),
(277, 'Strophe', 2, 'Si ce bois méprisé me fascine aussi fort, \n\r C\'est que Christ, le Sauveur, y est mort. \n\r Il a quitté son trône et la gloire du ciel \n\r Pour porter un salut éternel.', 3, 61),
(278, 'Refrain', 1, 'Je reçois par Jésus le pardon. \n\r Mon Sauveur a payé ma rançon. \n\r Je veux donc me saisir par la foi \n\r Du salut obtenu à la croix.', 4, 61),
(279, 'Strophe', 3, 'Je veux être fidèle au martyr de la croix, \n\r Témoigner par ma vie et ma voix. \n\r Aux pauvres de ses mains mon nom il a gravé, \n\r Par son', 5, 61),
(280, 'Refrain', 1, 'Je reçois par Jésus le pardon. \n\r Mon Sauveur a payé ma rançon. \n\r Je veux donc me saisir par la foi \n\r Du salut obtenu à la croix. \n\r Par son sang versé je suis sauvé.', 6, 61),
(281, 'Strophe', 1, 'Méprisé, abandonné, \n\r Homme de douleur et de souffrance, \n\r Nous l\'avons dédaigné, \n\r Détournant nos regards. \n\r Cependant, ce sont nos souffrances qu\'il a porté, …', 1, 62),
(282, 'Strophe', 2, '…De nos douleurs il s\'est chargé, \n\r Brisé pour nos iniquités. \n\r Maltraité, opprimé, \n\r Muet devant le sacrifice, \n\r Il a été élevé, à jamais glorifié.', 2, 62),
(283, 'Strophe', 1, 'Roi couvert de blessures, \n\r Meurtri pour mon péché, \n\r Roi tout couvert d\'injures, \n\r À la croix attaché, \n\r Des splendeurs éternelles \n\r Autrefois couronné, \n\r C\'est d\'épines cruelles \n\r Que je te vois orné.', 1, 63),
(284, 'Strophe', 2, 'Ainsi ton sang expie \n\r Mes péchés odieux: \n\r Pour me donner la vie \n\r Tu meurs en ces bas lieux. \n\r Rebelle et misérable, \n\r Je m\'abats devant toi; \n\r Rédempteur charitable, \n\r Ah! prends pitié de moi!', 2, 63),
(285, 'Strophe', 3, 'Tourne vers moi ta face, \n\r Fidèle et bon Sauveur! \n\r Oh! quel trésor de grâce \n\r Je trouve en ta douleur. \n\r Mourant pour mon offense, \n\r Tu m\'acquis, ô Jésus! \n\r Du cœur la renaissance \n\r Et la paix des élus.', 3, 63),
(286, 'Strophe', 4, 'Oui, pour ton agonie, \n\r Pour ta vive douleur, \n\r Je veux, toute ma vie, \n\r Te bénir, bon Sauveur! \n\r Ta grâce est éternelle, \n\r Et rien jusqu\'à la fin, \n\r Ne pourra, Dieu fidèle, \n\r Me ravir de ta main.', 4, 63),
(287, 'Strophe', 1, 'Merci pour la croix, pour le prix payé; \n\r Car tu t\'es donné sans rien retenir. \n\r Gloire à toi Jésus! (2×) \n\r Ils sont pardonnés, nos nombreux péchés, \n\r Couverts par ton sang, ils sont oubliés. \n\r Gloire à toi Jésus! (2×)', 1, 64),
(288, 'Refrain', 1, 'À toi mon amour, tout mon cœur, \n\r Seigneur, je ne comprendrai jamais pourquoi tu m\'aimes. \n\r Tu es mon bonheur, ma suprême joie, \n\r Mais voici le plus grand miracle à mes yeux: \n\r C\'est que je fais ta joie.', 2, 64),
(289, 'Strophe', 2, 'C\'est pour nous guérir que tu as souffert. \n\r Tu as pris nos peurs, donné ton amour, \n\r Gloire à toi Jésus! (2×) \n\r Tout est accompli, tu as triomphé; \n\r Tu peux nous sauver si parfaitement. \n\r Gloire à toi Jésus! (2×) \n\r La lumière se lève dans les ténèbres pour les hommes droits, \n\r Pour celui qui est miséricordieux, compatissant et juste.', 3, 64),
(290, 'Refrain', 1, 'À toi mon amour, tout mon cœur, \n\r Seigneur, je ne comprendrai jamais pourquoi tu m\'aimes. \n\r Tu es mon bonheur, ma suprême joie, \n\r Mais voici le plus grand miracle à mes yeux: \n\r C\'est que je fais ta joie.', 4, 64),
(291, 'Strophe', 1, 'Où est l\'amour, dans la nuit du monde? \n\r Tel un vautour, la mort fait sa ronde. \n\r Où est l\'amour, dans la nuit du monde? \n\r Le ciel est lourd, la mort vagabonde.', 1, 65),
(292, 'Refrain', 1, 'Mort, où est ta victoire? \n\r Où est ton aiguillon? \n\r Jésus soit loué, \n\r Ton amour nous a délivrés. \n\r Mort, où est ta victoire? \n\r Où est ton aiguillon? \n\r Jésus soit loué, \n\r Ton amour nous a délivrés.', 2, 65),
(293, 'Strophe', 2, 'Ne pleure plus dans la nuit du monde, \n\r La mort vaincue, s\'enfuit moribonde. \n\r Ne pleure plus dans la nuit du monde, \n\r La mort n\'est plus, la vie inonde.', 3, 65),
(294, 'Refrain', 1, 'Mort, où est ta victoire? \n\r Où est ton aiguillon? \n\r Jésus soit loué, \n\r Ton amour nous a délivrés. \n\r Mort, où est ta victoire? \n\r Où est ton aiguillon? \n\r Jésus soit loué, \n\r Ton amour nous a délivrés.', 4, 65),
(295, 'Strophe', 1, 'Torrents d\'amour et de grâce, \n\r Amour du Sauveur en croix! \n\r À ce grand fleuve qui passe, \n\r Je m\'abandonne et je crois.', 1, 66),
(296, 'Refrain', 1, 'Je crois à ton sacrifice, \n\r Ô Jésus, Agneau de Dieu; \n\r Et couvert par ta justice, \n\r J\'entrerai dans le saint lieu.', 2, 66),
(297, 'Strophe', 2, 'Ah! que partout se répande \n\r Ce fleuve à la grande voix: \n\r Que tout l\'univers entende \n\r L\'appel qui vient de la croix!', 3, 66),
(298, 'Refrain', 1, 'Je crois à ton sacrifice, \n\r Ô Jésus, Agneau de Dieu; \n\r Et couvert par ta justice, \n\r J\'entrerai dans le saint lieu.', 4, 66),
(299, 'Strophe', 3, 'Que toute âme condamnée, \n\r Pour qui tu versas ton sang, \n\r Soit au Père ramenée \n\r Par ton amour tout puissant!', 5, 66),
(300, 'Refrain', 1, 'Je crois à ton sacrifice, \n\r Ô Jésus, Agneau de Dieu; \n\r Et couvert par ta justice, \n\r J\'entrerai dans le saint lieu.', 6, 66),
(301, 'Strophe', 1, 'Combien était pénible \n\r Le chemin qui montait \n\r Vers ce moment terrible \n\r Où ses amis le lâcheraient! \n\r Pour eux, il avançait, \n\r Pour eux, il avançait.', 1, 67),
(302, 'Strophe', 2, 'Combien étaient indignes \n\r Tous ceux qui le jugeaient! \n\r Les coups et les épines, \n\r Les moqueries, les quolibets, \n\r Pour eux, il acceptait, \n\r Pour eux, il acceptait.', 2, 67),
(303, 'Strophe', 3, 'Combien était atroce \n\r Le temps qui s\'écoulait! \n\r Il eut encore la force \n\r De prier: «Veuille pardonner». \n\r Pour nous, il s\'est donné, \n\r Pour tous, il s\'est donné.', 3, 67),
(304, 'Strophe', 1, 'Dieu quel amour pour moi! Quelle patience! \n\r Tu as conduit vers toi mon ignorance. \n\r Comment pouvais-je alors saisir \n\r Ce que ton cœur voulait m\'offrir?', 1, 68),
(305, 'Refrain', 1, 'Un si grand miracle, Un don si précieux, \n\r Ta mort à la Pâque Nous ouvre les yeux. \n\r Ton salut remplace Ma condamnation, \n\r Mon péché s\'efface Par ta rédemption. \n\r Loué sois-tu, Dieu tout-puissant, \n\r Loué sois-tu.', 2, 68),
(306, 'Strophe', 2, 'Ce don si merveilleux, comment le vivre, \n\r Sans le répandre à ceux que tu veux suivre, \n\r Sur les chemins où ils s\'égarent, \n\r Loin de la vie et de l\'espoir?', 3, 68),
(307, 'Refrain', 1, 'Un si grand miracle, Un don si précieux, \n\r Ta mort à la Pâque Nous ouvre les yeux. \n\r Ton salut remplace Ma condamnation, \n\r Mon péché s\'efface Par ta rédemption. \n\r Loué sois-tu, Dieu tout-puissant, \n\r Loué sois-tu.', 4, 68),
(308, 'Strophe', 1, 'Agneau de Dieu, dans ta grandeur \n\r Tu règnes en Sion. \n\r Mais tu pris un poids de douleur \n\r Que, nous, nous méritons. \n\r Tu t\'es chargé de nos péchés \n\r Ô Rédempteur du monde. \n\r Portant ta croix tu as marché \n\r Dans une horreur profonde.', 1, 69),
(309, 'Strophe', 2, 'Agneau sans tache, c\'est pour moi \n\r Que tu voulu t\'offrir. \n\r Tout ce fardeau pesant sur toi. \n\r C\'était pour me guérir. \n\r Grâce à tes main, tes pieds cloués, \n\r Tu as brisé nos chaînes. \n\r Du tombeau tu t\'es relevé, \n\r Supprimant notre peine.', 2, 69),
(310, 'Strophe', 3, 'Autour de toi tous les élus \n\r Dans leurs vêtements blancs, \n\r Proclament le nom de Jésus, \n\r Béni dans tous les temps. \n\r Car le mystère de l\'Agneau \n\r En son dur esclavage \n\r Met dans nos cœurs un chant nouveau, \n\r Pour ce bel héritage.', 3, 69),
(311, 'Strophe', 4, 'Agneau de Dieu, pour ton amour, \n\r Merci du fond du cœur \n\r Sauvés de la mort pour toujours, \n\r Nous goûtons le bonheur. \n\r Dans le ciel enfin réunis, \n\r Nous chantons tes louanges. \n\r Ô père, ô Fils, ô Saint-Esprit \n\r Avec le chœur des anges.', 4, 69),
(312, 'Strophe', 1, 'À toi la gloire, Ô ressuscité! \n\r À toi la victoire Pour l\'éternité! \n\r Brillant de lumière, L\'ange est descendu, \n\r Il roule la pierre Du tombeau vaincu.', 1, 70),
(313, 'Refrain', 1, 'À toi la gloire! \n\r Ô ressuscité! \n\r À toi la victoire! \n\r Pour l\'éternité!', 2, 70),
(314, 'Strophe', 2, 'Vois-le paraître: C\'est lui, c\'est Jésus, \n\r Ton Sauveur, ton Maître! \n\r Oh! ne doute plus. \n\r Sois dans l\'allégresse, Peuple du Seigneur, \n\r Et redis sans cesse Que Christ est vainqueur.', 3, 70),
(315, 'Refrain', 1, 'À toi la gloire! \n\r Ô ressuscité! \n\r À toi la victoire! \n\r Pour l\'éternité!', 4, 70),
(316, 'Strophe', 3, 'Craindrais-je encore? \n\r Il vit à jamais, Celui que j\'adore, \n\r Le Prince de Paix; \n\r Il est ma victoire, Mon puissant soutien, \n\r Ma vie et ma gloire: Non, je ne crains rien.', 5, 70),
(317, 'Refrain', 1, 'À toi la gloire! \n\r Ô ressuscité! \n\r À toi la victoire! \n\r Pour l\'éternité!', 6, 70),
(318, 'Strophe', 1, 'Alléluia, alléluia, alléluia! \n\r Le grand combat est terminé: \n\r Le Christ Sauveur a triomphé \n\r Et dans sa gloire il s\'est montré. \n\r Alléluia!', 1, 71),
(319, 'Strophe', 2, 'Alléluia, alléluia, alléluia! \n\r Il est vivant, la mort n\'est plus; \n\r Dieu nous délivre par Jésus. \n\r Il rend la vie à ses élus. \n\r Alléluia!', 2, 71),
(320, 'Strophe', 3, 'Alléluia, alléluia, alléluia! \n\r Gloire au Seigneur ressuscité! \n\r Premier des morts il s\'est levé, \n\r Resplendissant de majesté. \n\r Alléluia!', 3, 71),
(321, 'Strophe', 1, 'Entonnons l\'hymne de victoire. \n\r Au ciel faisons monter nos chants, \n\r Au ciel faisons monter nos chants, \n\r Louons le Christ, il est vivant \n\r Dans sa splendeur et dans sa gloire!', 1, 72),
(322, 'Strophe', 2, 'À lui l\'honneur et la puissance: \n\r Il est le roi de l\'univers, \n\r Il est le roi de l\'univers, \n\r Le ciel, la terre et les enfers, \n\r Tout doit lui rendre obéissance.', 2, 72),
(323, 'Strophe', 3, 'Il a veillé sur son Église: \n\r Elle est toujours sous son regard, \n\r Elle est toujours sous son regard. \n\r Il est pour elle un sûr rempart \n\r Et qui la frappe un jour se brise.', 3, 72),
(324, 'Strophe', 4, 'Chantons le jour de la victoire, \n\r Au ciel faisons monter nos chants, \n\r Au ciel faisons monter nos chants, \n\r Et dans nos cœurs reconnaissants \n\r Rendons à Dieu louange et gloire!', 4, 72),
(325, 'Strophe', 1, 'Christ est ressuscité, \n\r Qu\'en des chants joyeux \n\r Son triomphe en tous lieux \n\r Soit exalté!', 1, 73),
(326, 'Refrain', 1, 'À toi la gloire et l\'honneur, \n\r Ô Sauveur, ô puissant Rédempteur! \n\r De la tombe tu sortis vainqueur, \n\r Prince de vie et Prince de paix. \n\r Gloire à toi, gloire à toi, \n\r Gloire à toi, gloire à jamais!', 2, 73),
(327, 'Strophe', 2, 'Christ est ressuscité, \n\r Cherche en lui toujours, \n\r Ô peuple racheté, \n\r Force et secours!', 3, 73),
(328, 'Refrain', 1, 'À toi la gloire et l\'honneur, \n\r Ô Sauveur, ô puissant Rédempteur! \n\r De la tombe tu sortis vainqueur, \n\r Prince de vie et Prince de paix. \n\r Gloire à toi, gloire à toi, \n\r Gloire à toi, gloire à jamais!', 4, 73),
(329, 'Strophe', 3, 'Christ est ressuscité, \n\r Par lui, nous vivrons \n\r Et dans l\'éternité \n\r Nous régnerons.', 5, 73),
(330, 'Refrain', 1, 'À toi la gloire et l\'honneur, \n\r Ô Sauveur, ô puissant Rédempteur! \n\r De la tombe tu sortis vainqueur, \n\r Prince de vie et Prince de paix. \n\r Gloire à toi, gloire à toi, \n\r Gloire à toi, gloire à jamais!', 6, 73),
(331, 'Strophe', 1, 'Christ est ressuscité, \n\r De son tombeau il s\'est levé, \n\r Alléluia! Alléluia!', 1, 74),
(332, 'Strophe', 1, 'Jour glorieux, jour heureux, Alléluia! \n\r Où Jésus s\'élève aux cieux! Alléluia! \n\r Aux mortels, un temps donné, Alléluia! \n\r Il rejoint l\'éternité. Alléluia!', 1, 75),
(333, 'Strophe', 2, 'Louange aux Roi éternel! Alléluia! \n\r Ouvrez-vous portes du ciel! Alléluia! \n\r Qu\'il entre, glorieux Roi. Alléluia! \n\r L\'univers est sous sa loi. Alléluia!', 2, 75),
(334, 'Strophe', 3, 'Dans un geste il nous bénit, Alléluia! \n\r Lui qui nous a tant aimés. Alléluia! \n\r Sur ces paumes qu\'il unit, Alléluia! \n\r Son amour est imprimé. Alléluia!', 3, 75),
(335, 'Strophe', 4, 'Élève nos cœurs, Seigneur, Alléluia! \n\r Jusqu\'à toi le grand vainqueur. Alléluia! \n\r Ta face, ô Seigneur Jésus, Alléluia! \n\r Éclaire tous tes élus. Alléluia!', 4, 75),
(336, 'Strophe', 1, 'On a planté sa croix en terre, \n\r On a sur lui roulé la pierre, \n\r Mais le voici dans la lumière! \n\r Alléluia! Il et vivant!', 1, 76),
(337, 'Strophe', 2, 'C\'est en lui seul que l\'on espère \n\r Car il entend notre prière, \n\r Le monde est beau, la vie est claire! \n\r Alléluia! Il et vivant!', 2, 76),
(338, 'Strophe', 3, 'Chante son nom, toute la terre, \n\r Criez de joie, toutes les pierres! \n\r Car nous vivons dans la lumière. \n\r Alléluia! Il et vivant!', 3, 76),
(339, 'Strophe', 1, 'Quand Jésus-Christ ressuscité \n\r S\'éleva jusqu\'aux cieux \n\r Fut-il reçu dans sa cité \n\r En Seigneur glorieux? \n\r Lorsqu\'il parcourt tous les sentiers \n\r Tracés au ciel par lui, \n\r Le feu jaillit-il de ses pieds \n\r Dans l\'espace infini?', 1, 77),
(340, 'Strophe', 2, 'À nos regards il est caché, \n\r Oui, mais en ces bas lieux, \n\r Où ses amis sont rassemblés, \n\r Il est au milieu d\'eux. \n\r C\'est là que brille sa splendeur. \n\r Qu\'il accorde ses dons. \n\r C\'est ainsi que notre Sauveur \n\r Rend gloire à son saint nom.', 2, 77),
(341, 'Strophe', 1, 'Ton amour divin surpasse, \n\r Les plus grands amours humains; \n\r Tu vins nous chercher par grâce, \n\r Sur nos ténébreux chemins. \n\r Jésus! Ton amour si tendre, \n\r Guérissant nos cœurs meurtris, \n\r Prêt à pardonner, comprendre, \n\r Ton amour nous a conquis!', 1, 78),
(342, 'Strophe', 2, 'Ton amour seul nous délivre, \n\r Ton amour seul nous rend forts \n\r Pour te servir et te suivre \n\r Dans la vie et dans la mort. \n\r Nous joignant au cœur des anges \n\r Dès ici-bas, nuit et jour, \n\r Nous proclamons tes louanges: \n\r Gloire à l\'éternel amour!', 2, 78),
(343, 'Strophe', 3, 'Ton amour fait sa demeure \n\r Dans nos cœurs, par ton esprit; \n\r Et sa flamme d\'heure en heure \n\r D\'espérance nous remplit. \n\r Christ, priant pour nous sans trêve, \n\r L\'œuvre que tu commenças \n\r En nous ton Esprit l\'achève, \n\r Toi l\'alpha, toi l\'oméga.', 3, 78),
(344, 'Strophe', 1, 'Merveille de l\'amour sauveur \n\r Jésus-Christ a quitté \n\r Le ciel, pour donner aux pécheurs \n\r Pardon et liberté.', 1, 79),
(345, 'Strophe', 2, 'En sacrifice il s\'est offert, \n\r Comme agneau sans péché. \n\r À notre place il a souffert, \n\r Il nous a rachetés.', 2, 79),
(346, 'Strophe', 3, 'Auprès du Père il est monté \n\r Devant tout l\'univers. \n\r Son sacrifice est accepté: \n\r Le ciel nous est ouvert.', 3, 79),
(347, 'Strophe', 4, 'Il intercède et par son sang \n\r Peut rendre purs nos cœurs. \n\r Dans note lutte, il nous comprend, \n\r Il nous rendra vainqueurs.', 4, 79),
(348, 'Strophe', 5, 'Quand le sauveur apparaîtra, \n\r Triomphera l\'amour. \n\r Chacun des siens acclamera \n\r Son glorieux retour.', 5, 79),
(349, 'Strophe', 1, 'Voici l\'étoile du matin \n\r Que fait briller l\'amour divin: \n\r Pure et sainte lumière! \n\r Répands en nos cœurs ta clarté, \n\r Viens dissiper l\'obscurité \n\r Qui règne sur la terre.', 1, 80),
(350, 'Refrain', 1, 'Seigneur! Sauveur! \n\r Fils du Père, \n\r Ta lumière salutaire \n\r Nous conduit et nous éclaire.', 2, 80),
(351, 'Strophe', 2, 'Voici le pain venu des cieux, \n\r Messager de l\'amour de Dieu \n\r Pour tous ceux qui espèrent. \n\r Remplis-nous jusqu\'à satiété \n\r De tes magnifiques bontés, \n\r Toi qui est notre frère.', 3, 80),
(352, 'Refrain', 1, 'Seigneur! Sauveur! \n\r Fils du Père, \n\r Ta lumière salutaire \n\r Nous conduit et nous éclaire.', 4, 80),
(353, 'Strophe', 3, 'Voici la porte du salut \n\r Ouverte à tous ceux qui ont cru \n\r Qu\'ils pourront voir ta face. \n\r Conduis nos pas jusqu\'à ce jour \n\r Où, accueillis par ton amour, \n\r Nous chanterons ta grâce.', 5, 80),
(354, 'Refrain', 1, 'Seigneur! Sauveur! \n\r Fils du Père, \n\r Ta lumière salutaire \n\r Nous conduit et nous éclaire.', 6, 80),
(355, 'Strophe', 1, 'Si Dieu est pour nous, pourquoi craindre? \n\r Qui donc pourrait nous ébranler? \n\r Pourquoi crier, pourquoi nous plaindre? \n\r Par son Fils, il a tout donné.', 1, 81),
(356, 'Strophe', 2, 'Dieu est celui qui justifie, \n\r Qui donc pourrait nous accuser? \n\r Pour nous, Jésus donna sa vie, \n\r Pour nous, il est ressuscité.', 2, 81),
(357, 'Strophe', 3, 'Il est à la droite du Père, \n\r Il intercède, il est vivant! \n\r Malgré la mort, malgré la guerre, \n\r En lui nous sommes triomphants.', 3, 81),
(358, 'Strophe', 1, 'Aube nouvelle, Dans notre nuit, \n\r Pour sauver son peuple \n\r Dieu va venir. \n\r Joie pour les pauvres, Fête aujourd\'hui:', 1, 82),
(359, 'Refrain', 1, 'Il faut préparer la route au Seigneur, \n\r Il faut préparer la route au Seigneur.', 2, 82),
(360, 'Strophe', 2, 'Bonne nouvelle, Cris et chansons, \n\r Pour sauver son peuple \n\r Dieu va venir. \n\r Voix qui s\'élève Dans nos déserts:', 3, 82),
(361, 'Refrain', 1, 'Il faut préparer la route au Seigneur, \n\r Il faut préparer la route au Seigneur.', 4, 82),
(362, 'Strophe', 3, 'Terre nouvelle, Monde nouveau, \n\r Pour sauver son peuple \n\r Dieu va venir. \n\r Paix sur la terre, Ciel parmi nous:', 5, 82),
(363, 'Refrain', 1, 'Il faut préparer la route au Seigneur, \n\r Il faut préparer la route au Seigneur.', 6, 82),
(364, 'Strophe', 1, 'Au séjour de la lumière, \n\r Chantez, anges radieux: \n\r Tous les fils de Dieu sur terre \n\r Ressuscitent glorieux. \n\r Que vos hymnes de louanges, \n\r Ô rachetés du Seigneur, \n\r S\'unissent aux voix des anges \n\r Pour bénir le Dieu Sauveur.', 1, 83),
(365, 'Strophe', 2, 'Ô Jésus, à toi la gloire \n\r Sur la terre et dans les cieux! \n\r C\'est le jour de ta victoire, \n\r De ton règne glorieux, \n\r Triomphal, ton char s\'avance, \n\r L\'ennemi fuit devant toi, \n\r Et dans l\'univers immense, \n\r Tout se courbe sous ta loi.', 2, 83),
(366, 'Strophe', 3, 'Plus de mort, plus de souffrance \n\r Pour le peuple du Seigneur. \n\r Ô sublime délivrance! \n\r Tout est paix, gloire et bonheur. \n\r Que nos âmes vigilantes, \n\r En attendant ce grand jour, \n\r Soient fidèles et vaillantes, \n\r Agissant par ton amour.', 3, 83),
(367, 'Strophe', 1, 'Chrétiens, mes frères, sonnez le clairon! \n\r Oui, c\'est Jésus qui revient! \n\r Et que la foule chante à l\'unisson: \n\r Oui, c\'est Jésus qui revient!', 1, 84),
(368, 'Refrain', 1, 'Oui, il revient! \n\r Oui, il revient! \n\r C\'est le Seigneur qui revient!', 2, 84),
(369, 'Strophe', 2, 'Monts et vallées, criez le bien haut: \n\r Oui, c\'est Jésus qui revient! \n\r Il vient en gloire, le Vainqueur, l\'Agneau! \n\r Oui, c\'est Jésus qui revient!', 3, 84),
(370, 'Refrain', 1, 'Oui, il revient! \n\r Oui, il revient! \n\r C\'est le Seigneur qui revient!', 4, 84),
(371, 'Strophe', 3, 'Fleuves et déserts, clamez tout autour: \n\r Oui, c\'est Jésus qui revient! \n\r Vents et tempêtes, annoncez le jour: \n\r Oui, c\'est Jésus qui revient!', 5, 84),
(372, 'Refrain', 1, 'Oui, il revient! \n\r Oui, il revient! \n\r C\'est le Seigneur qui revient!', 6, 84),
(373, 'Strophe', 1, 'Au nom du Christ et par son Esprit Saint qui nous assiste \n\r De tous horizons, nous faisons monter au ciel nos plus beaux chants \n\r Le cœur plein de joie, le cœur plein de foi. \n\r Jésus, par ta mort, tu nous offres la victoire.', 1, 85),
(374, 'Refrain', 1, 'Nous élèverons nos voix pour crier gloire, \n\r Gloire à toi Jésus, gloire à ton nom à jamais.', 2, 85),
(375, 'Strophe', 2, 'Au jour béni où nous verrons se lever l\'aube nouvelle, \n\r Au temps choisi tu nous vêtiras de ta gloire éternelle, \n\r Et ce jour viendra, il ne tarde pas. \n\r Jésus, le monde contemplera ta victoire.', 3, 85),
(376, 'Refrain', 1, 'Nous élèverons nos voix pour crier gloire, \n\r Gloire à toi Jésus, gloire à ton nom à jamais.', 4, 85),
(377, 'Strophe', 1, 'C\'est en Sion qu\'il règne, le Dieu de l\'univers. \n\r Autour de lui, nous, ses enfants, chantons-le de concert. \n\r Nos cœurs, nos voix s\'unissent pour le glorifier. \n\r Dans sa maison, tous sont venus l\'aimer, le rencontrer.', 1, 86),
(378, 'Strophe', 2, 'C\'est en Sion qu\'il règne, le Dieu des continents. \n\r Il a créé les hauts sommets et les grands océans. \n\r Il est le Dieu unique. À qui le comparer? \n\r Car sa bonté n\'a pas de fin: il veut tous nous sauver.', 2, 86),
(379, 'Strophe', 3, 'C\'est en Sion qu\'il règne, le temps lui appartient. \n\r Portons-lui donc en son saint lieu l\'honneur qui lui revient. \n\r Que dans son sanctuaire chacun tombe à genoux \n\r Il tient dans sa main nos destins: il est Seigneur de tous.', 3, 86),
(380, 'Strophe', 1, 'Dans sa gloire, notre Maître \n\r Bientôt va paraître. \n\r Oui, bientôt nous allons être \n\r Avec lui dans les cieux.', 1, 87),
(381, 'Refrain', 1, 'Du Sauveur qui nous donne \n\r La paix et nous pardonne, \n\r Nous serons la couronne, \n\r Les joyaux précieux.', 2, 87),
(382, 'Strophe', 2, 'Vous qui recevez sa grâce \n\r Et suivez sa trace, \n\r Il vous prépare une place \n\r Près de lui dans les cieux.', 3, 87),
(383, 'Refrain', 1, 'Du Sauveur qui nous donne \n\r La paix et nous pardonne, \n\r Nous serons la couronne, \n\r Les joyaux précieux.', 4, 87),
(384, 'Strophe', 1, 'Du haut des cieux, Jésus vient dans sa gloire, \n\r Tous ses élus peuvent le contempler. \n\r Au chœur des anges, \n\r Nos voix se mélangent. \n\r Gloire à toi Seigneur! \n\r Amen, alléluia!', 1, 88),
(385, 'Strophe', 2, 'Nous jouirons du fruit de sa victoire, \n\r Car notre roi vient pour nous libérer. \n\r Au chœur des anges, \n\r Nos voix se mélangent. \n\r Gloire à toi Seigneur! \n\r Amen, alléluia!', 2, 88),
(386, 'Strophe', 3, 'De nos douleurs, il n\'est plus de mémoire, \n\r À son triomphe, il veut nous associer. \n\r Au chœur des anges, \n\r Nos voix se mélangent. \n\r Gloire à toi Seigneur! \n\r Amen, alléluia! \n\r Amen, alléluia!', 3, 88),
(387, 'Strophe', 1, 'Ensemble, nous monterons! \n\r Ensemble, nous chanterons! \n\r Ensemble, nous régnerons! \n\r Ensemble, nous le verrons!…', 1, 89),
(388, 'Strophe', 2, '…C\'est la promesse que j\'ai dans la tête, \n\r La réalité du bonheur, \n\r C\'est la promesse qui nous est faite, \n\r Le rendez-vous du Seigneur!…', 2, 89),
(389, 'Strophe', 3, '…«Je suis l\'Alpha et l\'Oméga, \n\r Le commencement et la fin! \n\r Je t\'ouvre largement mes bras, \n\r Viens! Mon enfant, oh! oui, viens!» \n\r Alléluia dans ma tête, \n\r Alléluia dans mon cœur…', 3, 89),
(390, 'Strophe', 4, '…Oui, maintenant c\'est la fête, \n\r Voici le jour du Seigneur! \n\r «Je suis l\'Alpha et l\'Oméga, \n\r Le commencement et la fin! \n\r Je t\'ouvre largement mes bras, \n\r Viens! Mon enfant, oh! oui, viens!»…', 4, 89),
(391, 'Strophe', 5, '…Alléluia dans ma tête, \n\r Alléluia dans mon cœur. \n\r Oui, maintenant c\'est la fête, \n\r Voici le jour du Seigneur!…', 5, 89),
(392, 'Strophe', 6, '…Ensemble, nous monterons! \n\r Ensemble, nous chanterons! \n\r Ensemble, nous régnerons! \n\r Ensemble, nous le verrons!…', 6, 89),
(393, 'Strophe', 1, 'Du ciel bientôt, Jésus va revenir! \n\r Si c\'était aujourd\'hui! \n\r Tous, radieux, nous le verrons venir! \n\r Si c\'était aujourd\'hui! \n\r Alors tous les saints à sa vue, \n\r Ses enfants sûrs de sa venue, \n\r Iront avec lui sur la nue! \n\r Si c\'était aujourd\'hui!', 1, 90),
(394, 'Refrain', 1, 'Gloire! Gloire! Jésus revient bientôt! \n\r Gloire! Gloire! Nous le couronnerons. \n\r Gloire! Gloire! Et nous l\'adorerons. \n\r Gloire! Gloire! Jésus revient bientôt!', 2, 90),
(395, 'Strophe', 2, 'Dans ce beau ciel, le mal ne sera plus! \n\r Si c\'était aujourd\'hui! \n\r Plus de douleur, le regard de Jésus! \n\r Si c\'était aujourd\'hui! \n\r Notre âme après ce jour soupire, \n\r Mais déjà la nuit se retire, \n\r Le grand matin va bientôt luire! \n\r Si c\'était aujourd\'hui!', 3, 90),
(396, 'Refrain', 1, 'Gloire! Gloire! Jésus revient bientôt! \n\r Gloire! Gloire! Nous le couronnerons. \n\r Gloire! Gloire! Et nous l\'adorerons. \n\r Gloire! Gloire! Jésus revient bientôt!', 4, 90),
(397, 'Strophe', 3, 'Oh! Que Jésus trouve en nous des cœurs droits, \n\r S\'il revient aujourd\'hui! \n\r Veillant, priant, dociles à sa voix, \n\r S\'il revient aujourd\'hui! \n\r Avons-nous tous cette assurance \n\r D\'être enlevés en sa présence, \n\r Dans le séjour de sa puissance! \n\r S\'il revient aujourd\'hui!', 5, 90),
(398, 'Refrain', 1, 'Gloire! Gloire! Jésus revient bientôt! \n\r Gloire! Gloire! Nous le couronnerons. \n\r Gloire! Gloire! Et nous l\'adorerons. \n\r Gloire! Gloire! Jésus revient bientôt!', 6, 90),
(399, 'Strophe', 1, 'Ami, sois fidèle! Jésus vient bientôt \n\r Qui te donnera le repos. \n\r Avec vigilance poursuis ton combat, \n\r Tiens fermement ce que tu as. \n\r Ami, sois fidèle! \n\r Fidèle à Celui Qui a pour toi donné sa vie, \n\r Le Seigneur qui est, qui était et qui vient, \n\r Qui te veut au milieu des siens.', 1, 91),
(400, 'Strophe', 2, 'Ami, sois fidèle! Saisi par Jésus, \n\r Avance tout droit vers le but. \n\r Devant toi la porte bientôt s\'ouvrira, \n\r Ton Sauveur te libérera. \n\r Ami, sois fidèle! Car voici ton roi \n\r Qui te fait entrer dans sa joie! \n\r Et tu pourras vivre, citoyen des cieux, \n\r Pour toujours dans l\'amour de Dieu.', 2, 91),
(401, 'Strophe', 1, 'Dans tous nos cœurs, brûle cette espérance \n\r Du retour de notre Sauveur. \n\r Et nous mettons notre ardente confiance \n\r Dans les promesses du Seigneur. \n\r Bientôt du monde en émoi, tous les peuples réveillés \n\r Se lèveront pour chanter: Alléluia! Christ est Roi! \n\r Dans tous nos cœurs, brûle cette espérance \n\r Du retour de notre Sauveur.', 1, 92),
(402, 'Strophe', 2, 'Soyons unis en Christ, notre Seigneur \n\r Et motivés par son amour. \n\r Prions pour ceux qui vivent dans la peur, \n\r Et parlons-leur de son retour. \n\r C\'est l\'objet de notre foi: Jésus-Christ apparaîtra \n\r Et l\'Église chantera: «Alléluia! Christ est Roi!» \n\r Soyons unis en Christ, notre Seigneur \n\r Et motivés par son retour.', 2, 92),
(403, 'Strophe', 1, 'Entendez-vous la dernière trompette \n\r Qui retentit là-haut dans le saint lieu? \n\r Alléluia! Le ciel entier s\'apprête \n\r À faire escorte au puissant Fils de Dieu.', 1, 93),
(404, 'Refrain', 1, 'Oh! Gloire, gloire à l\'Éternel, \n\r Gloire au Sauveur qui vient du ciel!', 2, 93),
(405, 'Strophe', 2, 'Il vient, il vient, bientôt il va paraître, \n\r Le Roi divin, vêtu de majesté. \n\r Les rachetés pourront le reconnaître \n\r Et rendre hommage à sa grande bonté.', 3, 93),
(406, 'Refrain', 1, 'Oh! Gloire, gloire à l\'Éternel, \n\r Gloire au Sauveur qui vient du ciel!', 4, 93),
(407, 'Strophe', 3, 'Du jour sans fin, viens saluer l\'aurore, \n\r Sois dans la joie, ô peuple racheté, \n\r Le Roi des rois de sa gloire t\'honore, \n\r Il t\'introduit dans la sainte cité.', 5, 93),
(408, 'Refrain', 1, 'Oh! Gloire, gloire à l\'Éternel, \n\r Gloire au Sauveur qui vient du ciel!', 6, 93),
(409, 'Strophe', 1, 'Il va venir, le Seigneur que j\'adore; \n\r Bientôt sa voix retentira du ciel. \n\r À mes regards, l\'horizon se colore \n\r Des purs rayons du soleil éternel.', 1, 94),
(410, 'Refrain', 1, 'Gloire à toi seul, Seigneur Jésus, \n\r Gloire à jamais, gloire à ton nom! \n\r De ton beau ciel je ne sortirai plus, \n\r Et pour toujours, j\'exalterai ton nom.', 2, 94),
(411, 'Strophe', 2, 'Qu\'autour de moi s\'élève la tempête, \n\r Je suis en paix, car tu veilles sur moi; \n\r Je puis, ô mon Sauveur, lever la tête, \n\r Tu vas venir me prendre auprès de toi.', 3, 94),
(412, 'Refrain', 1, 'Gloire à toi seul, Seigneur Jésus, \n\r Gloire à jamais, gloire à ton nom! \n\r De ton beau ciel je ne sortirai plus, \n\r Et pour toujours, j\'exalterai ton nom.', 4, 94),
(413, 'Strophe', 3, 'Maison du Père où ton amour m\'appelle \n\r Sainte cité, demeure des élus, \n\r Les lieux qu\'éclaire une gloire éternelle \n\r Me sont ouverts par toi-même, ô Jésus!', 5, 94),
(414, 'Refrain', 1, 'Gloire à toi seul, Seigneur Jésus, \n\r Gloire à jamais, gloire à ton nom! \n\r De ton beau ciel je ne sortirai plus, \n\r Et pour toujours, j\'exalterai ton nom.', 6, 94),
(415, 'Strophe', 4, 'Bonheur ineffable! Quand dans ta gloire, \n\r Aux yeux des rachetés, tu paraîtras, \n\r Jésus, avec le cri de la victoire, \n\r Près de toi, pour toujours, tu me prendras.', 7, 94),
(416, 'Refrain', 1, 'Gloire à toi seul, Seigneur Jésus, \n\r Gloire à jamais, gloire à ton nom! \n\r De ton beau ciel je ne sortirai plus, \n\r Et pour toujours, j\'exalterai ton nom.', 8, 94),
(417, 'Strophe', 1, 'Jour de grâce, jour heureux, \n\r Oh! quand donc vas-tu paraître! \n\r Jour où Christ, le Fils de Dieu, \n\r Seul sera le roi, le maître, \n\r Le berger d\'un seul troupeau \n\r Rassemblé dans son repos!', 1, 95),
(418, 'Strophe', 2, 'Oui, la terre entière attend, \n\r Toute créature espère. \n\r Il viendra, cet heureux temps, \n\r Le royaume et sa lumière. \n\r Mais il est dès maintenant \n\r Au milieu de ses enfants.', 2, 95),
(419, 'Strophe', 3, 'Ô Jésus, que ton amour \n\r Fasse en nous briller sa flamme! \n\r C\'est le feu du nouveau jour \n\r Qui doit éclairer notre âme! \n\r Que le Saint-Esprit de Dieu \n\r Renouvelle terre et cieux.', 3, 95),
(420, 'Strophe', 1, 'Il va bientôt venir! \n\r Gloire, gloire à Jésus! \n\r Dans le ciel, il veut réunir \n\r Ses bien-aimés élus. \n\r Levons en haut les yeux, \n\r Tressaillons de bonheur, \n\r Il vient, sublime et glorieux, \n\r Le grand libérateur.', 1, 96),
(421, 'Strophe', 2, 'Il va bientôt venir! \n\r Gloire, gloire à Jésus! \n\r Toutes nos douleurs vont finir \n\r Le mal ne sera plus, \n\r La mort disparaîtra \n\r À la voix du Sauveur, \n\r Quand pour toujours il reviendra, \n\r Le grand libérateur.', 2, 96),
(422, 'Strophe', 3, 'Il va bientôt venir! \n\r Gloire, gloire à Jésus! \n\r Sur la nue, il va resplendir \n\r Frères, ne craignons plus. \n\r Levons, levons les yeux, \n\r Voici notre Sauveur. \n\r C\'est pour nous qu\'il revient des cieux, \n\r Le grand libérateur.', 3, 96),
(423, 'Strophe', 1, 'La trompette a retenti \n\r Écoutez, écoutez, \n\r Du ciel résonne ce cri: \n\r Le Seigneur revient! \n\r Oh! Toi qui sommeilles \n\r Écoutez, écoutez, \n\r Viens et te réveille: \n\r Le Seigneur revient!', 1, 97),
(424, 'Strophe', 2, 'Entendez-vous le clairon? \n\r Écoutez, écoutez, \n\r L\'Épouse en connaît le son: \n\r Jésus vient bientôt! \n\r Et, dans l\'allégresse, \n\r Écoutez, écoutez, \n\r Son cœur dit sans cesse: \n\r Jésus vient bientôt!', 2, 97),
(425, 'Strophe', 3, 'La trompette retentit \n\r Écoutez, écoutez, \n\r Levez-vous, il est minuit, \n\r Voici, l\'Époux vient! \n\r Au banquet de fête, \n\r Écoutez, écoutez, \n\r Que chacun s\'apprête: \n\r Voici, l\'Époux vient!', 3, 97),
(426, 'Strophe', 1, 'Je le verrai venant des cieux sur les nuées; \n\r Je le verrai tout radieux d\'éternité; \n\r Je le verrai bien tel qu\'il est. \n\r Louange et gloire, félicité! \n\r Et je serai en un clin d\'œil tout transformé.', 1, 98),
(427, 'Refrain', 1, 'Voir Jésus aujourd\'hui, \n\r Voir Jésus sans détour, \n\r Voir Jésus dans la nuit comme au grand jour, \n\r Voir Jésus dans nos vies, \n\r Quel beau jour!', 2, 98),
(428, 'Strophe', 2, 'Je le verrai et marcherai selon ses lois; \n\r Je le verrai puisque sa grâce me reçoit; \n\r Je le verrai puisque l\'amour, \n\r La foi, l\'espoir de son retour \n\r Remplissent mon cœur aujourd\'hui et pour toujours.', 3, 98),
(429, 'Refrain', 1, 'Voir Jésus aujourd\'hui, \n\r Voir Jésus sans détour, \n\r Voir Jésus dans la nuit comme au grand jour, \n\r Voir Jésus dans nos vies, \n\r Quel beau jour!', 4, 98),
(430, 'Refrain', 1, 'Les nations marcheront à la lumière de l\'Agneau, \n\r Les rois de la terre apporteront leur richesse et leur splendeur. \n\r Les portes de la ville plus jamais ne se fermeront. \n\r Dans sa gloire Dieu resplendira. \n\r Il n\'y aura plus de nuit', 1, 99),
(431, 'Strophe', 1, 'Car du trône de Dieu et de l\'Agneau \n\r Jaillira un fleuve scintillant semblable à du cristal. \n\r Sur la place, l\'arbre de la vie \n\r Chaque mois sera chargé de fruits. \n\r Par ses feuilles tous seront guéris.', 2, 99),
(432, 'Refrain', 1, 'Les nations marcheront à la lumière de l\'Agneau, \n\r Les rois de la terre apporteront leur richesse et leur splendeur. \n\r Les portes de la ville plus jamais ne se fermeront. \n\r Dans sa gloire Dieu resplendira. \n\r Il n\'y aura plus de nuit', 3, 99),
(433, 'Strophe', 2, 'Et la malédiction ne sera plus, \n\r Dans la ville sainte brillera le trône du Seigneur. \n\r Et voyant leur maître face à face, \n\r Les élus l\'adoreront sans fin. \n\r Sur leur front sera inscrit son nom.', 4, 99),
(434, 'Refrain', 1, 'Les nations marcheront à la lumière de l\'Agneau, \n\r Les rois de la terre apporteront leur richesse et leur splendeur. \n\r Les portes de la ville plus jamais ne se fermeront. \n\r Dans sa gloire Dieu resplendira. \n\r Il n\'y aura plus de nuit', 5, 99),
(435, 'Strophe', 3, 'Ils n\'auront plus besoin d\'aucun flambeau \n\r Ni de la lumière du soleil, car Dieu luira sur eux \n\r Et ils règneront de siècles en siècles. \n\r Ces paroles sont la vérité. \n\r Dieu a envoyé son messager.', 6, 99),
(436, 'Strophe', 1, 'Oh! quand serai-je avec Jésus au ciel pour toujours \n\r Pour entendre la trompette en ce jour-là? \n\r Boire à la source vive son éternel amour \n\r Et entendre la trompette en ce jour-là.', 1, 100),
(437, 'Refrain', 1, 'Alléluia, je monterai dans les hauts cieux. \n\r La trompette sonnera en ce jour-là.', 2, 100),
(438, 'Strophe', 2, 'Revêts pour ton armure: foi, espérance, amour, \n\r Pour entendre la trompette en ce jour-là. \n\r Que ce combat s\'achève tout à la fin de tes jours \n\r Pour entendre la trompette en ce jour-là.', 3, 100),
(439, 'Refrain', 1, 'Alléluia, je monterai dans les hauts cieux. \n\r La trompette sonnera en ce jour-là.', 4, 100),
(440, 'Strophe', 3, 'Entends-tu les louanges des saints victorieux \n\r Et le son de la trompette en ce jour-là? \n\r «Jésus, Seigneur et frère, Gloire à toi dans les cieux!» \n\r Pour toi sonne la trompette en ce jour-là.', 5, 100),
(441, 'Refrain', 1, 'Alléluia, je monterai dans les hauts cieux. \n\r La trompette sonnera en ce jour-là.', 6, 100),
(442, 'Strophe', 1, 'Victoire au Seigneur de la vie, \n\r Victoire au Dieu de l\'infini, \n\r Victoire au nom de Jésus-Christ, \n\r Victoire pour le Saint-Esprit!', 1, 101),
(443, 'Strophe', 2, 'Le règne est pour le Tout-Puissant, \n\r Le règne à lui dans tous les temps, \n\r Car l\'Agneau a donné son sang \n\r Qui crie victoire maintenant.', 2, 101),
(444, 'Strophe', 3, 'Son nom est Parole de Dieu, \n\r Il juge et règne dans les cieux, \n\r Il est le lion de Juda \n\r Qui a triomphé du combat.', 3, 101),
(445, 'Refrain', 1, 'Quand s\'éveilleront nos cœurs à la voix du Dieu vivant, \n\r Nous retrouverons la source du bonheur. \n\r Quand se lèveront nos mains pour chanter le Dieu vivant, \n\r Nous retrouverons l\'espoir des lendemains.', 1, 102),
(446, 'Strophe', 1, 'Il saura briser nos armes, \n\r Il saura changer nos cœurs. \n\r Il viendra sécher nos larmes \n\r Il viendra chasser nos peurs.', 2, 102),
(447, 'Refrain', 1, 'Quand s\'éveilleront nos cœurs à la voix du Dieu vivant, \n\r Nous retrouverons la source du bonheur. \n\r Quand se lèveront nos mains pour chanter le Dieu vivant, \n\r Nous retrouverons l\'espoir des lendemains.', 3, 102),
(448, 'Strophe', 2, 'Plus de nuit sur notre terre, \n\r Il sera notre flambeau. \n\r Plus de haine ni de guerre, \n\r Il nous ouvre un ciel nouveau.', 4, 102),
(449, 'Refrain', 1, 'Quand s\'éveilleront nos cœurs à la voix du Dieu vivant, \n\r Nous retrouverons la source du bonheur. \n\r Quand se lèveront nos mains pour chanter le Dieu vivant, \n\r Nous retrouverons l\'espoir des lendemains.', 5, 102),
(450, 'Strophe', 3, 'Il habite avec les hommes, \n\r Le vieux monde peut mourir. \n\r Il annonce son Royaume, \n\r Il nous ouvre l\'avenir.', 6, 102),
(451, 'Refrain', 1, 'Quand s\'éveilleront nos cœurs à la voix du Dieu vivant, \n\r Nous retrouverons la source du bonheur. \n\r Quand se lèveront nos mains pour chanter le Dieu vivant, \n\r Nous retrouverons l\'espoir des lendemains.', 7, 102),
(452, 'Strophe', 1, 'Vois, par avance le grand jour, \n\r Qui nous rassemblera, \n\r Quand, dans la gloire et dans l\'amour, \n\r Tout homme adorera, \n\r Tout homme adorera.', 1, 103),
(453, 'Strophe', 2, 'Vois, ces milliers d\'adorateurs, \n\r Fidèles ici bas; \n\r De la Parole du Seigneur \n\r Chacun d\'eux témoigna, \n\r Chacun d\'eux témoigna.', 2, 103),
(454, 'Strophe', 3, 'Écoute leurs hymnes éternel, \n\r Ce bruit de grandes eaux; \n\r Leur voix joyeuse emplit le ciel: \n\r «Louange au Dieu très-haut \n\r Louange au Dieu très-haut!»', 3, 103),
(455, 'Strophe', 4, 'Sa grâce attire tous les cœurs, \n\r À l\'éternel salut; \n\r Nous chanterons à son honneur \n\r Le grand nom de Jésus \n\r Le grand nom de Jésus.', 4, 103),
(456, 'Strophe', 1, 'Quand, sur l\'ordre du Seigneur, \n\r Bientôt les tombes s\'ouvriront, \n\r Et pour nous le grand matin se lèvera; \n\r Quand, heureux, près du Sauveur, \n\r Les élus se retrouveront, \n\r Et le grand appel, enfin, retentira…', 1, 104),
(457, 'Refrain', 1, 'Quand là-haut, dans le ciel même, \n\r Se fera l\'appel suprême, \n\r Par Jésus, que mon cœur aime, \n\r Je le sais, oui, gloire à Dieu, je serai là!', 2, 104),
(458, 'Strophe', 2, 'En ce jour si merveilleux, \n\r Les morts en Christ se lèveront, \n\r Pour l\'éternité, Jésus les recevra, \n\r Triomphants et glorieux, \n\r Ses rachetés apparaîtront, \n\r Et le grand appel enfin retentira…', 3, 104),
(459, 'Refrain', 1, 'Quand là-haut, dans le ciel même, \n\r Se fera l\'appel suprême, \n\r Par Jésus, que mon cœur aime, \n\r Je le sais, oui, gloire à Dieu, je serai là!', 4, 104),
(460, 'Strophe', 3, 'Sans relâche et d\'un seul cœur, \n\r Travaillons tous en ces bas lieux, \n\r Car bientôt le jour de grâce finira; \n\r Le repos et le bonheur \n\r Nous sont réservés dans les cieux, \n\r Où le grand appel enfin retentira…', 5, 104),
(461, 'Refrain', 1, 'Quand là-haut, dans le ciel même, \n\r Se fera l\'appel suprême, \n\r Par Jésus, que mon cœur aime, \n\r Je le sais, oui, gloire à Dieu, je serai là!', 6, 104),
(462, 'Strophe', 1, 'À Jésus, le roi, le Sauveur, \n\r Soient puissance, force et honneur. \n\r Du Seigneur de l\'histoire, \n\r Nous proclamons la gloire.', 1, 105),
(463, 'Strophe', 2, 'Sur les ruines du dernier jour, \n\r Tout s\'éclaire pour le retour \n\r De Jésus notre Maître. \n\r Tous vont le reconnaître.', 2, 105),
(464, 'Strophe', 3, 'À Jésus, le roi, le Sauveur, \n\r Soient puissance, force et honneur. \n\r Du Seigneur de l\'histoire, \n\r Nous proclamons la gloire.', 3, 105),
(465, 'Strophe', 1, 'Quel bonheur! Jésus vient! Sa voix se fait entendre; \n\r Les ténèbres s\'en vont, voici le point du jour. \n\r Qu\'il nous trouve veillant, les reins ceints pour l\'attendre, \n\r Portant du fruit pour lui, heureux dans son amour!', 1, 106),
(466, 'Strophe', 2, 'Bientôt près de son trône, où notre âme ravie \n\r Goûtera le repos du séjour glorieux, \n\r Nos yeux contempleront le Prince de la vie, \n\r Celui qui s\'abaissa pour nous ouvrir les cieux.', 2, 106),
(467, 'Strophe', 3, 'Alors éclatera l\'hymne de l\'allégresse; \n\r Nos lèvres s\'ouvriront dans un parfait bonheur, \n\r Et tous, à l\'unisson, répéteront sans cesse \n\r Les gloires de l\'Agneau, du divin Rédempteur.', 3, 106),
(468, 'Strophe', 1, 'Tenons nos lampes prêtes, \n\r Chrétiens, préparons-nous \n\r Pour l\'heure où les trompettes \n\r Annonceront l\'Époux.', 1, 107),
(469, 'Refrain', 1, 'Qu\'à répondre on s\'empresse, \n\r Hosanna! Hosanna! \n\r Et qu\'avec allégresse, \n\r On chante: Alléluia! \n\r On chante: Alléluia!', 2, 107),
(470, 'Strophe', 2, 'Voici déjà les anges! \n\r Avec eux, les élus \n\r Unissent leurs louanges \n\r En l\'honneur de Jésus.', 3, 107),
(471, 'Refrain', 1, 'Qu\'à répondre on s\'empresse, \n\r Hosanna! Hosanna! \n\r Et qu\'avec allégresse, \n\r On chante: Alléluia! \n\r On chante: Alléluia!', 4, 107),
(472, 'Strophe', 3, 'Voici Jésus lui-même, \n\r Puissant, victorieux! \n\r De son pur diadème \n\r L\'éclat remplit les cieux.', 5, 107),
(473, 'Refrain', 1, 'Qu\'à répondre on s\'empresse, \n\r Hosanna! Hosanna! \n\r Et qu\'avec allégresse, \n\r On chante: Alléluia! \n\r On chante: Alléluia!', 6, 107),
(474, 'Strophe', 4, 'Venez, bénis du Père, \n\r Qui m\'avez atttendu; \n\r Entrez dans la lumière, \n\r Le ciel vous est rendu.', 7, 107),
(475, 'Refrain', 1, 'Qu\'à répondre on s\'empresse, \n\r Hosanna! Hosanna! \n\r Et qu\'avec allégresse, \n\r On chante: Alléluia! \n\r On chante: Alléluia!', 8, 107),
(476, 'Strophe', 1, 'Tu ne sais le jour ni l\'heure, \n\r Où ton Maître paraîtra; \n\r Mais attends, veille et demeure, \n\r Assuré qu\'il reviendra. \n\r Confiance! Il s\'avance! \n\r Il s\'avance! Bientôt tout œil le verra.', 1, 108),
(477, 'Strophe', 2, 'Le voici sur la nuée, \n\r Dans l\'éclat de sa beauté! \n\r Sa venue est saluée, \n\r Par son peuple racheté. \n\r Les louanges des saints anges, \n\r Des saints anges, célèbrent sa majesté.', 2, 108),
(478, 'Strophe', 3, 'Dans la gloire de son maître, \n\r Le fidèle serviteur, \n\r Entrera, car il doit être, \n\r Tout comblé par sa faveur, \n\r Son partage l\'héritage, \n\r L\'héritage, d\'un indicible bonheur.', 3, 108),
(479, 'Strophe', 4, 'Le coupable est à sa vue, \n\r Plein d\'horreur pour le passé: \n\r Tant de grâce méconnue, \n\r Tant de pardon poussé! \n\r Le salaire est sévère, \n\r Est sévère, de celui qu\'il a percé.', 4, 108),
(480, 'Strophe', 1, 'Voir mon Sauveur face à face, \n\r Voir Jésus dans sa beauté, \n\r Ô joie! Ô suprême grâce! \n\r Ô bonheur! Félicité!', 1, 109),
(481, 'Refrain', 1, 'Oui, dans ta magnificence \n\r Je te verrai, divin Roi! \n\r Pour toujours en ta présence, \n\r Je serai semblable à toi!', 2, 109),
(482, 'Strophe', 2, 'Ta gloire est encore voilée \n\r Par les effets du péché. \n\r Bientôt sera révélée \n\r Ton ineffable beauté!', 3, 109),
(483, 'Refrain', 1, 'Oui, dans ta magnificence \n\r Je te verrai, divin Roi! \n\r Pour toujours en ta présence, \n\r Je serai semblable à toi!', 4, 109),
(484, 'Strophe', 3, 'Oh! quel instant d\'allégresse, \n\r Quand tes yeux baissés sur moi, \n\r Me diront avec tendresse: \n\r «Je mourus aussi pour toi!»', 5, 109),
(485, 'Refrain', 1, 'Oui, dans ta magnificence \n\r Je te verrai, divin Roi! \n\r Pour toujours en ta présence, \n\r Je serai semblable à toi!', 6, 109),
(486, 'Strophe', 1, 'Descends, Esprit du Dieu vivant, \n\r Tourne vers nous ta face; \n\r Demeure en nous, et retrouvons, \n\r Sur le chemin, ta trace.', 1, 110),
(487, 'Strophe', 2, 'Donne-nous des langues de feu, \n\r L\'unité fraternelle, \n\r Qui ramène les cœurs à Dieu \n\r Par la bonne nouvelle.', 2, 110),
(488, 'Strophe', 3, 'De nos ténèbres, fais le jour, \n\r Du chaos, l\'harmonie. \n\r Chasse la haine par l\'amour, \n\r Et la mort par la vie!', 3, 110),
(489, 'Strophe', 1, 'Brûle, brûle, flamme ardente, \n\r Flamme ardente dans mon cœur! \n\r Brûle et comble mon attente \n\r De lumière et de chaleur.', 1, 111),
(490, 'Refrain', 1, 'Brûle encore, purifie, \n\r Saint-Esprit qui vis en moi! \n\r Vois, ma volonté se plie \n\r À la tienne, par la foi!', 2, 111),
(491, 'Strophe', 2, 'Brûle, brûle, flamme claire, \n\r Flamme claire, feu divin! \n\r Mon désir est de te plaire, \n\r D\'accomplir tout ton dessein.', 3, 111),
(492, 'Refrain', 1, 'Brûle encore, purifie, \n\r Saint-Esprit qui vis en moi! \n\r Vois, ma volonté se plie \n\r À la tienne, par la foi!', 4, 111),
(493, 'Strophe', 3, 'Brûle, brûle, flamme sainte, \n\r Flamme sainte, feu d\'en haut! \n\r Ton amour pur, sans contrainte, \n\r Me donne un amour nouveau.', 5, 111),
(494, 'Refrain', 1, 'Brûle encore, purifie, \n\r Saint-Esprit qui vis en moi! \n\r Vois, ma volonté se plie \n\r À la tienne, par la foi!', 6, 111),
(495, 'Strophe', 1, 'Comme une terre altérée \n\r Soupire après l\'eau du ciel, \n\r Nous appelons la rosée \n\r De ta grâce, Emmanuel!', 1, 112),
(496, 'Refrain', 1, 'Fraîches rosées, \n\r Descendez sur nous tous! \n\r O divines ondées, \n\r Venez, arrosez-nous!', 2, 112),
(497, 'Strophe', 2, 'Descends, ô pluie abondante, \n\r Coule à flots dans notre cœur, \n\r Donne à l\'âme languissante \n\r Une nouvelle fraîcheur.', 3, 112),
(498, 'Refrain', 1, 'Fraîches rosées, \n\r Descendez sur nous tous! \n\r O divines ondées, \n\r Venez, arrosez-nous!', 4, 112),
(499, 'Strophe', 3, 'Ne laisse en nous rien d\'aride \n\r Qui ne soit fertilisé; \n\r Que le cœur le plus avide \n\r Soit pleinement arrosé.', 5, 112),
(500, 'Refrain', 1, 'Fraîches rosées, \n\r Descendez sur nous tous! \n\r O divines ondées, \n\r Venez, arrosez-nous!', 6, 112);
INSERT INTO `strophe` (`idStrophe`, `type`, `identifiant`, `texte`, `position`, `idChant`) VALUES
(501, 'Strophe', 4, 'Oui, que les déserts fleurissent \n\r Sous tes bienfaisantes eaux; \n\r Que les lieux secs reverdissent \n\r Et portent des fruits nouveaux.', 7, 112),
(502, 'Refrain', 1, 'Fraîches rosées, \n\r Descendez sur nous tous! \n\r O divines ondées, \n\r Venez, arrosez-nous!', 8, 112),
(503, 'Strophe', 5, 'Viens, ô salutaire pluie, \n\r Esprit de grâce et de paix, \n\r Répands en nous une vie \n\r Qui ne tarisse jamais.', 9, 112),
(504, 'Refrain', 1, 'Fraîches rosées, \n\r Descendez sur nous tous! \n\r O divines ondées, \n\r Venez, arrosez-nous!', 10, 112),
(505, 'Strophe', 1, 'Ô Saint-Esprit! Descends, descends \n\r Sur cette assemblée en prière; \n\r En nos cœurs faibles, languissants, \n\r Répands ta force et ta lumière', 1, 113),
(506, 'Strophe', 2, 'Ô Saint-Esprit! À ta clarté \n\r Nous voulons tous marcher sans crainte \n\r Sur un chemin de sainteté. \n\r Conduis-nous vers la cité sainte.', 2, 113),
(507, 'Strophe', 3, 'Ô Saint-Esprit! Rends-nous vainqueurs \n\r Dans nos combats, dans nos détresses, \n\r Et pour armer nos faibles cœurs \n\r Verse en eux toutes tes richesses.', 3, 113),
(508, 'Strophe', 4, 'Oui, pour rester toujours debout, \n\r Ce qu\'il nous faut, c\'est ta puissance; \n\r Par elle seule, jusqu\'au bout \n\r Nous lutterons sans défaillance.', 4, 113),
(509, 'Strophe', 5, 'Ô Saint-Esprit! viens comme au jour \n\r De la première Pentecôte, \n\r Et par ta grâce, ô Dieu d\'amour \n\r Que ce lieu soit la chambre haute!', 5, 113),
(510, 'Strophe', 1, 'Pour que le jour qui se lève soit plus beau, \n\r Pour que le ciel de nos rêves soit plus chaud, \n\r Et pour que la joie qui chante soit toujours dans notre vie.', 1, 114),
(511, 'Refrain', 1, 'Ô Seigneur, donne-nous ton Esprit, \n\r Ton Esprit d\'amour!', 2, 114),
(512, 'Strophe', 2, 'Pour que nos cœurs qui te cherchent soient plus forts, \n\r Pour que la paix soit au creux de nos efforts, \n\r Et pour que nos voix te chantent le merci de chaque jour.', 3, 114),
(513, 'Refrain', 1, 'Ô Seigneur, donne-nous ton Esprit, \n\r Ton Esprit d\'amour!', 4, 114),
(514, 'Strophe', 3, 'Pour que ton cœur brûle en nous comme un grand feu, \n\r Pour que l\'espoir illumine enfin nos yeux, \n\r Et pour que nos vies qui chantent soient lumière et vérité.', 5, 114),
(515, 'Refrain', 1, 'Ô Seigneur, donne-nous ton Esprit, \n\r Ton Esprit d\'amour!', 6, 114),
(516, 'Strophe', 1, 'Quand l\'Esprit de Dieu habite en moi \n\r Je chante comme David. \n\r Quand l\'Esprit de Dieu habite en moi \n\r Je chante comme David. \n\r Je chante, je chante, je chante comme David. \n\r Je chante, je chante, je chante comme David.', 1, 115),
(517, 'Strophe', 2, 'Quand l\'Esprit de Dieu habite en moi \n\r Je prie comme David. \n\r Quand l\'Esprit de Dieu habite en moi \n\r Je prie comme David. \n\r Je prie, je prie, je prie comme David. \n\r Je prie, je prie, je prie comme David.', 2, 115),
(518, 'Strophe', 3, 'Quand l\'Esprit de Dieu habite en moi \n\r Je loue comme David. \n\r Quand l\'Esprit de Dieu habite en moi \n\r Je loue comme David. \n\r Je loue, je loue, je loue comme David. \n\r Je loue, je loue, je loue comme David.', 3, 115),
(519, 'Strophe', 4, 'Quand l\'Esprit de Dieu habite en moi \n\r Je danse comme David. \n\r Quand l\'Esprit de Dieu habite en moi \n\r Je danse comme David. \n\r Je danse, je danse, je danse comme David. \n\r Je danse, je danse, je danse comme David.', 4, 115),
(520, 'Strophe', 1, 'Rends-toi maître de notre âme, \n\r Saint-Esprit, Esprit d\'amour! \n\r Que la charité l\'enflamme \n\r Plus ardente chaque jour.', 1, 116),
(521, 'Refrain', 1, 'Nous croyons à ta présence; \n\r Revêts-nous de ta puissance \n\r Et baptise-nous de feu, \n\r Saint-Esprit, Esprit de Dieu!', 2, 116),
(522, 'Strophe', 2, 'Viens, Esprit de la promesse, \n\r Défenseur venu d\'en haut, \n\r Sois vainqueur de nos faiblesses, \n\r Donne-nous des cœurs nouveaux!', 3, 116),
(523, 'Refrain', 1, 'Nous croyons à ta présence; \n\r Revêts-nous de ta puissance \n\r Et baptise-nous de feu, \n\r Saint-Esprit, Esprit de Dieu!', 4, 116),
(524, 'Strophe', 3, 'Saint-Esprit d\'obéissance, \n\r Fais de nous des serviteurs \n\r Engagés sans défaillance \n\r Dans les œuvres du Seigneur!', 5, 116),
(525, 'Refrain', 1, 'Nous croyons à ta présence; \n\r Revêts-nous de ta puissance \n\r Et baptise-nous de feu, \n\r Saint-Esprit, Esprit de Dieu!', 6, 116),
(526, 'Strophe', 4, 'Esprit de vie et de gloire, \n\r Conduis-nous de jour en jour \n\r Et de victoire en victoire, \n\r Jusqu\'au céleste séjour.', 7, 116),
(527, 'Refrain', 1, 'Nous croyons à ta présence; \n\r Revêts-nous de ta puissance \n\r Et baptise-nous de feu, \n\r Saint-Esprit, Esprit de Dieu!', 8, 116),
(528, 'Strophe', 1, 'Réponds à ma prière, \n\r Esprit saint et puissant, \n\r Et verse ta lumière \n\r Dans mon cœur ignorant.', 1, 117),
(529, 'Refrain', 1, 'Esprit qui vivifie, \n\r Répands ta vie en moi, \n\r Réveille et sanctifie \n\r Mon cœur qui croit en toi.', 2, 117),
(530, 'Strophe', 2, 'Viens, parle à ma conscience, \n\r Convaincs-moi de péché, \n\r Et mets en évidence \n\r Le mal le plus caché.', 3, 117),
(531, 'Refrain', 1, 'Esprit qui vivifie, \n\r Répands ta vie en moi, \n\r Réveille et sanctifie \n\r Mon cœur qui croit en toi.', 4, 117),
(532, 'Strophe', 3, 'Et si tu viens surprendre \n\r En moi quelque interdit, \n\r Oh! fais-le moi comprendre, \n\r Sonde mon cœur contrit.', 5, 117),
(533, 'Refrain', 1, 'Esprit qui vivifie, \n\r Répands ta vie en moi, \n\r Réveille et sanctifie \n\r Mon cœur qui croit en toi.', 6, 117),
(534, 'Strophe', 1, 'Saint-Esprit, que ta clarté \n\r Vienne briller sur nos cœurs; \n\r Et que ses rayons vainqueurs \n\r Chassent toute obscurité.', 1, 118),
(535, 'Strophe', 2, 'Saint-Esprit, ô vérité, \n\r Guide infaillible et divin, \n\r Montre nous le droit chemin, \n\r Courbe notre volonté.', 2, 118),
(536, 'Strophe', 3, 'Saint-Esprit, Consolateur, \n\r Viens calmer tous nos effrois. \n\r Apaise, à ta douce voix, \n\r La révolte et la douleur.', 3, 118),
(537, 'Strophe', 4, 'Saint-Esprit, ô toi qui sais \n\r Combien chancellent nos pas. \n\r Ne nous abandonne pas, \n\r Et conduis-nous dans la paix.', 4, 118),
(538, 'Strophe', 1, 'Saint-Esprit, écoute, \n\r Inspire nos chants. \n\r Écarte le doute \n\r Loin de tes enfants.', 1, 119),
(539, 'Strophe', 2, 'Saint-Esprit, inspire \n\r Ton peuple à genoux. \n\r Sous ton seul empire, \n\r Ce moment est doux.', 2, 119),
(540, 'Strophe', 3, 'Saint-Esprit, vient vivre \n\r En nous qui prenons \n\r Le pain de ton livre. \n\r Nous te bénissons.', 3, 119),
(541, 'Strophe', 1, 'Souffle du Dieu vivant, \n\r Anime tout en moi. \n\r Que ton amour me rende aimant \n\r Servant ainsi que toi.', 1, 120),
(542, 'Strophe', 2, 'Souffle du Dieu vivant, \n\r Viens purifier ma foi. \n\r Que ce soit ton vouloir puissant \n\r Qui s\'accomplisse en moi.', 2, 120),
(543, 'Strophe', 3, 'Souffle du Dieu vivant, \n\r Mets ton feu dans mon cœur; \n\r Que tout en moi soit rayonnant \n\r De ton éclat, Seigneur.', 3, 120),
(544, 'Strophe', 4, 'Souffle du Dieu vivant, \n\r Écarte enfin la mort; \n\r Que pour jamais, dès maintenant, \n\r J\'appartienne au Dieu fort.', 4, 120),
(545, 'Strophe', 1, 'Viens, Esprit du Dieu vivant, \n\r Sois le Maître en moi. \n\r Viens, Esprit du Dieu vivant, \n\r Sois le Maître en moi. \n\r Sonde-moi, courbe-moi, \n\r Brise-moi, façonne-moi. \n\r Viens, Esprit du Dieu vivant, \n\r Viens et règne en moi!', 1, 121),
(546, 'Strophe', 1, 'Vous qui sur la terre habitez, \n\r Chantez à haute voix, chantez! \n\r Réjouissez-vous au Seigneur, \n\r Par un saint hymne à son honneur!', 1, 122),
(547, 'Strophe', 2, 'N\'est-il pas le Dieu souverain \n\r Qui nous a formés de sa main, \n\r Nous, le peuple qu\'il veut chérir, \n\r Et le troupeau qu\'il veut nourrir.', 2, 122),
(548, 'Strophe', 3, 'Entrez dans son temple aujourd\'hui; \n\r Venez vous présenter à lui, \n\r Célébrez son nom glorieux, \n\r Et l\'élevez jusques aux cieux.', 3, 122),
(549, 'Strophe', 4, 'C\'est un Dieu rempli de bonté, \n\r D\'une éternelle vérité, \n\r Toujours propice à nos souhaits, \n\r Et sa grâce dure à jamais.', 4, 122),
(550, 'Strophe', 1, 'Béni soit le Seigneur le grand Dieu d\'Israël, \n\r L\'auteur de toute vie, \n\r tout-puissant éternel, \n\r Qui, touché de nos cris et de notre misère, \n\r Dans sa fidélité s\'est montré notre Père.', 1, 123),
(551, 'Strophe', 2, 'Rempli de compassion, il nous a visités; \n\r Il nous donne un Sauveur \n\r qui vient nous racheter; \n\r Et malgré nos péchés, \n\r ce Dieu tendre et propice \n\r A fait lever sur nous le Soleil de justice.', 2, 123),
(552, 'Strophe', 3, 'Tous les peuples assis \n\r dans l\'ombre de la mort \n\r Se lèveront joyeux à la voix du Dieu fort. \n\r Éclairés désormais par sa vive lumière, \n\r Nous marcherons en paix \n\r vers la maison du Père.', 3, 123),
(553, 'Strophe', 1, 'Alléluia! Louez l\'Éternel \n\r Car il est bon, \n\r Car sa miséricorde dure à toujours, \n\r Dure à toujours, amen, \n\r Dure à toujours, amen.', 1, 124),
(554, 'Strophe', 2, 'Non pas à nous, Seigneur, pas à nous, \n\r Mais à ton nom, \n\r À ton nom donne gloire pour ton amour, \n\r Pour ta fidélité, \n\r Pour ta fidélité.', 2, 124),
(555, 'Strophe', 3, 'Oui, chaque jour, louons l\'Éternel! \n\r Il est pour nous \n\r Le Dieu des délivrances, le Dieu puissant \n\r Qui sauve de la mort, \n\r Qui sauve de la mort.', 3, 124),
(556, 'Strophe', 1, 'À toi, mon Dieu, mon cœur monte, \n\r En toi mon espoir est mis. \n\r Dois-je être accablé de honte \n\r Aux yeux de mes ennemis? \n\r Jamais on n\'est confondu \n\r Quand sur toi l\'on se repose. \n\r Mais les méchants sont perdus \n\r Par le mal qu\'ils se proposent.', 1, 125),
(557, 'Strophe', 2, 'Montre-moi toujours la voie \n\r Conduis-moi dans tes chemins. \n\r Que je marche dans la joie, \n\r Sans jamais quitter ta main. \n\r Mets loin de ton souvenir \n\r Les erreurs de ma jeunesse, \n\r Et daigne encor me bénir, \n\r Seigneur, selon ta promesse.', 2, 125),
(558, 'Strophe', 3, 'Le Seigneur donne à connaître \n\r Son secret aux hommes droits. \n\r À leurs yeux, il fait paraître \n\r La sagesse de ses lois. \n\r Le mal vient de toutes parts: \n\r Prends, mon Dieu, prends ma défense, \n\r Car je tourne mes regards \n\r Vers toi seul, mon espérance!', 3, 125),
(559, 'Strophe', 1, 'Bénissons Dieu, notre roi, \n\r le puissant roi de gloire! \n\r De ses bienfaits incessants, \n\r célébrons la mémoire! \n\r Et qu\'en tous lieux, \n\r sur la terre et dans les cieux, \n\r Montent nos chants de victoire!', 1, 126),
(560, 'Strophe', 2, 'Bénissons Dieu, notre roi, \n\r qui toujours fait justice, \n\r À l\'orgueilleux redoutable, \n\r au plus humble propice! \n\r Riche en bonté, \n\r pour ses enfants révoltés \n\r Son amour est sans limite.', 2, 126),
(561, 'Strophe', 3, 'Bénissons Dieu, notre roi, \n\r célébrons ses louanges! \n\r Car il demeure à jamais quand tout passe \n\r et tout change. \n\r Il nous entend: adressons-lui notre chant, \n\r Unis aux chœurs de ses anges!', 3, 126),
(562, 'Strophe', 1, 'Je lève les yeux vers les monts que j\'aime; \n\r D\'où peut me venir ici le secours? \n\r Le secours me vient de l\'Éternel même, \n\r Du Dieu qui créa les nuits et les jours.', 1, 127),
(563, 'Strophe', 2, 'Pourra-t-il souffrir que ton pied chancelle? \n\r Ton gardien peut-il sommeiller jamais? \n\r Non, il ne dort pas, le gardien fidèle, \n\r Celui qui maintient Israël en paix.', 2, 127),
(564, 'Strophe', 3, 'Pour toi l\'Éternel est un retraite; \n\r Il te sert à droite et d\'ombre et d\'appui: \n\r Le soleil ne peut frapper sur ta tête, \n\r Ni la lune à l\'heure où le jour a fui.', 3, 127),
(565, 'Strophe', 4, 'Il te gardera de tout mal possible; \n\r Il garde ton âme, il garde tes jours; \n\r Il te gardera! rentre et sors paisible: \n\r L\'Éternel sur toi veillera toujours.', 4, 127),
(566, 'Strophe', 1, 'Dieu, je te loue car tu m\'as répondu, \n\r Et tu m\'as envoyé ton salut. \n\r Dieu, je te loue, car tu m\'as répondu, \n\r Et tu m\'as envoyé ton salut \n\r Pierre rejetée par la main des ouvriers,…', 1, 128),
(567, 'Strophe', 2, '…Elle est devenue la pierre d\'angle. \n\r Divin envoyé, par les hommes méprisé, \n\r Il est devenu le premier. \n\r Voici le jour que Dieu a fait, \n\r Soyons dans la joie et l\'allégresse,…', 2, 128),
(568, 'Strophe', 3, '…Voici le jour que Dieu a fait, \n\r Chantons notre joie sans cesse. \n\r Pierre rejetée par la main des ouvriers, \n\r Elle est devenue la pierre d\'angle. \n\r Divin envoyé, par les hommes méprisé, \n\r Il est devenu le premier.', 3, 128),
(569, 'Strophe', 1, 'Frappez dans vos mains, \n\r Vous, tous les humains! \n\r À cris redoublés, Peuples assemblés, \n\r Exultez de joie, Car voici le roi. \n\r Redoutable et doux, Dieu veille sur vous; \n\r Son bras souverain, Sa puissante main \n\r Étend à jamais Son règne de paix.', 1, 129),
(570, 'Strophe', 2, 'Si Dieu a choisi Israël pour fils, \n\r S\'il l\'a secouru, S\'il l\'a maintenu, \n\r C\'est pour proclamer Par son bien-aimé: \n\r «En moi s\'uniront Toutes les nations; \n\r Il faut maintenant Que s\'ouvre tout grand, \n\r Partout, pour toujours, Mon règne d\'amour».', 2, 129),
(571, 'Strophe', 3, 'Peuple racheté, Viens ici chanter! \n\r De tes oppresseurs Voici le vainqueur! \n\r Fais sonner du cor, Dieu est juste et fort. \n\r Chantez tous, chantez Sa grande bonté. \n\r Il vient rétablir, Il fait resplendir \n\r Plus haut que les cieux Le règne de Dieu.', 3, 129),
(572, 'Strophe', 1, 'Jésus est mon divin Berger, \n\r Réjouis-toi mon âme! \n\r Seul il délivre du danger, \n\r Tout en moi le réclame!', 1, 130),
(573, 'Strophe', 2, 'Non, je ne manquerai de rien, \n\r Car, oh! grâce infinie, \n\r Jésus est l\'Ami, le Gardien \n\r En qui je me confie.', 2, 130),
(574, 'Strophe', 3, 'Jour après jour, il me conduit \n\r Dans ses verts pâturages; \n\r De son amour il me nourrit, \n\r Sous ses divins ombrages.', 3, 130),
(575, 'Strophe', 4, 'Je ne craindrai donc aucun mal \n\r Quand, au bout du voyage, \n\r Il me faudra, du sombre val, \n\r Franchir le noir passage.', 4, 130),
(576, 'Strophe', 5, 'Toujours sa grâce et son amour \n\r Me suivront sur la terre; \n\r Et tout sera, dans son séjour, \n\r Paix, gloire, amour, lumière.', 5, 130),
(577, 'Strophe', 1, 'Comme un cerf altéré brâme \n\r Après le courant des eaux, \n\r Ainsi soupire mon âme, \n\r Seigneur, après tes ruisseaux; \n\r Elle a soif du Dieu vivant, \n\r Et s\'écrie, en le suivant: \n\r «Ô mon Dieu, \n\r Quand donc sera-ce \n\r Que mes yeux verront ta face?»', 1, 131),
(578, 'Strophe', 2, 'Pour pain je n\'ai que mes larmes, \n\r Et nuit et jour, en tout lieu, \n\r Lorsqu\'en mes dures alarmes \n\r On me dit: «Que fait ton Dieu?» \n\r Je regrette la saison \n\r Où j\'allais en ta maison, \n\r Chantant avec les fidèles \n\r Tes louanges immortelles.', 2, 131),
(579, 'Strophe', 3, 'Mais quel chagrin te dévore? \n\r Mon âme, rassure-toi! \n\r Espère en Dieu, car encore \n\r Il sera loué par moi, \n\r Quand d\'un regard seulement \n\r Il guérira mon tourment. \n\r Même au fort de ma souffrance \n\r J\'attendrai sa délivrance.', 3, 131),
(580, 'Strophe', 1, 'Comme un cerf altéré brâme \n\r Après le courant des eaux, \n\r Ainsi soupire mon âme, \n\r Seigneur, après tes ruisseaux; \n\r Elle a soif du Dieu vivant, \n\r Et s\'écrie, en le suivant: \n\r «Ô mon Dieu, \n\r Quand donc sera-ce \n\r Que mes yeux verront ta face?»', 1, 132),
(581, 'Strophe', 2, 'Pour pain je n\'ai que mes larmes, \n\r Et nuit et jour, en tout lieu, \n\r Lorsqu\'en mes dures alarmes \n\r On me dit: «Que fait ton Dieu?» \n\r Je regrette la saison \n\r Où j\'allais en ta maison, \n\r Chantant avec les fidèles \n\r Tes louanges immortelles.', 2, 132),
(582, 'Strophe', 3, 'Mais quel chagrin te dévore? \n\r Mon âme, rassure-toi! \n\r Espère en Dieu, car encore \n\r Il sera loué par moi, \n\r Quand d\'un regard seulement \n\r Il guérira mon tourment. \n\r Même au fort de ma souffrance \n\r J\'attendrai sa délivrance.', 3, 132),
(583, 'Strophe', 1, 'Il faut, grand Dieu, Que de mon cœur \n\r La sainte ardeur te glorifie \n\r Qu\'à toi, des mains et de la voix, \n\r Devant les rois, je psalmodie. \n\r J\'irai t\'adorer, ô mon Dieu! \n\r En ton saint lieu, D\'un nouveau zèle; \n\r Je chanterai ta vérité, Et ta bonté toujours fidèle.', 1, 133),
(584, 'Strophe', 2, 'Ton nom est célèbre à jamais \n\r Par les effets de tes paroles; \n\r Quand je t\'invoque, tu m\'entends, \n\r Quand il est temps, tu me consoles. \n\r Tous les rois viendront à tes pieds, \n\r Humiliés, prier sans cesse. \n\r Sitôt qu\'ils auront une fois \n\r Oui la voix de ta promesse.', 2, 133),
(585, 'Strophe', 3, 'Ils rempliront, par leurs concerts, \n\r Tout l\'univers de tes louanges. \n\r Les peuples qui les entendront \n\r Admireront tes faits étranges. \n\r Ô grand Dieu! \n\r Qui, de tes hauts cieux, \n\r Dans ces bas lieux vois toute chose, \n\r Quoique tu sembles être loin, \n\r C\'est sur ton soin que tout repose.', 3, 133),
(586, 'Strophe', 4, 'Si mon cœur dans l\'adversité \n\r Est agité, ta main l\'appuie; \n\r C\'est ton bras qui sauve des mains \n\r Des inhumains ma triste vie. \n\r Quand je suis le plus abattu, \n\r C\'est ta vertu qui me relève; \n\r Ce qu\'il t\'a plu de commencer, \n\r Sans se lasser ta main l\'achève.', 4, 133),
(587, 'Refrain', 1, 'Je chanterai le nom du Seigneur \n\r Toujours et partout.', 1, 134),
(588, 'Strophe', 1, 'Oui, je veux jouer, \n\r Je veux chanter pour le Seigneur \n\r Et, de tout mon cœur, \n\r Lui rendre grâce. \n\r Pour sa grandeur, \n\r Pour sa beauté, pour ses bienfaits, \n\r Toute ma vie je chanterai son nom.', 2, 134),
(589, 'Refrain', 1, 'Je chanterai le nom du Seigneur \n\r Toujours et partout.', 3, 134),
(590, 'Strophe', 2, 'Il est mon soutien, \n\r Ma forteresse, mon appui, \n\r Il est mon rempart et mon refuge. \n\r Il est le rocher sur lequel \n\r J\'ai fondé ma vie, \n\r Il me protège \n\r Et j\'ai confiance en Lui.', 4, 134),
(591, 'Refrain', 1, 'Je chanterai le nom du Seigneur \n\r Toujours et partout.', 5, 134),
(592, 'Strophe', 3, 'Il m\'a délivré \n\r Des ennemis qui m\'encerclaient \n\r En criant \n\r Comme des chiens féroces. \n\r Il m\'a guéri, \n\r Il m\'a tiré de mon malheur, \n\r Mon cœur exulte et je lui dis: merci.', 6, 134),
(593, 'Refrain', 1, 'Je chanterai le nom du Seigneur \n\r Toujours et partout.', 7, 134),
(594, 'Strophe', 4, 'Amis, venez! \n\r Criez de joie pour le Seigneur, \n\r Allez à lui en rendant grâce. \n\r Ne fermez pas \n\r Votre cœur à son amour, \n\r Chantez pour lui \n\r Et bénissez son nom.', 8, 134),
(595, 'Refrain', 1, 'Je chanterai le nom du Seigneur \n\r Toujours et partout.', 9, 134),
(596, 'Strophe', 1, 'Laisse-moi désormais, \n\r Seigneur, aller en paix, \n\r Car selon ta promesse \n\r Enfin mes yeux ont vu \n\r Resplendir le salut \n\r Que j\'attendais sans cesse.', 1, 135),
(597, 'Strophe', 2, 'C\'est lui qu\'en l\'univers \n\r Tant de peuples divers \n\r Vont recevoir et croire. \n\r En lui brille ton nom, \n\r Lumière des nations, \n\r Et d\'Israël la gloire.', 2, 135),
(598, 'Strophe', 1, 'L\'âme de douleur atteinte \n\r Je fais au Seigneur ma plainte; \n\r Vers Dieu mon cri montera; \n\r Sa bonté m\'exaucera. \n\r À l\'heure de la détresse, \n\r C\'est à lui que je m\'adresse, \n\r La nuit même, en mon ennui, \n\r Je lève mes mains vers lui.', 1, 136),
(599, 'Strophe', 2, 'Mon âme, dans sa souffrance, \n\r Refusait toute assistance; \n\r De Dieu je me souvenais, \n\r De mes maux je m\'étonnais; \n\r Je repassais dans mes veilles \n\r Des temps anciens les merveilles, \n\r Et je restais abattu, \n\r Sans courage et sans vertu.', 2, 136),
(600, 'Strophe', 3, 'Je me disais en moi-même: \n\r Est-il vrai que mon Dieu m\'aime? \n\r Dois-je croire désormais \n\r Qu\'il m\'abandonne à jamais? \n\r Ai-je épuisé ses largesses? \n\r Oublierait-il ses promesses, \n\r Et dans son juste courroux, \n\r N\'a-il plus de pitié de nous?', 3, 136),
(601, 'Strophe', 4, 'Mais voici, j\'ai souvenance \n\r De plus d\'une délivrance \n\r Que le bras de l\'Éternel \n\r Accomplit pour Israël. \n\r De ses grands exploits la gloire \n\r Me revint à la mémoire. \n\r J\'irai redire en tout lieu: \n\r Qui donc est grand comme Dieu?', 4, 136),
(602, 'Strophe', 5, 'Éternel, ta voix est sainte \n\r Et je l\'adore avec crainte; \n\r Tour à tour tu nous fais voir \n\r Ta clémence et ton pouvoir: \n\r Toi qui lances le tonnerre \n\r Et qui fais trembler la terre, \n\r Tu daignes nous diriger \n\r Comme un fidèle berger.', 5, 136),
(603, 'Strophe', 1, 'Je louerai l\'Éternel De tout mon cœur. \n\r Je raconterai toutes Tes merveilles, \n\r Je chanterai Ton nom. \n\r Je louerai l\'Éternel De tout mon cœur. \n\r Je ferai de Toi le sujet de ma joie. \n\r Alléluia!', 1, 137),
(604, 'Strophe', 2, 'Dieu, l\'Éternel est roi, Il règne à jamais. \n\r Pour le jugement, Il dresse son trône: \n\r Il jugera la terre. \n\r Dieu, l\'Éternel est roi, Il règne à jamais. \n\r Le monde verra La force de son bras. \n\r Alléluia!', 2, 137),
(605, 'Strophe', 3, 'Dieu voit les opprimés, Il est leur abri, \n\r Leur refuge au temps \n\r Des grandes détresses, \n\r Son nom est leur salut. \n\r Dieu voit les opprimés, Il est leur abri. \n\r Il sauve les siens, Car il est le Dieu saint. \n\r Alléluia!', 3, 137),
(606, 'Strophe', 4, 'Gloire au Père et au Fils \n\r Et au Saint-Esprit, Au commencement, \n\r Aujourd\'hui, toujours \n\r Et aux siècles des siècles! \n\r Gloire au Père et au Fils Et au Saint-Esprit, \n\r D\'une éternité à l\'autre éternité, \n\r Alléluia!', 4, 137),
(607, 'Strophe', 1, 'Le Seigneur seul est ma lumière, \n\r Ma délivrance et mon appui. \n\r Qu\'aurais-je à craindre sur la terre \n\r Puisque ma force est toute en lui?', 1, 138),
(608, 'Refrain', 1, 'Le Seigneur seul est ma lumière, \n\r Ma délivrance et mon appui. \n\r Le Seigneur seul est ma lumière, \n\r Ma délivrance et mon appui.', 2, 138),
(609, 'Strophe', 2, 'Pour m\'assaillir, quand une armée \n\r Tout autour de moi camperait, \n\r Mon âme, sans être alarmée, \n\r En l\'Éternel s\'assurerait.', 3, 138),
(610, 'Refrain', 1, 'Le Seigneur seul est ma lumière, \n\r Ma délivrance et mon appui. \n\r Le Seigneur seul est ma lumière, \n\r Ma délivrance et mon appui.', 4, 138),
(611, 'Strophe', 3, 'Son bras puissant, à ma requête, \n\r Un prompt secours me prêtera. \n\r Et dans le fort de la tempête, \n\r Sur un rocher m\'élèvera.', 5, 138),
(612, 'Refrain', 1, 'Le Seigneur seul est ma lumière, \n\r Ma délivrance et mon appui. \n\r Le Seigneur seul est ma lumière, \n\r Ma délivrance et mon appui.', 6, 138),
(613, 'Strophe', 4, 'Mes yeux verront la délivrance \n\r Que mon Sauveur m\'accordera. \n\r Aussi mon cœur, plein d\'assurance, \n\r En l\'attendant s\'affermira.', 7, 138),
(614, 'Refrain', 1, 'Le Seigneur seul est ma lumière, \n\r Ma délivrance et mon appui. \n\r Le Seigneur seul est ma lumière, \n\r Ma délivrance et mon appui.', 8, 138),
(615, 'Refrain', 1, 'Ô ma joie, quand je suis parti \n\r Pour la maison du Seigneur.', 1, 139),
(616, 'Strophe', 1, 'Quelle joie quand on m\'a dit: \n\r «Allons à la maison du Seigneur!» \n\r Et maintenant notre marche prend fin \n\r Devant tes portes, Jérusalem. \n\r Jérusalem bâtie comme une ville \n\r Où tout ensemble fait corps.', 2, 139),
(617, 'Refrain', 1, 'Ô ma joie, quand je suis parti \n\r Pour la maison du Seigneur.', 3, 139),
(618, 'Strophe', 2, 'C\'est là que montent les tribus \n\r Les tribus du Seigneur; \n\r C\'est là qu\'Israël doit rendre grâces \n\r Et confesser le nom du Seigneur; \n\r C\'est là le siège du pouvoir, \n\r Le siège de la maison de David.', 4, 139),
(619, 'Refrain', 1, 'Ô ma joie, quand je suis parti \n\r Pour la maison du Seigneur.', 5, 139),
(620, 'Strophe', 3, 'Appelez la paix sur Jérusalem: \n\r «Ceux qui t\'aiment \n\r Seront réconciliés. \n\r Que la paix soit dans tes murs \n\r Et la sécurité dans tes parvis.»', 6, 139),
(621, 'Refrain', 1, 'Ô ma joie, quand je suis parti \n\r Pour la maison du Seigneur.', 7, 139),
(622, 'Strophe', 4, 'Pour l\'amour de mes frères, \n\r De mes amis, \n\r Je dis: «Que la paix soit sur toi!» \n\r Par amour pour la maison \n\r Du Seigneur, notre Dieu, \n\r Je demande pour toi le bonheur.', 8, 139),
(623, 'Refrain', 1, 'Ô ma joie, quand je suis parti \n\r Pour la maison du Seigneur.', 9, 139),
(624, 'Strophe', 5, 'Gloire au Père, au Fils, \n\r Au Saint-Esprit, \n\r Maintenant et à jamais, \n\r Au Dieu qui est, \n\r Qui était et qui vient, \n\r Dans les siècles des siècles.', 10, 139),
(625, 'Refrain', 1, 'Ô ma joie, quand je suis parti \n\r Pour la maison du Seigneur.', 11, 139),
(626, 'Strophe', 1, 'Oh! Que c\'est chose belle \n\r De te louer, Seigneur, \n\r De chanter ta splendeur \n\r Au milieu des fidèles; \n\r Quand le jour vient de naître, \n\r D\'annoncer ta bonté \n\r Et ta fidélité Quand la nuit va paraître.', 1, 140),
(627, 'Strophe', 2, 'Tes œuvres surprenantes \n\r Ont réjoui mon cœur, Et je dirai, Seigneur, \n\r Leur sagesse étonnante; \n\r Tes pensées sont profondes, \n\r Plus il les étudie, \n\r Plus l\'homme est interdit; \n\r Ta main garde le monde.', 2, 140),
(628, 'Strophe', 3, 'Si les méchants fleurissent \n\r Comme l\'ivraie des champs \n\r Et si des arrogants les projets réussissent, \n\r C\'est pour qu\'ils disparaissent, \n\r Par la mort emportés, \n\r Et que soient dévoilés \n\r Les plans de ta sagesse.', 3, 140),
(629, 'Strophe', 4, 'Tu oins d\'une huile fraîche \n\r Le front de ton enfant; \n\r On le voit rayonnant, \n\r Vigoureux comme un cèdre. \n\r Sa gloire et sa richesse \n\r Sont d\'orner ta maison; \n\r Tes fruits chaque saison \n\r Combleront sa vieillesse.', 4, 140),
(630, 'Strophe', 1, 'Oui, je bénirai Dieu \n\r Tout le temps de ma vie; \n\r Et dirai sa bonté. \n\r En lui jour après jour \n\r Mon cœur se glorifie, \n\r C\'est ma sécurité. (bis)', 1, 141),
(631, 'Strophe', 2, 'Chrétiens, magnifions \n\r Et louons tous ensemble \n\r Le beau nom du Sauveur! \n\r Ses élus, à leurs cris, \n\r Sous son aile il rassemble, \n\r Et chasse leur frayeur. (bis)', 2, 141),
(632, 'Strophe', 3, 'L\'ange de l\'Éternel \n\r Se campe avec puissance \n\r Autour de ses enfants; \n\r Il les garde et soutient; \n\r Il est leur délivrance \n\r Dans leurs dangers pressants. (bis)', 3, 141),
(633, 'Strophe', 4, 'Venez et savourez, \n\r Sous son paisible empire, \n\r Sa fidèle bonté. \n\r Oh! que l\'homme est heureux, \n\r Qui vers Dieu se retire \n\r En sa calamité! (bis)', 4, 141),
(634, 'Strophe', 5, 'Dieu protège ses saints; \n\r Il rachète leur âme; \n\r Il garde tous leurs os. \n\r Oui, l\'homme qui le craint \n\r Et qui son nom réclame \n\r Vivra dans le repos. (bis)', 5, 141),
(635, 'Strophe', 1, 'Peuples, louez le grand Dieu, \n\r Qui réside en son saint lieu; \n\r Lui qui, d\'un mot seulement, \n\r A créé le firmament. \n\r Louez sa magnificence; \n\r Louez-le pour ses bienfaits, \n\r Et pour tous les grands effets \n\r De sa suprême puissance.', 1, 142),
(636, 'Strophe', 2, 'Jusque dans l\'éternité, \n\r Qu\'on célèbre sa bonté, \n\r Et que son nom glorieux \n\r Soit élevé jusqu\'aux cieux; \n\r Qu\'enfin tout ce qui respire, \n\r Qui vit, qui peut se mouvoir, \n\r Chante avec moi son pouvoir \n\r Et son glorieux empire.', 2, 142),
(637, 'Strophe', 1, 'Louez Dieu, car il est bon, \n\r Louez-le pour tous ses dons. \n\r Sa bonté dure à toujours, \n\r Éternel est son amour.', 1, 143),
(638, 'Refrain', 1, 'Sa bonté dure à toujours, \n\r Éternel est son amour.', 2, 143),
(639, 'Strophe', 2, 'Gloire au Seigneur des seigneurs, \n\r Au tout puissant Créateur. \n\r Sa bonté dure à toujours, \n\r Éternel est son amour.', 3, 143),
(640, 'Refrain', 1, 'Sa bonté dure à toujours, \n\r Éternel est son amour.', 4, 143),
(641, 'Strophe', 3, 'Ses ouvrages sont parés \n\r De sagesse et de beauté. \n\r Sa bonté dure à toujours, \n\r Éternel est son amour.', 5, 143),
(642, 'Refrain', 1, 'Sa bonté dure à toujours, \n\r Éternel est son amour.', 6, 143),
(643, 'Strophe', 4, 'Il a déployé les cieux, \n\r Il les a semés de feux. \n\r Sa bonté dure à toujours, \n\r Éternel est son amour.', 7, 143),
(644, 'Refrain', 1, 'Sa bonté dure à toujours, \n\r Éternel est son amour.', 8, 143),
(645, 'Strophe', 5, 'Il a donné les saisons, \n\r Les labours et les moissons. \n\r Sa bonté dure à toujours, \n\r Éternel est son amour', 9, 143),
(646, 'Refrain', 1, 'Sa bonté dure à toujours, \n\r Éternel est son amour.', 10, 143),
(647, 'Strophe', 6, 'Pour son peuple il a ouvert \n\r La route au creux de la mer. \n\r Sa bonté dure à toujours, \n\r Éternel est son amour.', 11, 143),
(648, 'Refrain', 1, 'Sa bonté dure à toujours, \n\r Éternel est son amour.', 12, 143),
(649, 'Strophe', 7, 'Quand nous étions opprimés, \n\r Sa main nous a relevés. \n\r Sa bonté dure à toujours, \n\r Éternel est son amour.', 13, 143),
(650, 'Refrain', 1, 'Sa bonté dure à toujours, \n\r Éternel est son amour.', 14, 143),
(651, 'Strophe', 8, 'Il nous donne d\'habiter \n\r Le pays de sa bonté. \n\r Sa bonté dure à toujours, \n\r Éternel est son amour.', 15, 143),
(652, 'Refrain', 1, 'Sa bonté dure à toujours, \n\r Éternel est son amour.', 16, 143),
(653, 'Strophe', 9, 'Pour tout homme il multiplie \n\r Un pain de force et de vie. \n\r Sa bonté dure à toujours, \n\r Éternel est son amour.', 17, 143),
(654, 'Refrain', 1, 'Sa bonté dure à toujours, \n\r Éternel est son amour.', 18, 143),
(655, 'Strophe', 10, 'Rendez grâce au Dieu des cieux, \n\r Tous ses dons sont merveilleux. \n\r Sa bonté dure à toujours, \n\r Éternel est son amour.', 19, 143),
(656, 'Refrain', 1, 'Sa bonté dure à toujours, \n\r Éternel est son amour.', 20, 143),
(657, 'Strophe', 1, 'Peuples, venez, et qu\'on entende \n\r Partout votre hymne solennel! \n\r Qu\'en cris de joie on se répande, \n\r Et que l\'on dise à l\'Éternel: \n\r Combien tu te montres terrible, \n\r Seigneur, en tout ce que tu fais! \n\r L\'ennemi longtemps invincible \n\r S\'abaisse pour avoir la paix.', 1, 144),
(658, 'Strophe', 2, 'Toute la terre est prosternée \n\r Au pied de ton trône, Seigneur! \n\r Et de tes bienfaits couronnée \n\r Elle célèbre ta grandeur. \n\r Venez, rendez-lui vos hommages, \n\r Et jugez, d\'un commun accord, \n\r Si tant de merveilleux ouvrages \n\r Sont d\'un autre que du Dieu fort.', 2, 144),
(659, 'Strophe', 3, 'Peuples, que toujours on bénisse \n\r Du Seigneur le nom glorieux, \n\r Et que le monde retentisse \n\r De ses louanges en tous lieux: \n\r C\'est lui qui garde notre vie, \n\r Et conduit sûrement nos pas; \n\r C\'est lui dont la grâce infinie \n\r Nous a garantis du trépas.', 3, 144),
(660, 'Strophe', 4, 'Seigneur, ta justice divine \n\r Voulut éprouver notre foi, \n\r Comme l\'argent que l\'on affine \n\r Lorsqu\'il n\'est pas de bon aloi. \n\r Mais, nous délivrant par ta grâce, \n\r Tu nous donnes des jours heureux, \n\r Et moi, j\'irai devant ta face, \n\r Éternel, accomplir mes vœux.', 4, 144),
(661, 'Strophe', 5, 'Venez tous et que je proclame, \n\r En la présence du Seigneur, \n\r Le bien qu\'il a fait à mon âme, \n\r Alors qu\'il m\'a vu droit de cœur. \n\r Béni soit mon Dieu secourable, \n\r Qui jamais ne m\'a rejeté, \n\r Et de moi, pécheur misérable, \n\r N\'a point détourné sa bonté.', 5, 144),
(662, 'Refrain', 1, 'Tournez les yeux vers le Seigneur, \n\r Et rayonnez de joie! \n\r Chantez son nom de tout votre cœur, \n\r Il est votre Sauveur, \n\r C\'est lui votre Seigneur.', 1, 145),
(663, 'Strophe', 1, 'J\'ai recherché le Seigneur \n\r Et il m\'a écouté, \n\r Il m\'a guéri de mes peurs \n\r Et sans fin je le louerai.', 2, 145),
(664, 'Refrain', 1, 'Tournez les yeux vers le Seigneur, \n\r Et rayonnez de joie! \n\r Chantez son nom de tout votre cœur, \n\r Il est votre Sauveur, \n\r C\'est lui votre Seigneur.', 3, 145),
(665, 'Strophe', 2, 'Dieu regarde ceux qu\'il aime, \n\r Il écoute leur voix, \n\r Il consolera leur peine \n\r Et il guidera leurs pas.', 4, 145),
(666, 'Refrain', 1, 'Tournez les yeux vers le Seigneur, \n\r Et rayonnez de joie! \n\r Chantez son nom de tout votre cœur, \n\r Il est votre Sauveur, \n\r C\'est lui votre Seigneur.', 5, 145),
(667, 'Strophe', 3, 'Ceux qui cherchent le Seigneur \n\r Ne manqueront de rien, \n\r En lui ouvrant grand leur cœur \n\r Ils seront comblés de biens.', 6, 145),
(668, 'Refrain', 1, 'Tournez les yeux vers le Seigneur, \n\r Et rayonnez de joie! \n\r Chantez son nom de tout votre cœur, \n\r Il est votre Sauveur, \n\r C\'est lui votre Seigneur.', 7, 145),
(669, 'Strophe', 1, 'Que Dieu se montre seulement, \n\r Et l\'on verra dans un moment \n\r Abandonner la place; \n\r Le camp des ennemis épars, \n\r Épouvanté de toutes parts, \n\r Fuira devant sa face…', 1, 146),
(670, 'Strophe', 1, '…On verra tout ce camp s\'enfuir \n\r Comme l\'on voit s\'évanouir \n\r Une épaisse fumée. \n\r Comme la cire fond au feu, \n\r Ainsi des méchants devant Dieu \n\r La force est consumée.', 2, 146),
(671, 'Strophe', 2, 'Réjouissez-vous devant lui! \n\r Il est des orphelins l\'appui, \n\r Le défenseur, le père; \n\r Il est des veuves le recours \n\r Et de son peuple, tous les jours, \n\r Il entend la prière…', 3, 146),
(672, 'Strophe', 2, '…Ce Dieu puissant, par sa bonté, \n\r Ramène la prospérité, \n\r La paix et l\'allégresse; \n\r Du captif il brise les fers \n\r Du juste, dans tout l\'univers, \n\r Son œil voit la détresse.', 4, 146),
(673, 'Strophe', 3, 'Ô Dieu! ceux qui t\'ont résisté \n\r Viendront avec humilité \n\r Au palais de ta gloire. \n\r Béni sois-tu, Dieu tout-puissant, \n\r Qui, des hauts cieux nous exauçant, \n\r Nous donnes la victoire!…', 5, 146),
(674, 'Strophe', 3, '…L\'Éternel est notre recours, \n\r Et nous avons par son secours \n\r Trouvé la délivrance. \n\r Il est le Dieu bon, le Dieu fort; \n\r Il est, en face de la mort, \n\r Notre seule espérance.', 6, 146),
(675, 'Strophe', 4, 'Louez ce Dieu si glorieux \n\r Qui voit sous ses pieds les hauts cieux \n\r Qu\'il a formés lui-même, \n\r Et de qui la puissante voix \n\r Fait trembler et peuples et rois \n\r Par sa force suprême…', 7, 146),
(676, 'Strophe', 4, '…Soumettez-vous à l\'Éternel; \n\r Reconnaissez qu\'en Israël \n\r Sa gloire est établie, \n\r Comme on voit luire dans les airs, \n\r Parmi la foudre et les éclairs, \n\r Sa puissance infinie.', 8, 146),
(677, 'Strophe', 1, 'Vous, créatures du Seigneur, Alléluia! \n\r Chantez ensemble à son honneur: Alléluia! \n\r Louez le nom de l\'Éternel, Alléluia! \n\r Régnant sur terre comme au ciel! Alléluia! \n\r Alléluia! Alléluia! Alléluia!', 1, 147),
(678, 'Strophe', 2, 'Louez le Seigneur dans les cieux, Alléluia! \n\r Louez-le dans les plus hauts lieux, Alléluia! \n\r Soleil et lune, feux ardents, Alléluia! \n\r Et vous, les anges rayonnants! Alléluia! \n\r Alléluia! Alléluia! Alléluia!', 2, 147),
(679, 'Strophe', 3, 'Acclamez-le, vergers et bois, Alléluia! \n\r Monts et coteaux, tous à la fois, Alléluia! \n\r Et vous les animaux divers, Alléluia! \n\r Louez le Dieu de l\'univers! Alléluia! \n\r Alléluia! Alléluia! Alléluia!', 3, 147),
(680, 'Strophe', 4, 'Célébrez-le, petits et grands, Alléluia! \n\r Vous, les enfants et les parents, Alléluia! \n\r Vous, tous les peuples, d\'une voix, Alléluia! \n\r Bénissez Dieu, le Roi des rois! Alléluia! \n\r Alléluia! Alléluia! Alléluia!', 4, 147),
(681, 'Strophe', 5, 'Dieu trois fois saint, nous te louons, Alléluia! \n\r Nous te chantons, nous t\'adorons, Alléluia! \n\r Louange au Père, gloire au Fils. Alléluia! \n\r Et béni soit le Saint-Esprit! Alléluia! \n\r Alléluia! Alléluia! Alléluia!', 5, 147),
(682, 'Strophe', 1, 'Adorons le Roi, Le Roi glorieux! \n\r Acceptons sa loi, Qui nous rend heureux! \n\r Son pouvoir nous garde, \n\r Son amour nous suit, \n\r Son œil nous regarde \n\r Le jour et la nuit.', 1, 148),
(683, 'Strophe', 2, 'Racontons en chœur Les dons qu\'il nous fit: \n\r Un puissant Sauveur \n\r Et son Saint-Esprit. \n\r Maître du tonnerre Et des ouragans, \n\r C\'est un tendre Père \n\r Pour tous ses enfants.', 2, 148),
(684, 'Strophe', 3, 'Ses divines lois Règlent l\'univers; \n\r Sa puissante voix Apaise les mers. \n\r Il est notre Maître, \n\r Notre défenseur; \n\r En Christ il veut être \n\r Notre Rédempteur.', 3, 148),
(685, 'Strophe', 4, 'Ses compassions Dépassent encor \n\r L\'océan, les monts Et les astres d\'or. \n\r Chantons ses louanges, \n\r Pécheurs rachetés, \n\r Et comme les anges \n\r Disons ses bontés.', 4, 148),
(686, 'Strophe', 1, 'À toi soit la gloire, Grand Dieu Créateur! \n\r Que tout genou ploie \n\r Devant ta grandeur. \n\r Que tout homme exalte Ton nom glorieux; \n\r Que toute la terre \n\r T\'adore en tout lieu.', 1, 149),
(687, 'Strophe', 2, 'À toi soit la gloire, Jésus mon Sauveur! \n\r Don expiatoire, Vrai libérateur. \n\r Que chacun bénisse \n\r Ton sang précieux. \n\r Est-il sur la terre \n\r Nom plus merveilleux!', 2, 149),
(688, 'Strophe', 3, 'À toi soit la gloire, Saint-Esprit de Dieu! \n\r Transforme mon être En temple des cieux. \n\r Mon cœur te réclame, \n\r Ô consolateur, \n\r Inonde mon âme, \n\r Source de bonheur.', 3, 149),
(689, 'Strophe', 1, 'Vers toi monte notre hommage, \n\r Fils de Dieu, puissant Sauveur, \n\r Qui demeures d\'âge en âge \n\r Le refuge du pécheur.', 1, 150),
(690, 'Refrain', 1, 'Loué soit ton amour, Loués soient à jamais \n\r Ton nom, Jésus, \n\r Ta gloire et tes bienfaits, \n\r Loué soit ton amour, \n\r Loués soient à jamais \n\r Ton nom, ta gloire Et tes bienfaits!', 2, 150),
(691, 'Strophe', 2, 'Par ta divine Parole \n\r Tu nous guides, nous instruis, \n\r Et, par l\'Esprit qui console, \n\r Sûrement tu nous conduis.', 3, 150),
(692, 'Refrain', 1, 'Loué soit ton amour, Loués soient à jamais \n\r Ton nom, Jésus, \n\r Ta gloire et tes bienfaits, \n\r Loué soit ton amour, \n\r Loués soient à jamais \n\r Ton nom, ta gloire Et tes bienfaits!', 4, 150),
(693, 'Strophe', 3, 'Bientôt, glorieuse attente! \n\r Tu combleras tous nos vœux: \n\r Sur la nue étincelante, \n\r Tu viendras à nous des cieux', 5, 150),
(694, 'Refrain', 1, 'Loué soit ton amour, Loués soient à jamais \n\r Ton nom, Jésus, \n\r Ta gloire et tes bienfaits, \n\r Loué soit ton amour, \n\r Loués soient à jamais \n\r Ton nom, ta gloire Et tes bienfaits!', 6, 150),
(695, 'Strophe', 1, 'Chantez l\'Éternel, votre Dieu, Alléluia! \n\r Vous les saints, vivant en ces lieux, Alléluia! \n\r Et les anges dans les cieux, Alléluia! \n\r Célébrez ses dons merveilleux! Alléluia!', 1, 151),
(696, 'Strophe', 2, 'Proclamez dans tout l\'univers, Alléluia! \n\r Au ciel et sur la terre entière, Alléluia! \n\r D\'âge en âge, acclamez sa grandeur, Alléluia! \n\r N\'oubliez jamais sa splendeur! Alléluia!', 2, 151),
(697, 'Strophe', 3, 'Louez Dieu, tous ses bienheureux, Alléluia! \n\r Son pardon toujours gracieux, Alléluia! \n\r Sa compassion et sa charité, Alléluia! \n\r Se prolongent à perpétuité. Alléluia!', 3, 151),
(698, 'Strophe', 1, 'Bénis-nous, Dieu, notre Roi, \n\r Apprends-nous à te louer, \n\r À te chanter. \n\r Père si merveilleux, pour nous victorieux, \n\r Nous nous tournons vers toi, ô notre Roi.', 1, 152),
(699, 'Strophe', 2, 'Bénis-nous, Seigneur Jésus, \n\r Descendu pour nous des cieux, \n\r Agneau de Dieu. \n\r Toi, notre frère, notre lumière, \n\r Par toi vient le salut, Seigneur Jésus.', 2, 152),
(700, 'Strophe', 3, 'Bénis-nous, ô Saint-Esprit, \n\r Messager de vérité, Viens nous aider. \n\r Toi qui es tout puissant, \n\r Donne-nous maintenant \n\r Ta sagesse infinie, ô Saint-Esprit.', 3, 152),
(701, 'Strophe', 4, 'À toi Seigneur Dieu d\'amour, \n\r Père, Fils et Saint-Esprit, \n\r Soit aujourd\'hui \n\r Toute la gloire. Notre victoire \n\r Est de t\'aimer toujours, Seigneur d\'amour!', 4, 152),
(702, 'Strophe', 1, 'Bénissez l\'Éternel! (2×) \n\r Que tout ce qui est en moi Bénisse son nom! \n\r Bénissez l\'Éternel! (2×) \n\r Que tout ce qui est en moi Bénisse son nom! \n\r Bénissez l\'Éternel! (2×) \n\r Que tout ce qui est en moi Bénisse son nom!', 1, 153),
(703, 'Strophe', 1, 'Magnifique est le Seigneur, \n\r Car il s\'est tourné vers moi. \n\r Sa louange est en mon cœur: \n\r Alléluia, alléluia!', 1, 154),
(704, 'Strophe', 2, 'Que puissance, gloire, honneur \n\r Soient à Dieu en qui je crois; \n\r Il est mon libérateur, \n\r Alléluia, alléluia!', 2, 154),
(705, 'Strophe', 1, 'Bénissons Dieu par nos cantiques, \n\r Exaltons-le d\'un même cœur. \n\r Chantons ses exploits magnifiques, \n\r Son grand amour, son bras vainqueur.', 1, 155),
(706, 'Refrain', 1, 'Louons, chantons \n\r Louons le grand nom du Seigneur \n\r Louons, chantons \n\r Louons notre Dieu rédempteur!', 2, 155),
(707, 'Strophe', 2, 'Pour nous il a donné sa vie, \n\r Nous sommes son peuple à jamais, \n\r Louons donc sa grâce infinie, \n\r Chantons son amour, ses bienfaits.', 3, 155),
(708, 'Refrain', 1, 'Louons, chantons \n\r Louons le grand nom du Seigneur \n\r Louons, chantons \n\r Louons notre Dieu rédempteur!', 4, 155),
(709, 'Strophe', 3, 'Ensemble donnons gloire au Père \n\r Qui des cieux nous a visités, \n\r Au Sauveur qui sur le Calvaire, \n\r Par son sang nous a rachetés.', 5, 155),
(710, 'Refrain', 1, 'Louons, chantons \n\r Louons le grand nom du Seigneur \n\r Louons, chantons \n\r Louons notre Dieu rédempteur!', 6, 155),
(711, 'Strophe', 4, 'Reçois, ô Dieu, tout notre hommage; \n\r Reste à jamais notre secours. \n\r Ton amour dure d\'âge en âge; \n\r Ta bonté subsiste à toujours.', 7, 155),
(712, 'Refrain', 1, 'Louons, chantons \n\r Louons le grand nom du Seigneur \n\r Louons, chantons \n\r Louons notre Dieu rédempteur!', 8, 155),
(713, 'Strophe', 1, 'Chaque jour, Acclamons le Seigneur! \n\r Chaque jour, Acclamons le Seigneur! \n\r Alléluia, alléluia, alléluia! \n\r Aujourd\'hui, louons Dieu \n\r De tout notre cœur!', 1, 156),
(714, 'Strophe', 1, 'Chante alléluia au Seigneur \n\r (Chante alléluia au Seigneur) \n\r Chante alléluia au Seigneur \n\r (Chante alléluia)', 1, 157),
(715, 'Strophe', 2, 'Chante alléluia, chante alléluia \n\r (Alléluia) \n\r Chante alléluia au Seigneur \n\r (Chante alléluia au Seigneur)', 2, 157),
(716, 'Strophe', 3, 'Chante alléluia au Seigneur \n\r (Chante alléluia au Seigneur) \n\r Chante alléluia au Seigneur \n\r (Chante alléluia)', 3, 157),
(717, 'Strophe', 4, 'Chante alléluia, chante alléluia \n\r (Alléluia) \n\r Chante alléluia au Seigneur \n\r (Chante alléluia au Seigneur)', 4, 157),
(718, 'Strophe', 1, 'Chante, chante peuple joyeux, \n\r Toi qui aimes ton Dieu! \n\r Chante, chante peuple joyeux, \n\r Toi qui aimes ton Dieu! \n\r Seigneur, nous te chanterons…', 1, 158),
(719, 'Strophe', 2, '…Nous proclamerons ton nom. \n\r En toi nous nous confions, \n\r Tu es fidèle et bon. \n\r En toi nous nous confions, \n\r Tu es fidèle et bon.', 2, 158),
(720, 'Strophe', 1, 'Louez, vous tous les peuples, \n\r Chantez pour le Seigneur! \n\r Il est toujours fidèle, \n\r Chantez: Alléluia!', 1, 159),
(721, 'Strophe', 1, 'Dieu, en qui notre espoir se fonde, \n\r Les anges te rendent l\'honneur; \n\r Maître de la terre et de l\'onde, \n\r Tu es pour nous plein de douceur.', 1, 160),
(722, 'Strophe', 2, 'Les rois, les grands de notre monde, \n\r Devant toi se prosterneront; \n\r Et remplis d\'une paix profonde \n\r Tous les peuples te serviront.', 2, 160),
(723, 'Strophe', 3, 'Le malheureux criant sa peine, \n\r Celui dont la vie est brisée, \n\r Sera délivré de la haine \n\r Et jouira de tes bontés.', 3, 160),
(724, 'Strophe', 4, 'Béni sois-tu, Seigneur de gloire, \n\r Béni soit ce jour merveilleux \n\r Où les tiens verront la victoire \n\r De leur Sauveur venu des cieux.', 4, 160),
(725, 'Strophe', 1, 'Donnons tous gloire au Seigneur \n\r Et proclamons son saint nom, \n\r Son salut, ses merveilles, \n\r Son pardon et sa paix. \n\r Chantons un cantique nouveau.', 1, 161),
(726, 'Strophe', 2, 'Cieux, résonnez de bonheur, \n\r Applaudissez le Seigneur! \n\r Sa bonté, sa justice, \n\r Son amour éternel \n\r Demeurent sans fin pour les siens.', 2, 161),
(727, 'Strophe', 3, 'Qu\'en des accents glorieux \n\r Tous nous disions notre joie, \n\r Et qu\'au son des cymbales, \n\r De la lyre et du cor \n\r Nous rendions honneur au grand Roi.', 3, 161),
(728, 'Strophe', 4, 'Mer et montagnes, dansez \n\r Et bondissez devant Dieu, \n\r Car il juge les peuples \n\r En parfaite équité. \n\r Louons le Seigneur qui revient.', 4, 161),
(729, 'Strophe', 1, 'Gloire, gloire à l\'Éternel! \n\r Qu\'un cantique solennel (bis) \n\r De nos cœurs monte à son trône! \n\r Quand il crée, oh! qu\'il est grand, (bis) \n\r Qu\'il est juste en punissant! \n\r Qu\'il est bon quand il pardonne!', 1, 162),
(730, 'Strophe', 2, 'Il parle! … et cet univers \n\r Se lève aux puissants concerts (bis) \n\r De sa parole vivante; \n\r Et des astres radieux (bis) \n\r Sa main jette dans les cieux \n\r La poussière étincelante.', 2, 162),
(731, 'Strophe', 3, 'Ô Dieu, que tes rachetés \n\r Toujours chantent les bontés (bis) \n\r De celui qui leur pardonne! \n\r Gloire, gloire à l\'Éternel! (bis) \n\r Ce cantique solennel \n\r Montera jusqu\'à son trône.', 3, 162),
(732, 'Strophe', 1, 'Grand Dieu, nous te bénissons, \n\r Nous célébrons tes louanges; \n\r Éternel, nous t\'exaltons \n\r De concert avec les anges, \n\r Et prosternés devant toi, \n\r Nous t\'adorons, ô grand Roi!', 1, 163),
(733, 'Strophe', 2, 'Saint, saint, saint est l\'Éternel, \n\r Le Seigneur, Dieu des armées! \n\r Son pouvoir est immortel; \n\r Ses œuvres partout semées \n\r Font éclater sa grandeur, \n\r Sa majesté, sa splendeur.', 2, 163),
(734, 'Strophe', 3, 'Sauve ton peuple, Seigneur, \n\r Et bénis ton héritage! \n\r Que ta gloire et ta splendeur \n\r Soient à jamais son partage! \n\r Conduis-le par ton amour \n\r Jusqu\'au céleste séjour.', 3, 163),
(735, 'Strophe', 4, 'Gloire soit au Saint-Esprit! \n\r Gloire soit à Dieu le Père! \n\r Gloire soit à Jésus-Christ \n\r Notre Sauveur, notre Frère! \n\r Son immense charité \n\r Dure pour l\'éternité.', 4, 163),
(736, 'Strophe', 1, 'Je chanterai, Seigneur, \n\r Tes œuvres magnifiques, \n\r Ton pouvoir infini, \n\r Ta suprême grandeur; \n\r Aux concerts de tes saints \n\r J\'unirai les cantiques \n\r Que pour toi me dicte mon cœur, \n\r Que pour toi me dicte mon cœur.', 1, 164),
(737, 'Strophe', 2, 'Oh! Que de l\'Eternel \n\r La parole est féconde! \n\r L\'univers fut jadis \n\r L\'ouvrage de sa voix; \n\r Il dit…les éléments, \n\r Le ciel, la terre et l\'onde \n\r Du néant sortent à la fois, \n\r Du néant sortent à la fois.', 2, 164),
(738, 'Strophe', 3, 'Le monde passera: \n\r Ce superbe édifice \n\r Un jour s\'ébranlera \n\r Jusqu\'en ses fondements. \n\r Ta sagesse, grand Dieu! \n\r Ta bonté, ta justice \n\r Subsisteront dans tous les temps, \n\r Subsisteront dans tous les temps.', 3, 164),
(739, 'Strophe', 1, 'Louange et gloire à ton nom, \n\r Alléluia, alléluia! \n\r Seigneur, Dieu de l\'univers, \n\r Alléluia, alléluia!', 1, 165),
(740, 'Refrain', 1, 'Gloire à Dieu, gloire à Dieu, \n\r Au plus haut des cieux. \n\r Gloire à Dieu, gloire à Dieu, \n\r Au plus haut des cieux.', 2, 165),
(741, 'Strophe', 2, 'Venez, chantons notre Dieu, \n\r Alléluia, alléluia! \n\r C\'est lui qui nous a créés, \n\r Alléluia, alléluia!', 3, 165),
(742, 'Refrain', 1, 'Gloire à Dieu, gloire à Dieu, \n\r Au plus haut des cieux. \n\r Gloire à Dieu, gloire à Dieu, \n\r Au plus haut des cieux.', 4, 165),
(743, 'Strophe', 3, 'Pour nous, il fit des merveilles, \n\r Alléluia, alléluia! \n\r Éternel est son amour, \n\r Alléluia, alléluia!', 5, 165),
(744, 'Refrain', 1, 'Gloire à Dieu, gloire à Dieu, \n\r Au plus haut des cieux. \n\r Gloire à Dieu, gloire à Dieu, \n\r Au plus haut des cieux.', 6, 165),
(745, 'Strophe', 4, 'Je veux chanter pour mon Dieu, \n\r Alléluia, alléluia! \n\r Tous les jours de ma vie, \n\r Alléluia, alléluia!', 7, 165),
(746, 'Refrain', 1, 'Gloire à Dieu, gloire à Dieu, \n\r Au plus haut des cieux. \n\r Gloire à Dieu, gloire à Dieu, \n\r Au plus haut des cieux.', 8, 165),
(747, 'Strophe', 1, 'Il est exalté, le Roi est au-dessus des cieux. \n\r Je veux le louer. \n\r Il est élevé, à jamais exalté \n\r et je loue son saint nom. Il est Seigneur. \n\r Sa vérité règnera. Tout l\'univers \n\r se réjouit dans son nom. \n\r Il est exalté, le Roi est au-dessus des cieux.', 1, 166),
(748, 'Strophe', 1, 'Louange, louange, louange à Jésus, \n\r notre Roi! \n\r Qui pourra condamner ceux qui sont délivrés \n\r Et qui marchent dans la sainteté?', 1, 167),
(749, 'Strophe', 1, 'Je chanterai l\'Éternel tant que je vivrai, \n\r Je chanterai l\'Éternel. \n\r Je célébrerai mon Dieu tant que j\'existerai, \n\r Je célébrerai mon Dieu.', 1, 168),
(750, 'Strophe', 2, 'Je chanterai l\'Éternel tant que je vivrai, \n\r Je chanterai l\'Éternel. \n\r Je célébrerai mon Dieu tant que j\'existerai, \n\r Je célébrerai mon Dieu.', 2, 168),
(751, 'Strophe', 3, 'Que mes paroles \n\r Lui soient agréables, \n\r Lui soient agréables. \n\r Je veux me réjouir en l\'Éternel.', 3, 168),
(752, 'Strophe', 4, 'Je chanterai l\'Éternel tant que je vivrai, \n\r Je chanterai l\'Éternel. \n\r Je célébrerai mon Dieu tant que j\'existerai, \n\r Je célébrerai mon Dieu.', 4, 168),
(753, 'Strophe', 1, 'Loué sois-tu pour ta lumière, \n\r Tout son éclat vient de ton nom. \n\r Tu nous arraches des ténèbres \n\r Par la richesse du pardon.', 1, 169),
(754, 'Refrain', 1, 'La paix nous vient des mains du Fils. \n\r Attachés à la croix du Christ, \n\r Dans sa promesse nous marchons.', 2, 169),
(755, 'Strophe', 2, 'Loué sois-tu pour ton visage, \n\r Dieu que nul œil n\'a jamais vu. \n\r Car nous adorons ton image \n\r Dans le mystère de Jésus.', 3, 169),
(756, 'Refrain', 1, 'La paix nous vient des mains du Fils. \n\r Attachés à la croix du Christ, \n\r Dans sa promesse nous marchons.', 4, 169),
(757, 'Strophe', 3, 'Tout dans les cieux et sur la terre, \n\r Tout ce qui peut être nommé, \n\r Tout ce qui vit, soupire, espère, \n\r Par sa parole fut créé.', 5, 169),
(758, 'Refrain', 1, 'La paix nous vient des mains du Fils. \n\r Attachés à la croix du Christ, \n\r Dans sa promesse nous marchons.', 6, 169),
(759, 'Strophe', 4, 'Ta royauté lui est remise, \n\r Lui premier-né d\'entre les morts, \n\r Tu l\'as donné à ton Église. \n\r Il est la tête de son corps.', 7, 169),
(760, 'Refrain', 1, 'La paix nous vient des mains du Fils. \n\r Attachés à la croix du Christ, \n\r Dans sa promesse nous marchons.', 8, 169),
(761, 'Strophe', 5, 'Il tient de toi la plénitude \n\r Et tu as tout réconcilié; \n\r C\'est dans sa mort que naît la vie. \n\r Gloire à Jésus crucifié!', 9, 169);
INSERT INTO `strophe` (`idStrophe`, `type`, `identifiant`, `texte`, `position`, `idChant`) VALUES
(762, 'Refrain', 1, 'La paix nous vient des mains du Fils. \n\r Attachés à la croix du Christ, \n\r Dans sa promesse nous marchons.', 10, 169),
(763, 'Strophe', 1, 'Louez le Seigneur \n\r Du haut des cieux. \n\r Chantez sa grandeur. \n\r Vous, anges de Dieu; \n\r Brillantes étoiles, \n\r Soleil, firmament, \n\r Louez, dans sa gloire, \n\r Le Dieu tout-puissant.', 1, 170),
(764, 'Strophe', 2, 'Louez le Seigneur, \n\r Terrestres voix. \n\r De tout votre cœur. \n\r Chantez ses exploits; \n\r Sa droite éternelle \n\r Soutient l\'univers, \n\r D\'une âme fidèle \n\r Louez le Seigneur.', 2, 170),
(765, 'Strophe', 3, 'Louez le Seigneur \n\r Pour tous ses biens. \n\r Rendez-lui honneur \n\r Aux siècles sans fin; \n\r Sa grâce est immense, \n\r Multiples ses dons; \n\r Louez sa clémence, \n\r Sa paix, son pardon.', 3, 170),
(766, 'Strophe', 1, 'Louez le Seigneur \n\r Au plus haut des cieux; \n\r Chantez sa grandeur, \n\r Saints anges de Dieu! \n\r Proclamez sa gloire, \n\r Vous qu\'il a crées, \n\r Chantez sa victoire \n\r Dans l\'éternité!', 1, 171),
(767, 'Strophe', 2, 'Louez le Seigneur, \n\r Vous ses rachetés! \n\r De tout votre cœur, \n\r Chantez ses bontés! \n\r Toujours il envoie \n\r D\'en-haut son Esprit, \n\r Promesse de joie \n\r Pour qui s\'offre à lui.', 2, 171),
(768, 'Strophe', 3, 'Louez le Seigneur \n\r Qui vient jusqu\'à nous! \n\r Devant le Sauveur, \n\r Tombez à genoux! \n\r Adorez la grâce \n\r Du prince de paix, \n\r Car rien ne surpasse \n\r Le don qu\'il nous fait.', 3, 171),
(769, 'Strophe', 4, 'Louez le Seigneur, \n\r Le Dieu trois fois saint! \n\r Chantez le bonheur \n\r Du règne qui vient! \n\r Le peuple fidèle \n\r Attend que son Dieu \n\r Enfin renouvelle \n\r La terre et les cieux.', 4, 171),
(770, 'Strophe', 1, 'Louons le Seigneur Dieu, \n\r Roi des rois glorieux; \n\r Dieu d\'Abraham, \n\r Il règne au plus haut des cieux. \n\r Venons chanter ton nom \n\r «Je suis celui qui suis», \n\r Grand Dieu d\'amour \n\r Nous t\'adorons, ô nom béni.', 1, 172),
(771, 'Strophe', 2, 'Louons le Seigneur Dieu, \n\r Yahveh victorieux; \n\r La terre chante avec ferveur \n\r Son grand bonheur. \n\r Que flotte l\'étendard \n\r Sur le plus haut rempart! \n\r En Dieu, le roi de paix, \n\r Trouvons grâce et pardon!', 2, 172),
(772, 'Strophe', 3, 'Louez le Dieu très haut, \n\r Vous toutes ses amies \n\r Venez le glorifier \n\r De l\'univers entier \n\r Ô Père, Fils, Esprit \n\r Dieu d\'Abraham, mon Dieu \n\r À toi la majesté, \n\r Roi des rois glorieux.', 3, 172),
(773, 'Strophe', 4, 'Louez Celui qui est, \n\r Qui règne pour jamais! \n\r Dieu de Sarah, Dieu d\'Abraham, \n\r Dieu de Myriam! \n\r C\'est le chaut de l\'Agneau \n\r Porté près du Très-haut, \n\r Qu\'il donne pour nous délivrer, \n\r Nous racheter.', 4, 172),
(774, 'Strophe', 5, 'Louange au Christ vainqueur, \n\r Jésus libérateur! \n\r Louange soit au Père, Au Fils, \n\r Au Saint-Esprit! \n\r Gloire à toi notre roi, \n\r Au ciel et ici-bas! \n\r Nous chantons pour l\'éternité \n\r Ta majesté!', 5, 172),
(775, 'Strophe', 1, 'Ô Dieu de sainteté \n\r Que, pour l\'éternité, \n\r Ici sur terre, \n\r Sur les flots, dans les cieux, \n\r L\'on proclame en tous lieux \n\r Le règne bienheureux \n\r De la lumière.', 1, 173),
(776, 'Strophe', 2, 'Ô Christ dont la bonté \n\r Vainquit infirmité, \n\r Mort et détresse. \n\r Toi dont l\'amour divin \n\r De nos maux se souvint. \n\r Dieu commande qu\'enfin \n\r Le jour paraisse!', 2, 173),
(777, 'Strophe', 3, 'Ô Saint Esprit, revoir \n\r Ton message d\'espoir \n\r Et d\'allégresse! \n\r Viens embraser nos cœurs, \n\r Du mal, rends-nous vainqueurs, \n\r Dissipe nos erreurs, \n\r Que le jour naisse!', 3, 173),
(778, 'Strophe', 1, 'Ô bonne nouvelle! \n\r Dieu donne en Jésus \n\r La vie éternelle \n\r Aux pécheurs perdus.', 1, 174),
(779, 'Refrain', 1, 'Puissance, richesse, \n\r Force, gloire, honneur, \n\r Louange et sagesse \n\r À notre Sauveur!', 2, 174),
(780, 'Strophe', 2, 'Victime innocente, \n\r Jésus a porté \n\r Sur la croix sanglante \n\r Notre iniquité.', 3, 174),
(781, 'Strophe', 3, 'Dans ses meurtrissures \n\r Est notre pardon, \n\r Et dans ses blessures \n\r Notre guérison.', 4, 174),
(782, 'Strophe', 4, 'En Christ la victoire \n\r Et la sainteté, \n\r La vie et la gloire \n\r Pour l\'éternité.', 5, 174),
(783, 'Strophe', 1, 'Majesté, à lui la majesté, \n\r À Jésus soit louange, honneur et gloire. \n\r Majesté, suprême autorité, \n\r Du haut des cieux, son règne vient sur tous les siens.', 1, 175),
(784, 'Strophe', 2, 'Exaltons et célébrons le nom de Jésus, \n\r Glorifions et proclamons Jésus-Christ, le Roi. \n\r Majesté, à lui la majesté, \n\r Dans notre cœur, Christ le Sauveur règne en Seigneur.', 2, 175),
(785, 'Strophe', 1, 'Nous t\'adorons, \n\r Ô Père, dans ton temple, \n\r Nous t\'adorons \n\r En esprit et en vérité. \n\r Tu habites nos louanges, Nous t\'adorons \n\r En esprit et en vérité.', 1, 176),
(786, 'Strophe', 2, 'Car un jour près de Toi \n\r Vaut mieux que mille ailleurs, \n\r Je désire habiter \n\r Dans Ton temple. \n\r Car un jour près de Toi \n\r Vaut mieux que mille ailleurs, \n\r Je désire habiter \n\r Dans ta maison, Seigneur.', 2, 176),
(787, 'Strophe', 3, 'Nous t\'adorons, \n\r Ô Père, dans ton temple, \n\r Nous t\'adorons \n\r En esprit et en vérité. \n\r Tu habites nos louanges, \n\r Nous t\'adorons \n\r En esprit et en vérité.', 3, 176),
(788, 'Strophe', 4, 'Car un jour, près de Toi \n\r Vaut mieux que mille ailleurs, \n\r Je désire habiter \n\r Dans Ton temple. \n\r Car un jour près de Toi \n\r Vaut mieux que mille ailleurs, \n\r Je désire habiter \n\r Dans ta maison, Seigneur.', 4, 176),
(789, 'Strophe', 5, 'Je désire habiter \n\r Dans ta maison, Seigneur. \n\r Je désire habiter \n\r Dans ta maison, Seigneur.', 5, 176),
(790, 'Strophe', 1, 'Ô célébrons, ô célébrons, Jésus mon roi, \n\r Exaltons son saint nom tous ensemble. \n\r Célébrons, le Seigneur, célébrons, le Seigneur, \n\r Et que son nom soit élevé à tout jamais!', 1, 177),
(791, 'Strophe', 2, 'Adorons-le, oui adorons Jésus mon roi, \n\r Exaltons son saint nom tous ensemble. \n\r Adorons le Seigneur, adorons le Seigneur \n\r Et que son nom soit élevé à tout jamais!', 2, 177),
(792, 'Strophe', 3, 'Roi de rois et Seigneur des seigneurs. \n\r Que son nom soit élevé à tout jamais!', 3, 177),
(793, 'Strophe', 4, 'Ô célébrons, ô célébrons, Jésus mon roi, \n\r Exaltons son saint nom tous ensemble. \n\r Célébrons, le Seigneur, célébrons, le Seigneur, \n\r Et que son nom soit élevé à tout jamais!', 4, 177),
(794, 'Strophe', 1, 'Ô mon âme, magnifie \n\r Le Seigneur, le roi des cieux! \n\r Il t\'a sauvée, affranchie \n\r Par son pardon généreux. \n\r Alléluia! Alléluia! \n\r Chante son nom glorieux.', 1, 178),
(795, 'Strophe', 2, 'Bénis sa main favorable, \n\r Prévenante en ses bontés, \n\r Et la grâce incomparable \n\r De ce Dieu de charité. \n\r Alléluia! Alléluia! \n\r Chante sa fidélité.', 2, 178),
(796, 'Strophe', 3, 'Près des anges qui l\'adorent \n\r Dans l\'éclat de sa splendeur, \n\r Les saints prosternés l\'honorent \n\r Et contemplent sa grandeur. \n\r Alléluia! Alléluia! \n\r Tous, adorons le Seigneur.', 3, 178),
(797, 'Strophe', 1, 'Alléluia! Louange à Dieu! \n\r Chrétiens, célébrons en tout lieu \n\r Son nom et sa puissance! \n\r Bénissons ce Dieu créateur, \n\r Chantons son règne et sa grandeur, \n\r Exaltons sa clémence!', 1, 179),
(798, 'Strophe', 2, 'Alléluia! Fils éternel! \n\r Sauveur de l\'homme criminel, \n\r Reçois notre humble hommage! \n\r Pénétré de ta charité, \n\r Ton peuple, par toi racheté, \n\r Te bénit d\'âge en âge.', 2, 179),
(799, 'Strophe', 3, 'Alléluia! Céleste Esprit! \n\r De notre cœur humble et contrit \n\r Accepte la prière! \n\r Qu\'en toi seul enfin consacré, \n\r Il soit à jamais pénétré \n\r De ta vive lumière.', 3, 179),
(800, 'Strophe', 4, 'Alléluia! Dieu trois fois saint! \n\r Que ton grand nom toujours soit craint \n\r Par tout ce qui respire! \n\r Règne sur ton peuple à jamais, \n\r Et que tout l\'univers en paix \n\r Adore ton empire!', 4, 179),
(801, 'Strophe', 1, 'Peuple de Dieu, prépare-toi, \n\r viens rencontrer ton Dieu. \n\r Il t\'a saisi ainsi qu\'un tison arraché du feu. \n\r Voici, le Seigneur a formé les montagnes, \n\r Il a créé l\'univers. \n\r Il vient dévoiler à l\'homme ses pensées, \n\r Son nom est Dieu, l\'Éternel.', 1, 180),
(802, 'Strophe', 2, 'Non par la force, \n\r mais par l\'Esprit tu viendras à moi. \n\r Non par puissance, \n\r mais par l\'Esprit tu te conduiras. \n\r Parole du Seigneur, Dieu de l\'univers, oui \n\r Tout est par ton Esprit Saint. \n\r C\'est toi seul qui peut renverser les montagnes, \n\r Tout est par ton Esprit Saint.', 2, 180),
(803, 'Strophe', 3, 'Chante la fête, chante la joie du pardon divin. \n\r Et, tous mes torts, efface les donc, \n\r prince de la paix. \n\r Mon Dieu, viens, \n\r affermis-moi par ton Saint Esprit, \n\r Recrée un cœur purifié. \n\r Seigneur, renouvelle dans ma vie ton souffle, \n\r Rends-moi la joie du salut.', 3, 180),
(804, 'Strophe', 1, '1. Que la gloire du Seigneur subsiste à jamais, \n\r 2. Qu\'il se réjouisse de ses œuvres! \n\r 3. Je chanterai le Seigneur tant que j\'existerai, \n\r 4. Je célébrerai son nom tant que je vivrai.', 1, 181),
(805, 'Strophe', 1, 'Que toute la terre \n\r Acclame ton nom, \n\r Jésus, notre frère, \n\r Toi notre pardon! \n\r Que chacun s\'incline \n\r Devant ta grandeur, \n\r Toi seul en es digne, \n\r À toi seul l\'honneur!', 1, 182),
(806, 'Strophe', 2, 'Toi qui es le Maître, \n\r Le Roi, le Seigneur, \n\r Tu as voulu naître \n\r Comme un serviteur, \n\r Parole éternelle, \n\r Verbe créateur, \n\r Une chair mortelle \n\r Voile ta splendeur.', 2, 182),
(807, 'Strophe', 3, 'Force, honneur, louange \n\r À toi, Crucifié! \n\r Plus haut que les anges \n\r Ton nom est chanté. \n\r Que chacun s\'incline \n\r Devant ta grandeur, \n\r Toi seul en es digne, \n\r À toi, seul l\'honneur!', 3, 182),
(808, 'Strophe', 1, 'Rendons gloire à Dieu notre Père, \n\r Bénissons notre Rédempteur \n\r Et que l\'Esprit-Saint nous libère \n\r De la tristesse et de la peur.', 1, 183),
(809, 'Strophe', 2, 'Que la joie de Dieu nous habite, \n\r Que la paix de notre Seigneur \n\r Ici-bas jamais ne nous quitte, \n\r Mais qu\'elle éclaire notre cœur!', 2, 183),
(810, 'Strophe', 3, 'Ô Seigneur, fais que tout notre être \n\r Te soit désormais consacré. \n\r De tout notre esprit sois le Maître \n\r Et nous délivre du péché.', 3, 183),
(811, 'Strophe', 4, 'De tout l\'amour que Dieu nous donne, \n\r À jamais nos cœurs sont remplis. \n\r Et que de nous tous il rayonne \n\r Pour tous, amis ou ennemis.', 4, 183),
(812, 'Strophe', 5, 'Pardonne à nos mains qui exigent, \n\r À nos yeux souvent aveuglés \n\r Et que ton Esprit nous dirige \n\r Sur les chemins de vérité.', 5, 183),
(813, 'Strophe', 1, 'Saint! Saint! Saint est le Seigneur. \n\r Le ciel et la terre sont remplis de sa gloire. \n\r Hosanna! Au plus haut des cieux. \n\r Béni soit celui qui vient au nom du Seigneur! \n\r Hosanna! Au plus haut des cieux! \n\r Hosanna! Au plus haut des cieux!', 1, 184),
(814, 'Strophe', 1, 'Tout joyeux, bénissons le Seigneur, \n\r Chantons et célébrons ses louanges, \n\r Adorons avec foi le Sauveur, \n\r Nous joignant aux célestes phalanges.', 1, 185),
(815, 'Refrain', 1, 'Gloire à Dieu! Gloire à Dieu! \n\r Que ce chant retentisse en tout lieu! \n\r Gloire à Dieu! Gloire à Dieu! \n\r Que ce chant retentisse en tout lieu!', 2, 185),
(816, 'Strophe', 2, 'Dieu, dans son incomparable amour, \n\r Du ciel envoya son Fils unique, \n\r Et la terre et les cieux, dans ce jour, \n\r S\'unissent pour chanter ce cantique.', 3, 185),
(817, 'Refrain', 1, 'Gloire à Dieu! Gloire à Dieu! \n\r Que ce chant retentisse en tout lieu! \n\r Gloire à Dieu! Gloire à Dieu! \n\r Que ce chant retentisse en tout lieu!', 4, 185),
(818, 'Strophe', 3, 'Nous voulons désormais, bon Sauveur, \n\r T\'aimer par dessus toute autre chose, \n\r Forme ton amour dans notre cœur, \n\r Et puis, de chacun de nous, dispose!', 5, 185),
(819, 'Refrain', 1, 'Gloire à Dieu! Gloire à Dieu! \n\r Que ce chant retentisse en tout lieu! \n\r Gloire à Dieu! Gloire à Dieu! \n\r Que ce chant retentisse en tout lieu!', 6, 185),
(820, 'Strophe', 1, 'Saint est le Seigneur, le Dieu de l\'univers. \n\r Hosanna au plus haut des cieux! \n\r Le ciel et la terre sont remplis de sa gloire, \n\r Hosanna au plus haut des cieux! (2×) \n\r Qu\'il soit béni au nom du Seigneur, \n\r Celui qui est, qui était et qui vient! \n\r Hosanna au plus haut des cieux! (2×)', 1, 186),
(821, 'Strophe', 1, 'Vers toi, Seigneur j\'élève ma voix \n\r Pour t\'adorer, mon Sauveur, mon roi. \n\r Reçois, Seigneur comme un doux parfum \n\r L\'amour et le chant de nos cœurs reconnaissants.', 1, 187),
(822, 'Strophe', 1, 'Alléluia, Alléluia, \n\r Alléluia, Alléluia,', 1, 188),
(823, 'Strophe', 2, 'Alléluia, Alléluia, \n\r Alléluia, Alléluia,', 2, 188),
(824, 'Strophe', 3, 'Allélu, Alléluia, \n\r Alléluia,', 3, 188),
(825, 'Strophe', 4, 'Al - lé - lu - ia, \n\r Al - lé - lu - ia.', 4, 188),
(826, 'Strophe', 1, 'Comme au premier jour le jour se lève. \n\r Un chant de rêve \n\r S\'élève aux cieux. \n\r En ce nouveau jour, dans ta lumière, \n\r En toi j\'espère, \n\r Seigneur, mon Dieu!', 1, 189),
(827, 'Strophe', 2, 'Dès le matin clair vient la rosée; \n\r L\'herbe enchantée \n\r Lève ses brins. \n\r L\'oiseau dans les airs te glorifie, \n\r Je te confie \n\r Mes lendemains.', 2, 189),
(828, 'Strophe', 3, 'Et la lumière donne naissance \n\r À l\'espérance, \n\r Jour de bonheur! \n\r Tout est prière, aussi j\'aspire \n\r À te redire: \n\r Merci Seigneur!', 3, 189),
(829, 'Strophe', 1, 'Dès le matin, Seigneur, nos âmes te bénissent; \n\r Veuille, en ce jour nouveau, diriger tous nos pas; \n\r Jamais pour tes enfants tes bontés ne tarissent, \n\r Et ton amour pour nous ne se fatigue pas.', 1, 190),
(830, 'Strophe', 2, 'Accomplis envers nous tes divines promesses; \n\r Nourris-nous aujourd\'hui de ton céleste pain; \n\r Sois notre défenseur dans toutes nos détresses; \n\r Couvre-nous à jamais de l\'ombre de ta main.', 2, 190),
(831, 'Strophe', 3, 'Toi qui donnas ton Fils pour racheter nos âmes, \n\r Toi qui l\'as envoyé pour sauver les pécheurs, \n\r Fais jaillir en nos cœurs l\'amour que tu réclames, \n\r Et de tous nos péchés rends-nous enfin vainqueurs.', 3, 190),
(832, 'Strophe', 4, 'Garde-nous en ta paix pendant notre voyage, \n\r Jusqu\'au jour bienheureux, où, loin de tous les maux, \n\r Nos âmes goûteront un bonheur sans nuages, \n\r Quand tu viendras, Seigneur, nous prendre en ton repos.', 4, 190),
(833, 'Strophe', 1, 'Au réveil, je veux louer mon Dieu, \n\r Au réveil, je veux louer mon Dieu, \n\r Alléluia, Alléluia, Alléluia, \n\r À genoux, je t\'apporte le don de mon cœur.', 1, 191),
(834, 'Strophe', 2, 'À midi, je veux chanter Jésus, \n\r À midi, je veux chanter Jésus, \n\r Alléluia, Alléluia, Alléluia, \n\r À genoux, je t\'adore, pénètre en mon cœur.', 2, 191),
(835, 'Strophe', 3, 'Et, le soir, je veux prier l\'Esprit, \n\r Et, le soir, je veux prier l\'Esprit, \n\r Alléluia, Alléluia, Alléluia, \n\r À genoux, je réclame ton feu dans mon cœur.', 3, 191),
(836, 'Strophe', 1, 'Pour moi l\'aurore d\'un nouveau jour \n\r Se lève encore dans ce séjour. \n\r Sur ton enfant, Dieu tout-puissant \n\r Dès le matin pose ta main; \n\r Verse en mon âme ta douce paix: \n\r Mon cœur réclame tous tes bienfaits.', 1, 192),
(837, 'Strophe', 2, 'Je voudrais faire, en ce bas lieu, \n\r Tout pour te plaire, Seigneur mon Dieu! \n\r Mais le péché reste caché \n\r Dans mon esprit et me séduit. \n\r Vois ma faiblesse et me soutiens \n\r Par ta tendresse, je t\'appartiens.', 2, 192),
(838, 'Strophe', 3, 'Fais-moi comprendre ta charité, \n\r Et bien entendre ta vérité! \n\r Loin du danger, divin berger! \n\r Conduis mes pas, jusqu\'au trépas. \n\r Que ta puissance soit chaque jour \n\r Ma délivrance, ô Dieu d\'amour!', 3, 192),
(839, 'Strophe', 1, 'Ô mon Dieu! Je cherche ta face \n\r Aux clartés de l\'aube du jour; \n\r Je cherche ta paix et ta grâce, \n\r Le vrai bonheur dans ton amour; \n\r Je cherche ta paix et ta grâce, \n\r Le vrai bonheur dans ton amour.', 1, 193),
(840, 'Strophe', 2, 'Ô mon Dieu! Fais luire en mon âme \n\r Le flambeau divin de ta loi. \n\r Oh! gagne mon cœur et l\'enflamme \n\r D\'une vivante et sainte foi. \n\r Oh! gagne mon cœur et l\'enflamme \n\r D\'une vivante et sainte foi.', 2, 193),
(841, 'Strophe', 3, 'Ô mon Dieu! Bénis mes pensées, \n\r Mes paroles et mes travaux. \n\r Qu\'après mes terrestres journées, \n\r En toi, je trouve un doux repos. \n\r Qu\'après mes terrestres journées, \n\r En toi, je trouve un doux repos.', 3, 193),
(842, 'Strophe', 1, 'Oh! que ta main paternelle \n\r Me bénisse à mon coucher. \n\r Et que ce soit sous ton aile \n\r Que je dorme, ô mon Berger! \n\r Que je dorme, ô mon Berger!', 1, 194),
(843, 'Strophe', 2, 'Dans ton grand amour, efface \n\r Les péchés que j\'ai commis, \n\r Et permets que par ta grâce \n\r J\'obéisse à ton Esprit, \n\r J\'obéisse à ton Esprit.', 2, 194),
(844, 'Strophe', 3, 'Fais reposer sous ta garde \n\r Mes amis et mes parents, \n\r Et que ton œil les regarde, \n\r De ton ciel, petits et grands, \n\r De ton ciel, petits et grands.', 3, 194),
(845, 'Strophe', 4, 'Que ta faveur se déploie \n\r Pour consoler l\'affligé. \n\r Donne au pauvre un peu de joie, \n\r Au malade la santé, \n\r Au malade la santé.', 4, 194),
(846, 'Strophe', 5, 'Seigneur, j\'ai fait ma prière; \n\r Sous ton aile je m\'endors, \n\r Heureux de savoir qu\'un Père \n\r Plein d\'amour veille au dehors, \n\r Plein d\'amour veille au dehors.', 5, 194),
(847, 'Strophe', 1, 'Le soir descend, le jour prend fin. \n\r Les ombres de la nuit \n\r Font un mystérieux dessin, \n\r Et la clarté s\'enfuit. \n\r Seigneur, dans mon cœur sois reçu \n\r En hôte bienvenu.', 1, 195),
(848, 'Refrain', 1, 'Reste avec moi, mon doux Sauveur, \n\r En cette nuit qui vient, \n\r Et sois encore dans mon cœur \n\r Demain, dès le matin.', 2, 195),
(849, 'Strophe', 2, 'Pendant tout le jour qui prend fin, \n\r Tu m\'as accompagné, \n\r Et tu m\'as tenu par la main \n\r Le long de mon sentier. \n\r Par ta parole j\'ai vécu, \n\r Car tu m\'as soutenu.', 3, 195),
(850, 'Refrain', 1, 'Reste avec moi, mon doux Sauveur, \n\r En cette nuit qui vient, \n\r Et sois encore dans mon cœur \n\r Demain, dès le matin.', 4, 195),
(851, 'Strophe', 3, 'Après ce jour qui a pris fin, \n\r Si tu n\'es près de moi \n\r Je serai seul et plein d\'effroi. \n\r Viens, tiens moi par la main. \n\r Que par toi je sois secouru, \n\r Que la peur ne soit plus!', 5, 195),
(852, 'Refrain', 1, 'Reste avec moi, mon doux Sauveur, \n\r En cette nuit qui vient, \n\r Et sois encore dans mon cœur \n\r Demain, dès le matin.', 6, 195),
(853, 'Strophe', 1, 'Ô Jésus, berger fidèle, \n\r Veille sur moi cette nuit, \n\r Garde ton agneau si frêle \n\r Jusqu\'à l\'heure où le jour luit.', 1, 196),
(854, 'Strophe', 2, 'Aujourd\'hui ta main puissante \n\r M\'a conduit et protégé. \n\r Ma prière confiante dit: \n\r «Merci, mon bon berger».', 2, 196),
(855, 'Strophe', 1, 'Je viens, Seigneur, au soir d\'une journée \n\r Qu\'en ton amour tu m\'as encor donnée, \n\r Je viens chercher le regard de ta face, \n\r Ta paix, ta grâce.', 1, 197),
(856, 'Strophe', 2, 'De tes bienfaits tu couronnes ma vie, \n\r Et dans ce jour ma coupe en fut remplie; \n\r J\'adore, ô Dieu, ta bonté paternelle, \n\r Ta main fidèle.', 2, 197),
(857, 'Strophe', 3, 'Mais trop souvent encore je t\'offense \n\r Prolonge, ô Dieu, ta longue patience. \n\r Vois, ton enfant à tes pieds s\'humilie: \n\r Pardonne, oublie!', 3, 197),
(858, 'Strophe', 4, 'Et quand viendra pour moi la nuit dernière, \n\r Quand finira ma terrestre carrière, \n\r Que je m\'endorme en paix, dans ta lumière. \n\r Ô divin Père.', 4, 197),
(859, 'Strophe', 1, 'À l\'horizon le jour s\'éloigne, \n\r L\'obscurité descend sur nous. \n\r Comme au matin, nos mains se joignent, \n\r Et nous te louons à genoux.', 1, 198),
(860, 'Strophe', 2, 'Seigneur, tandis que notre terre, \n\r Poursuit sa course dans les cieux, \n\r Toujours l\'église, à ta lumière, \n\r Est vigilante en quelques lieux.', 2, 198),
(861, 'Strophe', 3, 'Puisqu\'à chaque heure avec l\'aurore, \n\r Le soleil luit sur un sommet, \n\r La voix qui prie et qui t\'adore, \n\r Seigneur, ne se taira jamais.', 3, 198),
(862, 'Strophe', 4, 'Quand je repose, d\'autres veillent, \n\r A l\'occident sous ta clarté, \n\r Et chantant bien haut tes merveilles, \n\r Ne cessent pas de t\'exalter.', 4, 198),
(863, 'Strophe', 5, 'Jamais le soleil ne se couche, \n\r Sur ton royaume, ô Dieu puissant, \n\r Il faut qu\'un jour, toutes les bouches \n\r Chantent ta gloire, en saints accents.', 5, 198),
(864, 'Strophe', 1, 'Reste avec nous, Seigneur, le jour décline, \n\r La nuit s\'approche et nous menace tous; \n\r Nous implorons ta présence divine; \n\r Reste avec nous, Seigneur, reste avec nous.', 1, 199),
(865, 'Strophe', 2, 'En toi, nos cœurs ont salué leur maître, \n\r En toi, notre âme a trouvé son époux. \n\r À ta lumière, elle se sent renaître; \n\r Reste avec nous, Seigneur, reste avec nous.', 2, 199),
(866, 'Strophe', 3, 'Dans nos combats, si ta main nous délaisse, \n\r Satan vainqueur nous meurtrit de ses coups; \n\r Que ta puissance arme notre faiblesse, \n\r Reste avec nous, Seigneur, reste avec nous.', 3, 199),
(867, 'Strophe', 4, 'Sous ton regard, la joie est sainte et bonne, \n\r Près de ton cœur, les pleurs mêmes sont doux; \n\r Soit que ta main nous frappe ou nous couronne, \n\r Reste avec nous, Seigneur, reste avec nous.', 4, 199),
(868, 'Strophe', 1, 'Seigneur, quand vient l\'obscurité, \n\r Éclaire-nous de vérité, \n\r Et, dans la paix de cette nuit, \n\r Refais nos corps et nos esprits!', 1, 200),
(869, 'Strophe', 2, 'Ta grâce efface les péchés; \n\r À nous aussi, pardonne-les! \n\r Délivre-nous de ce fardeau \n\r Et nous aurons le vrai repos.', 2, 200),
(870, 'Strophe', 3, 'Si le sommeil nous fuit parfois, \n\r Que notre esprit s\'élève à toi, \n\r Se souvenant d\'intercéder \n\r Pour ceux que tu nous as donnés.', 3, 200),
(871, 'Strophe', 4, 'Seigneur, tu veux, pour notre bien, \n\r Après le jour, la nuit qui vient. \n\r Dans ce repos, tu nous bénis, \n\r Ô Père, ô Fils, ô Saint-Esprit!', 4, 200),
(872, 'Strophe', 1, 'Gloire à Dieu, notre Créateur! \n\r Gloire à Jésus, le Rédempteur! \n\r Gloire à l\'Esprit, le consolateur! \n\r Alléluia! Alléluia! Alléluia!', 1, 201),
(873, 'Strophe', 1, 'Jésus est au milieu de nous, \n\r Son regard s\'abaisse sur nous, \n\r Sa douce voix, l\'entendez-vous? \n\r Je veux vous bénir tous! \n\r Je veux vous bénir tous! \n\r Sa douce voix, l\'entendez-vous? \n\r Je veux vous bénir tous!', 1, 202),
(874, 'Strophe', 2, 'Jésus est au milieu de nous, \n\r Son regard s\'abaisse sur nous, \n\r Sa douce voix, l\'entendez-vous? \n\r Je veux vous sauver tous! \n\r Je veux vous sauver tous! \n\r Sa douce voix, l\'entendez-vous? \n\r Je veux vous sauver tous!', 2, 202),
(875, 'Strophe', 3, 'Jésus est au milieu de nous, \n\r Son regard s\'abaisse sur nous, \n\r Sa douce voix, l\'entendez-vous? \n\r Oh! je vous aime tous! \n\r Oh! je vous aime tous! \n\r Sa douce voix, l\'entendez-vous? \n\r Oh! je vous aime tous!', 3, 202),
(876, 'Refrain', 1, 'Avec toi Seigneur, tous ensemble, \n\r Nous voici, joyeux et sûrs de ton amour. \n\r Tu nous as rassemblés dans la joie de ta présence \n\r Et c\'est toi qui nous unis.', 1, 203),
(877, 'Strophe', 1, 'C\'est toi qui nous unis, \n\r Ton amour a fait de nous des frères, \n\r Tu es vivant au milieu de nous.', 2, 203),
(878, 'Refrain', 1, 'Avec toi Seigneur, tous ensemble, \n\r Nous voici, joyeux et sûrs de ton amour. \n\r Tu nous as rassemblés dans la joie de ta présence \n\r Et c\'est toi qui nous unis.', 3, 203),
(879, 'Strophe', 2, 'Les mains tendues vers toi, \n\r Nous venons t\'offrir notre prière, \n\r Tu es vivant au milieu de nous.', 4, 203),
(880, 'Refrain', 1, 'Avec toi Seigneur, tous ensemble, \n\r Nous voici, joyeux et sûrs de ton amour. \n\r Tu nous as rassemblés dans la joie de ta présence \n\r Et c\'est toi qui nous unis.', 5, 203),
(881, 'Strophe', 3, 'Venus à ton appel, \n\r Nous voulons entendre ta parole, \n\r Tu es vivant au milieu de nous.', 6, 203),
(882, 'Refrain', 1, 'Avec toi Seigneur, tous ensemble, \n\r Nous voici, joyeux et sûrs de ton amour. \n\r Tu nous as rassemblés dans la joie de ta présence \n\r Et c\'est toi qui nous unis.', 7, 203),
(883, 'Strophe', 4, 'Ô Christ ressuscité, \n\r Nous chantons la vie que tu nous donnes, \n\r Tu es vivant au milieu de nous.', 8, 203),
(884, 'Refrain', 1, 'Avec toi Seigneur, tous ensemble, \n\r Nous voici, joyeux et sûrs de ton amour. \n\r Tu nous as rassemblés dans la joie de ta présence \n\r Et c\'est toi qui nous unis.', 9, 203),
(885, 'Strophe', 1, 'Gloire soit au Père, gloire au Fils, \n\r Gloire au Saint-Esprit, \n\r Aux siècles des siècles, \n\r Amen!', 1, 204),
(886, 'Strophe', 1, 'C\'est vers toi que je me tourne, \n\r Je veux marcher dans tes voies; \n\r J\'élève mes mains pour te rencontrer, \n\r Mon cœur désire chanter \n\r Pour bénir et célébrer ton saint nom,…', 1, 205),
(887, 'Strophe', 1, '…Car tu es fidèle et bon. \n\r Seigneur, ô Seigneur, \n\r Je veux te donner, \n\r Seigneur, ô Seigneur, \n\r Ma vie à jamais.', 2, 205),
(888, 'Strophe', 2, 'Mes yeux contemplent ta gloire, \n\r Ta vie ranime ma foi, \n\r Ta paix et ta joie inondent mon cœur, \n\r Toi seul fais tout mon bonheur. \n\r Je veux proclamer que tu es celui…', 3, 205),
(889, 'Strophe', 2, '…Qui chaque jour nous bénit. \n\r Seigneur, ô Seigneur, \n\r Je veux partager, \n\r Seigneur, ô Seigneur, \n\r Ton éternité.', 4, 205),
(890, 'Strophe', 1, 'Louez l\'Éternel en tous lieux, \n\r Lui qui créa le firmament; \n\r Louez, louez-le constamment, \n\r Louez-le jusque dans les cieux.', 1, 206),
(891, 'Strophe', 2, 'Unissez aux plus belles voix \n\r Et la trompette et le hautbois, \n\r Faites résonner tour à tour \n\r Et la musette et le tambour.', 2, 206),
(892, 'Strophe', 3, 'Que l\'univers chante à son tour \n\r Le Dieu puissant, le Dieu d\'amour. \n\r Vous les élus, vous les humains, \n\r En son honneur, chantez sans fin.', 3, 206),
(893, 'Strophe', 1, 'Nous t\'adorons, nous t\'aimons, tendre Père. \n\r Glorifie ton nom sur la terre! \n\r Glorifie ton nom, glorifie ton nom, \n\r Glorifie ton nom sur la terre!', 1, 207),
(894, 'Strophe', 2, 'Nous t\'adorons, nous t\'aimons, ô Jésus. \n\r Glorifie ton nom dans nos vies! \n\r Glorifie ton nom, glorifie ton nom, \n\r Glorifie ton nom dans nos vies!', 2, 207),
(895, 'Strophe', 1, 'Nous voici rassemblés en ton nom, Seigneur, \n\r Pour t\'offrir la louange de nos cœurs. \n\r Alléluia, alléluia! \n\r Crions notre joie au Dieu vivant…', 1, 208),
(896, 'Strophe', 2, '…Alléluia, alléluia! \n\r Louons-le par notre chant. \n\r Nous voici rassemblés en ton nom, Seigneur, \n\r Pour t\'adorer, Jésus sauveur.', 2, 208),
(897, 'Strophe', 1, 'Ô Seigneur suprême, \n\r Dieu toujours le même, \n\r Daigne entendre ceux qui t\'aiment! \n\r L\'ange au ciel t\'adresse \n\r Son chant d\'allégresse…', 1, 209),
(898, 'Strophe', 1, '…Pour te célébrer sans cesse. \n\r Nous aussi, nous voici \n\r Pour te rendre hommage, \n\r Dieu seul bon, seul sage!', 2, 209),
(899, 'Strophe', 2, 'Que dans la lumière \n\r Notre âme en prière \n\r S\'ouvre à ton amour, ô Père! \n\r Chaque instant qui passe \n\r Nous redit ta grâce,…', 3, 209),
(900, 'Strophe', 2, '…Toute crainte en nous s\'efface. \n\r Ô Dieu fort, reste encor\' \n\r Notre confiance, \n\r Même en la souffrance!', 4, 209),
(901, 'Strophe', 3, 'Pour nous, dans le monde, \n\r Sur toi seul se fonde \n\r Tout espoir de paix profonde. \n\r Grâce salutaire, \n\r Dans ce jour, viens faire…', 5, 209),
(902, 'Strophe', 3, '…De nos cœurs ton sanctuaire! \n\r Gloire à toi, divin roi, \n\r Qui nous fortifies \n\r Par l\'Esprit de vie.', 6, 209),
(903, 'Strophe', 1, 'Prosternez-vous devant votre Roi. \n\r Adorez-le de tout votre cœur. \n\r Faites monter vers sa majesté \n\r Des chants de gloire pour votre Roi des rois!', 1, 210),
(904, 'Strophe', 1, 'Seigneur, sois au milieu de nous, \n\r Seigneur, sois au milieu de nous, \n\r Seigneur, sois au milieu de nous, \n\r Seigneur, sois au milieu de nous.', 1, 211),
(905, 'Strophe', 1, 'Seigneur, tu es dans ton saint temple, \n\r Seigneur, tu es dans ton saint temple, \n\r Que toute la terre s\'incline devant toi en silence. \n\r Silence, silence, devant toi, Seigneur. \n\r Amen.', 1, 212),
(906, 'Strophe', 1, 'Silence! Silence! \n\r Un souffle descend: \n\r Voici la présence \n\r Du Dieu tout-puissant!', 1, 213),
(907, 'Refrain', 1, 'Silence! Le Maître \n\r Nous parle en ce jour \n\r Et nous fait connaître \n\r Sa paix, son amour.', 2, 213),
(908, 'Strophe', 2, 'Silence! Silence! \n\r Révérons ce lieu, \n\r Oh! Que nulle offense \n\r N\'y soit faite à Dieu!', 3, 213),
(909, 'Refrain', 1, 'Silence! Le Maître \n\r Nous parle en ce jour \n\r Et nous fait connaître \n\r Sa paix, son amour. \n\r le Dieu du ciel!', 4, 213),
(910, 'Strophe', 3, 'Silence! Silence! \n\r Et recueillons-nous. \n\r Dans la repentance \n\r Prions à genoux.', 5, 213),
(911, 'Refrain', 1, 'Silence! Le Maître \n\r Nous parle en ce jour \n\r Et nous fait connaître \n\r Sa paix, son amour. \n\r le Dieu du ciel!', 6, 213),
(912, 'Strophe', 4, 'Silence! Silence! \n\r Moment solennel, \n\r Où l\'âme s\'élance \n\r Vers', 7, 213),
(913, 'Refrain', 1, 'Silence! Le Maître \n\r Nous parle en ce jour \n\r Et nous fait connaître \n\r Sa paix, son amour. \n\r le Dieu du ciel!', 8, 213),
(914, 'Strophe', 1, 'Tendre Père, j\'aime te contempler. \n\r Tendre Père, j\'aime te contempler. \n\r Je t\'offre ma vie, je chante ta gloire. \n\r Tendre Père, j\'aime te contempler.', 1, 214),
(915, 'Strophe', 2, 'Fils de Dieu, j\'aime exalter ton nom. \n\r Fils de Dieu, j\'aime exalter ton nom. \n\r Tu m\'aimes, tu m\'aides, tu combles ma vie. \n\r Fils de Dieu, j\'aime exalter ton nom.', 2, 214),
(916, 'Strophe', 3, 'Saint-Esprit, tu éclaires ma vie. \n\r Saint-Esprit, tu éclaires ma vie. \n\r Tu diriges mes pas, tu restes près de moi. \n\r Saint-Esprit, tu éclaires ma vie.', 3, 214),
(917, 'Strophe', 1, 'Viens dans mon cœur, \n\r Viens dans mon cœur, \n\r Oui, viens dans mon cœur, ô Jésus. \n\r Viens aujourd\'hui et pour toujours. \n\r Oui, viens dans mon cœur, ô Jésus.', 1, 215),
(918, 'Strophe', 2, 'Reste en mon cœur, \n\r Reste en mon cœur, \n\r Oui, reste en mon cœur, ô Jésus. \n\r Reste aujourd\'hui et pour toujours. \n\r Oui, reste en mon cœur, ô Jésus.', 2, 215),
(919, 'Strophe', 1, 'Du fond de l\'abîme je t\'invoque, ô Éternel! \n\r Seigneur, écoute ma voix! \n\r Que tes oreilles soient attentives \n\r À la voix de mes supplications.', 1, 216),
(920, 'Strophe', 1, 'Change-moi, Seigneur, et purifie-moi. \n\r Change-moi, Seigneur, que je sois comme toi. \n\r Jésus, tu es le potier, je suis l\'argile. \n\r Brise-moi, façonne-moi, oui, c\'est mon désir. \n\r Change-moi, Seigneur, et purifie-moi. \n\r Change-moi, Seigneur, que je sois comme toi.', 1, 217),
(921, 'Strophe', 1, 'Ô Seigneur, entends, \n\r Ô Seigneur, entends, \n\r Le cri de notre cœur \n\r Et donne-nous ta paix. \n\r Amen.', 1, 218),
(922, 'Refrain', 1, 'Jésus, apprends-moi, je t\'en prie, \n\r À vivre selon ton Esprit. \n\r Jésus, apprends-moi, je t\'en prie, \n\r À vivre selon ton Esprit.', 1, 219),
(923, 'Strophe', 1, 'Seigneur, voici tout mon péché \n\r D\'hier, d\'aujourd\'hui, de demain. \n\r Je ne peux pratiquer le bien; \n\r Merci de m\'avoir pardonné.', 2, 219),
(924, 'Refrain', 1, 'Jésus, apprends-moi, je t\'en prie, \n\r À vivre selon ton Esprit. \n\r Jésus, apprends-moi, je t\'en prie, \n\r À vivre selon ton Esprit.', 3, 219),
(925, 'Strophe', 2, 'Seigneur, je ne désire pas \n\r Laisser le péché vivre en moi. \n\r Tu peux me montrer, chaque fois, \n\r Ce qui dans mon cœur ne va pas.', 4, 219),
(926, 'Refrain', 1, 'Jésus, apprends-moi, je t\'en prie, \n\r À vivre selon ton Esprit. \n\r Jésus, apprends-moi, je t\'en prie, \n\r À vivre selon ton Esprit.', 5, 219),
(927, 'Strophe', 3, 'Seigneur, fais ce que tu voudras; \n\r Ma vie maintenant est à toi. \n\r Je veux être prêt au combat \n\r En sachant que tu vis en moi.', 6, 219),
(928, 'Refrain', 1, 'Jésus, apprends-moi, je t\'en prie, \n\r À vivre selon ton Esprit. \n\r Jésus, apprends-moi, je t\'en prie, \n\r À vivre selon ton Esprit.', 7, 219),
(929, 'Strophe', 1, 'Seigneur, à ton regard de flamme \n\r Rien n\'est couvert, rien n\'est caché; \n\r Qu\'il pénètre au fond de notre âme, \n\r Et qu\'il juge en nous le péché, qu\'il juge le péché!', 1, 220),
(930, 'Strophe', 2, 'Sonde nos cœurs et nos pensées, \n\r Nos plus intimes souvenirs, \n\r Nos œuvres présentes, passées; \n\r Sonde nos plus secrets désirs, nos plus secrets désirs.', 2, 220),
(931, 'Strophe', 3, 'Qu\'à la lumière tout paraisse \n\r Pour que tout soit purifié, \n\r Et qu\'en nous ton Esprit ne laisse \n\r Rien qui ne soit sanctifié, vraiment sanctifié.', 3, 220),
(932, 'Strophe', 4, 'Alors, brûlants d\'un nouveau zèle, \n\r Seigneur, nous pourrons t\'obéir. \n\r Oh! que la vie est grande et belle \n\r Pour ceux qui veulent te servir, qui veulent te servir!', 4, 220),
(933, 'Strophe', 1, 'Paralysés par les nombreuses peurs \n\r Qui tuent en nous l\'audace des témoins, \n\r Nous nous taisons quand souffre l\'innocent, \n\r Nous sommeillons, tandis qu\'il meurt de faim. \n\r Mais toi, Seigneur, qui portas nos péchés, \n\r Change nos cœurs pour que naisse ta paix.', 1, 221),
(934, 'Strophe', 2, 'Désespérés par tant de reniements \n\r Qui nous reprochent notre lâcheté, \n\r Nous n\'avons plus la force d\'avancer, \n\r Rien ne soulève nos pas fatigués. \n\r Mais toi, Seigneur, qui portas nos péchés, \n\r Change nos cœurs et nous serons sauvés.', 2, 221),
(935, 'Strophe', 3, 'Toujours enclins à ne pas pardonner, \n\r À critiquer et même à condamner, \n\r Nous entourons nos frères de mépris, \n\r Nous refusons de leur tendre la main. \n\r Mais toi, Seigneur, qui portas nos péchés, \n\r Change nos cœurs et que vienne l\'amour.', 3, 221),
(936, 'Strophe', 1, 'Sonde-moi, ô Dieu, et connais mon cœur. \n\r Éprouve-moi, et connais mes pensées! \n\r Regarde si je suis sur une mauvaise voie, \n\r Et conduis-moi sur la voie de l\'éternité.', 1, 222),
(937, 'Strophe', 1, 'Nos mains, nos dons, tu veux bénir, \n\r Remplir nos vies et nos projets; \n\r Qu\'à notre foi, à nos soupirs, \n\r Réponde, ô Dieu, toujours ta paix. \n\r Amen.', 1, 223),
(938, 'Strophe', 1, 'Nous voici, Seigneur, pour louer ton nom \n\r Et pour te donner le meilleur de nos dons. \n\r Notre amour pour toi, \n\r Nos vies tout entières, \n\r Et nos cœurs crient de joie: «Abba Père». \n\r Aide-nous sans cesse à te faire plaisir \n\r Et te dire avec tendresse: \n\r «Je t\'aime, Seigneur».', 1, 224),
(939, 'Strophe', 1, '1. Père, je t\'adore, je te donne ma vie. \n\r Je t\'aime tant. \n\r 2. Jésus, je t\'adore, je te donne ma vie. \n\r Je t\'aime tant. \n\r 3. Saint-Esprit, je t\'adore, je te donne ma vie. \n\r Je t\'aime tant.', 1, 225),
(940, 'Strophe', 1, 'Prends ma vie, je suis à toi \n\r Tout ce que j\'ai est à toi. \n\r Prends mes mains, mon Dieu, je t\'appartiens. \n\r Je suis à toi. \n\r Je suis à toi. \n\r Prends ma vie, je suis à toi, me voici.', 1, 226),
(941, 'Strophe', 1, 'Seigneur, nous t\'apportons \n\r Ce qui revient à toi; \n\r Reçois l\'hommage de nos dons \n\r Pour fruit de notre foi.', 1, 227),
(942, 'Strophe', 1, 'Tous nos biens, nos dons, Seigneur, \n\r Sont l\'effet de tes largesses. \n\r Amen.', 1, 228),
(943, 'Strophe', 1, 'Affermis-nous par ton Esprit \n\r Dans ta parole, ô Jésus-Christ! \n\r Reste avec nous dans les combats, \n\r Et loin du mal conduis nos pas!', 1, 229),
(944, 'Strophe', 1, 'Alléluia, alléluia, alléluia!', 1, 230),
(945, 'Strophe', 1, 'À tes pieds, ô divin Maître! \n\r Je me place avec bonheur. \n\r En t\'adorant je veux être \n\r Ton disciple, ô mon Sauveur! \n\r Parle! j\'ai soif de t\'entendre \n\r Parle! je n\'ai qu\'un désir: \n\r Que ta voix puissante et tendre, \n\r À mon cœur se fasse ouïr.', 1, 231),
(946, 'Strophe', 2, 'Faites place, ô voix humaines \n\r À la voix de mon Sauveur! \n\r Pensers vains et choses vaines, \n\r Éloignez-vous de mon cœur! \n\r Tous les bruits, faites silence! \n\r Toi, mon Maître, parle-moi! \n\r Recueillie en ta présence, \n\r Mon âme n\'entend que toi.', 2, 231),
(947, 'Strophe', 3, 'Parle-moi, témoin fidèle! \n\r Découvre-moi le trésor \n\r De ta sagesse éternelle; \n\r Parle, ô Maitre! parle encor. \n\r Ta parole, c\'est la vie, \n\r C\'est la lumière et la paix. \n\r À ton école bénie \n\r Je veux rester à jamais.', 3, 231),
(948, 'Strophe', 4, 'Parle! et pour que je comprenne \n\r Ton enseignement divin, \n\r Dans ta grâce souveraine, \n\r Instruis-moi par l\'Esprit-Saint. \n\r Parle, et pour que j\'obéisse \n\r À tes ordres aussitôt, \n\r Forme-moi pour ton service, \n\r Pour te suivre, saint Agneau!', 4, 231),
(949, 'Strophe', 1, 'Jésus, tu parles à notre cœur. \n\r Malgré tous les bruits d\'ici-bas, \n\r Nous voulons t\'écouter, Seigneur, \n\r Sans jamais trahir d\'un iota.', 1, 232),
(950, 'Strophe', 2, 'Parole de vie et d\'amour, \n\r Ta vérité m\'affranchira. \n\r En toi je trouve le secours, \n\r Chaque jour tu me conduiras.', 2, 232),
(951, 'Strophe', 3, 'En ta lumière nous restons. \n\r Ta loi, ta paix sont dans nos cœurs. \n\r Vivons donc ce que nous croyons: \n\r Ta parole est notre bonheur.', 3, 232),
(952, 'Strophe', 1, 'Alléluia, alléluia, parole du Seigneur! \n\r Alléluia, alléluia, parole dans nos cœurs!', 1, 233),
(953, 'Strophe', 1, 'Juste au moment d\'ouvrir la Bible… \n\r Tant de versets, tant de questions! \n\r Pour qu\'elle me soit compréhensible, \n\r Je recherche ta communion. \n\r Seigneur mon Dieu, je te demande,…', 1, 234),
(954, 'Strophe', 2, '…Avec instance, sincèrement, \n\r De recevoir comme une offrande, \n\r Cette prière, tout simplement. \n\r Que l\'Esprit Saint ôte la buée \n\r Sur les carreaux de mes pensées!…', 2, 234),
(955, 'Strophe', 3, '…Que ta lumière puisse entrer, \n\r Dans ma vie pour la réchauffer. \n\r Que ta lumière puisse entrer, \n\r Dans ma vie pour la transformer.', 3, 234),
(956, 'Strophe', 1, 'Me voici, Seigneur, Sauveur: \n\r Je t\'apporte ma prière. \n\r Je voudrais t\'ouvrir mon cœur, \n\r Et m\'offrir à ta lumière, \n\r Écouter dans le silence \n\r Ta parole en ta présence.', 1, 235),
(957, 'Strophe', 2, 'Devant toi, seul juste et grand, \n\r Je suis faible et misérable. \n\r N\'entre pas en jugement \n\r Avec ton enfant coupable. \n\r C\'est dans ton amour immense \n\r Que je mets mon espérance.', 2, 235),
(958, 'Strophe', 3, 'Car j\'ai vu ta charité \n\r Sur la croix, ô Dieu suprême! \n\r En ton Fils, tu t\'es donné, \n\r Quand il s\'est livré lui-même. \n\r Ton pardon me justifie, \n\r Et je trouve en lui la vie.', 3, 235),
(959, 'Strophe', 1, 'Mon âme en silence \n\r Se tient devant toi, \n\r Oh! rends ta présence \n\r Sensible à ma foi.', 1, 236),
(960, 'Strophe', 2, 'Je ne veux connaître \n\r Aucune autre voix \n\r Que la tienne, ô Maître, \n\r Toi seul, parle-moi!', 2, 236),
(961, 'Strophe', 3, 'Parole bénie \n\r De mon Rédempteur, \n\r Parole de vie, \n\r Pénètre en mon cœur.', 3, 236),
(962, 'Strophe', 4, 'C\'est toi la lumière \n\r Qui brille en la nuit, \n\r L\'eau qui désaltère, \n\r Le pain qui nourrit.', 4, 236),
(963, 'Strophe', 5, 'Seigneur, je t\'écoute, \n\r Seigneur, parle-moi! \n\r Sans crainte, ni doute, \n\r Je m\'attends à toi.', 5, 236),
(964, 'Strophe', 6, 'Qu\'en moi, ta voix fasse \n\r Abonder toujours \n\r Les fruits de la grâce, \n\r Les fruits de l\'amour.', 6, 236),
(965, 'Strophe', 1, 'Parle-moi, parle-moi: c\'est là ma prière. \n\r Je t\'écoute, ô Père, \n\r Prêt à t\'obéir, prêt à t\'obéir.', 1, 237),
(966, 'Strophe', 1, 'Nous réclamons, Seigneur, ta présence, \n\r De ton Esprit le feu, la puissance. \n\r Viens et demeure au milieu de nous, \n\r Bénis ce rendez-vous!', 1, 238),
(967, 'Refrain', 1, 'Parle, bien-aimé Maître! \n\r Oh, oui, parle à nos cœurs! \n\r Ta gloire fait paraître, \n\r Verse en nous tes faveurs.', 2, 238),
(968, 'Strophe', 2, 'Viens nous donner, selon ta promesse, \n\r Paix et bonheur, parfaite allégresse. \n\r Fais abonder, en nous, chaque jour, \n\r Ta grâce, ton amour!', 3, 238),
(969, 'Refrain', 1, 'Parle, bien-aimé Maître! \n\r Oh, oui, parle à nos cœurs! \n\r Ta gloire fait paraître, \n\r Verse en nous tes faveurs.', 4, 238),
(970, 'Strophe', 3, 'Mets sur nos yeux ton divin collyre, \n\r Place nos cœurs sous ton doux empire. \n\r Que de t\'aimer et de t\'obéir, \n\r Soit notre seul désir!', 5, 238),
(971, 'Refrain', 1, 'Parle, bien-aimé Maître! \n\r Oh, oui, parle à nos cœurs! \n\r Ta gloire fait paraître, \n\r Verse en nous tes faveurs.', 6, 238),
(972, 'Strophe', 4, 'Alors, par nous, le flot salutaire \n\r De ton amour, coulant au Calvaire. \n\r Se répandra parmi les perdus \n\r Pour ta gloire, ô Jésus!', 7, 238),
(973, 'Refrain', 1, 'Parle, bien-aimé Maître! \n\r Oh, oui, parle à nos cœurs! \n\r Ta gloire fait paraître, \n\r Verse en nous tes faveurs.', 8, 238),
(974, 'Refrain', 1, 'Ô Seigneur dans mon cœur je t\'écoute, \n\r Ta parole est une lampe sur ma route.', 1, 239),
(975, 'Strophe', 1, 'Nous voulons chercher \n\r Tout ce qui peut te plaire, \n\r Mais pour le trouver, \n\r Il nous faut ta lumière.', 2, 239),
(976, 'Refrain', 1, 'Ô Seigneur dans mon cœur je t\'écoute, \n\r Ta parole est une lampe sur ma route.', 3, 239),
(977, 'Strophe', 2, 'Tu nous dis d\'aimer \n\r Tous les autres, nos frères, \n\r Mais pour les aider, \n\r Il nous faut ta lumière.', 4, 239),
(978, 'Refrain', 1, 'Ô Seigneur dans mon cœur je t\'écoute, \n\r Ta parole est une lampe sur ma route.', 5, 239),
(979, 'Strophe', 3, 'Nous voulons chanter \n\r Notre Dieu, notre Père, \n\r Mais pour lui parler, \n\r Il nous faut ta lumière.', 6, 239),
(980, 'Refrain', 1, 'Ô Seigneur dans mon cœur je t\'écoute, \n\r Ta parole est une lampe sur ma route.', 7, 239),
(981, 'Strophe', 4, 'Tu aimes écouter \n\r Tous nos chants, nos prières, \n\r Mais pour te louer, \n\r Il nous faut ta lumière.', 8, 239),
(982, 'Refrain', 1, 'Ô Seigneur dans mon cœur je t\'écoute, \n\r Ta parole est une lampe sur ma route.', 9, 239),
(983, 'Strophe', 5, 'Viens pour éclairer \n\r Notre vie, notre terre, \n\r Car en vérité, \n\r Il nous faut ta lumière.', 10, 239),
(984, 'Refrain', 1, 'Ô Seigneur dans mon cœur je t\'écoute, \n\r Ta parole est une lampe sur ma route.', 11, 239),
(985, 'Strophe', 1, 'Ton jour, Seigneur, \n\r Parle à mon cœur, \n\r De paix et de lumière. \n\r Bénis ce jour \n\r Où ton amour \n\r M\'appelle à la prière.', 1, 240),
(986, 'Strophe', 2, 'Dieu tout-puissant, \n\r Dieu bienfaisant, \n\r J\'ai besoin de ta grâce. \n\r Éclaire-moi, \n\r Soutiens ma foi. \n\r Je viens chercher ta face.', 2, 240),
(987, 'Strophe', 3, 'Ta vérité, \n\r Ta charité \n\r Brillent dans ta Parole. \n\r Seule elle instruit, \n\r Guide et conduit \n\r Notre âme et la console.', 3, 240),
(988, 'Strophe', 4, 'J\'entends ta voix, \n\r Tes saintes lois \n\r Ne sont pas difficiles. \n\r Viens les graver, \n\r Les conserver \n\r Dans des âmes dociles.', 4, 240),
(989, 'Strophe', 1, 'Viens en cette heure, ô tendre Père! \n\r Te révéler à tes enfants! \n\r Ensemble, unis dans la prière, \n\r Vers toi nous élevons nos chants.', 1, 241),
(990, 'Refrain', 1, 'Remplis nos cœurs, par ta présence, \n\r De foi, d\'amour et d\'espérance.', 2, 241),
(991, 'Strophe', 2, 'Permets, Seigneur, qu\'à ta voix sainte \n\r Nos cœurs se rangent sous ta loi, \n\r Vivant toujours selon ta crainte, \n\r Conduits et soutenus par toi.', 3, 241),
(992, 'Refrain', 1, 'Remplis nos cœurs, par ta présence, \n\r De foi, d\'amour et d\'espérance.', 4, 241),
(993, 'Strophe', 3, 'Viens nous parler de ton Royaume \n\r Où nous appelle ton amour, \n\r Et pour livrer nos luttes d\'hommes, \n\r Arme nos bras jour après jour.', 5, 241),
(994, 'Refrain', 1, 'Remplis nos cœurs, par ta présence, \n\r De foi, d\'amour et d\'espérance.', 6, 241),
(995, 'Strophe', 1, 'Sous ton regard, ô notre Père \n\r Tu vois ton peuple rassemblé; \n\r Reçois nos chants et nos prières; \n\r Car ton amour nous a comblés.', 1, 242),
(996, 'Strophe', 2, 'Fais-nous sentir par ta présence \n\r Que nous avons besoin de toi; \n\r Les bruits du monde font silence; \n\r Pour que tu parles Roi des rois.', 2, 242),
(997, 'Strophe', 3, 'De ton Esprit remplis mon âme \n\r Pour mieux te suivre tous les jours. \n\r Ravive en nous comme une flamme. \n\r Le zèle du premier amour.', 3, 242),
(998, 'Strophe', 4, 'Fais-nous sortir de nos ornières \n\r Pour labourer de nouveaux champs \n\r Et montre-nous dans la prière, \n\r Où les besoins sont les plus grands. \n\r Amen.', 4, 242),
(999, 'Strophe', 1, 'Christ, sois dans mon âme, habite mon esprit. \n\r Sois dans ma parole, inspire mes écrits! \n\r Reçois ma louange, ô Christ je veux chanter, \n\r Te consacrer mon être est toute ma prière.', 1, 243),
(1000, 'Strophe', 1, 'Conduis-moi, montre-moi le bon chemin, \n\r La force de mes lendemains. \n\r Car tu es Dieu, le Dieu qui me sauve, \n\r Le Dieu qui me garde dans son lieu saint.', 1, 244),
(1001, 'Strophe', 1, 'Demeure par ta grâce \n\r Avec nous, Dieu Sauveur! \n\r Quoi que l\'ennemi fasse, \n\r Veille sur notre cœur.', 1, 245),
(1002, 'Strophe', 2, 'Que ta sainte Parole, \n\r Chaque jour, ici-bas, \n\r Soit la seule boussole \n\r Qui dirige nos pas.', 2, 245),
(1003, 'Strophe', 3, 'Éternelle lumière, \n\r Que ta vive splendeur \n\r Nous guide, nous éclaire \n\r Et nous garde d\'erreur.', 3, 245),
(1004, 'Strophe', 4, 'Étends sur nous ton aile \n\r Et demeure à jamais \n\r Avec nous, Dieu fidèle! \n\r Répands sur nous ta paix.', 4, 245),
(1005, 'Strophe', 1, 'Ô Seigneur! bénis la parole \n\r Que nous venons d\'ouïr; \n\r Ne permets pas qu\'elle s\'envole \n\r De notre souvenir.', 1, 246),
(1006, 'Strophe', 1, 'En toi j\'ai mis ma confiance, \n\r ô Dieu très saint, \n\r Toi seul es mon espérance et mon soutien. \n\r C\'est pourquoi je ne crains rien, \n\r J\'ai foi en toi, ô Dieu très saint. \n\r J\'ai foi en toi, ô Dieu très saint.', 1, 247),
(1007, 'Strophe', 1, 'Que la grâce de notre Seigneur Jésus-Christ, \n\r Et l\'amour de Dieu le Père, \n\r Et la communion du Saint-Esprit \n\r Soient avec nous tous, avec nous tous! Amen!', 1, 248),
(1008, 'Strophe', 1, 'Que la grâce de Dieu soit sur toi \n\r Pour t\'aider à marcher dans ses voies. \n\r Reçois tout son pardon et sa bénédiction. \n\r Va en paix, dans la joie, dans l\'amour.', 1, 249),
(1009, 'Strophe', 2, 'Que le Maître te garde toujours \n\r Dans l\'attente de son grand retour. \n\r Mets ta main dans sa main \n\r Tout le long du chemin. \n\r Va en paix, dans la joie, dans l\'amour.', 2, 249),
(1010, 'Strophe', 1, '(Que la grâce de Jésus et l\'amour de Dieu le Père, et la communion du Saint-Esprit qui vit en nous.) (2×) \n\r (Demeure pour toujours avec nous tous, amen!) (2×)', 1, 250),
(1011, 'Strophe', 1, 'Que la paix de Dieu reste dans vos cœurs, \n\r Que la paix de Dieu reste dans vos cœurs! \n\r Que sa force vous anime \n\r Dans la joie ou dans les pleurs! \n\r Que la paix de Dieu reste dans vos cœurs!', 1, 251),
(1012, 'Strophe', 2, 'Que l\'amour de Dieu reste dans vos cœurs, \n\r Que l\'amour de Dieu reste dans vos cœurs! \n\r Que sa force vous anime \n\r Dans la joie ou dans les pleurs! \n\r Que l\'amour de Dieu reste dans vos cœurs!', 2, 251),
(1013, 'Strophe', 3, 'Que le Saint-Esprit reste dans vos cœurs, \n\r Que le Saint-Esprit reste dans vos cœurs! \n\r Que sa force vous anime \n\r Dans la joie ou dans les pleurs! \n\r Que le Saint-Esprit reste dans vos cœurs!', 3, 251),
(1014, 'Strophe', 1, 'Que l\'éternel nous bénisse, \n\r Qu\'il nous garde. \n\r Que l\'Éternel fasse luire sa face sur nous, \n\r Et qu\'il nous accorde sa grâce. \n\r Que l\'Éternel tourne sa face vers nous, \n\r Et nous donne la paix.', 1, 252),
(1015, 'Strophe', 1, 'Amen.', 1, 253),
(1016, 'Strophe', 1, 'Amen.', 1, 254),
(1017, 'Strophe', 1, 'Amen.', 1, 255),
(1018, 'Strophe', 1, 'Amen, amen, amen.', 1, 256),
(1019, 'Strophe', 1, 'Amen, amen, amen.', 1, 257),
(1020, 'Strophe', 1, 'Amen, alléluia! \n\r Alléluia! \n\r Amen, alléluia!', 1, 258),
(1021, 'Strophe', 1, 'Amen, amen, amen', 1, 259),
(1022, 'Strophe', 1, 'Amen, amen, amen', 1, 260),
(1023, 'Strophe', 1, 'Entends-tu? Jésus t\'appelle; \n\r Viens ô pécheur! il t\'attend. \n\r À cette voix si fidèle \n\r Tu résistas trop souvent.', 1, 261),
(1024, 'Refrain', 1, 'Laisse entrer le Roi de gloire; \n\r Ouvre ton cœur à Jésus! \n\r Laisse entrer le Roi de gloire; \n\r Hâte-toi, ne tarde plus.', 2, 261),
(1025, 'Strophe', 2, 'Jésus frappe, il frappe encore; \n\r Ouvre à ton Libérateur, \n\r Et pour toi luira l\'aurore \n\r Du véritable bonheur.', 3, 261),
(1026, 'Refrain', 1, 'Laisse entrer le Roi de gloire; \n\r Ouvre ton cœur à Jésus! \n\r Laisse entrer le Roi de gloire; \n\r Hâte-toi, ne tarde plus.', 4, 261),
(1027, 'Strophe', 3, 'Aujourd\'hui, c\'est jour de grâce; \n\r Ne compte pas sur demain. \n\r Pendant que ton Sauveur passe, \n\r Saisis sa puissante main!', 5, 261),
(1028, 'Refrain', 1, 'Laisse entrer le Roi de gloire; \n\r Ouvre ton cœur à Jésus! \n\r Laisse entrer le Roi de gloire; \n\r Hâte-toi, ne tarde plus.', 6, 261),
(1029, 'Strophe', 1, 'Dès le printemps de ta vie \n\r Donne ton cœur à Jésus, \n\r Et que ta plus chère envie \n\r Soit de l\'aimer toujours plus. \n\r Sa jeunesse offrit l\'exemple \n\r Noble et pur de la bonté. \n\r Dans son courage contemple \n\r La douceur, la loyauté,', 1, 262),
(1030, 'Refrain', 1, 'Dès le printemps de ta vie \n\r Donne ton cœur à Jésus. \n\r À le suivre il te convie, \n\r Revêts-toi de ses vertus.', 2, 262),
(1031, 'Strophe', 2, 'Oui, le meilleur de toi-même \n\r Offre-le sur son autel, \n\r Au printemps, que ta main sème \n\r Ce qui mûrit pour le ciel. \n\r Donne comme ton Dieu donne \n\r L\'infini de son amour. \n\r Dans un Sauveur qui pardonne \n\r Et pour toi mourut un jour.', 3, 262),
(1032, 'Refrain', 1, 'Dès le printemps de ta vie \n\r Donne ton cœur à Jésus. \n\r À le suivre il te convie, \n\r Revêts-toi de ses vertus.', 4, 262),
(1033, 'Strophe', 3, 'Viens, prends les ordres du Maître \n\r Dès l\'aube où c\'est un plaisir. \n\r Cette heure va disparaître \n\r Avec ce pieux désir. \n\r Hâte-toi, la vie est brève, \n\r La vigueur a son déclin. \n\r D\'autres attraits, fuis le rêve \n\r Et viens au bonheur divin.', 5, 262);
INSERT INTO `strophe` (`idStrophe`, `type`, `identifiant`, `texte`, `position`, `idChant`) VALUES
(1034, 'Refrain', 1, 'Dès le printemps de ta vie \n\r Donne ton cœur à Jésus. \n\r À le suivre il te convie, \n\r Revêts-toi de ses vertus.', 6, 262),
(1035, 'Strophe', 1, 'Doucement, tendrement, Jésus appelle, \n\r Nous appelle, vous et moi. \n\r Patiemment Jésus attend le rebelle, \n\r Il nous attend vous et moi.', 1, 263),
(1036, 'Refrain', 1, 'Venez, venez, venez à moi, \n\r Fatigués, chargés, venez à moi. \n\r Doucement, tendrement, Jésus appelle: \n\r «Venez, oh! venez à moi.»', 2, 263),
(1037, 'Strophe', 2, 'Le monde, hélas, est une ombre qui passe, \n\r Passe pour vous et pour moi. \n\r Gloire à Jésus! Au ciel est une place \n\r Prête pour vous et pour moi.', 3, 263),
(1038, 'Refrain', 1, 'Venez, venez, venez à moi, \n\r Fatigués, chargés, venez à moi. \n\r Doucement, tendrement, Jésus appelle: \n\r «Venez, oh! venez à moi.»', 4, 263),
(1039, 'Strophe', 3, 'Au vrai bonheur Jésus-Christ nous convie, \n\r Bonheur pour vous et pour moi. \n\r Il nous l\'acquit quand il donna sa vie, \n\r Sa vie est pour vous et moi.', 5, 263),
(1040, 'Refrain', 1, 'Venez, venez, venez à moi, \n\r Fatigués, chargés, venez à moi. \n\r Doucement, tendrement, Jésus appelle: \n\r «Venez, oh! venez à moi.»', 6, 263),
(1041, 'Strophe', 1, 'Du ciel, l\'échelle de Jacob \n\r Nous ouvre la voie du salut. \n\r Du ciel, Dieu tend son bras puissant, \n\r Montrant le chemin.', 1, 264),
(1042, 'Strophe', 2, '«En vérité je vous le dis, \n\r Vous verrez le ciel grand ouvert, \n\r Les anges descendre et monter \n\r Sur le Fils de Dieu.»', 2, 264),
(1043, 'Strophe', 3, 'Jésus, tu es chemin de vie, \n\r Jésus, tu nous ouvres les cieux. \n\r Jésus, tu es notre salut. \n\r Ô Jésus, je viens!', 3, 264),
(1044, 'Strophe', 1, 'Nous voguons vers un beau rivage \n\r Que Jésus nous prépara; \n\r Nous ne craignons aucun naufrage, \n\r Sa grâce nous conduira.', 1, 265),
(1045, 'Refrain', 1, 'Viens avec nous, la voile est prête, \n\r Frère, viens, que rien ne t\'arrête. \n\r Plus d\'ouragan, de tempête, \n\r Au séjour du Dieu d\'amour, \n\r Plus d\'ouragan, de tempête, \n\r Au séjour du Dieu d\'amour.', 2, 265),
(1046, 'Strophe', 2, 'Pour notre céleste patrie \n\r Voici l\'heure du départ; \n\r À venir Jésus te convie; \n\r Demain il serait trop tard.', 3, 265),
(1047, 'Refrain', 1, 'Viens avec nous, la voile est prête, \n\r Frère, viens, que rien ne t\'arrête. \n\r Plus d\'ouragan, de tempête, \n\r Au séjour du Dieu d\'amour, \n\r Plus d\'ouragan, de tempête, \n\r Au séjour du Dieu d\'amour.', 4, 265),
(1048, 'Strophe', 3, 'Devant nous, de la cité sainte \n\r Bientôt va s\'ouvrir le port; \n\r De nos cœurs bannissant la crainte, \n\r Chantons tous d\'un même accord.', 5, 265),
(1049, 'Refrain', 1, 'Viens avec nous, la voile est prête, \n\r Frère, viens, que rien ne t\'arrête. \n\r Plus d\'ouragan, de tempête, \n\r Au séjour du Dieu d\'amour, \n\r Plus d\'ouragan, de tempête, \n\r Au séjour du Dieu d\'amour.', 6, 265),
(1050, 'Strophe', 1, 'Dans ta charité suprême, \n\r Jésus, tu m\'as appelé; \n\r Tout en moi t\'adore et t\'aime, \n\r Toi qui m\'as tout pardonné.', 1, 266),
(1051, 'Strophe', 2, 'J\'ai la paix, la délivrance. \n\r Où trouver des biens meilleurs? \n\r Une heure dans ta présence \n\r Vaut bien mieux que mille ailleurs.', 2, 266),
(1052, 'Strophe', 3, 'À tes pieds, Seigneur, j\'oublie \n\r Le monde et sa vanité. \n\r Hors de toi tout est folie, \n\r Toi seul es la vérité!', 3, 266),
(1053, 'Strophe', 1, 'Evénou shalom aléhem, \n\r Evénou shalom aléhem, \n\r Evénou shalom aléhem, \n\r Evénou shalom, shalom, shalom aléhem.', 1, 267),
(1054, 'Strophe', 2, 'Nous vous annonçons la paix, \n\r Nous vous annonçons la paix, \n\r Nous vous annonçons la paix, \n\r Nous vous annonçons la paix, la paix en Jésus.', 2, 267),
(1055, 'Strophe', 3, 'Nous vous annonçons la joie, \n\r Nous vous annonçons la joie, \n\r Nous vous annonçons la joie, \n\r Nous vous annonçons la joie, la joie en Jésus.', 3, 267),
(1056, 'Strophe', 4, 'Nous vous annonçons l\'amour, \n\r Nous vous annonçons l\'amour, \n\r Nous vous annonçons l\'amour, \n\r Nous vous annonçons l\'amour, l\'amour en Jésus.', 4, 267),
(1057, 'Strophe', 5, 'Nous vous annonçons la paix, \n\r Nous vous annonçons la joie, \n\r Nous vous annonçons l\'amour, \n\r Nous vous annonçons la paix, la joie, l\'amour en Jésus.', 5, 267),
(1058, 'Strophe', 1, 'Pour toi, j\'ai donné ma vie \n\r Pour toi, j\'ai versé mon sang; \n\r Ton âme, à Satan ravie, \n\r Échappe à son bras puissant. \n\r L\'amour qui se sacrifie, \n\r L\'as-tu saisi par la foi? \n\r Pour toi, j\'ai donné ma vie: \n\r Pécheur, qu\'as-tu fait pour moi?', 1, 268),
(1059, 'Strophe', 2, 'Pour toi, j\'ai quitté mon Père, \n\r Mon trône dans les hauts cieux, \n\r Le séjour de la lumière, \n\r Les alléluias joyeux. \n\r Pauvre, isolé sur la terre, \n\r Moi, le grand et puissant Roi, \n\r Pour toi, j\'ai quitté mon Père: \n\r Toi, qu\'as-tu laissé pour moi?', 2, 268),
(1060, 'Strophe', 3, 'Pour toi, j\'ai pris la souffrance, \n\r Pour toi, j\'ai versé des pleurs; \n\r J\'ai, dans mon amour immense, \n\r Bu ta coupe de douleurs. \n\r Mon pardon, ma délivrance, \n\r Mon salut tout est à toi! \n\r Pour toi, j\'ai pris la souffrance: \n\r Toi, qu\'as-tu souffert pour moi?', 3, 268),
(1061, 'Strophe', 4, 'Veux-tu me donner ta vie? \n\r Veux-tu me donner ton cœur? \n\r Renoncer à ta folie, \n\r Et du mal être vainqueur? \n\r Ton âme heureuse et bénie \n\r Vivra d\'amour et de foi. \n\r Veux-tu me donner ta vie? \n\r Pécheur, je suis mort pour toi!', 4, 268),
(1062, 'Strophe', 1, 'Qui parle de la danse à l\'homme lassé? \n\r Qui parle d\'abondance à l\'homme affamé? \n\r Qui parle de la danse à l\'homme lassé? \n\r Qui parle d\'abondance à l\'homme affamé? \n\r Ceux qui pliaient sous le fardeau marchent le front levé. \n\r Une semence germera dans le désert.', 1, 269),
(1063, 'Strophe', 2, 'Qui montre la lumière à l\'homme aveuglé? \n\r Qui chante sa musique à l\'homme assourdi? \n\r Qui montre la lumière à l\'homme aveuglé? \n\r Qui chante sa musique à l\'homme assourdi? \n\r Ceux qui avaient perdu l\'espoir osent lever les yeux. \n\r Dans le vacarme a retenti un chant de paix.', 2, 269),
(1064, 'Strophe', 3, 'Christ ouvrira la porte à l\'homme égaré, \n\r Il donne son eau vive à l\'homme assoiffé. \n\r Christ ouvrira la porte à l\'homme égaré, \n\r Il donne son eau vive à l\'homme assoiffé. \n\r Ceux qui étaient déracinés entrent dans leurs logis. \n\r Source qui ne tarit jamais, Dieu a surgi.', 3, 269),
(1065, 'Strophe', 4, 'Dieu mènera la danse d\'un peuple nouveau. \n\r Il fonde l\'espérance d\'un monde sauvé. \n\r Dieu mènera la danse d\'un peuple nouveau. \n\r Il fonde l\'espérance d\'un monde sauvé.', 4, 269),
(1066, 'Strophe', 1, 'Si vous saviez quel Sauveur je possède! \n\r Il est l\'ami, le plus tendre de tous: \n\r Pour moi devant le Père il intercède: \n\r Oh! je voudrais qu\'il fût aussi pour vous!', 1, 270),
(1067, 'Refrain', 1, 'Mon Sauveur vous aime, \n\r Ah! cherchez en lui \n\r Votre ami suprême, \n\r Votre seul appui!', 2, 270),
(1068, 'Strophe', 2, 'Si vous saviez la paix douce et profonde \n\r Que mon Sauveur, en mon âme apporta \n\r Pour cette paix, que peut donner le monde? \n\r Elle jaillit pour vous de Golgotha.', 3, 270),
(1069, 'Refrain', 1, 'Mon Sauveur vous aime, \n\r Ah! cherchez en lui \n\r Votre ami suprême, \n\r Votre seul appui!', 4, 270),
(1070, 'Strophe', 3, 'Si vous saviez quelle douce espérance \n\r Le Dieu de paix fait rayonner des cieux! \n\r Combien sa voix sait calmer la souffrance \n\r Et son regard rendre le cœur joyeux!', 5, 270),
(1071, 'Refrain', 1, 'Mon Sauveur vous aime, \n\r Ah! cherchez en lui \n\r Votre ami suprême, \n\r Votre seul appui!', 6, 270),
(1072, 'Strophe', 4, 'Quand vous saurez combien Jésus vous aime, \n\r Quand vous saurez combien son joug est doux, \n\r Ne gardez pas ce trésor pour vous-même, \n\r Mais proclamez ce qu\'il a fait pour vous!', 7, 270),
(1073, 'Refrain', 1, 'Mon Sauveur vous aime, \n\r Ah! cherchez en lui \n\r Votre ami suprême, \n\r Votre seul appui!', 8, 270),
(1074, 'Strophe', 1, 'Aujourd\'hui, tu m\'appelles, \n\r Tu veux laver mon cœur; \n\r Et mes pensers rebelles, \n\r Tu veux changer, Seigneur. \n\r Loin de ta bergerie \n\r J\'errais dans le péché. \n\r Pour me sauver la vie, \n\r Jésus, tu m\'as cherché.', 1, 271),
(1075, 'Strophe', 2, 'Aujourd\'hui. ta grâce ouvre \n\r La porte du salut: \n\r Tous ceux que ton sang couvre \n\r Sont parmi tes élus. \n\r Et le Père avec joie, \n\r Pardonne au pauvre enfant. \n\r Qui délaissant sa voie, \n\r Vient à lui, repentant.', 2, 271),
(1076, 'Strophe', 3, 'La maison de mon Père, \n\r Je ne puis m\'en passer; \n\r Il l\'ouvre à ma prière, \n\r Sans jamais se lasser. \n\r Jésus, c\'est lui la porte, \n\r Me connaît par mon nom. \n\r Si tout passe, qu\'importe! \n\r J\'ai place en sa maison!', 3, 271),
(1077, 'Strophe', 1, 'Je vivais sans foi, sans Dieu, \n\r Quand Jésus m\'ouvrit les cieux; \n\r Son amour était plus fort \n\r Que les chaînes de la mort; \n\r En Jésus glorifié \n\r Par la foi je revivrai.', 1, 272),
(1078, 'Strophe', 2, 'Son incomparable amour \n\r Est le plus puissant secours; \n\r Plus profond que l\'océan, \n\r Sans égal dans son élan; \n\r C\'est pour moi qu\'il est venu \n\r Et je ne le quitte plus.', 2, 272),
(1079, 'Strophe', 3, 'Oh! combien je suis heureux \n\r Quand je marche avec mon Dieu; \n\r Si je me confie en lui, \n\r Il dissipe mes soucis; \n\r Car Jésus m\'a tout donné: \n\r Le bonheur, la liberté.', 3, 272),
(1080, 'Strophe', 1, 'Longtemps perdu, j\'errais Seigneur, \n\r Je reviens à toi, \n\r Portant le poids de mon malheur, \n\r Je reviens, Seigneur.', 1, 273),
(1081, 'Refrain', 1, 'Je reviens, je reviens, \n\r Oui, j\'entends ta voix. \n\r Dans tes bras, oh! quel bonheur, \n\r Je reviens, Seigneur.', 2, 273),
(1082, 'Strophe', 2, 'Mes larmes disent mon chagrin, \n\r Je reviens à toi, \n\r Pour tout ce temps sans loi, sans frein, \n\r Je reviens, Seigneur.', 3, 273),
(1083, 'Refrain', 1, 'Je reviens, je reviens, \n\r Oui, j\'entends ta voix. \n\r Dans tes bras, oh! quel bonheur, \n\r Je reviens, Seigneur.', 4, 273),
(1084, 'Strophe', 3, 'Je suis lassé du mal, mon Dieu, \n\r Je reviens à toi, \n\r Auprès de toi je suis heureux, \n\r Je reviens, Seigneur.', 5, 273),
(1085, 'Refrain', 1, 'Je reviens, je reviens, \n\r Oui, j\'entends ta voix. \n\r Dans tes bras, oh! quel bonheur, \n\r Je reviens, Seigneur.', 6, 273),
(1086, 'Strophe', 4, 'Tu viens briser mon cœur si dur, \n\r Je reviens à toi, \n\r Verser ton sang, me rendre pur, \n\r Je reviens, Seigneur.', 7, 273),
(1087, 'Refrain', 1, 'Je reviens, je reviens, \n\r Oui, j\'entends ta voix. \n\r Dans tes bras, oh! quel bonheur, \n\r Je reviens, Seigneur.', 8, 273),
(1088, 'Strophe', 5, 'Mon seul espoir, ma seule envie, \n\r Je reviens à toi, \n\r C\'est d\'être au ciel, dans ma patrie, \n\r Je reviens, Seigneur.', 9, 273),
(1089, 'Refrain', 1, 'Je reviens, je reviens, \n\r Oui, j\'entends ta voix. \n\r Dans tes bras, oh! quel bonheur, \n\r Je reviens, Seigneur.', 10, 273),
(1090, 'Strophe', 1, 'Nous voici devant ta face, \n\r Seigneur, nous venons à toi. \n\r Devant ton trône de grâce \n\r Nous nous tenons par la foi. \n\r Reçois-nous dans ton Église, \n\r Viens donner gloire à ton nom, \n\r Et que ta grâce promise, \n\r Nous apporte le pardon.', 1, 274),
(1091, 'Strophe', 2, 'Nous t\'offrons notre faiblesse; \n\r Mieux que nous, tu la connais. \n\r Et dans ta grande tendresse, \n\r Tu répands en nous la paix. \n\r Ton amour calme et console, \n\r Ta voix parle à notre cœur; \n\r Nous savourons ta Parole, \n\r Ô Jésus, notre Sauveur.', 2, 274),
(1092, 'Strophe', 3, 'Fais de nous, ô divin Maître, \n\r Des disciples qui, sans peur, \n\r En tout lieu s\'efforcent d\'être, \n\r Des témoins de leur Sauveur. \n\r Et quand nous verrons ta face, \n\r Ressuscités, glorieux, \n\r Nous célébrerons ta grâce \n\r Avec tous les bienheureux.', 3, 274),
(1093, 'Strophe', 1, 'Ô Dieu! crée en moi un cœur pur, \n\r Ô Dieu! crée en moi un cœur pur, \n\r Renouvelle en moi un esprit bien disposé. \n\r Ô Dieu! crée en moi un cœur pur.', 1, 275),
(1094, 'Strophe', 1, 'Ô Dieu, relève-nous! Rends-nous la vie. \n\r Prête l\'oreille, Berger d\'Israël. \n\r Oui, sauve-nous dans ta grâce infinie, \n\r Tourne vers nous ta face, Emmanuel. \n\r Ô Dieu, relève-nous, ô Dieu, relève-nous! \n\r Et que ton Fils à nos yeux se révèle. \n\r Qu\'en nous jaillisse une vie nouvelle, \n\r Ô Dieu, relève-nous, relève-nous!', 1, 276),
(1095, 'Strophe', 2, 'Ô Dieu, réveille-nous! Que ta lumière \n\r Vienne chasser la nuit qui nous endort. \n\r Brise et transforme enfin nos cœurs de pierre \n\r Relève-nous, Seigneur, d\'entre les morts. \n\r Ô Dieu, réveille-nous, ô Dieu réveille-nous! \n\r Et que ton Fils à nos yeux se révèle. \n\r Qu\'en nous jaillisse une vie nouvelle, \n\r Ô Dieu, réveille-nous, réveille-nous!', 2, 276),
(1096, 'Strophe', 3, 'Ô Dieu, visite-nous! Viens et pardonne \n\r La tolérance de nos compromis. \n\r Que ton Esprit nous remplisse et nous donne \n\r D\'être en ta grâce toujours affermis. \n\r Ô Dieu, visite-nous, ô Dieu, visite-nous! \n\r Et que ton Fils à nos yeux se révèle. \n\r Qu\'en nous jaillisse une vie nouvelle, \n\r Ô Dieu, visite-nous, visite-nous!', 3, 276),
(1097, 'Strophe', 1, 'Si mon peuple s\'humilie, \n\r S\'il prie en cherchant ma face, \n\r Se détourne de sa voie impure, \n\r Je l\'exaucerai des cieux. \n\r Si mon peuple s\'humilie, \n\r S\'il prie en cherchant ma face, \n\r Se détourne de sa voie impure, \n\r Son péché j\'effacerai, \n\r Son pays je guérirai.', 1, 277),
(1098, 'Strophe', 1, 'Christ a rompu ma chaîne \n\r De son bras tout puissant. \n\r Il a porté ma peine, \n\r Il a versé son sang, \n\r Il a versé son sang.', 1, 278),
(1099, 'Strophe', 2, 'Jésus est ma richesse, \n\r Il connaît mes besoins. \n\r Jamais dans la détresse \n\r Ne me manquent ses soins, \n\r Ne me manquent ses soins.', 2, 278),
(1100, 'Strophe', 3, 'Jésus est pour mon âme \n\r Comme l\'ombre à midi, \n\r Comme en hiver la flamme \n\r Pour mon corps engourdi, \n\r Pour mon corps engourdi.', 3, 278),
(1101, 'Strophe', 4, 'Jésus est mon modèle. \n\r Que ne suis-je avec lui \n\r Saint, aimant et fidèle, \n\r Parfait dès aujourd\'hui, \n\r Parfait dès aujourd\'hui.', 4, 278),
(1102, 'Refrain', 1, 'Joie pour ceux qui ont pardonné: \n\r L\'Esprit de Dieu les a comblés. \n\r Ils reçoivent miséricorde, \n\r Dieu les a relevés.', 1, 279),
(1103, 'Strophe', 1, 'Aimez-vous en frères, \n\r Car tout amour vient de Dieu. \n\r Celui dont le cœur aime \n\r Devient enfant du Père.', 2, 279),
(1104, 'Refrain', 1, 'Joie pour ceux qui ont pardonné: \n\r L\'Esprit de Dieu les a comblés. \n\r Ils reçoivent miséricorde, \n\r Dieu les a relevés.', 3, 279),
(1105, 'Strophe', 2, 'La paix, fruit de l\'Esprit, \n\r Par nos mains est à construire. \n\r Celui qui porte en lui la paix \n\r Devient enfant du Père.', 4, 279),
(1106, 'Refrain', 1, 'Joie pour ceux qui ont pardonné: \n\r L\'Esprit de Dieu les a comblés. \n\r Ils reçoivent miséricorde, \n\r Dieu les a relevés.', 5, 279),
(1107, 'Strophe', 3, 'L\'Esprit de justice \n\r Nous invite à partager. \n\r Ceux qui s\'y abandonnent \n\r Deviennent enfants du Père.', 6, 279),
(1108, 'Refrain', 1, 'Joie pour ceux qui ont pardonné: \n\r L\'Esprit de Dieu les a comblés. \n\r Ils reçoivent miséricorde, \n\r Dieu les a relevés.', 7, 279),
(1109, 'Strophe', 1, 'Mon rocher, je cherche en toi \n\r Un refuge pour ma foi; \n\r Que ton sang, jadis versé \n\r Sur la croix pour mon péché \n\r Soit ma source de pardon, \n\r Ma parfaite protection.', 1, 280),
(1110, 'Strophe', 2, 'Nulle épreuve, nul effort \n\r Ne me sauve de la mort. \n\r Que mon zèle soit total, \n\r Mon courage sans égal. \n\r Mon espoir, c\'est toi, Jésus. \n\r En toi seul est mon salut.', 2, 280),
(1111, 'Strophe', 3, 'Quand enfin viendra le jour \n\r Où dans ton divin séjour, \n\r Sur le trône, je verrai \n\r Ton pouvoir, ta majesté, \n\r Mon rocher, tu resteras \n\r Un refuge pour ma foi.', 3, 280),
(1112, 'Strophe', 1, 'Mon Sauveur m\'a sauvé, je suis purifié, \n\r Il m\'a rendu digne des cieux. \n\r Sa parole de vie, son pardon gratuit, \n\r Sont mes dons les plus précieux. \n\r Sa parole proclame bien fort, \n\r Que je suis libéré de la mort. \n\r Libre par son pardon, j\'invoque son nom, \n\r Cadeau merveilleux.', 1, 281),
(1113, 'Strophe', 2, 'J\'ai crié ma douleur, mon indignité, \n\r Mes vains combats, vers toi, Seigneur. \n\r Trop longtemps je pensais \n\r Gagner ton salut: Je n\'ai récolté que la peur. \n\r Entre tes mains j\'ai tout déposé, \n\r Mes peines, mes soucis, mes échecs. \n\r De toi j\'ai tout reçu, \n\r En toi j\'ai trouvé la voie du bonheur.', 2, 281),
(1114, 'Strophe', 3, 'Amis, levons les yeux, le Seigneur des cieux \n\r Pour nous a souffert sur la croix. \n\r Il a connu l\'enfer, au mal fait la guerre: \n\r Pour nous il a ouvert la voie. \n\r L\'agneau de Dieu a vaincu la mort; \n\r Sa vie a triomphé de nos torts. \n\r Nous voulons t\'accueillir de tout notre cœur: \n\r Deviens notre roi.', 3, 281),
(1115, 'Strophe', 1, 'Le Seigneur est, pour toujours, \n\r Mon rocher et mon bonheur, \n\r Et sera Dieu à jamais \n\r Par son grand amour.', 1, 282),
(1116, 'Strophe', 2, 'Son sang a été versé \n\r Sur mon cœur, si sale et noir, \n\r Et il m\'a rendu si pur: \n\r Oh! quelle joie!', 2, 282),
(1117, 'Strophe', 3, 'Saint-Esprit, viens sur nous tous, \n\r Pour nous préparer au ciel \n\r Où nous verrons la Lumière. \n\r Seigneur, viens bientôt!', 3, 282),
(1118, 'Strophe', 1, 'Tu es toute ma vie, \n\r Jésus, Sauveur aimé. \n\r En toi je me confie, \n\r Car tu m\'as racheté.', 1, 283),
(1119, 'Strophe', 2, 'Tu es le don suprême \n\r Que le Père nous fit. \n\r Au ciel ta bonté même \n\r Nous guide et nous conduit.', 2, 283),
(1120, 'Strophe', 3, 'Si tu vis en mon âme, \n\r Toi ma part et mon lot, \n\r Tu ranimes la flamme \n\r Et me mets en repos.', 3, 283),
(1121, 'Strophe', 4, 'Tu soignes mes blessures \n\r Avec l\'huile et le vin, \n\r Et ta parole sûre \n\r Me rend toujours serein.', 4, 283),
(1122, 'Strophe', 5, 'Tu es la manne sainte \n\r Qui nourrit au désert. \n\r Tu réponds à ma plainte \n\r Et tu me mets au vert.', 5, 283),
(1123, 'Strophe', 6, 'Tu es la source pure, \n\r L\'eau qui rafraîchit. \n\r Tu laves ma souillure, \n\r Ton amour me guérit.', 6, 283),
(1124, 'Strophe', 7, 'Que désirer encore? \n\r En toi tout m\'est donné. \n\r Jusqu\'à la grande aurore \n\r Sur toi je peux compter.', 7, 283),
(1125, 'Strophe', 1, 'C\'est Jésus, quand je chancelle, \n\r Seul en mon chemin, \n\r Qui vient prendre ma main frêle \n\r Dans sa forte main. \n\r Il me tient la main, \n\r Il me tient la main, \n\r Oui, je suis quand je chancelle \n\r Tenu par sa main.', 1, 284),
(1126, 'Strophe', 2, 'Il connaît bien ma faiblesse, \n\r Mon indignité; \n\r Mais il me bénit sans cesse, \n\r Grande est sa bonté. \n\r Grande est sa bonté, \n\r Grande est sa bonté, \n\r Il supplée à ma faiblesse; \n\r Grande est sa bonté.', 2, 284),
(1127, 'Strophe', 3, 'J\'ai dans ma misère extrême, \n\r Les yeux sur Jésus. \n\r Je suis assuré qu\'il m\'aime: \n\r Que faut-il de plus? \n\r Les yeux sur Jésus, \n\r Les yeux sur Jésus: \n\r Gloire à son amour suprême! \n\r Que faut-il de plus?', 3, 284),
(1128, 'Strophe', 4, 'Ton sang, sur la croix bénie, \n\r A coulé pour moi, \n\r Et je ne me glorifie \n\r Qu\'en toi, divin Roi! \n\r Oui, ce n\'est qu\'en toi, \n\r Oui, ce n\'est qu\'en toi, \n\r Que j\'ai le salut, la vie, \n\r Ô Jésus, mon Roi!', 4, 284),
(1129, 'Strophe', 1, 'À Dieu soit la gloire! \n\r Par son grand amour, \n\r Grâce à sa victoire, \n\r S\'est levé le jour. \n\r Jésus, à ma place, \n\r Mourut sur la croix, \n\r Il m\'offre sa grâce \n\r Et je la reçois.', 1, 285),
(1130, 'Refrain', 1, 'Gloire à Dieu! Gloire à Dieu! \n\r Terre, écoute sa voix. \n\r Gloire à Dieu! Gloire à Dieu! \n\r Monde, réjouis-toi! \n\r Oh! venez au Père, \n\r Jésus est vainqueur; \n\r Que toute la terre, \n\r Chante en son honneur!', 2, 285),
(1131, 'Strophe', 2, 'De Jésus la joie \n\r Remplit notre cœur, \n\r Qu\'importe qu\'on voie \n\r Tout notre bonheur, \n\r Selon sa promesse, \n\r Jésus changera \n\r Deuil en allégresse \n\r Quand il reviendra.', 3, 285),
(1132, 'Refrain', 1, 'Gloire à Dieu! Gloire à Dieu! \n\r Terre, écoute sa voix. \n\r Gloire à Dieu! Gloire à Dieu! \n\r Monde, réjouis-toi! \n\r Oh! venez au Père, \n\r Jésus est vainqueur; \n\r Que toute la terre, \n\r Chante en son honneur!', 4, 285),
(1133, 'Strophe', 3, 'Céleste patrie, \n\r Mon cœur pense à toi; \n\r Là, mon âme unie \n\r À Jésus mon roi, \n\r Bénira sans cesse \n\r Le grand Dieu sauveur, \n\r Qui, dans sa tendresse, \n\r Sauve le pécheur.', 5, 285),
(1134, 'Refrain', 1, 'Gloire à Dieu! Gloire à Dieu! \n\r Terre, écoute sa voix. \n\r Gloire à Dieu! Gloire à Dieu! \n\r Monde, réjouis-toi! \n\r Oh! venez au Père, \n\r Jésus est vainqueur; \n\r Que toute la terre, \n\r Chante en son honneur!', 6, 285),
(1135, 'Strophe', 1, 'Il est le Dieu de mon salut, \n\r Il est le Dieu de ma délivrance. \n\r Un Dieu d\'amour, \n\r Un Dieu d\'espérance. \n\r Alléluia, alléluia.', 1, 286),
(1136, 'Strophe', 1, 'Entends-tu le chant joyeux? \n\r Jésus sauve aujourd\'hui! \n\r Il retentit en tous lieux; \n\r Jésus sauve aujourd\'hui! \n\r C\'est un cri de délivrance, \n\r Un cantique d\'espérance, \n\r Qui remplit l\'espace immense: \n\r Jésus sauve aujourd\'hui!', 1, 287),
(1137, 'Strophe', 2, 'Partout élève la voix: \n\r Jésus sauve aujourd\'hui! \n\r Vaillant héraut de la croix, \n\r Jésus sauve aujourd\'hui! \n\r À parler, Jésus t\'appelle, \n\r Répands au loin la nouvelle, \n\r En connais-tu de plus belle? \n\r Jésus sauve aujourd\'hui!', 2, 287),
(1138, 'Strophe', 3, 'Répète au pécheur contrit: \n\r Jésus sauve aujourd\'hui! \n\r Ceux que le mal asservit, \n\r Jésus sauve aujourd\'hui! \n\r Va jusqu\'à l\'île lointaine, \n\r Briser du captif la chaîne, \n\r Redire aux cœurs dans la peine: \n\r Jésus sauve aujourd\'hui!', 3, 287),
(1139, 'Strophe', 4, 'Jusqu\'aux confins des déserts, \n\r Jésus sauve aujourd\'hui! \n\r Jusque par-delà les mers, \n\r Jésus sauve aujourd\'hui! \n\r Que d\'un pôle à l\'autre pôle \n\r Coure la sainte Parole \n\r Qui relève, instruit, console: \n\r Jésus sauve aujourd\'hui!', 4, 287),
(1140, 'Strophe', 1, 'Il est une source abondante, toujours ouverte au pèlerin. \n\r Son eau limpide et jaillissante est le remède souverain. \n\r Ses bords sont d\'un accès facile, l\'enfant même peut y puiser. \n\r Son onde rend toujours fertile le sol qu\'elle vient arroser.', 1, 288),
(1141, 'Strophe', 2, 'De cette source bienveillante, de son flot pur, vivifiant, \n\r S\'élève une voix consolante parlant d\'amour à tout passant. \n\r Ô flot sanglant, source bénie, seule richesse du pécheur, \n\r Tu lui donnes paix, joie et vie, le ciel et l\'éternel bonheur.', 2, 288),
(1142, 'Strophe', 3, 'Elle est au pied du bois infâme que l\'amour divin a dressé. \n\r Ô croix bénie, ancre de l\'âme, refuge du cœur oppressé, \n\r Sous ton ombre mystérieuse, je sens mon cœur se ranimer. \n\r Mon âme sort victorieuse de la lutte et vit pour aimer.', 3, 288),
(1143, 'Strophe', 1, 'Divine grâce, je suis sauvé! \n\r Jésus efface l\'iniquité. \n\r Pour tous les hommes, Christ est sauveur. \n\r C\'est lui qui donne paix et bonheur.', 1, 289),
(1144, 'Refrain', 1, 'C\'est l\'espérance qui nous soutient: \n\r Dans sa puissance, Jésus revient! \n\r C\'est l\'espérance qui nous soutient: \n\r Dans sa puissance, Jésus revient!', 2, 289),
(1145, 'Strophe', 2, 'Dieu nous enseigne à nous garder \n\r Du mal qui règne, de tout péché. \n\r Vivre sans cesse, persévérer \n\r Dans la sagesse, la sainteté.', 3, 289),
(1146, 'Refrain', 1, 'C\'est l\'espérance qui nous soutient: \n\r Dans sa puissance, Jésus revient! \n\r C\'est l\'espérance qui nous soutient: \n\r Dans sa puissance, Jésus revient!', 4, 289),
(1147, 'Strophe', 3, 'Peuple fidèle, prépare-toi! \n\r Rempli de zèle, prêche ta foi! \n\r Par sa justice, purifié, \n\r À son service, sois tout entier!', 5, 289),
(1148, 'Refrain', 1, 'C\'est l\'espérance qui nous soutient: \n\r Dans sa puissance, Jésus revient! \n\r C\'est l\'espérance qui nous soutient: \n\r Dans sa puissance, Jésus revient!', 6, 289),
(1149, 'Strophe', 1, 'Jésus, ton nom est le plus beau, \n\r Car il contient l\'immense amour, \n\r L\'amour de Dieu, Alléluia!', 1, 290),
(1150, 'Strophe', 2, 'Jésus, ton nom est notre paix, \n\r Car il unit les noms de tous, \n\r Tous tes amis, Alléluia!', 2, 290),
(1151, 'Strophe', 3, 'Jésus, ton nom est notre espoir, \n\r Car seul tu as vaincu la mort \n\r Et pour toujours, Alléluia!', 3, 290),
(1152, 'Strophe', 4, 'Jésus, ton nom est notre joie, \n\r Car nous pouvons aimer aussi \n\r Dans ton amour, Alléluia!', 4, 290),
(1153, 'Strophe', 5, 'Formons la ronde et tu viendras \n\r Te joindre à nous, tenir nos mains, \n\r Chantez aussi, Alléluia!', 5, 290),
(1154, 'Strophe', 6, 'Alléluia, Alléluia \n\r Alléluia, Alléluia \n\r Alléluia, Alléluia!', 6, 290),
(1155, 'Strophe', 1, 'Du Maître qui m\'appelle \n\r À le suivre ici-bas, \n\r J\'entends la voix fidèle \n\r Qui me dit: ne crains pas! \n\r L\'ennemi te menace? \n\r Je suis le Tout-Puissant; \n\r Je verse en toi ma grâce, \n\r Moi le Vivant!', 1, 291),
(1156, 'Refrain', 1, 'Il vit, il vit, Jésus vit aujourd\'hui. \n\r Je sais qu\'il est tout près de moi \n\r Sur le chemin étroit. \n\r Il vit, il vit, toujours il me conduit, \n\r Et je puis dire par la foi: \n\r Je sais qu\'il vit en moi.', 2, 291),
(1157, 'Strophe', 2, 'Il vit, et rien au monde \n\r Ne saurait me ravir \n\r Le bonheur qui m\'inonde \n\r Et m\'aide à le servir. \n\r En lui j\'ai force et joie, \n\r Tout le long du chemin, \n\r Où son amour m\'envoie, \n\r Moi, faible humain!', 3, 291),
(1158, 'Refrain', 1, 'Il vit, il vit, Jésus vit aujourd\'hui. \n\r Je sais qu\'il est tout près de moi \n\r Sur le chemin étroit. \n\r Il vit, il vit, toujours il me conduit, \n\r Et je puis dire par la foi: \n\r Je sais qu\'il vit en moi.', 4, 291),
(1159, 'Strophe', 3, 'Tressaille d\'allégresse, \n\r Racheté de Jésus, \n\r Fais retentir sans cesse \n\r L\'hymne saint des élus! \n\r Oh! sublime espérance, \n\r Que rien ne peut ternir, \n\r Merveilleuse assurance: \n\r Il va venir!', 5, 291),
(1160, 'Refrain', 1, 'Il vit, il vit, Jésus vit aujourd\'hui. \n\r Je sais qu\'il est tout près de moi \n\r Sur le chemin étroit. \n\r Il vit, il vit, toujours il me conduit, \n\r Et je puis dire par la foi: \n\r Je sais qu\'il vit en moi.', 6, 291),
(1161, 'Strophe', 1, 'Je ne sais pourquoi, dans sa grâce, \n\r Jésus m\'a tant aimé, \n\r Pourquoi par son sang il efface \n\r Ma dette, mon péché.', 1, 292),
(1162, 'Refrain', 1, 'Mais je sais qu\'en lui j\'ai la vie, \n\r Il m\'a sauvé dans son amour; \n\r Et gardé par sa main meurtrie, \n\r J\'attends l\'heure de son retour.', 2, 292),
(1163, 'Strophe', 2, 'Je ne sais comment la lumière \n\r Éclaire tout mon cœur, \n\r Comment je compris ma misère \n\r Et reçus mon Sauveur!', 3, 292),
(1164, 'Refrain', 1, 'Mais je sais qu\'en lui j\'ai la vie, \n\r Il m\'a sauvé dans son amour; \n\r Et gardé par sa main meurtrie, \n\r J\'attends l\'heure de son retour.', 4, 292),
(1165, 'Strophe', 3, 'Je ne sais quelle est la mesure \n\r De joie et de douleur \n\r Que pour moi, faible créature, \n\r Réserve mon Sauveur.', 5, 292),
(1166, 'Refrain', 1, 'Mais je sais qu\'en lui j\'ai la vie, \n\r Il m\'a sauvé dans son amour; \n\r Et gardé par sa main meurtrie, \n\r J\'attends l\'heure de son retour.', 6, 292),
(1167, 'Strophe', 4, 'Je ne sais quand de la victoire \n\r L\'heure enfin sonnera, \n\r Quand l\'Agneau, l\'Époux, dans sa gloire \n\r Avec lui me prendra.', 7, 292),
(1168, 'Refrain', 1, 'Mais je sais qu\'en lui j\'ai la vie, \n\r Il m\'a sauvé dans son amour; \n\r Et gardé par sa main meurtrie, \n\r J\'attends l\'heure de son retour.', 8, 292),
(1169, 'Strophe', 1, 'Jésus, ma force et mon rocher, \n\r Mon grand libérateur, \n\r Quel ennemi peut m\'approcher \n\r Sous ton bras protecteur? \n\r Je veux te bénir à jamais, \n\r Te louer constamment, \n\r Et ne m\'assurer désormais \n\r Que sur ton bras puissant.', 1, 293),
(1170, 'Strophe', 2, 'Je t\'aimerai de tout mon cœur, \n\r Je te serai soumis; \n\r Je rendrai gloire au grand vainqueur \n\r De tous mes ennemis. \n\r Au temps de mon affliction \n\r Tu fus mon ferme appui; \n\r Ta sainte bénédiction \n\r Me console aujourd\'hui.', 2, 293),
(1171, 'Strophe', 3, 'De ta puissance environné, \n\r Mon cœur est ferme en toi; \n\r De ton grand salut couronné, \n\r Je puis tout par la foi. \n\r Gloire à ton amour précieux, \n\r Si doux, si paternel, \n\r Qui me prépare un trône aux cieux, \n\r Un royaume éternel!', 3, 293),
(1172, 'Refrain', 1, 'Je passerai ma vie \n\r À dire à tous mes frères que tu es bon. \n\r Oui, je passerai ma vie \n\r À dire à tous mes frères que tu es bon.', 1, 294),
(1173, 'Strophe', 1, 'Oh! donne à mon cœur \n\r Le rythme de ta joie, \n\r Ô Seigneur, avec moi! \n\r Oh! donne à ma joie \n\r Le rythme de ton cœur, \n\r Ô Seigneur!', 2, 294),
(1174, 'Refrain', 1, 'Je passerai ma vie \n\r À dire à tous mes frères que tu es bon. \n\r Oui, je passerai ma vie \n\r À dire à tous mes frères que tu es bon.', 3, 294),
(1175, 'Strophe', 2, 'Donne à ma chanson \n\r Le rythme de ta vie, \n\r Ô Seigneur, mon ami! \n\r Oh! donne à ma vie \n\r Le rythme des chansons \n\r De moissons!', 4, 294),
(1176, 'Refrain', 1, 'Je passerai ma vie \n\r À dire à tous mes frères que tu es bon. \n\r Oui, je passerai ma vie \n\r À dire à tous mes frères que tu es bon.', 5, 294),
(1177, 'Strophe', 1, 'Le Seigneur m\'aime, bonheur suprême, le Seigneur m\'aime, il est amour! \n\r (Refrain) \n\r Je redirai toujours: le Seigneur m\'aime, le Seigneur m\'aime, il est amour!', 1, 295),
(1178, 'Strophe', 2, 'Le poids immense de mon offense, le poids immense tomba sur lui. \n\r (Refrain) \n\r Je redirai toujours: le Seigneur m\'aime, le Seigneur m\'aime, il est amour!', 2, 295),
(1179, 'Strophe', 3, 'En sacrifice à la justice, en sacrifice il vint s\'offrir. \n\r (Refrain) \n\r Je redirai toujours: le Seigneur m\'aime, le Seigneur m\'aime, il est amour!', 3, 295),
(1180, 'Strophe', 4, 'Il purifie toute ma vie, il purifie avec son sang. \n\r (Refrain) \n\r Je redirai toujours: le Seigneur m\'aime, le Seigneur m\'aime, il est amour!', 4, 295),
(1181, 'Strophe', 5, 'À toi la gloire et la victoire, à toi la gloire, Agneau de Dieu. \n\r (Refrain) \n\r Je redirai toujours: le Seigneur m\'aime, le Seigneur m\'aime, il est amour!', 5, 295),
(1182, 'Strophe', 1, 'L\'immense grâce de Jésus, \n\r Sublime don de Dieu, \n\r M\'a retrouvé, pécheur perdu, \n\r J\'étais si malheureux.', 1, 296),
(1183, 'Strophe', 2, 'C\'est par sa grâce que mon cœur \n\r Connaît enfin la paix; \n\r Ô jour béni, quand le Seigneur \n\r Paya tous mes forfaits.', 2, 296),
(1184, 'Strophe', 3, 'Combien de pièges, de dangers, \n\r Ralentissent mes pas, \n\r Mais, dans sa grâce; il vient m\'aider, \n\r Il me prend dans ses bras.', 3, 296),
(1185, 'Strophe', 1, 'Abba, Père, je veux être à toi seulement \n\r Et ma volonté soumettre à toi constamment. \n\r Que mon cœur reste enflammé, \n\r Près de toi je veux rester. \n\r Abba, Père, je veux être à toi seulement.', 1, 297),
(1186, 'Strophe', 1, 'À toi, mon Dieu, je me donne, \n\r Je me donne tout entier! \n\r Ton amour est ma couronne \n\r Ta force est mon bouclier.', 1, 298),
(1187, 'Strophe', 2, 'Je te donne mes journées \n\r Mes succès ou mes revers. \n\r Je te donne mes années \n\r Mes printemps et mes hivers.', 2, 298),
(1188, 'Strophe', 3, 'Dans la joie ou la souffrance, \n\r Je veux te suivre en tout lieu. \n\r Toute ma vie à l\'avance, \n\r Je te l\'apporte, ô mon Dieu.', 3, 298),
(1189, 'Strophe', 1, 'C\'est mon joyeux service \n\r D\'offrir à Jésus-Christ, \n\r En vivant sacrifice, \n\r Mon corps et mon esprit.', 1, 299),
(1190, 'Refrain', 1, 'Accepte mon offrande, \n\r Bien-aimé Fils de Dieu, \n\r Et que sur moi descende \n\r La flamme du saint lieu!', 2, 299),
(1191, 'Strophe', 2, 'J\'abandonne ma vie, \n\r Sans regret ni frayeur, \n\r À ta grâce infinie, \n\r Ô mon libérateur!', 3, 299),
(1192, 'Refrain', 1, 'Accepte mon offrande, \n\r Bien-aimé Fils de Dieu, \n\r Et que sur moi descende \n\r La flamme du saint lieu!', 4, 299),
(1193, 'Strophe', 3, 'Qu\'un feu nouveau s\'allume \n\r Par ton amour en moi \n\r Et dans mon cœur consume \n\r Ce qui n\'est pas à toi!', 5, 299),
(1194, 'Refrain', 1, 'Accepte mon offrande, \n\r Bien-aimé Fils de Dieu, \n\r Et que sur moi descende \n\r La flamme du saint lieu!', 6, 299),
(1195, 'Strophe', 4, 'Viens, Jésus, sois mon maître, \n\r Car tu m\'as racheté. \n\r À toi seul je veux être \n\r Et pour l\'éternité.', 7, 299),
(1196, 'Refrain', 1, 'Accepte mon offrande, \n\r Bien-aimé Fils de Dieu, \n\r Et que sur moi descende \n\r La flamme du saint lieu!', 8, 299),
(1197, 'Strophe', 1, 'Entre tes mains j\'abandonne \n\r Tout ce que j\'appelle mien. \n\r Oh! ne permets à personne, \n\r Seigneur, d\'en reprendre rien!', 1, 300),
(1198, 'Refrain', 1, 'Oui, prends tout, Seigneur! \n\r Oui, prends tout, Seigneur! \n\r Entre tes mains j\'abandonne \n\r Tout avec bonheur.', 2, 300),
(1199, 'Strophe', 2, 'Je n\'ai pas peur de te suivre \n\r Sur le chemin de la croix. \n\r C\'est pour toi que je veux vivre, \n\r Je connais, j\'aime ta voix.', 3, 300),
(1200, 'Refrain', 1, 'Oui, prends tout, Seigneur! \n\r Oui, prends tout, Seigneur! \n\r Sans rien garder, je te livre \n\r Tout avec bonheur.', 4, 300),
(1201, 'Strophe', 3, 'Tu connais mieux que moi-même \n\r Tous les besoins de mon cœur, \n\r Et, pour mon bonheur suprême, \n\r Tu peux me rendre vainqueur.', 5, 300),
(1202, 'Refrain', 1, 'Oui, prends tout, Seigneur! \n\r Oui, prends tout, Seigneur! \n\r Je ne vis plus pour moi-même, \n\r Mais pour mon Sauveur.', 6, 300),
(1203, 'Strophe', 4, 'Prends mon corps et prends mon âme; \n\r Que tout en moi soit à toi! \n\r Que par ta divine flamme \n\r Tout mal soit détruit en moi!', 7, 300),
(1204, 'Refrain', 1, 'Oui, prends tout, Seigneur! \n\r Oui, prends tout, Seigneur! \n\r Prends mon corps et prends mon âme, \n\r Règne sur mon cœur!', 8, 300),
(1205, 'Strophe', 1, 'Fais de moi un instrument, un instrument d\'adoration, \n\r J\'élève mes mains en ton nom. \n\r Fais de moi un instrument, un instrument d\'adoration, \n\r J\'élève mes mains en ton nom.', 1, 301),
(1206, 'Strophe', 2, 'Fais de nous une symphonie, une symphonie d\'adoration, \n\r J\'élève mes mains en ton nom. \n\r Fais de nous une symphonie, une symphonie d\'adoration, \n\r J\'élève mes mains en ton nom.', 2, 301),
(1207, 'Strophe', 3, 'Je te chanterai toujours un chant d\'amour d\'adoration, \n\r J\'élève mes mains en ton nom. \n\r Je te chanterai toujours un chant d\'amour d\'adoration, \n\r J\'élève mes mains en ton nom.', 3, 301),
(1208, 'Strophe', 1, 'J\'entends ta douce voix, \n\r Jésus, je viens à toi. \n\r Je viens, ô Sauveur, lave-moi \n\r Dans le sang de ta croix!', 1, 302),
(1209, 'Refrain', 1, 'Jésus, Roi des rois, \n\r Qui mourus pour moi, \n\r Je veux mourir avec toi, \n\r Avec toi sur la croix.', 2, 302),
(1210, 'Strophe', 2, 'J\'entends ta douce voix, \n\r Qui me dit: «Crois en moi!» \n\r Je crois, Seigneur, soutiens ma foi, \n\r Tiens-moi près de ta croix!', 3, 302),
(1211, 'Refrain', 1, 'Jésus, Roi des rois, \n\r Qui mourus pour moi, \n\r Je veux mourir avec toi, \n\r Avec toi sur la croix.', 4, 302),
(1212, 'Strophe', 3, 'J\'entends ta douce voix, \n\r Elle pénètre en moi \n\r Et me dit d\'aimer comme toi \n\r De l\'amour de la croix!', 5, 302),
(1213, 'Refrain', 1, 'Jésus, Roi des rois, \n\r Qui mourus pour moi, \n\r Je veux mourir avec toi, \n\r Avec toi sur la croix.', 6, 302),
(1214, 'Strophe', 4, 'J\'entends ta douce voix, \n\r Toi qui mourus pour moi, \n\r Seigneur, que je m\'unisse à toi \n\r Dans ta mort, par la foi!', 7, 302),
(1215, 'Refrain', 1, 'Jésus, Roi des rois, \n\r Qui mourus pour moi, \n\r Je veux mourir avec toi, \n\r Avec toi sur la croix.', 8, 302),
(1216, 'Strophe', 1, 'Jésus, je te suivrai partout, \n\r Jésus, je te suivrai partout, \n\r Soit dans la tristesse, \n\r Soit dans l\'allégresse, \n\r Jésus, je te suivrai partout.', 1, 303),
(1217, 'Strophe', 2, 'Jésus, je te suivrai toujours, \n\r Jésus, je te suivrai toujours, \n\r Si l\'épreuve abonde \n\r Ta paix est profonde. \n\r Jésus, je te suivrai toujours.', 2, 303),
(1218, 'Strophe', 3, 'Je te suivrai dès aujourd\'hui, \n\r Je te suivrai dès aujourd\'hui, \n\r Mon âme affranchie \n\r Vivra de ta vie. \n\r Je te suivrai dès aujourd\'hui.', 3, 303),
(1219, 'Strophe', 4, 'Je te suivrai jusques au bout, \n\r Je te suivrai jusques au bout, \n\r Si pour moi tu plaides, \n\r Seigneur, si tu m\'aides, \n\r Je te suivrai jusques au bout.', 4, 303),
(1220, 'Strophe', 1, 'Je veux t\'aimer, ô toi qui m\'aimes, \n\r Je veux te consacrer mon cœur; \n\r T\'aimer enfin plus que moi-même, \n\r Trouver en toi mon seul bonheur. \n\r En te suivant, je veux toujours \n\r Rester dans ton amour.', 1, 304),
(1221, 'Strophe', 2, 'Je veux t\'aimer d\'un cœur fidèle, \n\r Car le premier tu m\'as aimé. \n\r Je veux te prendre pour modèle \n\r Pour mieux servir et me donner, \n\r Faisant paraître tous les jours \n\r Un peu de ton amour.', 2, 304),
(1222, 'Strophe', 3, 'Souvent, la nuit qui m\'environne \n\r Me fait douter de mon chemin; \n\r Mais l\'assurance que tu donnes, \n\r C\'est que nous sommes dans ta main: \n\r À l\'horizon du dernier jour \n\r Rayonne ton amour.', 3, 304),
(1223, 'Strophe', 1, 'La voix du Seigneur m\'appelle: \n\r «Prends ta croix et viens, suis-moi.» \n\r Je réponds: «Sauveur fidèle, \n\r Me voici, je suis à toi.»', 1, 305),
(1224, 'Refrain', 1, 'Jusqu\'au bout je veux te suivre, \n\r Dans les bons, les mauvais jours, \n\r À toi pour mourir et vivre, \n\r À toi, Jésus, pour toujours.', 2, 305),
(1225, 'Strophe', 2, 'Mais le chemin du Calvaire \n\r Est étroit et périlleux. \n\r C\'est un chemin solitaire, \n\r Difficile et ténébreux.', 3, 305),
(1226, 'Refrain', 1, 'Jusqu\'au bout je veux te suivre, \n\r Dans les bons, les mauvais jours, \n\r À toi pour mourir et vivre, \n\r À toi, Jésus, pour toujours.', 4, 305),
(1227, 'Strophe', 3, 'Le Seigneur nous encourage \n\r À le suivre par la foi \n\r Et toujours donne en partage \n\r Son pardon, sa paix, sa joie.', 5, 305),
(1228, 'Refrain', 1, 'Jusqu\'au bout je veux te suivre, \n\r Dans les bons, les mauvais jours, \n\r À toi pour mourir et vivre, \n\r À toi, Jésus, pour toujours.', 6, 305),
(1229, 'Strophe', 4, 'Jésus donne grâce et gloire \n\r Pour le suivre pas à pas. \n\r Avec lui, joie et victoire, \n\r Paix et bonheur ici-bas!', 7, 305),
(1230, 'Refrain', 1, 'Jusqu\'au bout je veux te suivre, \n\r Dans les bons, les mauvais jours, \n\r À toi pour mourir et vivre, \n\r À toi, Jésus, pour toujours.', 8, 305),
(1231, 'Strophe', 1, 'Le cri de mon âme s\'élève vers toi. \n\r Elle te réclame, Jésus, pour son roi. \n\r Ton joug est facile, Ton fardeau léger; \n\r Sur mon cœur docile, \n\r Règne, ô bon berger.', 1, 306),
(1232, 'Strophe', 2, 'Éclaire ma route, Inspire mes choix, \n\r Dissipe mes douts et soutiens ma foi. \n\r Dans la nuit profonde, \n\r Tiens-moi par la main; \n\r Lumière du monde, luit sur mon chemin.', 2, 306),
(1233, 'Strophe', 3, 'Source de l\'eau vive, \n\r Pain venu des cieux, \n\r Que par toi je vive paisible et joyeux. \n\r Quand luira l\'aurore du jour éternel, \n\r Que je vive encore pour toi, \n\r Dans le ciel.', 3, 306),
(1234, 'Strophe', 1, 'Me voici, jeune ouvrier. \n\r Seigneur, veux-tu m\'employer? \n\r Je suis faible et sans talent, \n\r Mais, Jésus, je t\'aime tant! \n\r Toute petite est ma main, \n\r Mais son effort n\'est pas vain.', 1, 307),
(1235, 'Refrain', 1, 'Me voici, jeune ouvrier, \n\r Seigneur, veux-tu m\'employer?', 2, 307),
(1236, 'Strophe', 2, 'Que j\'apprenne chaque jour \n\r À lire au livre d\'amour. \n\r Que je marche devant toi \n\r En chantant: «Gloire à mon Roi». \n\r Oh! prends mes mains, prends mon cœur, \n\r Et rends-moi toujours vainqueur.', 3, 307),
(1237, 'Refrain', 1, 'Me voici, jeune ouvrier, \n\r Seigneur, veux-tu m\'employer?', 4, 307),
(1238, 'Strophe', 3, 'Apprends-moi la charité, \n\r La douceur, la sainteté. \n\r Par ton Esprit bienfaisant, \n\r Sois en moi toujours présent. \n\r Ô mon Maître, enseigne-moi \n\r À bien travailler pour toi!', 5, 307),
(1239, 'Refrain', 1, 'Me voici, jeune ouvrier, \n\r Seigneur, veux-tu m\'employer?', 6, 307),
(1240, 'Strophe', 1, 'Mon Sauveur, mon divin Maître, \n\r Pour toujours je suis à toi, \n\r Tu possèdes tout mon être, \n\r Purifie et blanchis-moi.', 1, 308),
(1241, 'Refrain', 1, 'Dans la parfaite lumière, \n\r Fais-moi marcher avec toi; \n\r Règne sur ma vie entière. \n\r Pour toujours, sois tout pour moi!', 2, 308),
(1242, 'Strophe', 2, 'Dans l\'heureuse bergerie, \n\r J\'apprends à me décharger \n\r Des fardeaux de cette vie, \n\r Sur ton cœur, ô bon Berger.', 3, 308),
(1243, 'Refrain', 1, 'Dans la parfaite lumière, \n\r Fais-moi marcher avec toi; \n\r Règne sur ma vie entière. \n\r Pour toujours, sois tout pour moi!', 4, 308),
(1244, 'Strophe', 3, 'Jésus, sauveur adorable, \n\r Que j\'avance chaque jour \n\r Sur le chemin véritable, \n\r Dans la joie et dans l\'amour!', 5, 308),
(1245, 'Refrain', 1, 'Dans la parfaite lumière, \n\r Fais-moi marcher avec toi; \n\r Règne sur ma vie entière. \n\r Pour toujours, sois tout pour moi!', 6, 308),
(1246, 'Strophe', 1, 'Ô Jésus, je t\'aime, Car tu m\'as sauvé, \n\r En t\'offrant toi-même. \n\r Ton sang m\'a lavé! \n\r Sur la croix bénie, \n\r Pour moi, tu mourus. \n\r Ta mort est ma vie, Je t\'aime, ô Jésus!', 1, 309),
(1247, 'Strophe', 2, 'Qu\'ici-bas je t\'aime, \n\r Jusque dans la mort, \n\r À l\'heure suprême du dernier effort, \n\r Ma voix expirante ne s\'entendra plus, \n\r Sachez que je chante: \n\r Je t\'aime, ô Jésus!', 2, 309),
(1248, 'Strophe', 3, 'Je verrai ta face, quel ravissement! \n\r Je louerai ta grâce \n\r Éternellement, et dirai sans cesse \n\r Avec les élus \n\r L\'hymne d\'allégresse: \n\r Je t\'aime, ô Jésus!', 3, 309),
(1249, 'Strophe', 1, 'Ô Seigneur, dont l\'amour immense \n\r Nourrit tout être sous les cieux, \n\r Tu nous donnas, dans ta clémence, \n\r Des commandements précieux. \n\r Avec amour, persévérance, \n\r Nous les gardons d\'un cœur joyeux.', 1, 310),
(1250, 'Strophe', 2, 'Ton joug n\'est pas un esclavage; \n\r Ta loi fait tout notre bonheur; \n\r Notre foi saisit l\'héritage \n\r Que ta croix mérite au pécheur. \n\r Tu nous gardes pendant l\'orage, \n\r Tu fais cesser toute douleur.', 2, 310),
(1251, 'Strophe', 3, 'Si l\'ennemi vient, plein d\'envie, \n\r Semer d\'écueils notre chemin, \n\r C\'est ton bras qui garde la vie \n\r De tes enfants, chaque matin, \n\r Avec ardeur, notre âme crie: \n\r Jésus, sois mon secours divin!', 3, 310),
(1252, 'Strophe', 4, 'Par ton Esprit, fais-nous renaître, \n\r Et remplis-nous d\'humilité. \n\r De nos péchés, fais disparaître \n\r La trace, en ta grande bonté! \n\r Nous te consacrons tout notre être, \n\r Pour vivre en toi l\'éternité.', 4, 310),
(1253, 'Strophe', 1, 'Je m\'approche de toi, je m\'approche de toi, \n\r Source de vie, source de vie, \n\r Mon Sauveur, bénis-moi!', 1, 311),
(1254, 'Strophe', 2, 'Fais silence en mon cœur, fais silence en mon cœur, \n\r Viens et me parle, viens et me parle, \n\r Ô mon divin Sauveur!', 2, 311),
(1255, 'Strophe', 3, 'Rends-moi conforme à toi, rends-moi conforme à toi, \n\r Divin modèle, divin modèle, \n\r Mets ton image en moi!', 3, 311),
(1256, 'Strophe', 4, 'Rends-moi bouillant, Seigneur, rends-moi bouillant, Seigneur, \n\r Pour ton service, pour ton service, \n\r Que je parte en vainqueur!', 4, 311),
(1257, 'Strophe', 5, 'Et quand tu reviendras, et quand tu reviendras, \n\r Dans ton royaume, dans ton royaume, \n\r Tu nous accueilleras!', 5, 311),
(1258, 'Strophe', 1, 'Ouvre mes yeux pour découvrir \n\r Ton chemin pour moi tout tracé; \n\r Place en mes mains la clé pour ouvrir \n\r L\'horizon de ma liberté.', 1, 312),
(1259, 'Refrain', 1, 'Confiant, je regarde à toi, \n\r Cherchant ta volonté, mon Dieu; \n\r Ouvre mes yeux, éclaire-moi \n\r Esprit de Dieu.', 2, 312),
(1260, 'Strophe', 2, 'Ouvre mon oreille à ta voix, \n\r Rends-moi sensible à ton appel; \n\r Et j\'entendrai ta musique en moi \n\r Jouer la partition du ciel.', 3, 312),
(1261, 'Refrain', 1, 'Confiant, je regarde à toi, \n\r Cherchant ta volonté, mon Dieu; \n\r Ouvre mes yeux, éclaire-moi \n\r Esprit de Dieu.', 4, 312),
(1262, 'Strophe', 3, 'Ouvre mes lèvres pour chanter \n\r La beauté de ta vérité; \n\r Ouvre mon cœur, fais-moi partager \n\r Tout l\'amour que tu m\'as donné.', 5, 312),
(1263, 'Refrain', 1, 'Confiant, je regarde à toi, \n\r Cherchant ta volonté, mon Dieu; \n\r Ouvre mes yeux, éclaire-moi \n\r Esprit de Dieu.', 6, 312),
(1264, 'Strophe', 1, 'Pardonne, ô Dieu, les vains tourments \n\r De nos cœurs agités. \n\r Rends-nous plus droits, plus confiants. \n\r Que par nos cœurs purifiés, \n\r Tu sois mieux honoré, \n\r Tu sois mieux honoré.', 1, 313),
(1265, 'Strophe', 2, 'Comme autrefois, au bord des flots \n\r Tes premiers serviteurs, \n\r A ton appel fais qu\'aussitôt \n\r Nous nous levions vaillants de cœur, \n\r Pour te suivre, ô Seigneur, \n\r Pour te suivre, ô Seigneur.', 2, 313),
(1266, 'Strophe', 3, 'Repos du lac galiléen \n\r Et de ses doux sommets, \n\r Où Jésus, priant pour les siens, \n\r Rayonnait de l\'éclat divin, \n\r De l\'amour, de la paix! \n\r De l\'amour, de la paix!', 3, 313),
(1267, 'Strophe', 1, 'Père, je viens à toi, \n\r Je t\'implore avec foi. \n\r Seigneur, exauce-moi, dans ton amour. \n\r Qu\'il est triste mon sort \n\r Sans ton aide, ô Dieu fort! \n\r Sois mon seul réconfort, \n\r Jour après jour.', 1, 314),
(1268, 'Refrain', 1, 'Père, à toi j\'ai recours \n\r Dès le matin. \n\r Dans la tienne, oh! toujours, \n\r Garde ma main!', 2, 314),
(1269, 'Strophe', 2, 'Soulage tous mes maux, \n\r Allège mes fardeaux \n\r Et sois mon seul repos dans le malheur. \n\r Mon pas est chancelant; \n\r Que ton bras tout-puissant \n\r Donne à ton faible enfant \n\r Force et vigueur.', 3, 314),
(1270, 'Refrain', 1, 'Père, à toi j\'ai recours \n\r Dès le matin. \n\r Dans la tienne, oh! toujours, \n\r Garde ma main!', 4, 314),
(1271, 'Strophe', 3, 'De ton sceau marque-moi. \n\r Par ta grâce, ô mon roi! \n\r Que je marche avec toi jusqu\'à la mort! \n\r Conduis-moi par la main \n\r Tout le long du chemin \n\r Et que j\'arrive enfin \n\r Au divin port!', 5, 314),
(1272, 'Refrain', 1, 'Père, à toi j\'ai recours \n\r Dès le matin. \n\r Dans la tienne, oh! toujours, \n\r Garde ma main!', 6, 314),
(1273, 'Strophe', 1, 'Qu\'il fait bon à ton service, \n\r Jésus, mon Sauveur! \n\r Qu\'il m\'est doux, en sacrifice, \n\r De t\'offrir mon cœur!', 1, 315),
(1274, 'Refrain', 1, 'Prends, ô Jésus, prends ma vie, \n\r Elle est toute à toi! \n\r Et, dans ta grâce infinie, \n\r Du mal sauve-moi.', 2, 315),
(1275, 'Strophe', 2, 'Pour me posséder toi-même, \n\r Tu m\'as racheté; \n\r Fais que désormais je n\'aime \n\r Que ta volonté.', 3, 315),
(1276, 'Refrain', 1, 'Prends, ô Jésus, prends ma vie, \n\r Elle est toute à toi! \n\r Et, dans ta grâce infinie, \n\r Du mal sauve-moi.', 4, 315),
(1277, 'Strophe', 3, 'Comme l\'ange au vol rapide, \n\r Je veux te servir; \n\r Je veux te suivre, ô mon guide! \n\r Toujours t\'obéir.', 5, 315),
(1278, 'Refrain', 1, 'Prends, ô Jésus, prends ma vie, \n\r Elle est toute à toi! \n\r Et, dans ta grâce infinie, \n\r Du mal sauve-moi.', 6, 315),
(1279, 'Strophe', 4, 'Travaux, luttes et souffrances, \n\r Que craindrais-je encor\'? \n\r En Christ est mon espérance, \n\r Au ciel mon trésor.', 7, 315),
(1280, 'Refrain', 1, 'Prends, ô Jésus, prends ma vie, \n\r Elle est toute à toi! \n\r Et, dans ta grâce infinie, \n\r Du mal sauve-moi.', 8, 315),
(1281, 'Strophe', 1, 'Seigneur, sanctifie \n\r Nos jours, nos moments; \n\r Fais que notre vie \n\r T\'honore en tout temps. \n\r Que de ta présence \n\r Parmi nous, pécheurs, \n\r L\'heureuse influence \n\r Pénètre nos cœurs.', 1, 316),
(1282, 'Strophe', 2, 'Nous voulons sans cesse \n\r Marcher par la foi, \n\r Et, dans la détresse, \n\r Regarder à toi. \n\r Heureux qui repose \n\r Sur ton bras puissant! \n\r On a toute chose \n\r En te possédant.', 2, 316),
(1283, 'Strophe', 1, 'Un chrétien, je voudrais être, de tout mon cœur, de tout mon cœur. \n\r Un chrétien, je voudrais être, de tout mon cœur, de tout mon cœur, de tout mon cœur, \n\r Un chrétien, je voudrais être, de tout mon cœur.', 1, 317),
(1284, 'Strophe', 2, 'Plus aimant, je voudrais être, de tout mon cœur, de tout mon cœur. \n\r Plus aimant, je voudrais être, de tout mon cœur, de tout mon cœur, de tout mon cœur, \n\r Plus aimant, je voudrais être, de tout mon cœur.', 2, 317),
(1285, 'Strophe', 3, 'Pur et saint, je voudrais être, de tout mon cœur, de tout mon cœur. \n\r Pur et saint, je voudrais être, de tout mon cœur, de tout mon cœur, de tout mon cœur, \n\r Pur et saint, je voudrais être, de tout mon cœur.', 3, 317),
(1286, 'Strophe', 4, 'Tel Jésus, je voudrais être, de tout mon cœur, de tout mon cœur. \n\r Tel Jésus, je voudrais être, de tout mon cœur, de tout mon cœur, de tout mon cœur, \n\r Tel Jésus, je voudrais être, de tout mon cœur.', 4, 317),
(1287, 'Strophe', 1, 'Veux-tu briser du péché le pouvoir? \n\r La force est en Christ, la force est en Christ. \n\r Si dans ton cœur tu veux le recevoir: \n\r La force est dans le sang de Christ!', 1, 318),
(1288, 'Refrain', 1, 'Je suis fort, fort! oui, plus que vainqueur, par le sang de Jésus! \n\r Je suis fort, fort! oui, plus que vainqueur, par le sang de Jésus mon Sauveur.', 2, 318),
(1289, 'Strophe', 2, 'Veux-tu marcher toujours pur, triomphant? \n\r La force est en Christ, la force est en Christ. \n\r Pour te garder, Jésus est tout-puissant: \n\r La force est dans le sang de Christ!', 3, 318),
(1290, 'Refrain', 1, 'Je suis fort, fort! oui, plus que vainqueur, par le sang de Jésus! \n\r Je suis fort, fort! oui, plus que vainqueur, par le sang de Jésus mon Sauveur.', 4, 318),
(1291, 'Strophe', 3, 'Veux-tu du ciel t\'approcher chaque jour? \n\r La force est en Christ, la force est en Christ. \n\r Avec Jésus, demeurer pour toujours? \n\r La force est dans le sang de Christ!', 5, 318);
INSERT INTO `strophe` (`idStrophe`, `type`, `identifiant`, `texte`, `position`, `idChant`) VALUES
(1292, 'Refrain', 1, 'Je suis fort, fort! oui, plus que vainqueur, par le sang de Jésus! \n\r Je suis fort, fort! oui, plus que vainqueur, par le sang de Jésus mon Sauveur.', 6, 318),
(1293, 'Strophe', 1, 'Prends ma vie, elle doit être \n\r À toi seul, ô divin Maître, \n\r Que sur le flot de mes jours, \n\r Ton regard brille toujours!', 1, 319),
(1294, 'Strophe', 2, 'Que mes mains, à ton service, \n\r S\'offrent pour le sacrifice, \n\r Qu\'à te suivre pas à pas \n\r Mes pieds ne faiblissent pas!', 2, 319),
(1295, 'Strophe', 3, 'Prends ma voix et qu\'elle chante \n\r Ta grâce auguste et touchante; \n\r Par mes lèvres que ton nom \n\r Parle aux pécheurs du pardon!', 3, 319),
(1296, 'Strophe', 4, 'Que mon esprit s\'illumine \n\r De ta sagesse divine; \n\r Prends mon argent et mon or \n\r Et toi seul, sois mon trésor.', 4, 319),
(1297, 'Strophe', 5, 'Que ma volonté devienne \n\r La servante de la tienne; \n\r Fais ton trône de mon cœur: \n\r Il t\'appartient, bon Sauveur.', 5, 319),
(1298, 'Strophe', 6, 'Qu\'ainsi mon amour répande \n\r À tes pieds son humble offrande: \n\r Prends-moi, dès les premiers jours, \n\r Tout, à toi seul, pour toujours!', 6, 319),
(1299, 'Strophe', 1, 'Que je sois comme un rayon d\'or \n\r Qui vient du ciel et luit. \n\r Que, sans faiblir, je brille encor\' \n\r Dans la plus sombre nuit.', 1, 320),
(1300, 'Strophe', 2, 'Que je sois semblable à la fleur \n\r Éclose sous mes pas, \n\r Semant la joie et le bonheur \n\r Chaque jour ici-bas.', 2, 320),
(1301, 'Strophe', 3, 'Que je sache apaiser toujours \n\r Les peines, les douleurs, \n\r Parlant à chacun de l\'amour \n\r De Jésus, le Sauveur.', 3, 320),
(1302, 'Strophe', 4, 'Que je sois toujours le soutien \n\r Des faibles, des exclus. \n\r Force, santé, j\'offre mon bien, \n\r À toi, Seigneur Jésus.', 4, 320),
(1303, 'Refrain', 1, 'Voici mon cœur, je viens vers toi, Seigneur.', 1, 321),
(1304, 'Strophe', 1, 'Jésus, je suis devant ta croix, \n\r J\'y vois tout ton amour pour moi. \n\r (Refrain) \n\r Voici mon cœur, je viens vers toi, Seigneur.', 2, 321),
(1305, 'Strophe', 2, 'Tu as pardonné jusqu\'au bout, \n\r Pour toi je me mets à genoux. \n\r (Refrain) \n\r Voici mon cœur, je viens vers toi, Seigneur.', 3, 321),
(1306, 'Strophe', 3, 'Jésus, tu es ressuscité, \n\r Ta joie, tu veux nous la donner. \n\r (Refrain) \n\r Voici mon cœur, je viens vers toi, Seigneur.', 4, 321),
(1307, 'Strophe', 4, 'Oui, j\'ai reconnu ton amour, \n\r Pour toi je chanterai toujours. \n\r (Refrain) \n\r Voici mon cœur, je viens vers toi, Seigneur.', 5, 321),
(1308, 'Strophe', 1, 'Ah! donne à mon âme \n\r Plus de sainteté, \n\r Plus d\'ardente flamme, \n\r De sérénité, \n\r Plus de confiance, \n\r Pour rester debout, \n\r Plus de patience, \n\r Pur supporter tout!', 1, 322),
(1309, 'Strophe', 2, 'Fais que je contemple \n\r Sans cesse l\'Agneau, \n\r Son vivant exemple, \n\r Sa croix, son tombeau, \n\r Sa grâce fidèle, \n\r Son immense amour, \n\r Sa gloire éternelle, \n\r Son prochain retour!', 2, 322),
(1310, 'Strophe', 3, 'Jésus, à mes \n\r Tu veux compatir; \n\r De toutes tes armes \n\r Viens me revêtir. \n\r Par plus de prière, \n\r De zèle et de foi, \n\r Que dans la lumière \n\r Je marche avec toi!', 3, 322),
(1311, 'Strophe', 4, 'Donne à ton service \n\r Un cœur plus joyeux, \n\r Prompt au sacrifice, \n\r Toujours sous tes yeux, \n\r Qui chante et qui tremble, \n\r Humble en sa ferveur, \n\r Un cœur qui ressemble \n\r Au tien, mon sauveur.', 4, 322),
(1312, 'Strophe', 1, 'D\'un ferme pas je veux monter \n\r Jusqu\'au sommet de mon sentier; \n\r Je t\'en supplie, ô Dieu de paix, \n\r Fais-moi gravir tes hauts sommets.', 1, 323),
(1313, 'Refrain', 1, 'Élève-moi vers les hauteurs \n\r De ton pays, divin Sauveur, \n\r Du ciel dévoile les attraits, \n\r Fais-moi gravir tes hauts sommets.', 2, 323),
(1314, 'Strophe', 2, 'Je veux partir, mon cœur est las \n\r De tant d\'horreur, de tant d\'effroi! \n\r Que l\'on préfère les marais, \n\r Je rêve, ô Dieu, des hauts sommets.', 3, 323),
(1315, 'Refrain', 1, 'Élève-moi vers les hauteurs \n\r De ton pays, divin Sauveur, \n\r Du ciel dévoile les attraits, \n\r Fais-moi gravir tes hauts sommets.', 4, 323),
(1316, 'Strophe', 3, 'Je n\'aime plus vivre ici-bas, \n\r Satan détruit ma paix, ma joie; \n\r J\'entends, ravi, des sons parfaits: \n\r Les chants heureux des hauts sommets.', 5, 323),
(1317, 'Refrain', 1, 'Élève-moi vers les hauteurs \n\r De ton pays, divin Sauveur, \n\r Du ciel dévoile les attraits, \n\r Fais-moi gravir tes hauts sommets.', 6, 323),
(1318, 'Strophe', 4, 'Je veux fouler le sol béni, \n\r Entrer, Seigneur, dans ton pays; \n\r Jouir, sans fin, de tes bienfaits, \n\r Conduis-moi vers tes hauts sommets.', 7, 323),
(1319, 'Refrain', 1, 'Élève-moi vers les hauteurs \n\r De ton pays, divin Sauveur, \n\r Du ciel dévoile les attraits, \n\r Fais-moi gravir tes hauts sommets.', 8, 323),
(1320, 'Strophe', 1, 'La nature parle d\'un Créateur \n\r Et le vent murmure sa grandeur, \n\r Mais, si l\'on me parle d\'un Sauveur, \n\r Qu\'est-ce donc pour moi? \n\r La nature parle d\'un Créateur \n\r Et le vent murmure sa grandeur, \n\r Mais, si l\'on me parle d\'un Sauveur, \n\r Qu\'est-ce donc pour moi?', 1, 324),
(1321, 'Refrain', 1, 'Jusqu\'au jour où voulant face à face, \n\r Rencontrer celui qui me créa, \n\r Oui, j\'ai compris qu\'il est mieux qu\'un simple nom, \n\r Qu\'un simple éclat, que tout montre sa grâce. \n\r Qu\'il vient vers moi pour prendre ma main, \n\r En m\'accompagnant soir et matin,…', 2, 324),
(1322, 'Refrain', 1, '…Et me menant dans le droit chemin, \n\r Il est tout pour moi! \n\r Jusqu\'au jour où voulant face à face, \n\r Rencontrer celui qui me créa, \n\r Oui, j\'ai compris qu\'il est mieux qu\'un simple nom, \n\r Qu\'un simple éclat, que tout montre sa grâce…', 3, 324),
(1323, 'Refrain', 1, '…Qu\'il vient vers moi pour prendre ma main, \n\r En m\'accompagnant soir et matin, \n\r Et me menant dans le droit chemin, \n\r Il est tout pour moi! \n\r Oui, il est tout pour moi!', 4, 324),
(1324, 'Strophe', 2, 'On me dit qu\'un Sauveur nous est né \n\r Il y a longtemps en Galilée; \n\r S\'il est venu pour nous racheter, \n\r Qu\'est-ce donc pour moi? \n\r On me dit qu\'un Sauveur nous est né \n\r Il y a longtemps en Galilée; \n\r S\'il est venu pour nous racheter, \n\r Qu\'est-ce donc pour moi?', 5, 324),
(1325, 'Refrain', 1, 'Jusqu\'au jour où voulant face à face, \n\r Rencontrer celui qui me créa, \n\r Oui, j\'ai compris qu\'il est mieux qu\'un simple nom, \n\r Qu\'un simple éclat, que tout montre sa grâce. \n\r Qu\'il vient vers moi pour prendre ma main, \n\r En m\'accompagnant soir et matin,…', 6, 324),
(1326, 'Refrain', 1, '…Et me menant dans le droit chemin, \n\r Il est tout pour moi! \n\r Jusqu\'au jour où voulant face à face, \n\r Rencontrer celui qui me créa, \n\r Oui, j\'ai compris qu\'il est mieux qu\'un simple nom, \n\r Qu\'un simple éclat, que tout montre sa grâce…', 7, 324),
(1327, 'Refrain', 1, '…Qu\'il vient vers moi pour prendre ma main, \n\r En m\'accompagnant soir et matin, \n\r Et me menant dans le droit chemin, \n\r Il est tout pour moi! \n\r Oui, il est tout pour moi!', 8, 324),
(1328, 'Strophe', 1, 'En ce monde de tristesse, \n\r Seigneur, conduis-moi! \n\r Prends pitié de ma faiblesse, \n\r Tiens-moi près de toi!', 1, 325),
(1329, 'Refrain', 1, 'Ô tout-puissant roi, \n\r Ô tout-puissant roi, \n\r Toujours, toujours garde-moi \n\r Dans la paix en toi!', 2, 325),
(1330, 'Strophe', 2, 'Si je perds toute espérance, \n\r Ranime ma foi! \n\r Aide-moi dans la souffrance \n\r À m\'attendre à toi!', 3, 325),
(1331, 'Refrain', 1, 'Ô tout-puissant roi, \n\r Ô tout-puissant roi, \n\r Toujours, toujours garde-moi \n\r Dans la paix en toi!', 4, 325),
(1332, 'Strophe', 3, 'Si mes amis me délaissent, \n\r Oh! reste avec moi! \n\r Fais, ô Dieu, qu\'ils reconnaissent \n\r Que je suis à toi!', 5, 325),
(1333, 'Refrain', 1, 'Ô tout-puissant roi, \n\r Ô tout-puissant roi, \n\r Toujours, toujours garde-moi \n\r Dans la paix en toi!', 6, 325),
(1334, 'Strophe', 4, 'Quand passera cette terre, \n\r Prends-moi près de toi! \n\r Ton nom que j\'aime et révère \n\r Sera tout pour moi.', 7, 325),
(1335, 'Refrain', 1, 'Ô tout-puissant roi, \n\r Ô tout-puissant roi, \n\r Toujours, toujours garde-moi \n\r Dans la paix en toi!', 8, 325),
(1336, 'Strophe', 5, 'Pour être un jour sur ton trône \n\r Assis avec toi, \n\r Et posséder ta couronne, \n\r Seigneur, conduis-moi!', 9, 325),
(1337, 'Refrain', 1, 'Ô tout-puissant roi, \n\r Ô tout-puissant roi, \n\r Toujours, toujours garde-moi \n\r Dans la paix en toi!', 10, 325),
(1338, 'Strophe', 1, 'Je me tiendrai tout près du Maître, \n\r Plus près, jour après jour; \n\r À lui sans retour je veux être, \n\r Gardé dans son amour.', 1, 326),
(1339, 'Strophe', 2, 'Qu\'en tous lieux, toujours je reflète \n\r Les traits de mon Sauveur; \n\r Et que bien haut ma voix répète \n\r Qu\'il est le Rédempteur.', 2, 326),
(1340, 'Strophe', 3, 'Que par lui je sois une source \n\r De paix, de pureté; \n\r Montrant tout le long de la course \n\r De Christ la sainteté.', 3, 326),
(1341, 'Strophe', 4, 'J\'ai soif de t\'aimer, divin Maître, \n\r Jésus, puissant Sauveur; \n\r De ton Esprit, remplis mon être, \n\r Mets ton ciel dans mon cœur.', 4, 326),
(1342, 'Strophe', 5, 'Sur mon cœur, mon âme indocile, \n\r Sois pleinement le Roi; \n\r Que je sois le vase d\'argile \n\r Façonné seul pour toi.', 5, 326),
(1343, 'Strophe', 1, 'Seigneur, par la clarté de ton amour, \n\r Chasse l\'obscurité qui nous entoure! \n\r Jésus, toi la lumière qui nous éclaire, \n\r Vérité qui nous guide et qui nous libère, \n\r Brille sur nous, brille sur nous!', 1, 327),
(1344, 'Refrain', 1, 'Brille, ô Jésus, couvre ce pays de ta gloire! \n\r Brûle, Esprit saint, embrase nos cœurs! \n\r Coule en torrents, de ta grâce remplis les nations! \n\r Parle, Seigneur, que la lumière soit!', 2, 327),
(1345, 'Strophe', 2, 'Seigneur, tu me conduis en ta présence, \n\r De la nuit dans l\'éclat de ta puissance. \n\r Par ton sang, je prends part à ta sainteté; \n\r Sonde-moi, mes ténèbres, consume-les! \n\r Brille sur moi, brille sur moi!', 3, 327),
(1346, 'Refrain', 1, 'Brille, ô Jésus, couvre ce pays de ta gloire! \n\r Brûle, Esprit saint, embrase nos cœurs! \n\r Coule en torrents, de ta grâce remplis les nations! \n\r Parle, Seigneur, que la lumière soit!', 4, 327),
(1347, 'Strophe', 3, 'Et quand nous contemplons ta royauté, \n\r Nos visages reflètent ta beauté. \n\r Transformée sans cesse, de gloire en gloire, \n\r Que sans fin notre vie dise ton histoire! \n\r Brille sur nous, brille sur nous!', 5, 327),
(1348, 'Refrain', 1, 'Brille, ô Jésus, couvre ce pays de ta gloire! \n\r Brûle, Esprit saint, embrase nos cœurs! \n\r Coule en torrents, de ta grâce remplis les nations! \n\r Parle, Seigneur, que la lumière soit!', 6, 327),
(1349, 'Strophe', 1, 'Jésus, doux Maître, \n\r Règne sur moi, \n\r Soumets mon être, \n\r Sois-en le roi. \n\r Je suis l\'argile, \n\r Toi le potier, \n\r Rends-moi docile, \n\r Ton prisonnier.', 1, 328),
(1350, 'Strophe', 2, 'Jésus, lumière, \n\r Pénètre en moi, \n\r Éprouve, éclaire \n\r Ma faible foi. \n\r Plus blanc que neige \n\r Rends-moi Seigneur, \n\r Et de tout piège, \n\r Garde mon cœur.', 2, 328),
(1351, 'Strophe', 3, 'Ô Jésus, source \n\r De guérison, \n\r Sois dans ma course, \n\r Santé, pardon. \n\r Par ta puissance, \n\r Viens, soutiens-moi; \n\r Par ta présence, \n\r Révèle-toi.', 3, 328),
(1352, 'Strophe', 4, 'Je m\'abandonne, \n\r Jésus à toi, \n\r Détruis, pardonne \n\r Tout mal en moi. \n\r Remplis mon âme \n\r De ton Esprit, \n\r Et qu\'il m\'enflamme, \n\r Et jour et nuit!', 4, 328),
(1353, 'Strophe', 1, 'Te ressembler, \n\r Jésus, c\'est mon espoir suprême. \n\r Penser, agir, aimer, \n\r toujours plus comme toi. \n\r Te ressembler, \n\r Jésus, c\'est mon espoir suprême. \n\r Par ton Esprit, \n\r rends-moi semblable à toi.', 1, 329),
(1354, 'Strophe', 1, 'Ô Jésus, ma joie, \n\r Toi que Dieu m\'envoie, \n\r Mon Sauveur, mon roi. \n\r Viens à ma prière, mettre ta lumière \n\r Et ta vie en moi! \n\r De mon cœur sois le Seigneur: \n\r Hors de toi, seul adorable, \n\r Rien n\'est désirable.', 1, 330),
(1355, 'Strophe', 2, 'Quand le mal menace, \n\r Ô Jésus, ta grâce \n\r S\'offre à notre foi. \n\r Tu nous justifies, \n\r Tu promets la vie à celui qui croit. \n\r Ton amour est mon recours; \n\r En lui seul je me confie. \n\r Veille sur ma vie!', 2, 330),
(1356, 'Strophe', 3, 'Seul Jésus m\'attire, seul il peut suffire \n\r Pour remplir mon cœur. \n\r Il est ma richesse: \n\r Tous les biens paraissent \n\r Pour moi sans valeur. \n\r Les terreurs ni les douleurs \n\r Ne méritent qu\'on les craigne \n\r Puisque Jésus règne!', 3, 330),
(1357, 'Strophe', 4, 'Sois le seul exemple \n\r Que mes yeux contemplent, \n\r Ô Jésus, mon roi! \n\r Donne-moi de vivre \n\r Pour t\'aimer, te suivre Et mourir en toi! \n\r Chaque jour, que ton amour \n\r Me soutienne dans ma voie, \n\r Ô Jésus, ma joie!', 4, 330),
(1358, 'Refrain', 1, 'Comme un souffle fragile, \n\r Ta parole se donne; \n\r Comme un vase d\'argile, \n\r Ton amour nous façonne.', 1, 331),
(1359, 'Strophe', 1, 'Ta parole est murmure, \n\r Comme un secret d\'amour, \n\r Ta parole est blessure \n\r Qui nous ouvre le jour.', 2, 331),
(1360, 'Refrain', 1, 'Comme un souffle fragile, \n\r Ta parole se donne; \n\r Comme un vase d\'argile, \n\r Ton amour nous façonne.', 3, 331),
(1361, 'Strophe', 2, 'Ta parole est naissance, \n\r Comme on sort de prison, \n\r Ta parole est semence \n\r Qui promet la moisson.', 4, 331),
(1362, 'Refrain', 1, 'Comme un souffle fragile, \n\r Ta parole se donne; \n\r Comme un vase d\'argile, \n\r Ton amour nous façonne.', 5, 331),
(1363, 'Strophe', 3, 'Ta parole est partage, \n\r Comme on coupe du pain, \n\r Ta parole est passage \n\r Qui nous dit un chemin.', 6, 331),
(1364, 'Refrain', 1, 'Comme un souffle fragile, \n\r Ta parole se donne; \n\r Comme un vase d\'argile, \n\r Ton amour nous façonne.', 7, 331),
(1365, 'Strophe', 1, 'Lampe allumée en cette nuit profonde, \n\r Seigneur, ton livre éclaire le sentier \n\r De tes enfants en qui la joie abonde \n\r Pour le salut offert au monde entier.', 1, 332),
(1366, 'Refrain', 1, 'Par ta parole, céleste message \n\r De vérité, de justice et d\'amour, \n\r Guide mes pas dans le pèlerinage \n\r Où vers le ciel j\'avance chaque jour.', 2, 332),
(1367, 'Strophe', 2, 'Tu nous conduis dans une route sûre \n\r Que tu montras aux prophètes d\'antan. \n\r Bonne nouvelle à la sagesse pure, \n\r Ta Bible indique un chemin droit et franc.', 3, 332),
(1368, 'Strophe', 3, 'Tes saints écrits, quand mon âme est tremblante, \n\r Ou quand je vis des moments de douleur, \n\r Donnent la paix en cette lutte ardente, \n\r En me montrant celui qui rend vainqueur.', 4, 332),
(1369, 'Refrain', 1, 'Par ta parole, céleste message \n\r De vérité, de justice et d\'amour, \n\r Guide mes pas dans le pèlerinage \n\r Où vers le ciel j\'avance chaque jour.', 5, 332),
(1370, 'Strophe', 4, 'Soumets mon cœur grâce à cette parole, \n\r Jusqu\'au grand jour où chez toi j\'entrerai. \n\r Que ta sagesse en tout temps me console \n\r Et m\'introduise un jour dans tes palais.', 6, 332),
(1371, 'Refrain', 1, 'Par ta parole, céleste message \n\r De vérité, de justice et d\'amour, \n\r Guide mes pas dans le pèlerinage \n\r Où vers le ciel j\'avance chaque jour.', 7, 332),
(1372, 'Strophe', 1, 'Ta parole, ô Père, \n\r Est la vraie lumière \n\r Qui conduit nos vies, \n\r Et nous sanctifie.', 1, 333),
(1373, 'Strophe', 2, 'Quand Satan nous guette, \n\r Ta parole est prête: \n\r Contre lui se dresse \n\r Notre forteresse.', 2, 333),
(1374, 'Strophe', 3, 'Si de noirs nuages \n\r Forment des orages, \n\r Cette lampe éclaire \n\r Notre route entière.', 3, 333),
(1375, 'Strophe', 4, 'Quelle joie immense, \n\r Quelle récompense \n\r Offre ta parole! \n\r Elle nous console.', 4, 333),
(1376, 'Strophe', 5, 'Face à la détresse, \n\r Grande est ta tendresse; \n\r Ta parole est vie; \n\r Tu nous fortifies.', 5, 333),
(1377, 'Strophe', 6, 'Seigneur, Dieu fidèle, \n\r Remplis nous de zèle. \n\r Viens et nous enseigne. \n\r Sur ton peuple règne.', 6, 333),
(1378, 'Strophe', 1, 'Pour triompher dans les combats, \n\r Quelle est notre force ici-bas? \n\r Quelle est la divine lumière \n\r Qui nous dirige et nous éclaire \n\r Dans la nuit sombre de l\'erreur? \n\r C\'est la parole du Seigneur.', 1, 334),
(1379, 'Strophe', 2, 'Quelle est l\'épée à deux tranchants, \n\r Qui sonde l\'âme du méchant \n\r Qui met à jour bien des mobiles, \n\r Dont l\'aveu est si difficile, \n\r Et qui pénètre au fond du cœur? \n\r C\'est la parole du Seigneur.', 2, 334),
(1380, 'Strophe', 3, 'Que ta parole habite en moi, \n\r Éclaire et ranime ma foi, \n\r Et que telle une source pure, \n\r Elle m\'abreuve et me rassure. \n\r Je veux devenir un vainqueur, \n\r Par ta parole, ô Seigneur!', 3, 334),
(1381, 'Strophe', 1, 'Joyeusement nous saluons \n\r Le saint jour du Seigneur. \n\r Avec bonheur nous adorons \n\r Dieu, notre rédempteur.', 1, 335),
(1382, 'Strophe', 2, 'Dans ta maison tous rassemblés \n\r Nous t\'offrons notre cœur; \n\r Reçois nos hommages sacrés, \n\r Ô notre Dieu sauveur!', 2, 335),
(1383, 'Strophe', 3, 'Avec ton Église ici-bas, \n\r Tous guidés par l\'Esprit, \n\r Nous prendrons part aux saints combats \n\r Du Seigneur Jésus-Christ.', 3, 335),
(1384, 'Strophe', 4, 'Salut, ô jour saint et béni! \n\r Donné par le Seigneur, \n\r Où tout son peuple réuni \n\r Le loue avec ferveur.', 4, 335),
(1385, 'Strophe', 1, 'La semaine a fui, Seigneur, \n\r Du repos le jour a lui. \n\r Nous venons, remplis d\'ardeur, \n\r Chercher ta face aujourd\'hui. \n\r Ô festin doux et joyeux, \n\r Avant-goût déjà des cieux! \n\r Ô festin doux et joyeux, \n\r Avant-goût déjà des cieux!', 1, 336),
(1386, 'Strophe', 2, 'Rédempteur toujours clément, \n\r En qui seul est le bonheur, \n\r Reçois-nous en pardonnant, \n\r Viens et lave notre cœur. \n\r Nous voulons, dans ton amour, \n\r Te louer en ce saint jour. \n\r Nous voulons, dans ton amour, \n\r Te louer en ce saint jour.', 2, 336),
(1387, 'Strophe', 3, 'Ta présence dans ces lieux \n\r À nos cœurs se fait sentir, \n\r Et ta gloire, ô Dieu des cieux, \n\r À nos yeux vient resplendir. \n\r Reste avec nous chaque jour, \n\r Conduis-nous dans ton amour. \n\r Reste avec nous chaque jour, \n\r Conduis-nous dans ton amour.', 3, 336),
(1388, 'Strophe', 1, 'L\'astre à l\'horizon descend, \n\r Sur les monts l\'ombre s\'étend; \n\r Veillons pendant que la nuit \n\r Au ciel allume sans bruit \n\r Ses lampes d\'or.', 1, 337),
(1389, 'Refrain', 1, 'Gloire, gloire à l\'Éternel, \n\r Au Dieu de paix, \n\r Sur la terre et dans le ciel, \n\r Tout te loue, Emmanuel, \n\r Pour tes bienfaits.', 2, 337),
(1390, 'Strophe', 2, 'Tandis que s\'éteint le jour, \n\r La prière, ô Dieu d\'amour! \n\r Vers ton saint trône voilé, \n\r Au fond du ciel étoilé \n\r Prend son essor.', 3, 337),
(1391, 'Refrain', 1, 'Gloire, gloire à l\'Éternel, \n\r Au Dieu de paix, \n\r Sur la terre et dans le ciel, \n\r Tout te loue, Emmanuel, \n\r Pour tes bienfaits.', 4, 337),
(1392, 'Strophe', 3, 'Dans ton Église, en tous lieux, \n\r Seigneur, sous les vastes cieux, \n\r Rassemble tous tes enfants; \n\r Rends-les courageux, vaillants \n\r Sous ton drapeau.', 5, 337),
(1393, 'Refrain', 1, 'Gloire, gloire à l\'Éternel, \n\r Au Dieu de paix, \n\r Sur la terre et dans le ciel, \n\r Tout te loue, Emmanuel, \n\r Pour tes bienfaits.', 6, 337),
(1394, 'Strophe', 4, 'Quand tu reviendras, Jésus, \n\r Quand le mal ne sera plus, \n\r Tu feras luire à nos yeux \n\r L\'aube du jour radieux \n\r Qui luit là-haut.', 7, 337),
(1395, 'Refrain', 1, 'Gloire, gloire à l\'Éternel, \n\r Au Dieu de paix, \n\r Sur la terre et dans le ciel, \n\r Tout te loue, Emmanuel, \n\r Pour tes bienfaits.', 8, 337),
(1396, 'Strophe', 1, 'Le saint sabbat va commencer \n\r Mon âme pour te reposer, \n\r Alléluia, alléluia! \n\r Profite de ce jour béni; \n\r Notre labeur est accompli', 1, 338),
(1397, 'Refrain', 1, 'Alléluia, alléluia! \n\r Alléluia, alléluia! \n\r Alléluia!', 2, 338),
(1398, 'Strophe', 2, 'Rendons grâce à Dieu dont l\'amour \n\r A voulu réserver ce jour \n\r Alléluia, alléluia! \n\r Pour en faire un repos pour tous. \n\r Bénissons-le à deux genoux.', 3, 338),
(1399, 'Refrain', 1, 'Alléluia, alléluia! \n\r Alléluia, alléluia! \n\r Alléluia!', 4, 338),
(1400, 'Strophe', 3, 'Que, de nos cœurs reconnaissants, \n\r Les notes claires de nos chants \n\r Alléluia, alléluia! \n\r S\'élèvent libres vers les cieux \n\r En ce saint jour si précieux.', 5, 338),
(1401, 'Refrain', 1, 'Alléluia, alléluia! \n\r Alléluia, alléluia! \n\r Alléluia!', 6, 338),
(1402, 'Strophe', 4, 'Le sabbat est pour tes enfants, \n\r Il annonce à tous les croyants \n\r Alléluia, alléluia! \n\r Que bientôt finiront nos maux \n\r Et nous goûterons le repos.', 7, 338),
(1403, 'Refrain', 1, 'Alléluia, alléluia! \n\r Alléluia, alléluia! \n\r Alléluia!', 8, 338),
(1404, 'Strophe', 1, 'Nous t\'invoquons, exauce-nous, ô Dieu. \n\r Dans ton amour, féconde notre foi. \n\r Lumière et vie sont auprès de toi. \n\r Que ce sabbat béni soit lumineux! \n\r Amen.', 1, 339),
(1405, 'Strophe', 1, 'Repos si doux, repos béni, \n\r Consacré par le créateur, \n\r Ô sabbat! ô don infini, \n\r Tu conduis à la paix du cœur.', 1, 340),
(1406, 'Strophe', 2, 'Repos si doux, repos de Dieu, \n\r Où se révèle son amour, \n\r Tu fus pour les siens, en tout lieu, \n\r De tous, le plus précieux jour.', 2, 340),
(1407, 'Strophe', 3, 'Repos si doux, repos du ciel, \n\r Où sur les ailes de la foi, \n\r Mon âme, au rivage éternel, \n\r S\'élance, allègre, vers son roi.', 3, 340),
(1408, 'Strophe', 4, 'Sabbat béni, bonheur divin, \n\r Consacré par le créateur, \n\r Tu restes sur notre chemin, \n\r La joie et le repos du cœur. \n\r Amen.', 4, 340),
(1409, 'Strophe', 1, 'Repos si doux, repos béni, \n\r Consacré par le créateur, \n\r Ô sabbat! ô don infini, \n\r Tu conduis à la paix du cœur.', 1, 341),
(1410, 'Strophe', 2, 'Repos si doux, repos de Dieu, \n\r Où se révèle son amour, \n\r Tu fus pour les siens, en tout lieu, \n\r De tous, le plus précieux jour.', 2, 341),
(1411, 'Strophe', 3, 'Repos si doux, repos du ciel, \n\r Où sur les ailes de la foi, \n\r Mon âme, au rivage éternel, \n\r S\'élance, allègre, vers son roi.', 3, 341),
(1412, 'Strophe', 4, 'Sabbat béni, bonheur divin, \n\r Consacré par le créateur, \n\r Tu restes sur notre chemin, \n\r La joie et le repos du cœur. \n\r Amen.', 4, 341),
(1413, 'Strophe', 1, 'Une paix soulage \n\r Aujourd\'hui nos cœurs; \n\r Libres, ils partagent \n\r Un divin bonheur. \n\r Ce bonheur se nomme \n\r Repos du sabbat. \n\r Gloire au Fils de l\'homme, \n\r Car son jour est là!', 1, 342),
(1414, 'Strophe', 2, 'Jésus nous convie, \n\r Même à deux ou trois, \n\r À mettre en nos vies \n\r L\'écho de sa foi. \n\r Nous voulons connaître \n\r Jésus, Fils de Dieu. \n\r Il nous donne d\'être \n\r Un parfum des cieux.', 2, 342),
(1415, 'Strophe', 3, 'Comme au sanctuaire, \n\r Dans les anciens jours, \n\r Apprenons du Père \n\r Justice et amour. \n\r Soyons à l\'écoute \n\r De ce qu\'il nous dit. \n\r Marchons sur la route \n\r Vers ses saints parvis.', 3, 342),
(1416, 'Strophe', 4, 'Nous serons son temple, \n\r Par lui habité, \n\r Nous suivrons l\'exemple \n\r De sa sainteté. \n\r Jésus nous devance \n\r Dans le lieu très saint. \n\r Entrons en confiance \n\r Au repos divin.', 4, 342),
(1417, 'Strophe', 1, 'Arbres que l\'Esprit féconde, \n\r Produisons un fruit parfait; \n\r Et que dans nos cœurs abondent \n\r Ton amour, ta joie, ta paix.', 1, 343),
(1418, 'Strophe', 2, 'Que nos mains, avec largesse \n\r Rendent, dans l\'élan du cœur, \n\r Ces bons fruits de gentillesse, \n\r De patience et de douceur.', 2, 343),
(1419, 'Strophe', 3, 'En Jésus, notre modèle, \n\r Fortement enracinés, \n\r Nous voulons qu\'il nous révèle \n\r Foi, maîtrise et vraie bonté.', 3, 343),
(1420, 'Strophe', 4, 'Que Dieu crée en nous la vie; \n\r Que l\'Esprit du Tout-Puissant \n\r Régénère et fortifie \n\r Le fruit de l\'amour vivant.', 4, 343),
(1421, 'Refrain', 1, 'C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur! \n\r En Église, tous ensemble, \n\r Demeurons corps du Seigneur! \n\r C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur!', 1, 344),
(1422, 'Refrain', 1, 'C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur! \n\r En Église, tous ensemble, \n\r Demeurons corps du Seigneur! \n\r C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur!', 2, 344),
(1423, 'Strophe', 1, 'Dieu immense et Dieu si proche, \n\r Le Seigneur est avec nous. \n\r Dieu fait chair en notre monde, \n\r Le Seigneur est avec nous. \n\r Fils de l\'homme, notre frère, \n\r Le Seigneur est avec nous', 3, 344),
(1424, 'Refrain', 1, 'C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur! \n\r En Église, tous ensemble, \n\r Demeurons corps du Seigneur! \n\r C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur!', 4, 344),
(1425, 'Strophe', 2, 'Envoyé vers les plus pauvres, \n\r Le Seigneur est avec nous. \n\r Partageant toute misère, \n\r Le Seigneur est avec nous. \n\r Pour montrer l\'amour du Père \n\r Le Seigneur est avec nous.', 5, 344),
(1426, 'Refrain', 1, 'C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur! \n\r En Église, tous ensemble, \n\r Demeurons corps du Seigneur! \n\r C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur!', 6, 344),
(1427, 'Strophe', 3, 'Pain de vie, vraie nourriture, \n\r Le Seigneur est avec nous. \n\r Pain rompu pour notre monde, \n\r Le Seigneur est avec nous. \n\r Pain de Dieu pour toute route, \n\r Le Seigneur est avec nous.', 7, 344),
(1428, 'Refrain', 1, 'C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur! \n\r En Église, tous ensemble, \n\r Demeurons corps du Seigneur! \n\r C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur!', 8, 344),
(1429, 'Strophe', 4, 'Dieu lumière en nos ténèbres, \n\r Le Seigneur est avec nous. \n\r Dieu de grâce, Dieu fidèle, \n\r Le Seigneur est avec nous. \n\r Pour la joie de son Église, \n\r Le Seigneur est avec nous.', 9, 344),
(1430, 'Refrain', 1, 'C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur! \n\r En Église, tous ensemble, \n\r Demeurons corps du Seigneur! \n\r C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur!', 10, 344),
(1431, 'Strophe', 5, 'Par l\'Esprit qui nous fait vivre \n\r Le Seigneur est avec nous. \n\r Par son souffle dans nos âmes, \n\r Le Seigneur est avec nous. \n\r Par sa force au cœur de l\'homme, \n\r Le Seigneur est avec nous.', 11, 344),
(1432, 'Refrain', 1, 'C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur! \n\r En Église, tous ensemble, \n\r Demeurons corps du Seigneur! \n\r C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur!', 12, 344),
(1433, 'Strophe', 6, 'Messager de l\'espérance, \n\r Le Seigneur est avec nous. \n\r Espérance du royaume, \n\r Le Seigneur est avec nous. \n\r Maintenant et d\'âge en âge, \n\r Le Seigneur est avec nous.', 13, 344),
(1434, 'Refrain', 1, 'C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur! \n\r En Église, tous ensemble, \n\r Demeurons corps du Seigneur! \n\r C\'est Jésus qui nous rassemble! \n\r C\'est Jésus notre Sauveur!', 14, 344),
(1435, 'Strophe', 1, 'Ils ont marché au pas des siècles \n\r Vers un pays de joie, \n\r Ils ont marché vers la lumière \n\r Pour habiter la joie.', 1, 345),
(1436, 'Refrain', 1, 'Écoute, écoute, surtout ne fais pas de bruit, \n\r On marche sur la route, on marche dans la nuit. \n\r Écoute, écoute, les pas du Seigneur vers toi: \n\r Il marche sur ta route, il marche près de toi.', 2, 345),
(1437, 'Strophe', 2, 'Ils ont laissé leurs cris de guerre \n\r Pour des chansons de paix, \n\r Ils ont laissé leur bout de terre \n\r Pour habiter la paix.', 3, 345),
(1438, 'Refrain', 1, 'Écoute, écoute, surtout ne fais pas de bruit, \n\r On marche sur la route, on marche dans la nuit. \n\r Écoute, écoute, les pas du Seigneur vers toi: \n\r Il marche sur ta route, il marche près de toi.', 4, 345),
(1439, 'Strophe', 3, 'Ils sont venus les mains ouvertes \n\r Pour accueillir l\'amour, \n\r Ils sont venus chercher des frères \n\r Pour habiter l\'amour.', 5, 345),
(1440, 'Refrain', 1, 'Écoute, écoute, surtout ne fais pas de bruit, \n\r On marche sur la route, on marche dans la nuit. \n\r Écoute, écoute, les pas du Seigneur vers toi: \n\r Il marche sur ta route, il marche près de toi.', 6, 345),
(1441, 'Strophe', 1, 'Dieu fait de nous en Jésus-Christ des hommes libres. \n\r Tout vient de lui, tout est pour lui: qu\'il nous délivre!', 1, 346),
(1442, 'Strophe', 2, 'Peuple choisi pour témoigner de l\'Évangile, \n\r Laisse ta vie se ranimer aux sources vives!', 2, 346),
(1443, 'Strophe', 3, 'Peuple choisi pour exister dans la prière, \n\r Prends en tes mains le monde entier et sa misère!', 3, 346),
(1444, 'Strophe', 4, 'Peuple choisi pour l\'unité de tous tes frères, \n\r Ouvre tes bras et reconnais les dons du Père!', 4, 346),
(1445, 'Strophe', 1, 'Réjouis-toi, peuple du Seigneur: \n\r Ton Dieu t\'a visité, ton Dieu t\'a visité. \n\r Réjouis-toi, peuple du Seigneur, \n\r Adore et glorifie son nom. \n\r Il a fait de toi un royaume, des sacrificateurs. \n\r Il t\'a fait asseoir sur le trône, \n\r Il a fait de toi un peuple de vainqueurs.', 1, 347),
(1446, 'Strophe', 1, 'Réjouis-toi, chrétien, voici ton Maître, \n\r Il va quitter son glorieux palais; \n\r Trois messagers ici-bas vont paraître \n\r Et préparer le royaume de paix.', 1, 348),
(1447, 'Refrain', 1, 'Veille encor\', bientôt dans la gloire \n\r Notre Roi paraîtra des cieux. \n\r C\'est pour Sion l\'immortelle victoire: \n\r Voici le jour, frère, lève les yeux!', 2, 348),
(1448, 'Strophe', 2, 'Le premier ange, au vol grand et rapide, \n\r Sur tous les lieux répète, solennel: \n\r Crains le Seigneur, ô toi, monde perfide, \n\r Le jugement va descendre du ciel!', 3, 348),
(1449, 'Refrain', 1, 'Veille encor\', bientôt dans la gloire \n\r Notre Roi paraîtra des cieux. \n\r C\'est pour Sion l\'immortelle victoire: \n\r Voici le jour, frère, lève les yeux!', 4, 348),
(1450, 'Strophe', 3, 'Fuis Babylone! annonce le deuxième. \n\r Le bon Berger rassemble ses troupeaux; \n\r Il les conduit à la maison qu\'il aime \n\r Et sur l\'impie il répand ses fléaux.', 5, 348),
(1451, 'Refrain', 1, 'Veille encor\', bientôt dans la gloire \n\r Notre Roi paraîtra des cieux. \n\r C\'est pour Sion l\'immortelle victoire: \n\r Voici le jour, frère, lève les yeux!', 6, 348),
(1452, 'Strophe', 4, 'Le troisième ange apporte son message, \n\r Et les appels de la grâce ont pris fin. \n\r Loin de l\'Agneau, funeste est le breuvage \n\r Où l\'ennemi consomme son destin!', 7, 348),
(1453, 'Refrain', 1, 'Veille encor\', bientôt dans la gloire \n\r Notre Roi paraîtra des cieux. \n\r C\'est pour Sion l\'immortelle victoire: \n\r Voici le jour, frère, lève les yeux!', 8, 348),
(1454, 'Strophe', 5, 'Sauveur du monde, écoute la prière \n\r De tes enfants, sur le déclin du jour: \n\r Fais, par l\'Esprit, qu\'ils portent la lumière \n\r De l\'Évangile annonçant ton retour!', 9, 348),
(1455, 'Refrain', 1, 'Veille encor\', bientôt dans la gloire \n\r Notre Roi paraîtra des cieux. \n\r C\'est pour Sion l\'immortelle victoire: \n\r Voici le jour, frère, lève les yeux!', 10, 348),
(1456, 'Strophe', 1, 'Jugement, jour de merveilles, \n\r La terre entière est avertie; \n\r La trompette à nos oreilles \n\r Dit le retour de Jésus-Christ: \n\r Elle sonne, elle sonne, \n\r Seul le rebelle est surpris.', 1, 349),
(1457, 'Strophe', 2, 'Voici le Seigneur en gloire: \n\r Jésus descend vers nous des cieux; \n\r C\'est en lui qu\'est la victoire, \n\r Nous reconnaissons notre Dieu. \n\r Ô bon Sauveur, ô bon Sauveur, \n\r Pour nous miséricordieux.', 2, 349),
(1458, 'Strophe', 3, 'À sa voix, à sa lumière, \n\r Les morts retrouvent leur vigueur; \n\r La nature toute entière \n\r Frémit devant son Créateur. \n\r Devant Jésus, devant Jésus, \n\r Tous les hommes sont pécheurs.', 3, 349),
(1459, 'Strophe', 4, 'Et pourtant à tous les êtres \n\r Qui auront voulu ici-bas \n\r Aimer et servir leur maître, \n\r Dans sa bonté, Jésus dira: \n\r Je vous donne la couronne, \n\r Venez, entrez dans ma joie.', 4, 349),
(1460, 'Strophe', 1, 'Je connais un pays par-delà le ciel bleu, \n\r Où tous les bienheureux seront auprès de Dieu,', 1, 350),
(1461, 'Strophe', 2, 'Dans le repos parfait, le séjour ravissant; \n\r Là, plus de deuil cruel, de douleur, de tourment.', 2, 350),
(1462, 'Refrain', 1, 'Dans la maison là-haut, \n\r Où je serai bientôt, \n\r Rien ne troublera plus \n\r Le bonheur des élus.', 3, 350),
(1463, 'Strophe', 2, 'Je connais un palais dans la sainte cité, \n\r Plein de charme ineffable et de félicité,', 4, 350),
(1464, 'Strophe', 3, 'Où Jésus est entré depuis longtemps déjà, \n\r Et dans ses doux parvis, mon Dieu m\'introduira.', 5, 350),
(1465, 'Refrain', 1, 'Dans la maison là-haut, \n\r Où je serai bientôt, \n\r Rien ne troublera plus \n\r Le bonheur des élus.', 6, 350),
(1466, 'Strophe', 3, 'Je connais un rivage où l\'ouragan n\'est plus, \n\r Où tout est pleine paix, car le port, c\'est Jésus,', 7, 350),
(1467, 'Strophe', 4, 'Aucun flot menaçant n\'atteint le racheté, \n\r Dans le céleste abri, loin du monde agité.', 8, 350),
(1468, 'Refrain', 1, 'Dans la maison là-haut, \n\r Où je serai bientôt, \n\r Rien ne troublera plus \n\r Le bonheur des élus.', 9, 350),
(1469, 'Strophe', 4, 'Je connais un cantique entonné dans les cieux, \n\r C\'est le chant de l\'Agneau qui retentit joyeux;', 10, 350),
(1470, 'Strophe', 5, 'Ô sublime harmonie, ô glorieux transport, \n\r Bientôt je l\'entendrai, qu\'il est heureux mon sort!', 11, 350),
(1471, 'Refrain', 1, 'Dans la maison là-haut, \n\r Où je serai bientôt, \n\r Rien ne troublera plus \n\r Le bonheur des élus.', 12, 350),
(1472, 'Strophe', 1, 'Jérusalem, chère à mon cœur, \n\r Combien j\'aspire à toi! \n\r Quand viendras-tu sécher mes pleurs \n\r Et me combler de joie?', 1, 351),
(1473, 'Strophe', 2, 'Voici des pierres colorées \n\r Scintillant sur tes murs. \n\r De perles sont tes douze entrées \n\r Tes ruelles d\'or pur.', 2, 351),
(1474, 'Strophe', 3, 'À ta beauté, l\'âme s\'éveille. \n\r Quand te verrai-je enfin? \n\r Parterres et fleurs font merveille \n\r Au cœur de tes jardins.', 3, 351),
(1475, 'Strophe', 4, 'Seigneur, par ta puissante grâce \n\r Affermis notre foi, \n\r Jusqu\'au jour où nous aurons place \n\r Au ciel, tout près de toi.', 4, 351),
(1476, 'Strophe', 1, 'Je ne sais pas le jour où je verrai mon Roi, \n\r Mais je sais qu\'il me veut dans sa sainte demeure; \n\r La lumière vaincra les ombres à cette heure: \n\r Ce sera la gloire pour moi. \n\r Ce sera la gloire pour moi, ce sera la gloire pour moi. \n\r La lumière vaincra les ombres à cette heure: \n\r Ce sera la gloire pour moi.', 1, 352),
(1477, 'Strophe', 2, 'Je ne sais quels seront les chants des bienheureux, \n\r Les accents, les accords des hymnes angéliques, \n\r Mais je sais que, joignant ma voix aux saints cantiques, \n\r Bientôt j\'adorerai comme eux. \n\r Bientôt j\'adorerai comme eux, bientôt j\'adorerai comme eux. \n\r Mais je sais que, joignant ma voix aux saints cantiques, \n\r Bientôt j\'adorerai comme eux.', 2, 352),
(1478, 'Strophe', 3, 'Je ne sais quel sera le palais éternel, \n\r Mais je sais que mon âme y sera reconnue. \n\r Un regard de Jésus sera ma bienvenue. \n\r Pour moi, pour moi s\'ouvre le ciel. \n\r Pour moi, pour moi s\'ouvre le ciel, pour moi, pour moi s\'ouvre le ciel. \n\r Un regard de Jésus sera ma bienvenue. \n\r Pour moi, pour moi s\'ouvre le ciel.', 3, 352),
(1479, 'Strophe', 1, 'Jésus, un jour sur la terre, \n\r Apprend qu\'un ami est mort. \n\r Il parle alors en mystères: \n\r «Lazare, notre ami dort. \n\r Sa mort sera pour ma gloire, \n\r Oui, il ressuscitera. \n\r Celui qui en moi veut croire, \n\r Bien qu\'il soit mort, revivra.»', 1, 353),
(1480, 'Strophe', 2, 'Voyant la foule qui crie, \n\r Jésus frémit en son cœur: \n\r «Révèle-toi, Père», il prie, \n\r «Qu\'ils croient en moi, leur Sauveur.» \n\r Tourné vers la tombe ouverte, \n\r Jésus dit: «Lazare, sors!» \n\r Ainsi la vie est offerte \n\r Par le vainqueur de la mort.', 2, 353),
(1481, 'Strophe', 3, 'La terre tremble, quel drame! \n\r Les gardes tombent, vaincus. \n\r À l\'aube pleurent deux femmes \n\r Cherchant un corps disparu. \n\r Un ange a roulé la pierre; \n\r Ne restons plus au tombeau! \n\r Jésus crée, auprès du Père, \n\r Pour nous, un monde plus beau.', 3, 353),
(1482, 'Strophe', 4, 'Préparons-nous, le temps presse, \n\r Ce monde sera dissous. \n\r Beaucoup diront en détresse: \n\r «Montagnes, tombez sur nous!» \n\r Jésus, sublime miracle, \n\r Réveillera ses enfants. \n\r Vivants et morts, quel spectacle! \n\r S\'élèveront en chantant.', 4, 353),
(1483, 'Strophe', 1, 'Jeunes et vieux se réjouiront ensemble, \n\r Les jeunes filles danseront de joie. \n\r Laï, laï, laï, laï, laï, laï, \n\r Laï, laï, laï, laï, laï, laï, laï, laï, \n\r Laï, laï, laï, laï, laï, laï, laï, laï, laï;…', 1, 354),
(1484, 'Strophe', 2, '…Je changerai leur deuil en allégresse \n\r Et je les consolerai. \n\r Je leur donnerai la joie au lieu du chagrin, \n\r Je leur donnerai la joie, \n\r Je leur donnerai la joie au lieu du chagrin, \n\r Je leur donnerai la joie!', 2, 354),
(1485, 'Strophe', 1, 'Oh! quel beau jour où, devant ta face, \n\r Tous tes rachetés apparaîtront, \n\r En célébrant ta gloire et ta grâce! \n\r De leurs chants les cieux retentiront.', 1, 355),
(1486, 'Refrain', 1, 'Nombreux comme le sable des plages! \n\r Oh! que ce sera beau, \n\r Lorsque nous irons là-haut, \n\r Aussi nombreux que le sable des plages!', 2, 355),
(1487, 'Strophe', 2, 'Je la vois, cette armée innombrable, \n\r Ses rangs reflétant l\'éclat des cieux, \n\r Tressaillant d\'un bonheur ineffable, \n\r Sur le seuil du séjour radieux.', 3, 355),
(1488, 'Refrain', 1, 'Nombreux comme le sable des plages! \n\r Oh! que ce sera beau, \n\r Lorsque nous irons là-haut, \n\r Aussi nombreux que le sable des plages!', 4, 355),
(1489, 'Strophe', 3, 'De son trône éclatant de lumière, \n\r J\'entends la douce voix du Seigneur: \n\r «Venez, vous, bien-aimés de mon Père, \n\r La couronne est à chaque vainqueur.»', 5, 355),
(1490, 'Refrain', 1, 'Nombreux comme le sable des plages! \n\r Oh! que ce sera beau, \n\r Lorsque nous irons là-haut, \n\r Aussi nombreux que le sable des plages!', 6, 355),
(1491, 'Strophe', 4, 'Puis au sein du vaste océan d\'anges, \n\r Nos cohortes déversent leurs flots; \n\r L\'univers tremble au son des louanges, \n\r Plus de combats, de pleurs, de sanglots!', 7, 355),
(1492, 'Refrain', 1, 'Nombreux comme le sable des plages! \n\r Oh! que ce sera beau, \n\r Lorsque nous irons là-haut, \n\r Aussi nombreux que le sable des plages!', 8, 355),
(1493, 'Strophe', 1, 'Oh! viens Seigneur, ne tarde pas, \n\r Viens dissiper l\'obscurité \n\r Où nous devons toujours marcher, \n\r Remplis nos yeux de ton éclat.', 1, 356),
(1494, 'Strophe', 2, 'Brillant soleil, très pur matin, \n\r Nous aimerions te contempler, \n\r Nous réjouir de ta beauté, \n\r Oh! lève-toi sur nos chemins.', 2, 356),
(1495, 'Strophe', 3, 'Nous n\'abordons jamais au port, \n\r Tous nos pas sombrent dans le soir. \n\r Rends-nous la joie, rends-nous l\'espoir, \n\r Délivre-nous de notre mort.', 3, 356),
(1496, 'Strophe', 4, 'Parais enfin et nous vivrons, \n\r Nous n\'aurons plus aucune peur, \n\r En joie tu changeras nos pleurs, \n\r Et pour toujours nous chanterons.', 4, 356),
(1497, 'Strophe', 1, 'Ô toi qui fus fondée \n\r Sur l\'immortalité, \n\r Par Dieu même gardée, \n\r Glorieuse cité, \n\r Ma voix, mon cœur palpitent \n\r D\'espérance et d\'effroi; \n\r Heureux ceux qui t\'habitent, \n\r Demeure du grand Roi!', 1, 357),
(1498, 'Strophe', 2, 'Oui, quand je te contemple, \n\r Ô céleste séjour, \n\r Toi dont Christ est le temple, \n\r Toi dont Christ est le jour, \n\r L\'espoir, l\'espoir m\'anime; \n\r Déjà mes sens ravis \n\r Goûtent la paix sublime \n\r Au sein de tes parvis.', 2, 357),
(1499, 'Strophe', 3, 'Parfois aussi la crainte \n\r Vient obscurcir ma foi. \n\r N\'es-tu pas, cité sainte, \n\r Trop sainte, hélas, pour moi? \n\r Alors, sous tes portiques, \n\r Vibrant jusqu\'à mon cœur, \n\r J\'entends de doux cantiques \n\r Louer le Christ vainqueur!', 3, 357),
(1500, 'Strophe', 4, 'Séjour où Dieu m\'invite, \n\r Je ne sais pas encor\' \n\r Quelle splendeur s\'abrite \n\r Dans tes murailles d\'or; \n\r J\'ignore, mais j\'espère. \n\r J\'ignore, mais je sais \n\r Que là-haut est mon Père, \n\r Et pour moi, c\'est assez!', 4, 357),
(1501, 'Strophe', 1, 'Contempler mon Dieu sur son trône, \n\r Vivre avec Jésus dans le ciel, \n\r Jeter à ses pieds ma couronne, \n\r C\'est là le bonheur éternel.', 1, 358),
(1502, 'Refrain', 1, 'Dans le ciel, dans le ciel, \n\r Vivre avec Jésus dans le ciel. \n\r Dans le ciel, dans le ciel, \n\r C\'est là le bonheur éternel.', 2, 358),
(1503, 'Strophe', 2, 'Unir ma voix aux chants des anges, \n\r Bénir, louer Emmanuel, \n\r Chanter à jamais ses louanges, \n\r C\'est là le bonheur éternel.', 3, 358),
(1504, 'Refrain', 1, 'Dans le ciel, dans le ciel, \n\r Vivre avec Jésus dans le ciel. \n\r Dans le ciel, dans le ciel, \n\r C\'est là le bonheur éternel.', 4, 358),
(1505, 'Strophe', 3, 'Jouir d\'une paix infinie, \n\r Revoir mes amis dans le ciel, \n\r Posséder l\'immortelle vie, \n\r C\'est là le bonheur éternel.', 5, 358),
(1506, 'Refrain', 1, 'Dans le ciel, dans le ciel, \n\r Vivre avec Jésus dans le ciel. \n\r Dans le ciel, dans le ciel, \n\r C\'est là le bonheur éternel.', 6, 358),
(1507, 'Strophe', 4, 'Retrouver les saints dans la gloire, \n\r Près du trône de l\'Éternel, \n\r Célébrer la même victoire, \n\r C\'est là le bonheur éternel.', 7, 358),
(1508, 'Refrain', 1, 'Dans le ciel, dans le ciel, \n\r Vivre avec Jésus dans le ciel. \n\r Dans le ciel, dans le ciel, \n\r C\'est là le bonheur éternel.', 8, 358),
(1509, 'Strophe', 1, 'Ah! qu\'il est beau de voir des frères \n\r D\'un même amour unis entre eux! \n\r Esprit de Dieu, tu les éclaires, \n\r Tu les embrases de tes feux. \n\r Leurs chants pieux et leurs prières \n\r Comme un encens montent aux cieux. \n\r Leurs chants pieux et leurs prières \n\r Comme un encens montent aux cieux.', 1, 359),
(1510, 'Strophe', 2, 'Ô Rédempteur, en ta présence, \n\r Dans ta sainte communion, \n\r Ils savourent la jouissance \n\r D\'une céleste affection. \n\r Aussi leurs cœurs en assurance \n\r T\'offrent leur adoration. \n\r Aussi leurs cœurs en assurance \n\r T\'offrent leur adoration.', 2, 359),
(1511, 'Strophe', 3, 'Répands sur nous, Dieu charitable! \n\r Ton Esprit de grâce et de paix. \n\r Accueillis à la même table \n\r Et goûtant les mêmes bienfaits, \n\r Qu\'un amour saint et véritable \n\r Nous unisse en toi pour jamais. \n\r Qu\'un amour saint et véritable \n\r Nous unisse en toi pour jamais.', 3, 359),
(1512, 'Strophe', 1, 'Béni soit le lien \n\r Qui nous unit en Christ, \n\r Le saint amour, l\'amour divin \n\r Que verse en nous l\'Esprit!', 1, 360),
(1513, 'Strophe', 2, 'Au ciel, vers notre Dieu, \n\r Avec joie et ferveur, \n\r S\'élèvent nos chants et nos vœux, \n\r Parfum doux au Seigneur.', 2, 360),
(1514, 'Strophe', 3, 'Nous mettons en commun \n\r Nos fardeaux, nos labeurs; \n\r En Jésus nous ne sommes qu\'un \n\r Dans la joie et les pleurs!', 3, 360),
(1515, 'Strophe', 4, 'Si nous devons bientôt \n\r Quitter ces lieux bénis, \n\r Nous nous retrouverons là-haut, \n\r Pour toujours réunis.', 4, 360),
(1516, 'Strophe', 1, 'Ensemble, nous pouvons chanter, \n\r Ensemble, nous pouvons prier, \n\r Élever nos voix et adorer celui qui nous a réunis. \n\r Ensemble, nous pouvons donner, \n\r Ensemble, nous pouvons porter \n\r Nos fardeaux, nos joies, et proclamer \n\r L\'amour que Dieu met dans nos vies…', 1, 361),
(1517, 'Strophe', 2, '…Tous ensemble, tous ensemble, \n\r Nous pouvons montrer son amour au monde entier. \n\r Ensemble, nous pouvons chanter, \n\r Ensemble, nous pouvons prier, \n\r Élever nos voix et adorer celui qui nous a réunis.', 2, 361),
(1518, 'Strophe', 1, 'Nous sommes un dans un lien d\'amour, \n\r Nous sommes un dans un lien d\'amour. \n\r Notre esprit est uni avec l\'Esprit de Dieu, \n\r Nous sommes un dans un lien d\'amour.', 1, 362),
(1519, 'Strophe', 2, 'Maintenant, chantons tous ensemble; \n\r Et que son amour se répande. \n\r Prenons-nous par la main et le monde verra: \n\r Nous sommes un dans un lien d\'amour.', 2, 362),
(1520, 'Strophe', 1, 'Nous t\'adorons, toi que notre cœur aime, \n\r Ô Rabboni! \n\r Nous avons soif de ta sainteté même, \n\r Maître béni!', 1, 363),
(1521, 'Refrain', 1, 'Verse en nos cœurs, \n\r Ô Christ, ta charité, \n\r Et mets sur nous ta sublime beauté.', 2, 363),
(1522, 'Strophe', 2, 'Parfait modèle, incomparable Maître, \n\r Bénis-nous tous. \n\r Comme tu fus, nous voulons toujours être, \n\r Humbles et doux.', 3, 363),
(1523, 'Refrain', 1, 'Verse en nos cœurs, \n\r Ô Christ, ta charité, \n\r Et mets sur nous ta sublime beauté.', 4, 363),
(1524, 'Strophe', 3, 'Oh! donne-nous ton amour inlassable \n\r Pour les perdus, \n\r Cet amour vrai qui cherche le coupable \n\r Loin de Jésus.', 5, 363),
(1525, 'Refrain', 1, 'Verse en nos cœurs, \n\r Ô Christ, ta charité, \n\r Et mets sur nous ta sublime beauté.', 6, 363),
(1526, 'Strophe', 4, 'Qu\'en nous voyant, oublieux de nous-mêmes, \n\r Tous pour chacun, \n\r Le monde ému dise: «Oh! combien ils s\'aiment! \n\r Ils sont tous un!»', 7, 363),
(1527, 'Refrain', 1, 'Verse en nos cœurs, \n\r Ô Christ, ta charité, \n\r Et mets sur nous ta sublime beauté.', 8, 363),
(1528, 'Strophe', 5, 'Ouvre le ciel, réponds à la prière \n\r Que nous t\'offrons. \n\r Imprime enfin ton divin caractère \n\r Sur tous nos fronts.', 9, 363),
(1529, 'Refrain', 1, 'Rien ne surpasse, \n\r Ô Christ, ta charité, \n\r Rien ici-bas n\'égale ta beauté.', 10, 363),
(1530, 'Strophe', 1, 'Ô Jésus tu nous appelles \n\r À former un même corps; \n\r Unis-nous, Seigneur fidèle, \n\r À ta vie et à ta mort. \n\r Détruis ce qui nous divise, \n\r Mets en nous ta vérité, \n\r Et qu\'à jamais ton Église \n\r Demeure dans l\'unité.', 1, 364),
(1531, 'Strophe', 2, 'Ô toi qui scellas toi-même \n\r Notre unité par ton sang, \n\r Apprends-nous comment on aime \n\r D\'un amour toujours vivant. \n\r Et le monde pourra croire, \n\r Pour sa joie et son salut, \n\r Que son espoir et sa gloire \n\r Sont en toi, Seigneur Jésus.', 2, 364),
(1532, 'Strophe', 1, 'Qu\'elle est pure et belle, \n\r Au matin des ans, \n\r L\'amitié fidèle, \n\r La fleur du printemps! \n\r Ô sainte alliance \n\r Où l\'on a pour loi \n\r La même espérance \n\r Et la même foi!', 1, 365),
(1533, 'Strophe', 2, 'Joyeuse auréole, \n\r Aux jours de bonheur, \n\r L\'amitié console, \n\r Quand vient la douleur, \n\r À l\'heure où tout semble \n\r Défaillir en nous, \n\r Qu\'il fait bon ensemble, \n\r Ployer les genoux!', 2, 365),
(1534, 'Strophe', 3, 'Celui qui s\'avance, \n\r Tout seul ici-bas \n\r Reste sans défense, \n\r Tombe à chaque pas. \n\r Mais l\'orage même \n\r Rend les vrais amis \n\r Ô grâce suprême! \n\r Plus forts, plus unis.', 3, 365),
(1535, 'Strophe', 4, 'Au cours de la vie, \n\r Amis, gardons-la, \n\r L\'amitié bénie \n\r Qui nous gardera. \n\r Amitié céleste, \n\r Éclose au printemps, \n\r Que ta fleur nous reste \n\r Jusqu\'au soir des ans!', 4, 365),
(1536, 'Strophe', 1, 'Seigneur Jésus, ta grâce est pour nous précieuse, \n\r Qui remplit ici-bas le cœur des rachetés. \n\r Rassemblée en ce lieu ton Église est heureuse. \n\r C\'est toi qui nous unis dans la fraternité.', 1, 366),
(1537, 'Strophe', 2, 'Qu\'il est doux, en suivant le sentier de la vie, \n\r Loin des vains bruits du monde et de ses faux attraits, \n\r De trouver des amis dont l\'âme est enrichie \n\r De ces biens éternels qui seuls donnent la paix!', 2, 366),
(1538, 'Strophe', 3, 'Resserre nos liens, ô toi qui nous rassemble; \n\r Garde-nous de tes dons le trésor précieux. \n\r Ici-bas, ô Jésus, nous les goûtons ensemble, \n\r Pour les connaître un jour, plus parfaits dans les cieux.', 3, 366),
(1539, 'Refrain', 1, 'Seigneur, rassemble-nous \n\r Dans la paix de ton amour!', 1, 367),
(1540, 'Strophe', 1, 'Nos fautes nous séparent, \n\r Ta grâce nous unit: \n\r La joie de ta victoire \n\r Éclaire notre nuit.', 2, 367),
(1541, 'Refrain', 1, 'Seigneur, rassemble-nous \n\r Dans la paix de ton amour!', 3, 367),
(1542, 'Strophe', 2, 'Tu es notre espérance \n\r Parmi nos divisions; \n\r Plus haut que nos offenses \n\r S\'élève ton pardon.', 4, 367),
(1543, 'Refrain', 1, 'Seigneur, rassemble-nous \n\r Dans la paix de ton amour!', 5, 367),
(1544, 'Strophe', 3, 'Seigneur, vois la misère \n\r Des peuples affamés; \n\r Partage sur la terre \n\r Le pain de l\'unité!', 6, 367),
(1545, 'Refrain', 1, 'Seigneur, rassemble-nous \n\r Dans la paix de ton amour!', 7, 367),
(1546, 'Strophe', 4, 'Heureux le cœur des pauvres \n\r Qui cherchent l\'unité! \n\r Heureux dans ton royaume \n\r Les frères retrouvés!', 8, 367),
(1547, 'Refrain', 1, 'Seigneur, rassemble-nous \n\r Dans la paix de ton amour!', 9, 367),
(1548, 'Strophe', 5, 'Fais croître en notre attente \n\r L\'amour de ta maison! \n\r L\'Esprit dans le silence \n\r Fait notre communion.', 10, 367),
(1549, 'Refrain', 1, 'Seigneur, rassemble-nous \n\r Dans la paix de ton amour!', 11, 367),
(1550, 'Strophe', 6, 'Ta croix est la lumière \n\r Qui nous a rassemblés: \n\r Ô joie de notre terre, \n\r Tu nous a rachetés.', 12, 367),
(1551, 'Refrain', 1, 'Seigneur, rassemble-nous \n\r Dans la paix de ton amour!', 13, 367),
(1552, 'Strophe', 7, 'La mort est engloutie, \n\r Nous sommes délivrés; \n\r Qu\'éclate en nous la vie, \n\r Seigneur ressuscité!', 14, 367),
(1553, 'Refrain', 1, 'Seigneur, rassemble-nous \n\r Dans la paix de ton amour!', 15, 367),
(1554, 'Strophe', 1, 'Tous unis dans l\'Esprit, tous unis en Jésus, \n\r Tous unis dans l\'Esprit, tous unis en Jésus, \n\r Nous prions que bientôt ce qui divise ne soit plus.', 1, 368),
(1555, 'Refrain', 1, 'Et le monde saura que nous sommes chrétiens \n\r Par l\'amour dont nos actes sont empreints.', 2, 368),
(1556, 'Strophe', 2, 'Nous marchons côte à côte et la main dans la main, \n\r Nous marchons côte à côte et la main dans la main, \n\r À la table du roi nous partageons le même pain.', 3, 368),
(1557, 'Refrain', 1, 'Et le monde saura que nous sommes chrétiens \n\r Par l\'amour dont nos actes sont empreints.', 4, 368);
INSERT INTO `strophe` (`idStrophe`, `type`, `identifiant`, `texte`, `position`, `idChant`) VALUES
(1558, 'Strophe', 3, 'D\'un seul cœur nous voulons travailler pour Jésus, \n\r D\'un seul cœur nous voulons travailler pour Jésus, \n\r Proclamer à tout homme qu\'il nous offre le salut.', 5, 368),
(1559, 'Refrain', 1, 'Et le monde saura que nous sommes chrétiens \n\r Par l\'amour dont nos actes sont empreints.', 6, 368),
(1560, 'Strophe', 4, 'Gloire au Dieu créateur de la terre et des cieux, \n\r Gloire au Fils éternel, rédempteur glorieux, \n\r Gloire, gloire à l\'Esprit qui verse en nous l\'amour de Dieu.', 7, 368),
(1561, 'Refrain', 1, 'Et le monde saura que nous sommes chrétiens \n\r Par l\'amour dont nos actes sont empreints.', 8, 368),
(1562, 'Strophe', 1, 'Père, unis-nous tous, \n\r Père, unis-nous tous, \n\r Que le monde croie à ton amour; \n\r Père, unis-nous tous!', 1, 369),
(1563, 'Refrain', 1, 'Un jour dans tes parvis \n\r Vaut mieux que mille ailleurs; \n\r Seigneur, tu nous unis, \n\r Nous sommes frères et sœurs.', 1, 370),
(1564, 'Strophe', 1, 'Rassemblés en ce lieu, \n\r Nous louons notre Père. \n\r Voici l\'Esprit de Dieu, \n\r Nous sommes dans la lumière.', 2, 370),
(1565, 'Refrain', 1, 'Un jour dans tes parvis \n\r Vaut mieux que mille ailleurs; \n\r Seigneur, tu nous unis, \n\r Nous sommes frères et sœurs.', 3, 370),
(1566, 'Strophe', 2, 'Jésus est parmi nous: \n\r L\'un prie, l\'autre pleure. \n\r Son regard est si doux, \n\r Sa joie remplit nos cœurs.', 4, 370),
(1567, 'Refrain', 1, 'Un jour dans tes parvis \n\r Vaut mieux que mille ailleurs; \n\r Seigneur, tu nous unis, \n\r Nous sommes frères et sœurs.', 5, 370),
(1568, 'Strophe', 3, 'Bientôt nous partirons, \n\r Nous serons réunis. \n\r Sans fin, nous le verrons, \n\r Nous louerons Jésus-Christ.', 6, 370),
(1569, 'Refrain', 1, 'Un jour dans tes parvis \n\r Vaut mieux que mille ailleurs; \n\r Seigneur, tu nous unis, \n\r Nous sommes frères et sœurs.', 7, 370),
(1570, 'Strophe', 1, 'Ah! qu\'il est doux pour des frères de demeurer ensemble, \n\r Dans l\'unité, la prière, par l\'Esprit qui rassemble. \n\r Ah! qu\'il est doux de demeurer ensemble, \n\r Unis pour prier par l\'Esprit qui rassemble.', 1, 371),
(1571, 'Strophe', 1, 'On est bien à la maison, \n\r Quand règne l\'amour. \n\r La joie est dans chaque son, \n\r Quand règne l\'amour. \n\r C\'est la paix, la bonne humeur, \n\r Le véritable bonheur, \n\r Le temps passe avec douceur, \n\r Quand règne l\'amour.', 1, 372),
(1572, 'Refrain', 1, 'Au foyer, chaque jour, \n\r Le temps passe avec douceur, quand règne l\'amour.', 2, 372),
(1573, 'Strophe', 2, 'Vers Dieu s\'élève un doux chant, \n\r Quand règne l\'amour. \n\r Point de mot rude et méchant, \n\r Quand règne l\'amour. \n\r Partout sur notre chemin \n\r Fleurissent rose et jasmin: \n\r La terre est un beau jardin, \n\r Quand règne l\'amour.', 3, 372),
(1574, 'Refrain', 1, 'Au foyer, chaque jour, \n\r Le temps passe avec douceur, quand règne l\'amour.', 4, 372),
(1575, 'Strophe', 3, 'Le ciel sourit, bienveillant, \n\r Quand règne l\'amour. \n\r Chacun se montre accueillant, \n\r Quand règne l\'amour. \n\r Plus gaiement chante l\'oiseau, \n\r Et murmure le ruisseau, \n\r L\'azur du ciel est plus beau, \n\r Quand règne l\'amour.', 5, 372),
(1576, 'Refrain', 1, 'Au foyer, chaque jour, \n\r Le temps passe avec douceur, quand règne l\'amour.', 6, 372),
(1577, 'Strophe', 4, 'Jésus, tout entier prends-moi, \n\r Pour que chaque jour \n\r Notre foyer soit par toi \n\r Gardé dans l\'amour. \n\r Tu me mettras à l\'abri \n\r De tout mal, de tout souci. \n\r Par toi je serai béni, \n\r Jésus, roi d\'amour.', 7, 372),
(1578, 'Refrain', 1, 'Au foyer, chaque jour, \n\r Le temps passe avec douceur, quand règne l\'amour.', 8, 372),
(1579, 'Strophe', 1, 'Nous bâtirons notre maison, Seigneur, \n\r Sur le rocher de ta parole. \n\r Nous bâtirons notre maison, \n\r Nous appuyant sur ton amour.', 1, 373),
(1580, 'Strophe', 2, 'Nous fermerons notre maison, Seigneur, \n\r Aux vents de haine et de violence. \n\r Nous fermerons notre maison: \n\r N\'y entrera que ton amour.', 2, 373),
(1581, 'Strophe', 3, 'Nous ouvrirons notre maison, Seigneur, \n\r À ceux qui souffrent l\'injustice. \n\r Nous ouvrirons notre maison \n\r Pour qu\'y rayonne ton amour.', 3, 373),
(1582, 'Strophe', 4, 'Nous fleurirons notre maison, Seigneur, \n\r Pour que l\'enfant y soit en fête. \n\r Nous fleurirons notre maison, \n\r Donnant la vie et ton amour.', 4, 373),
(1583, 'Strophe', 5, 'Nous garderons notre maison, Seigneur, \n\r Dans la grisaille et dans l\'orage. \n\r Nous garderons notre maison, \n\r Vainqueurs du mal par ton amour.', 5, 373),
(1584, 'Refrain', 1, 'Moi et ma maison, nous servirons l\'Éternel. \n\r Moi et ma maison, nous servirons le Seigneur.', 1, 374),
(1585, 'Strophe', 1, 'Qu\'il est doux de célébrer ton nom, réunis en famille. \n\r Qu\'il est doux de célébrer ton nom, réunis en famille.', 2, 374),
(1586, 'Refrain', 1, 'Moi et ma maison, nous servirons l\'Éternel. \n\r Moi et ma maison, nous servirons le Seigneur.', 3, 374),
(1587, 'Strophe', 2, 'Qu\'il est bon de goûter à ta paix, unis dans ton amour. \n\r Qu\'il est bon de goûter à ta paix, unis dans ton amour.', 4, 374),
(1588, 'Refrain', 1, 'Moi et ma maison, nous servirons l\'Éternel. \n\r Moi et ma maison, nous servirons le Seigneur.', 5, 374),
(1589, 'Strophe', 3, 'Qu\'il est beau de prier en famille, dans ta communion. \n\r Qu\'il est beau de prier en famille, dans ta communion.', 6, 374),
(1590, 'Refrain', 1, 'Moi et ma maison, nous servirons l\'Éternel. \n\r Moi et ma maison, nous servirons le Seigneur.', 7, 374),
(1591, 'Strophe', 1, 'Dès que l\'aube dépose \n\r Ses perles sur les fleurs, \n\r Dès que s\'ouvre la rose \n\r Aux brillantes couleurs, \n\r Dès que l\'ombre s\'efface \n\r Devant le jour qui luit, \n\r À l\'œuvre, le temps passe! \n\r À l\'œuvre avant la nuit!', 1, 375),
(1592, 'Strophe', 2, 'Quand le soleil inonde \n\r Et remplit le ciel bleu, \n\r Illuminant le monde \n\r De ses rayons de feu, \n\r À l\'œuvre sans relâche, \n\r À l\'œuvre, le jour fuit! \n\r Si pénible est la tâche, \n\r Bientôt viendra la nuit!', 2, 375),
(1593, 'Strophe', 3, 'À cette heure indécise, \n\r Où le jour disparaît, \n\r Où murmure la brise \n\r À travers la forêt, \n\r Quand le couchant se dore \n\r Et que s\'éteint le bruit, \n\r Frères, à l\'œuvre encore, \n\r Voici, voici la nuit!', 3, 375),
(1594, 'Refrain', 1, 'Allez-vous-en sur les places et sur les parvis. \n\r Allez-vous-en sur les places \n\r Y chercher mes amis, \n\r Tous mes enfants de lumière \n\r Qui vivent dans la nuit, \n\r Tous les enfants de mon Père, séparés de lui! \n\r Allez-vous-en sur les places \n\r Et soyez mes témoins chaque jour!', 1, 376),
(1595, 'Strophe', 1, 'En quittant cette terre, \n\r Je vous ai laissé \n\r Un message de lumière: \n\r Qu\'en avez-vous donc fait? \n\r Quand je vois aujourd\'hui mes enfants révoltés, \n\r Aigris et douloureux d\'avoir pleuré…', 2, 376),
(1596, 'Refrain', 1, 'Allez-vous-en sur les places et sur les parvis. \n\r Allez-vous-en sur les places \n\r Y chercher mes amis, \n\r Tous mes enfants de lumière \n\r Qui vivent dans la nuit, \n\r Tous les enfants de mon Père, séparés de lui! \n\r Allez-vous-en sur les places \n\r Et soyez mes témoins chaque jour!', 3, 376),
(1597, 'Strophe', 2, 'En quittant cette terre, \n\r Je vous ai donné \n\r La justice de mon Père: \n\r L\'avez-vous partagée? \n\r Quand je vois aujourd\'hui \n\r Mes enfants qui ont peur, \n\r Sans amour et sans foi et sans honneur…', 4, 376),
(1598, 'Refrain', 1, 'Allez-vous-en sur les places et sur les parvis. \n\r Allez-vous-en sur les places \n\r Y chercher mes amis, \n\r Tous mes enfants de lumière \n\r Qui vivent dans la nuit, \n\r Tous les enfants de mon Père, séparés de lui! \n\r Allez-vous-en sur les places \n\r Et soyez mes témoins chaque jour!', 5, 376),
(1599, 'Strophe', 3, 'En quittant cette terre, \n\r Je vous avais dit: \n\r Aimez-vous en sœurs et frères! \n\r M\'avez-vous obéi? \n\r Quand je vois aujourd\'hui \n\r Mes enfants torturés, \n\r Sans amis, sans espoir, abandonnés…', 6, 376),
(1600, 'Refrain', 1, 'Allez-vous-en sur les places et sur les parvis. \n\r Allez-vous-en sur les places \n\r Y chercher mes amis, \n\r Tous mes enfants de lumière \n\r Qui vivent dans la nuit, \n\r Tous les enfants de mon Père, séparés de lui! \n\r Allez-vous-en sur les places \n\r Et soyez mes témoins chaque jour!', 7, 376),
(1601, 'Strophe', 1, 'Annonçons partout le dernier message \n\r Du Seigneur Jésus revenant des cieux; \n\r Dans le monde entier, rendons témoignage \n\r Au roi saint et glorieux.', 1, 377),
(1602, 'Refrain', 1, 'Annonçons partout la bonne nouvelle: \n\r Jésus réunit tous ses rachetés; \n\r Il vient leur donner la vie éternelle. \n\r Oh! publions ses bontés.', 2, 377),
(1603, 'Strophe', 2, 'Annonçons partout le dernier message: \n\r Le Seigneur Jésus vient sécher nos pleurs, \n\r Dénouer les liens de notre esclavage, \n\r Mettre un terme à nos douleurs.', 3, 377),
(1604, 'Refrain', 1, 'Annonçons partout la bonne nouvelle: \n\r Jésus réunit tous ses rachetés; \n\r Il vient leur donner la vie éternelle. \n\r Oh! publions ses bontés.', 4, 377),
(1605, 'Strophe', 3, 'Annonçons partout le dernier message: \n\r Bientôt nous verrons la sainte cité. \n\r Gloire à notre Dieu! Chrétiens, bon courage! \n\r Car voici l\'éternité.', 5, 377),
(1606, 'Refrain', 1, 'Annonçons partout la bonne nouvelle: \n\r Jésus réunit tous ses rachetés; \n\r Il vient leur donner la vie éternelle. \n\r Oh! publions ses bontés.', 6, 377),
(1607, 'Strophe', 1, 'Grâce à la lumière \n\r De ta vérité, \n\r Nous marchons, ô Père \n\r En toute clarté.', 1, 378),
(1608, 'Strophe', 2, 'Actif en service, \n\r Notre cœur entier \n\r Marche avec délice \n\r Sur ton droit sentier.', 2, 378),
(1609, 'Strophe', 3, 'Change nos pensées \n\r Et libère-nous \n\r Des fausses idées \n\r Pour porter ton joug.', 3, 378),
(1610, 'Strophe', 4, 'Que l\'amour inspire \n\r Toutes nos actions, \n\r Et que ton sourire \n\r Éclaire nos fronts.', 4, 378),
(1611, 'Strophe', 5, 'Au long de l\'histoire, \n\r Tu nous as conduits, \n\r Mets-nous en mémoire \n\r Que tu nous instruits.', 5, 378),
(1612, 'Strophe', 6, 'D\'abord à l\'écoute \n\r De ta volonté, \n\r Nous prenons la route \n\r De l\'éternité.', 6, 378),
(1613, 'Strophe', 1, 'Laisserons-nous à notre table \n\r Un peu d\'espace à l\'étranger? \n\r Trouvera-t-il, quand il viendra, \n\r Un peu de pain et d\'amitié?', 1, 379),
(1614, 'Refrain', 1, 'Ne laissons pas mourir la terre, \n\r Ne laissons pas mourir le feu, \n\r Tendons nos mains vers la lumière, \n\r Pour accueillir le don de Dieu. \n\r Pour accueillir le don de Dieu.', 2, 379),
(1615, 'Strophe', 2, 'Laisserons-nous à nos paroles \n\r Un peu de temps à l\'étranger? \n\r Trouvera-t-il, quand il viendra, \n\r Un cœur ouvert pour l\'écouter?', 3, 379),
(1616, 'Refrain', 1, 'Ne laissons pas mourir la terre, \n\r Ne laissons pas mourir le feu, \n\r Tendons nos mains vers la lumière, \n\r Pour accueillir le don de Dieu. \n\r Pour accueillir le don de Dieu.', 4, 379),
(1617, 'Strophe', 3, 'Laisserons-nous à nos fontaines \n\r Un peu d\'eau vive à l\'étranger? \n\r Trouvera-t-il, quand il viendra, \n\r Des hommes libres et assoifés?', 5, 379),
(1618, 'Refrain', 1, 'Ne laissons pas mourir la terre, \n\r Ne laissons pas mourir le feu, \n\r Tendons nos mains vers la lumière, \n\r Pour accueillir le don de Dieu. \n\r Pour accueillir le don de Dieu.', 6, 379),
(1619, 'Strophe', 4, 'Laisserons-nous à nos églises \n\r Un peu d\'espace à l\'étranger? \n\r Trouvera-t-il, quand il viendra, \n\r Des cœurs de pauvres affamés?', 7, 379),
(1620, 'Refrain', 1, 'Ne laissons pas mourir la terre, \n\r Ne laissons pas mourir le feu, \n\r Tendons nos mains vers la lumière, \n\r Pour accueillir le don de Dieu. \n\r Pour accueillir le don de Dieu.', 8, 379),
(1621, 'Strophe', 1, 'La voix du Christ nous appelle; \n\r Il est temps de s\'éveiller: \n\r «La moisson est vaste et belle! \n\r Qui veut pour moi travailler?» \n\r C\'est ton Sauveur, ô mon frère, \n\r Dont l\'appel s\'adresse à toi. \n\r Réponds-lui d\'un cœur sincère: \n\r «Me voici, Maître, prends-moi.»', 1, 380),
(1622, 'Strophe', 2, 'Sans franchir les mers bruyantes, \n\r Tu peux annoncer Jésus. \n\r Que d\'âmes insouciantes, \n\r De cœurs souffrants et perdus! \n\r Autour de nous l\'œuvre est grande, \n\r Mais petite est notre foi. \n\r À Jésus qui nous commande, \n\r Répondons: «Maître, aide-moi!»', 2, 380),
(1623, 'Strophe', 3, 'Si d\'un sublime langage \n\r Tu n\'as pas reçu le don, \n\r Tu peux rendre témoignage \n\r Qu\'en Jésus est le pardon. \n\r À ton frère tu peux dire \n\r Ce que Christ a fait pour toi. \n\r Pour que lui-même t\'inspire, \n\r Dis-lui: «Maître, enseigne-moi!»', 3, 380),
(1624, 'Strophe', 4, 'Mais que nul ne nous entende \n\r Dire encor\': «Je ne puis rien!» \n\r Lorsque Jésus nous commande \n\r De faire et d\'aimer le bien. \n\r Poursuivons l\'œuvre bénie \n\r Avec zèle, amour et foi. \n\r Puis, notre tâche finie, \n\r Nous dirons: «Maître, prends-moi.»', 4, 380),
(1625, 'Strophe', 1, 'Lève-toi, vaillante armée, \n\r Pour les combats du Seigneur; \n\r C\'est ton Dieu qui t\'a formée, \n\r C\'est lui seul qui rend vainqueur. \n\r Vous, soldats, prenez l\'armure, \n\r Préparez-vous en ce jour. \n\r Dieu lui-même vous assure \n\r La victoire de l\'amour.', 1, 381),
(1626, 'Strophe', 2, 'Allez révéler au monde \n\r L\'amour de Dieu tout-puissant; \n\r Dans l\'obscurité profonde \n\r Annoncez le jour naissant. \n\r Aux peuples sans espérance \n\r Dites: «Jésus est venu, \n\r Apportant la délivrance \n\r Au cœur coupable et perdu!»', 2, 381),
(1627, 'Strophe', 3, 'Sur le plus lointain rivage, \n\r Portez l\'éternelle croix; \n\r Avec un nouveau courage, \n\r Élevez partout la voix. \n\r Consolez dans leur misère \n\r Ceux qui souffrent en tous lieux; \n\r En Dieu montrez-leur un Père, \n\r Vers lui dirigez leurs yeux.', 3, 381),
(1628, 'Strophe', 4, 'Heureux ceux qui, pour leur Maître, \n\r Auront voulu tout souffrir! \n\r Le grand jour fera connaître \n\r L\'honneur qui les doit couvrir. \n\r Dans l\'immortelle victoire \n\r De Jésus, le Roi des rois, \n\r Ils auront part à sa gloire, \n\r Ayant eu part à sa croix.', 4, 381),
(1629, 'Strophe', 1, 'Nous servirons l\'Éternel, \n\r Le Dieu qui créa le ciel, \n\r Nous chanterons de tout cœur sa grandeur \n\r Car il est notre Seigneur. \n\r Nous servirons l\'Éternel, \n\r Nous voulons toujours lui être fidèles, \n\r Nous servirons l\'Éternel.', 1, 382),
(1630, 'Strophe', 2, 'Nous marcherons par la foi \n\r Avec confiance et joie, \n\r Les yeux fixés sur notre Rédempteur \n\r Nous serons plus que vainqueurs. \n\r Nous marcherons par la foi, \n\r Suivant les pas de Jésus, Roi des rois, \n\r Nous marcherons par la foi.', 2, 382),
(1631, 'Strophe', 3, 'Nous chanterons tes bontés, \n\r Ô Dieu de l\'éternité, \n\r À tout humain nous dirons tes merveilles: \n\r Tes œuvres sont sans pareilles. \n\r Nous chanterons tes bontés, \n\r La vie et tout ce que tu as donné, \n\r Nous chanterons tes bontés.', 3, 382),
(1632, 'Strophe', 4, 'Nous hâterons ton retour, \n\r Jésus, notre Dieu d\'amour, \n\r Par ton Esprit tu conduis tes enfants \n\r Pour te servir constamment. \n\r Nous hâterons ton retour, \n\r Nous voulons vivre avec toi pour toujours, \n\r Nous hâterons ton retour.', 4, 382),
(1633, 'Refrain', 1, 'Seigneur, fais de nous des ouvriers de paix. \n\r Seigneur, fais de nous des bâtisseurs d\'amour.', 1, 383),
(1634, 'Strophe', 1, 'Là où demeure la haine, que nous apportions l\'amour. \n\r Là où se trouve l\'offense, que nous mettions le pardon.', 2, 383),
(1635, 'Refrain', 1, 'Seigneur, fais de nous des ouvriers de paix. \n\r Seigneur, fais de nous des bâtisseurs d\'amour.', 3, 383),
(1636, 'Strophe', 2, 'Là où grandit la discorde, que nous fassions l\'unité, \n\r Là où séjourne l\'erreur, que nous mettions la vérité.', 4, 383),
(1637, 'Refrain', 1, 'Seigneur, fais de nous des ouvriers de paix. \n\r Seigneur, fais de nous des bâtisseurs d\'amour.', 5, 383),
(1638, 'Strophe', 3, 'Là où persistent les ténèbres, que nous mettions la lumière, \n\r Là où règne la tristesse, que nous fassions chanter la joie.', 6, 383),
(1639, 'Refrain', 1, 'Seigneur, fais de nous des ouvriers de paix. \n\r Seigneur, fais de nous des bâtisseurs d\'amour.', 7, 383),
(1640, 'Strophe', 4, 'Là où s\'attarde le doute, que nous apportions la foi. \n\r Sur les chemins du désespoir, que nous portions l\'espérance.', 8, 383),
(1641, 'Refrain', 1, 'Seigneur, fais de nous des ouvriers de paix. \n\r Seigneur, fais de nous des bâtisseurs d\'amour.', 9, 383),
(1642, 'Strophe', 5, 'Donne-nous de consoler, plutôt que d\'être consolés, \n\r Donne-nous de comprendre, plus souvent que d\'être compris.', 10, 383),
(1643, 'Refrain', 1, 'Seigneur, fais de nous des ouvriers de paix. \n\r Seigneur, fais de nous des bâtisseurs d\'amour.', 11, 383),
(1644, 'Strophe', 6, 'Car il faut savoir donner, pour pouvoir être comblés, \n\r Car il faut s\'oublier, pour pouvoir se retrouver.', 12, 383),
(1645, 'Refrain', 1, 'Seigneur, fais de nous des ouvriers de paix. \n\r Seigneur, fais de nous des bâtisseurs d\'amour.', 13, 383),
(1646, 'Strophe', 1, 'Si tu dénoues les liens de servitude, \n\r Si tu libères ton frère enchaîné, \n\r La nuit de ton chemin sera lumière de midi. \n\r La nuit de ton chemin sera lumière de midi. \n\r Alors, de tes mains pourra naître une source, \n\r La source qui fait vivre la terre de demain, \n\r La source qui fait vivre la terre de Dieu.', 1, 384),
(1647, 'Strophe', 2, 'Si tu partages le pain que Dieu te donne \n\r Avec celui qui est ta propre chair, \n\r La nuit de ton amour sera lumière de midi. \n\r La nuit de ton amour sera lumière de midi. \n\r Alors, de ton cœur pourra sourdre une eau vive, \n\r L\'eau vive qui abreuve la terre de demain, \n\r L\'eau vive qui abreuve la terre de Dieu.', 2, 384),
(1648, 'Strophe', 3, 'Si tu détruis ce qui opprime l\'autre, \n\r Si tu relèves ton frère humilié, \n\r La nuit de ton combat sera lumière de midi. \n\r La nuit de ton combat sera lumière de midi. \n\r Alors, de ton pas pourra naître une danse, \n\r La danse qui invente la terre de demain, \n\r La danse qui invente la terre de Dieu.', 3, 384),
(1649, 'Strophe', 4, 'Si tu dénonces le mal qui brise l\'homme, \n\r Si tu soutiens ton frère abandonné, \n\r La nuit de ton appel sera lumière de midi. \n\r La nuit de ton appel sera lumière de midi. \n\r Alors, de tes yeux pourra luire une étoile, \n\r L\'étoile qui annonce la terre de demain, \n\r L\'étoile qui annonce la terre de Dieu.', 4, 384),
(1650, 'Strophe', 5, 'Si tu abats les murs entre les hommes, \n\r Si tu pardonnes à ton frère ennemi, \n\r La nuit de ta passion sera lumière de midi. \n\r La nuit de ta passion sera lumière de midi. \n\r Alors, de ton pain pourra vivre une Église, \n\r L\'Église qui rassemble la terre de demain, \n\r L\'Église qui rassemble la terre de Dieu.', 5, 384),
(1651, 'Refrain', 1, 'Toi, lève-toi et va au nom du Christ \n\r Porter l\'amour de Dieu au monde! \n\r Toi, lève-toi et va au nom du Christ \n\r Porter l\'amour de Dieu au monde!', 1, 385),
(1652, 'Strophe', 1, 'Comment les autres croiront-ils ce qu\'ils n\'ont pas connu? \n\r Comment les peuples de la terre serviront-ils Jésus?', 2, 385),
(1653, 'Refrain', 1, 'Toi, lève-toi et va au nom du Christ \n\r Porter l\'amour de Dieu au monde! \n\r Toi, lève-toi et va au nom du Christ \n\r Porter l\'amour de Dieu au monde!', 3, 385),
(1654, 'Strophe', 2, 'Comment les pauvres, les mendiants seront-ils rassasiés? \n\r Les prisonniers, les réfugiés seront-ils consolés?', 4, 385),
(1655, 'Refrain', 1, 'Toi, lève-toi et va au nom du Christ \n\r Porter l\'amour de Dieu au monde! \n\r Toi, lève-toi et va au nom du Christ \n\r Porter l\'amour de Dieu au monde!', 5, 385),
(1656, 'Strophe', 1, 'Pour inventer la liberté, \n\r Brisons nos murs de solitude. \n\r Pour accueillir la vérité, \n\r Brisons nos vieilles habitudes.', 1, 386),
(1657, 'Refrain', 1, 'Dieu nous appelle aujourd\'hui \n\r À nous lever de nos tombeaux, \n\r Dieu nous réveille aujourd\'hui \n\r Pour nous donner un cœur nouveau.', 2, 386),
(1658, 'Strophe', 2, 'Pour que renaisse notre espoir, \n\r Brisons les murs de l\'impossible. \n\r Pour que fleurisse l\'amitié, \n\r Tissons les liens de l\'invisible.', 3, 386),
(1659, 'Refrain', 1, 'Dieu nous appelle aujourd\'hui \n\r À nous lever de nos tombeaux, \n\r Dieu nous réveille aujourd\'hui \n\r Pour nous donner un cœur nouveau.', 4, 386),
(1660, 'Strophe', 3, 'Pour partager sur nos chemins \n\r Nos cris de joie, nos chants de peine, \n\r Pour découvrir chaque matin \n\r L\'Esprit qui fait germer la graine.', 5, 386),
(1661, 'Refrain', 1, 'Dieu nous appelle aujourd\'hui \n\r À nous lever de nos tombeaux, \n\r Dieu nous réveille aujourd\'hui \n\r Pour nous donner un cœur nouveau.', 6, 386),
(1662, 'Strophe', 1, 'Nous contemplons le saint mystère: \n\r Le Fils de Dieu fut baptisé. \n\r Le ciel s\'ouvrit, la voix du Père \n\r Lui dit: Mon Fils, mon bien-aimé, \n\r Sur toi repose pour toujours \n\r Le bon plaisir de mon amour.', 1, 387),
(1663, 'Strophe', 2, 'Nous contemplons le saint mystère \n\r De la colombe de l\'Esprit \n\r Qui vint du Père des lumières \n\r Pour se poser sur Jésus-Christ: \n\r Voici qu\'au monde est révélé \n\r Ton saint mystère, ô Trinité!', 2, 387),
(1664, 'Strophe', 3, 'Nous contemplons le saint mystère: \n\r C\'est notre histoire à nous aussi! \n\r Filles et fils du même Père, \n\r Depuis toujours en Christ choisis, \n\r Dans la tendresse de l\'Esprit, \n\r Dieu trois fois saint, ton peuple vit.', 3, 387),
(1665, 'Strophe', 4, 'Nous contemplons le saint mystère: \n\r Par le baptême en Christ plongés \n\r Dans ce grand fleuve trinitaire, \n\r Porteur de vie et de bonté, \n\r En nous l\'amour s\'est incarné \n\r Pour se donner au monde entier.', 4, 387),
(1666, 'Strophe', 1, 'Bienvenue dans la famille, \n\r Quelle joie de te voir là! \n\r Là, vivant parmi nous, grandissant dans son amour. \n\r Nous voulons être pour toi ce que Dieu attend de nous: \n\r Une famille toujours là, qui se tient à tes côtés…', 1, 388),
(1667, 'Strophe', 1, '…Apprenons à nous aimer toujours plus chaque jour. \n\r Que toutes nos paroles soient remplies de son amour. \n\r Que l\'Esprit unisse nos cœurs, nous enseigne à prier. \n\r Que nous formions une vraie famille.', 2, 388),
(1668, 'Strophe', 1, 'Seigneur, dirige et sanctifie \n\r Toute la vie de tes enfants. \n\r Que ta lumière, \n\r Sur leur carrière, \n\r Brille en tout temps! \n\r Que, sous ta garde et sous tes ailes, \n\r Ils soient fidèles, forts et constants!', 1, 389),
(1669, 'Strophe', 2, 'Soumets leur âme à l\'Évangile, \n\r Au joug facile, plein de douceur; \n\r Fais-leur entendre \n\r L\'appel si tendre \n\r De leur Sauveur; \n\r Que, pour répondre à sa promesse, \n\r Ils aient sans cesse, le même cœur!', 2, 389),
(1670, 'Strophe', 1, 'Seigneur Jésus, je veux répondre par le baptême à ton appel. \n\r (Refrain) \n\r Je veux te suivre, près de toi vivre, \n\r T\'être fidèle, marcher sans peur. \n\r Je veux te suivre, près de toi vivre, \n\r Avec ta force, mon doux Sauveur.', 1, 390),
(1671, 'Strophe', 2, 'Mort avec toi, je ressuscite pour proclamer ton grand amour. \n\r (Refrain) \n\r Je veux te suivre, près de toi vivre, \n\r T\'être fidèle, marcher sans peur. \n\r Je veux te suivre, près de toi vivre, \n\r Avec ta force, mon doux Sauveur.', 2, 390),
(1672, 'Strophe', 3, 'Que mon cœur soit plein de ta joie, pour la répandre autour de moi. \n\r (Refrain) \n\r Je veux te suivre, près de toi vivre, \n\r T\'être fidèle, marcher sans peur. \n\r Je veux te suivre, près de toi vivre, \n\r Avec ta force, mon doux Sauveur.', 3, 390),
(1673, 'Strophe', 4, 'Lorsque le doute remplit mon âme, remplis-moi de ta vérité. \n\r (Refrain) \n\r Je veux te suivre, près de toi vivre, \n\r T\'être fidèle, marcher sans peur. \n\r Je veux te suivre, près de toi vivre, \n\r Avec ta force, mon doux Sauveur.', 4, 390),
(1674, 'Strophe', 5, 'Que je sois l\'un de tes disciples, les yeux fixés sur mon Seigneur. \n\r (Refrain) \n\r Je veux te suivre, près de toi vivre, \n\r T\'être fidèle, marcher sans peur. \n\r Je veux te suivre, près de toi vivre, \n\r Avec ta force, mon doux Sauveur.', 5, 390),
(1675, 'Strophe', 1, 'Tu peux naître de nouveau, \n\r Tu peux tout recommencer, \n\r Balayer ta vie passée, \n\r Et repartir à zéro, (2×) \n\r Avec Jésus pour berger. \n\r Et repartir à zéro, (2×) \n\r Avec Jésus pour berger.', 1, 391),
(1676, 'Strophe', 2, 'Tu peux boire de cette eau, \n\r Source de la pureté, \n\r Source de la vérité. \n\r Tu peux boire de cette eau, (2×) \n\r Source de l\'éternité. \n\r Tu peux boire de cette eau, (2×) \n\r Source de l\'éternité.', 2, 391),
(1677, 'Strophe', 3, 'Tu peux être pardonné \n\r Pour tous tes péchés passés \n\r Car Jésus a tout payé. \n\r Tu peux recevoir la paix, (2×) \n\r Source de la liberté, \n\r Et repartir à zéro, (2×) \n\r Avec Jésus pour berger.', 3, 391),
(1678, 'Strophe', 1, 'Tel que je suis, sans rien à moi, \n\r Sinon ton sang versé pour moi \n\r Et ta voix qui m\'appelle à toi, \n\r Agneau de Dieu, je viens, je viens!', 1, 392),
(1679, 'Strophe', 2, 'Tel que je suis, bien vacillant, \n\r En proie au doute à chaque instant, \n\r Lutte au dehors, crainte au dedans, \n\r Agneau de Dieu, je viens, je viens!', 2, 392),
(1680, 'Strophe', 3, 'Tel que je suis, ton cœur est prêt, \n\r À prendre le mien, tel qu\'il est \n\r Pour tout changer, Sauveur parfait! \n\r Agneau de Dieu, je viens, je viens!', 3, 392),
(1681, 'Strophe', 4, 'Tel que je suis, ton grand amour \n\r A tout pardonné sans retour \n\r Je veux être à toi dès ce jour; \n\r Agneau de Dieu, je viens, je viens!', 4, 392),
(1682, 'Strophe', 1, 'Tel que je suis, sans rien à moi, \n\r Sinon ton sang versé pour moi \n\r Et ta voix qui m\'appelle à toi, \n\r Agneau de Dieu, je viens, je viens!', 1, 393),
(1683, 'Strophe', 2, 'Tel que je suis, bien vacillant, \n\r En proie au doute à chaque instant, \n\r Lutte au dehors, crainte au dedans, \n\r Agneau de Dieu, je viens, je viens!', 2, 393),
(1684, 'Strophe', 3, 'Tel que je suis, ton cœur est prêt, \n\r À prendre le mien, tel qu\'il est \n\r Pour tout changer, Sauveur parfait! \n\r Agneau de Dieu, je viens, je viens!', 3, 393),
(1685, 'Strophe', 4, 'Tel que je suis, ton grand amour \n\r A tout pardonné sans retour \n\r Je veux être à toi dès ce jour; \n\r Agneau de Dieu, je viens, je viens!', 4, 393),
(1686, 'Strophe', 1, 'Et comme les épis jadis épars dans les campagnes, \n\r Et comme les grappes autrefois dispersées sur les collines \n\r Sont maintenant réunis sur cette table,…', 1, 394),
(1687, 'Strophe', 2, '…Dans ce pain et dans ce vin, \n\r Qu\'ainsi, Seigneur, toute ton Église \n\r Bientôt soit rassemblée des extrémités de la terre \n\r Dans ton royaume!', 2, 394),
(1688, 'Strophe', 1, 'Pour que nos cœurs deviennent de chair, \n\r Tu as rompu le pain, \n\r Comme un fruit de justice, \n\r Comme un signe d\'amour.', 1, 395),
(1689, 'Strophe', 2, 'Pour que nos cœurs deviennent de sang, \n\r Tu as versé le vin, \n\r Comme un puits de tendresse, \n\r Comme un signe de paix.', 2, 395),
(1690, 'Strophe', 3, 'Pour que nos cœurs respirent ta vie, \n\r Tu as donné ta mort \n\r Comme un jour qui se lève, \n\r Comme un cri d\'avenir.', 3, 395),
(1691, 'Strophe', 4, 'Pour que nos cœurs éclatent de vie, \n\r Nous fêtons ta mémoire, \n\r Tu libères ton peuple \n\r Et tu es son chemin.', 4, 395),
(1692, 'Strophe', 1, 'Le Seigneur nous a aimés comme on n\'a jamais aimé. \n\r Il nous guide chaque jour comme une étoile dans la nuit. \n\r Quand nous partageons le pain, il nous donne son amour…', 1, 396),
(1693, 'Strophe', 2, '…C\'est le pain de l\'amitié, le pain de Dieu \n\r C\'est mon corps, prenez et mangez. \n\r C\'est mon sang, prenez et buvez, \n\r Car je suis la vie et je suis l\'amour. \n\r Ô, Seigneur emporte nous dans ton amour!', 2, 396),
(1694, 'Strophe', 1, 'Seigneur Jésus, par ton Esprit, \n\r Viens nous apprendre à partager! \n\r Tu es le pain, tu es la vie, \n\r De toi j\'ai faim, par toi je prie.', 1, 397),
(1695, 'Strophe', 2, 'Seigneur Jésus, par ton Esprit, \n\r Viens nous apprendre à t\'adorer! \n\r Tu es le pain, tu es la vie, \n\r De toi j\'ai faim, par toi je prie.', 2, 397),
(1696, 'Strophe', 1, 'Seigneur, tu es notre joie, notre vie, notre espérance, \n\r Seigneur, tu es notre joie, nous ne sommes rien sans toi. \n\r Quand parfois dans notre vie, tout est froid et sans lumière, \n\r C\'est vers toi que nous conduit l\'espoir d\'être ton ami.', 1, 398),
(1697, 'Strophe', 2, 'Seigneur, tu es notre joie, notre vie, notre espérance, \n\r Seigneur, tu es notre joie, nous ne sommes rien sans toi. \n\r Dans ce monde qui a faim, tu nous invites à la table: \n\r Dans un merveilleux festin, ton corps devient notre pain.', 2, 398),
(1698, 'Strophe', 1, 'Quand vint le jour d\'étendre les bras, \n\r Et de lier la mort sur la croix, \n\r Le Fils de l\'homme au cours d\'un repas, \n\r Livra son corps aux mains des pécheurs. \n\r Livra son corps aux mains des pécheurs.', 1, 399),
(1699, 'Strophe', 2, 'Voici mon corps, prenez et mangez; \n\r Voici mon sang, prenez et buvez. \n\r Pour que ma mort vous soit rappelée, \n\r Faites ainsi jusqu\'à mon retour. \n\r Faites ainsi jusqu\'à mon retour.', 2, 399),
(1700, 'Strophe', 3, 'Ne craignons plus la soif ni la faim, \n\r Le corps du Christ est notre festin, \n\r Quand nous prenons sa coupe en nos mains, \n\r Elle a le goût du monde nouveau. \n\r Elle a le goût du monde nouveau.', 3, 399),
(1701, 'Strophe', 4, 'Banquet pascal où Dieu s\'est donné, \n\r Signe d\'amour, ferment d\'unité, \n\r Où tous les hommes renouvelés, \n\r Trouvent les biens du règne à venir. \n\r Trouvent les biens du règne à venir.', 4, 399),
(1702, 'Strophe', 1, 'Rompons ensemble le pain à genoux, \n\r Rompons ensemble le pain à genoux. \n\r Quand je tombe à genoux, \n\r Le regard tourné vers le ciel, \n\r Ô Dieu, prends pitié de moi.', 1, 400),
(1703, 'Strophe', 2, 'Buvons ensemble le vin à genoux; \n\r Buvons ensemble le vin à genoux. \n\r Quand je tombe à genoux, \n\r Le regard tourné vers le ciel, \n\r Ô Dieu, prends pitié de moi.', 2, 400),
(1704, 'Strophe', 3, 'Louons ensemble le Père à genoux, \n\r Louons ensemble le Père à genoux, \n\r Quand je tombe à genoux, \n\r Le regard tourné vers le ciel, \n\r Ô Dieu, prends pitié de moi.', 3, 400),
(1705, 'Strophe', 1, 'Je suis le pain de vie. (2×) \n\r Ceux qui viennent à moi n\'auront jamais faim. \n\r Je suis le pain de vie. \n\r Je suis la source d\'eau, \n\r Je suis la source d\'eau, \n\r De l\'eau vive qui jaillit du rocher, \n\r Je suis la source d\'eau.', 1, 401),
(1706, 'Strophe', 1, 'Seigneur, tu nous partages ton corps et ton sang, \n\r Seigneur, tu nous partages ton corps et ton sang, \n\r Et nous allons, tout joyeux, vers toi, en chantant:', 1, 402),
(1707, 'Refrain', 1, 'Tu es le Dieu fidèle, éternellement, \n\r Tu es le Dieu fidèle, éternellement!', 2, 402),
(1708, 'Strophe', 2, 'L\'amour que tu nous donnes nous a libérés, \n\r L\'amour que tu nous donnes nous a libérés, \n\r Et nous marchons vers la sainteté de ton nom.', 3, 402),
(1709, 'Refrain', 1, 'Tu es le Dieu fidèle, éternellement, \n\r Tu es le Dieu fidèle, éternellement!', 4, 402),
(1710, 'Strophe', 3, 'Tu as tracé la route qui mène à toi, \n\r Tu as tracé la route qui mène à toi, \n\r Et nous allons, invitant le monde à ta joie:', 5, 402),
(1711, 'Refrain', 1, 'Tu es le Dieu fidèle, éternellement, \n\r Tu es le Dieu fidèle, éternellement!', 6, 402),
(1712, 'Strophe', 1, 'Tu m\'appelles, Seigneur, à venir à ta table \n\r Où tu veux me nourrir du céleste aliment; \n\r Ta mort, divin Jésus, ta mort pour moi, coupable, \n\r Donne à mon cœur chargé le vrai soulagement.', 1, 403),
(1713, 'Strophe', 2, 'Je ne t\'apporte rien que ma grande misère, \n\r Mon orgueil, mon néant, mes péchés odieux; \n\r Mais tu mourus pour moi sur la croix du Calvaire, \n\r Et tu m\'as racheté par ton sang précieux.', 2, 403),
(1714, 'Strophe', 3, 'En ton sang qui coula pour la rançon du monde, \n\r Je mets tout mon espoir de salut, de bonheur; \n\r Qui lave de mon cœur la tache si profonde, \n\r Qui le rende aussi pur que le tien, ô Sauveur!', 3, 403),
(1715, 'Strophe', 4, 'À toi donc, pour toujours, sera toute ma vie, \n\r À toi mon corps, mon cœur, mon esprit racheté. \n\r Viens, Esprit du Très-Haut, dans mon âme ravie; \n\r Revêts-la pour jamais d\'amour, de sainteté', 4, 403),
(1716, 'Strophe', 1, 'Tu nous invites à la fête, Jésus-Christ ressuscité! \n\r Tu nous invites à la fête, et nous venons te chanter. \n\r Il y a des fleurs et la table est prête, \n\r Nous venons te rencontrer,…', 1, 404),
(1717, 'Strophe', 1, '…Le pain de la vie, le vin de la fête, \n\r Nous venons les partager. \n\r Tu nous invites à la fête, Jésus-Christ ressuscité! \n\r Tu nous invites à la fête, et nous venons te chanter.', 2, 404),
(1718, 'Strophe', 2, 'Tu nous invites à la fête, Jésus-Christ ressuscité! \n\r Tu nous invites à la fête, et nous venons te chanter. \n\r Nous venons vers toi, avec nos misères, \n\r Et la nuit de nos péchés…', 3, 404),
(1719, 'Strophe', 2, '…Écoute nos voix, entends nos prières, \n\r Viens, Seigneur, nous pardonner! \n\r Tu nous invites à la fête, Jésus-Christ ressuscité! \n\r Tu nous invites à la fête, et nous venons te chanter.', 4, 404),
(1720, 'Strophe', 1, 'Dieu, ton amour est source intarissable \n\r De vrai bonheur pour qui s\'attache à toi; \n\r Fais à chacun ce don impérissable, \n\r Qu\'ils soient unis d\'un même élan de foi.', 1, 405),
(1721, 'Strophe', 2, 'Ô Dieu vivant, conduis en assurance \n\r Ces cœurs fidèles, tiens-les dans ta main; \n\r L\'âme confiante, riche d\'espérance, \n\r Qu\'ils te soumettent chaque lendemain.', 2, 405),
(1722, 'Strophe', 3, 'D\'un cœur joyeux, qu\'ils suivent ta lumière, \n\r Doux et sereins, qu\'ils vivent dans ta paix; \n\r Dès maintenant, et pour la vie entière, \n\r Scelle leurs vœux dans un amour parfait.', 3, 405),
(1723, 'Refrain', 1, 'Toi qui aimes ceux qui s\'aiment, \n\r Car tu es l\'amour, \n\r Dans nos vies, comme un poème, \n\r Fais chanter l\'amour.', 1, 406),
(1724, 'Strophe', 1, 'Porter à deux bonheur et misère, \n\r Tournés vers le même horizon. \n\r Les yeux éclairés d\'une même lumière, \n\r Chanter une même chanson.', 2, 406),
(1725, 'Refrain', 1, 'Toi qui aimes ceux qui s\'aiment, \n\r Car tu es l\'amour, \n\r Dans nos vies, comme un poème, \n\r Fais chanter l\'amour.', 3, 406),
(1726, 'Strophe', 2, 'Fais-nous briser barrières et murailles, \n\r Avec la violence d\'aimer; \n\r Chasser la haine, sans armes, ni batailles, \n\r Armés seulement d\'amitié.', 4, 406),
(1727, 'Refrain', 1, 'Toi qui aimes ceux qui s\'aiment, \n\r Car tu es l\'amour, \n\r Dans nos vies, comme un poème, \n\r Fais chanter l\'amour.', 5, 406),
(1728, 'Strophe', 3, 'Seigneur, bénis ceux qui devant leurs frères, \n\r Se donnent l\'un à l\'autre leur foi, \n\r Pour que leur amour, tout comme une prière, \n\r Toujours les rapproche de toi.', 6, 406),
(1729, 'Refrain', 1, 'Toi qui aimes ceux qui s\'aiment, \n\r Car tu es l\'amour, \n\r Dans nos vies, comme un poème, \n\r Fais chanter l\'amour.', 7, 406),
(1730, 'Strophe', 4, 'Esprit d\'amour qui, réchauffe le monde, \n\r Fais-nous vivre de charité, \n\r Et pour que nos vies soient belles et fécondes, \n\r Enseigne-nous à tout donner.', 8, 406),
(1731, 'Refrain', 1, 'Toi qui aimes ceux qui s\'aiment, \n\r Car tu es l\'amour, \n\r Dans nos vies, comme un poème, \n\r Fais chanter l\'amour.', 9, 406),
(1732, 'Strophe', 1, 'Heureux dès maintenant \n\r Ceux qui sont morts dans le Seigneur. \n\r Oui, dit le Saint-Esprit, \n\r Ils sont dans la paix, dans la main du Père. \n\r En Dieu ils ont trouvé le repos,…', 1, 407),
(1733, 'Strophe', 2, '…Leurs peines, leur labeur les ont suivis. \n\r Heureux dès maintenant \n\r Ceux qui sont morts dans le Seigneur. \n\r Oui, dit le Saint-Esprit, \n\r Ils sont dans la paix, dans la main du Père.', 2, 407),
(1734, 'Strophe', 1, 'Jésus soit avec vous à jamais, \n\r Vous guidant avec sagesse, \n\r Vous entourant de tendresse, \n\r Vous remplissant toujours de sa paix!', 1, 408),
(1735, 'Refrain', 1, 'Avec vous toujours, avec vous, \n\r Oui, qu\'il soit tous les jours avec vous! \n\r Avec vous toujours, avec vous \n\r Jusqu\'au jour du dernier rendez-vous!', 2, 408),
(1736, 'Strophe', 2, 'Avec vous au milieu du danger, \n\r À l\'heure de la détresse, \n\r Qu\'il soit votre forteresse, \n\r Qu\'il soit toujours votre bon Berger!', 3, 408),
(1737, 'Refrain', 1, 'Avec vous toujours, avec vous, \n\r Oui, qu\'il soit tous les jours avec vous! \n\r Avec vous toujours, avec vous \n\r Jusqu\'au jour du dernier rendez-vous!', 4, 408),
(1738, 'Strophe', 3, 'Avec vous quand votre ciel est bleu, \n\r Comme au jour de la tempête, \n\r Que son cœur soit la retraite \n\r Où vous retrouvez la paix de Dieu!', 5, 408),
(1739, 'Refrain', 1, 'Avec vous toujours, avec vous, \n\r Oui, qu\'il soit tous les jours avec vous! \n\r Avec vous toujours, avec vous \n\r Jusqu\'au jour du dernier rendez-vous!', 6, 408),
(1740, 'Strophe', 4, 'Enfin, dans le dernier des combats, \n\r Pour échanger cette terre \n\r Contre la maison du Père, \n\r Que Jésus vous porte dans ses bras!', 7, 408),
(1741, 'Refrain', 1, 'Avec vous toujours, avec vous, \n\r Oui, qu\'il soit tous les jours avec vous! \n\r Avec vous toujours, avec vous \n\r Jusqu\'au jour du dernier rendez-vous!', 8, 408),
(1742, 'Strophe', 1, 'Merci, Seigneur, pour ta lumière, \n\r Merci pour tous les clairs matins; \n\r Merci d\'entendre ma prière, \n\r D\'être mon soutien.', 1, 409),
(1743, 'Strophe', 2, 'Merci, Seigneur, pour tous mes frères, \n\r Merci de les avoir donnés. \n\r Merci, car si l\'amour s\'altère, \n\r Tu veux pardonner.', 2, 409),
(1744, 'Strophe', 3, 'Merci, Seigneur, pour toute joie, \n\r Merci pour mes divers travaux, \n\r Merci car, quand soudain je ploie, \n\r Tu prends mes fardeaux.', 3, 409),
(1745, 'Strophe', 4, 'Merci, Seigneur, pour la vieillesse, \n\r Merci pour les petits enfants, \n\r Merci pour toutes tes promesses, \n\r Pour tes soins constants.', 4, 409),
(1746, 'Strophe', 5, 'Merci, Seigneur pour ta parole, \n\r Merci par elle tu m\'instruis, \n\r Merci car elle est ma boussole, \n\r Elle me conduit.', 5, 409),
(1747, 'Strophe', 6, 'Merci, Seigneur, pour ta présence, \n\r Merci pour ton parfait salut. \n\r Merci des dons que tu dispenses \n\r À tous tes élus.', 6, 409),
(1748, 'Strophe', 7, 'Merci Seigneur, pour ta puissance, \n\r Merci de me donner l\'Esprit. \n\r Merci pour ton amour immense \n\r Qui partout nous suit.', 7, 409),
(1749, 'Strophe', 1, 'Merci pour ce repas, alléluia. \n\r Merci pour ce repas, alléluia. \n\r Merci pour ce repas, alléluia.', 1, 410),
(1750, 'Strophe', 1, 'Pour ce repas, \n\r Pour toutes joies, \n\r Nous te louons, Seigneur.', 1, 411),
(1751, 'Strophe', 1, 'Seigneur, toi qui donnes pâture \n\r Aux tout petits oiseaux \n\r Oh! bénis notre nourriture \n\r Et purifie notre eau.', 1, 412),
(1752, 'Strophe', 1, 'Pour le pain qui nourrit, \n\r Pour le pain d\'aujourd\'hui, \n\r Ô Dieu de Jésus-Christ, \n\r Nous te disons merci.', 1, 413),
(1753, 'Strophe', 2, 'Rendons grâce au Seigneur, \n\r Il est charitable, \n\r Sa bonté, sa vérité, \n\r Durent pour l\'éternité.', 2, 413),
(1754, 'Refrain', 1, 'Qu\'il est formidable d\'aimer, qu\'il est formidable! \n\r Qu\'il est formidable d\'aimer, qu\'il est formidable \n\r De tout donner pour aimer!', 1, 414),
(1755, 'Strophe', 1, 'Quand on n\'a que ses mains à tendre ou à donner, \n\r Quand on n\'a que ses yeux pour rire ou pour pleurer, \n\r Quand on n\'a que sa voix pour crier, chanter, \n\r Quand on n\'a que sa vie et qu\'on veut la donner:', 2, 414),
(1756, 'Refrain', 1, 'Qu\'il est formidable d\'aimer, qu\'il est formidable! \n\r Qu\'il est formidable d\'aimer, qu\'il est formidable \n\r De tout donner pour aimer!', 3, 414),
(1757, 'Strophe', 2, 'Quand on n\'a que sa peine à dire ou à cacher, \n\r Quand on n\'a que ses joies à taire ou à partager, \n\r Quand on n\'a que ses rêves à faire voyager, \n\r Quand on n\'a que sa vie et qu\'on veut la donner:', 4, 414),
(1758, 'Refrain', 1, 'Qu\'il est formidable d\'aimer, qu\'il est formidable! \n\r Qu\'il est formidable d\'aimer, qu\'il est formidable \n\r De tout donner pour aimer!', 5, 414),
(1759, 'Strophe', 1, 'Merci, Seigneur, merci, Seigneur, \n\r Merci, Seigneur, merci, Seigneur, \n\r Amen, amen.', 1, 415),
(1760, 'Strophe', 1, 'Viens, âme qui pleures, \n\r Viens à ton Sauveur; \n\r Dans tes tristes heures, \n\r Dis-lui ta douleur; \n\r Dis tout bas ta plainte \n\r Au Seigneur Jésus, \n\r Parle-lui sans crainte, \n\r Et ne pleure plus.', 1, 416),
(1761, 'Strophe', 2, 'Dis tout à ce frère, \n\r À ce tendre ami, \n\r Ton épreuve amère, \n\r Ton deuil, ton souci. \n\r Il aime, il console \n\r Les cœurs abattus; \n\r Crois à sa parole, \n\r Et ne pleure plus.', 2, 416),
(1762, 'Strophe', 3, 'Aux cœurs en détresse, \n\r Ployant sous le faix, \n\r Dis que Dieu les presse \n\r De chercher sa paix. \n\r Calme leurs alarmes, \n\r Dis-leur que Jésus \n\r A séché nos larmes… \n\r Va, ne pleure plus.', 3, 416),
(1763, 'Strophe', 1, 'Viens, Seigneur, dans ta tendresse, \n\r Prendre soin de cet enfant. \n\r Qu\'il découvre la richesse \n\r D\'un amour si fort, si grand. \n\r De bonheur ses yeux pétillent, \n\r Son sourire est lumineux, \n\r Il reflète ainsi l\'image \n\r De Jésus, le Fils de Dieu.', 1, 417),
(1764, 'Strophe', 2, 'Montre-lui déjà la route, \n\r Qu\'il apprenne à bien marcher. \n\r Que jamais il ne redoute \n\r De te suivre et de t\'aimer. \n\r Mais du mal, Seigneur, protège \n\r Cet enfant si merveilleux, \n\r Qu\'il grandisse dans ta grâce, \n\r Et qu\'il soit vraiment heureux.', 2, 417),
(1765, 'Strophe', 1, 'Voici, je vous dis un mystère: \n\r Nous ne mourrons pas tous, \n\r Mais tous nous serons transformés, \n\r Changés en un instant. \n\r À la dernière trompette, \n\r La vie aura triomphé, \n\r Les morts auront ouvert les yeux \n\r Et nous, nous serons changés.', 1, 418),
(1766, 'Strophe', 2, 'La grâce soit rendue à Dieu \n\r Qui nous fait triompher \n\r Par notre Seigneur Jésus-Christ \n\r Qui a vaincu la mort. \n\r Ainsi, mes bien-aimés frères, \n\r Soyez fermes, inébranlables, \n\r Progressez dans l\'œuvre de Dieu \n\r Sachant que rien n\'est vain.', 2, 418),
(1767, 'Strophe', 1, 'Plus haut que l\'azur des cieux, \n\r Espoir, espoir si doux! \n\r Est un pays merveilleux, \n\r Espoir, espoir si doux! \n\r Là, Jésus réunira \n\r Ceux que la mort sépara! \n\r Le soleil de Dieu luira. \n\r Espoir, espoir si doux!', 1, 419),
(1768, 'Strophe', 2, 'Là-haut cessent les douleurs, \n\r Espoir, espoir si doux! \n\r Dieu séchera tous les pleurs. \n\r Espoir, espoir si doux! \n\r Qu\'ils sont beaux, les saints palais, \n\r Asile éternel de paix \n\r Où la mort n\'entre jamais! \n\r Espoir, espoir si doux!', 2, 419),
(1769, 'Strophe', 3, 'Là-haut, radieux espoir! \n\r Espoir, espoir si doux! \n\r Ce sera le grand revoir. \n\r Espoir, espoir si doux! \n\r Rassemblés près du Seigneur, \n\r Nous jouirons d\'un bonheur \n\r Éternel en sa splendeur. \n\r Espoir, espoir si doux!', 3, 419),
(1770, 'Strophe', 4, 'Là-haut, sur la harpe d\'or, \n\r Espoir, espoir si doux! \n\r Unis dans un saint transport, \n\r Espoir, espoir si doux! \n\r Les élus n\'ont qu\'un seul vœu, \n\r Exalter l\'Amour de Dieu, \n\r L\'Amour, l\'amour de Dieu.', 4, 419),
(1771, 'Strophe', 1, 'Quand j\'étais seul, rejeté, qu\'as-tu fait, qu\'as-tu fait? \n\r Quand j\'étais seul, rejeté, qu\'as-tu fait? \n\r (Refrain) \n\r Peu m\'importe ton nom, ta religion, ta couleur: je suis là.', 1, 420),
(1772, 'Strophe', 2, 'J\'avais soif et j\'avais faim, qu\'as-tu fait, qu\'as-tu fait? \n\r J\'avais soif et j\'avais faim, qu\'as-tu fait? \n\r (Refrain) \n\r Peu m\'importe ton nom, ta religion, ta couleur: je suis là.', 2, 420),
(1773, 'Strophe', 3, 'J\'avais froid sans un foyer, qu\'as-tu fait, qu\'as-tu fait? \n\r J\'avais froid sans un foyer, qu\'as-tu fait? \n\r (Refrain) \n\r Peu m\'importe ton nom, ta religion, ta couleur: je suis là.', 3, 420),
(1774, 'Strophe', 4, 'Quand j\'étais si malade, qu\'as-tu fait, qu\'as-tu fait? \n\r Quand j\'étais si malade, qu\'as-tu fait? \n\r (Refrain) \n\r Peu m\'importe ton nom, ta religion, ta couleur: je suis là.', 4, 420),
(1775, 'Strophe', 5, 'Tout au long de ton chemin, je suis là, je suis là. \n\r Tout au long de ton chemin, je suis là. \n\r (Refrain) \n\r Peu m\'importe ton nom, ta religion, ta couleur: je suis là.', 5, 420),
(1776, 'Strophe', 1, 'Mon Sauveur, je voudrais être \n\r Une fleur de tes parvis; \n\r Briller pour toi seul, ô Maître, \n\r Puisque par toi seul je vis! \n\r Dans le silence répandre \n\r Le parfum de ton amour, \n\r Dans la nuit obscure attendre \n\r L\'aurore de ton retour.', 1, 421),
(1777, 'Strophe', 2, 'Mon Sauveur, je voudrais être \n\r Sur le sentier ténébreux \n\r Un faible reflet, ô Maître, \n\r De ton éclat radieux! \n\r Luire sur la sombre route \n\r Du cœur perdu loin de toi; \n\r Dissiper la nuit du doute \n\r Par la clarté de la foi.', 2, 421),
(1778, 'Strophe', 3, 'Mon Sauveur, je voudrais être \n\r Comm\'un écho de ta voix \n\r Pour proclamer, ô doux Maître, \n\r Le mystère de ta croix! \n\r Pour parler de délivrance \n\r À ceux qu\'assaille la mort, \n\r Pour consoler la souffrance \n\r Et montrer à tous le port.', 3, 421),
(1779, 'Strophe', 1, 'Si je parlais le plus parfait langage, \n\r Si même un ange me prêtait le sien, \n\r Que me vaudrait ce futile avantage? \n\r Si je n\'ai pas l\'amour, je ne suis rien!…', 1, 422),
(1780, 'Strophe', 1, '…Je ne suis rien qu\'un airain qui résonne, \n\r Une cymbale au rythme répété, \n\r Tout manque encore aux grâces que Dieu donne, \n\r Si je n\'ai pas, au cœur, l\'amour, la Charité.', 2, 422),
(1781, 'Strophe', 2, 'Et quand j\'aurais le don de prophétie, \n\r Quand tout secret me serait révélé, \n\r Quand j\'atteindrais au savoir, au génie, \n\r Quand je devrais, martyr, être brûlé,…', 3, 422),
(1782, 'Strophe', 2, '…Ou si ma foi transportait la montagne, \n\r Et ma pitié distribuait mon bien, \n\r Quelque mérite en cela que je gagne \n\r Sans cet amour tout ne me servirait de rien.', 4, 422),
(1783, 'Strophe', 3, 'La charité se montre patiente, \n\r Et bienveillante à l\'égard du prochain, \n\r N\'est pas jalouse et non plus ne se vante, \n\r Ne s\'enfle pas d\'orgueil, est sans dédain…', 5, 422),
(1784, 'Strophe', 3, '…Pleine d\'égards, ne fait tort à personne, \n\r Tout égoïsme est chez elle écarté, \n\r Point ne s\'irrite et jamais ne soupçonne, \n\r Réprouve l\'injustice et veut la vérité.', 6, 422),
(1785, 'Strophe', 4, 'Que de douceur et de paix elle apporte, \n\r Car elle excuse tout, elle croit tout, \n\r Tout elle espère et tout elle supporte, \n\r Mettant son baume et son parfum partout…', 7, 422),
(1786, 'Strophe', 4, '…Les plus beaux dons disparaissent et meurent, \n\r Cette vertu vit en éternité, \n\r Foi, espérance et charité demeurent, \n\r Mais de ces trois la plus grande est la Charité.', 8, 422),
(1787, 'Strophe', 1, 'Ô toi, mon allégresse, \n\r Aimable et doux Sauveur, \n\r Dans les jours de tristesse, \n\r Tu consoles mon cœur.', 1, 423),
(1788, 'Refrain', 1, 'C\'est toi seul que je chante, \n\r Jésus, mon Rédempteur. \n\r Oh! que d\'amour je sente \n\r Pour toi vibrer mon cœur!', 2, 423),
(1789, 'Strophe', 2, 'Toi qui devins mon frère, \n\r Sublime Roi des rois, \n\r Quel autre sur la terre \n\r Est comparable à toi?', 3, 423),
(1790, 'Refrain', 1, 'C\'est toi seul que je chante, \n\r Jésus, mon Rédempteur. \n\r Oh! que d\'amour je sente \n\r Pour toi vibrer mon cœur!', 4, 423),
(1791, 'Strophe', 3, 'Dans les plus sombres heures, \n\r Quand mon ciel devient noir, \n\r Ô Jésus, tu demeures \n\r Pour moi le seul espoir.', 5, 423),
(1792, 'Refrain', 1, 'C\'est toi seul que je chante, \n\r Jésus, mon Rédempteur. \n\r Oh! que d\'amour je sente \n\r Pour toi vibrer mon cœur!', 6, 423),
(1793, 'Strophe', 1, 'Chrétien, réjouis-toi! \n\r Tressaille d\'allégresse, \n\r Car ton Sauveur est roi, \n\r Regarde à lui sans cesse.', 1, 424),
(1794, 'Refrain', 1, 'En son amour, chrétien, \n\r Réjouis-toi, réjouis-toi toujours!', 2, 424),
(1795, 'Strophe', 2, 'Exalte le Seigneur \n\r Il a sauvé ton âme! \n\r Dieu l\'a comblé d\'honneur \n\r Après la croix infâme.', 3, 424),
(1796, 'Refrain', 1, 'En son amour, chrétien, \n\r Réjouis-toi, réjouis-toi toujours!', 4, 424),
(1797, 'Strophe', 3, 'Pourquoi pleurer encor\'? \n\r Voici, ton Dieu confie \n\r Les portes de la mort \n\r Au prince de la vie.', 5, 424),
(1798, 'Refrain', 1, 'En son amour, chrétien, \n\r Réjouis-toi, réjouis-toi toujours!', 6, 424),
(1799, 'Strophe', 4, 'Espère, adore et crois! \n\r Jésus seul est le Maître; \n\r Au loin, déjà je vois \n\r Sa gloire enfin paraître.', 7, 424),
(1800, 'Refrain', 1, 'En son amour, chrétien, \n\r Réjouis-toi, réjouis-toi toujours!', 8, 424),
(1801, 'Strophe', 5, 'Christ règne dans le ciel \n\r Au sein de la lumière \n\r Et son règne éternel \n\r Bientôt viendra sur terre.', 9, 424),
(1802, 'Refrain', 1, 'En son amour, chrétien, \n\r Réjouis-toi, réjouis-toi toujours!', 10, 424),
(1803, 'Strophe', 1, 'Je l\'ai trouvé, je l\'ai trouvé, \n\r Le bonheur ineffable! \n\r Je suis sauvé, je suis sauvé, \n\r Ô joie inexprimable! \n\r Tous mes péchés sont effacés, \n\r Le sang de Christ me lave. \n\r Les jours des larmes sont passés: \n\r Je ne suis plus esclave!', 1, 425),
(1804, 'Strophe', 2, 'Oh! quel bonheur! oh! quel bonheur \n\r D\'avoir Jésus pour Maître! \n\r Ô mon Sauveur, mon seul Sauveur! \n\r À toi seul je veux être! \n\r Tu vins briser, puissant Vainqueur, \n\r Du mal la tyrannie, \n\r Affranchissant mon pauvre cœur \n\r Et me donnant la vie!', 2, 425),
(1805, 'Strophe', 3, 'Dans ton amour, tu m\'as cherché, \n\r Errant bien loin du Père! \n\r Tu m\'as sauvé de mon péché, \n\r Tu fis de moi ton frère: \n\r Et maintenant, et pour jamais, \n\r Sous ton joug je me plie. \n\r Je ne puis vivre désormais, \n\r Jésus, que de ta vie!', 3, 425),
(1806, 'Strophe', 4, 'Ah! laissez-moi chanter mon Roi, \n\r Oui, qu\'à genoux, je chante! \n\r Jésus n\'est-il pas tout pour moi? \n\r Gloire à sa croix sanglante! \n\r Sans se lasser, jour après jour, \n\r Il m\'aime, il m\'aime encore. \n\r Comment répondre à tant d\'amour? \n\r Je crois, j\'aime et j\'adore.', 4, 425);
INSERT INTO `strophe` (`idStrophe`, `type`, `identifiant`, `texte`, `position`, `idChant`) VALUES
(1807, 'Strophe', 1, 'Pour cet immense bonheur, alléluia, \n\r Que tu as mis dans mon cœur, alléluia, \n\r Je veux te chanter, Seigneur, alléluia, \n\r Oui, Jésus est mon Sauveur, alléluia!', 1, 426),
(1808, 'Strophe', 2, 'Je le redirai encore, alléluia, \n\r Que pour moi, Jésus est mort, alléluia, \n\r Voilà pourquoi je l\'adore, alléluia, \n\r Lui seul est tout mon trésor, alléluia!', 2, 426),
(1809, 'Strophe', 3, 'Que chaque jour, à chaque heure, alléluia, \n\r En moi tu aies ta demeure, alléluia, \n\r Que ma vie soit une fleur, alléluia, \n\r Un parfum pour toi, Seigneur, alléluia!', 3, 426),
(1810, 'Strophe', 1, 'Tout est fait pour la gloire de Dieu, \n\r Amen! Amen!', 1, 427),
(1811, 'Refrain', 1, 'Tout dépend de ce que tu en fais. \n\r Amen! Amen! Amen! Amen! Amen! \n\r Tout est fait pour la gloire de Dieu, \n\r Amen! Amen!', 2, 427),
(1812, 'Strophe', 2, 'La vie, c\'est pour la gloire de Dieu, \n\r Amen! Amen!', 3, 427),
(1813, 'Refrain', 1, 'Tout dépend de ce que tu en fais. \n\r Amen! Amen! Amen! Amen! Amen! \n\r Tout est fait pour la gloire de Dieu, \n\r Amen! Amen!', 4, 427),
(1814, 'Strophe', 3, 'Le culte est pour la gloire de Dieu, \n\r Amen! Amen!', 5, 427),
(1815, 'Refrain', 1, 'Tout dépend de ce que tu en fais. \n\r Amen! Amen! Amen! Amen! Amen! \n\r Tout est fait pour la gloire de Dieu, \n\r Amen! Amen!', 6, 427),
(1816, 'Strophe', 4, 'Le printemps pour la gloire de Dieu, \n\r Amen! Amen!', 7, 427),
(1817, 'Refrain', 1, 'Tout dépend de ce que tu en fais. \n\r Amen! Amen! Amen! Amen! Amen! \n\r Tout est fait pour la gloire de Dieu, \n\r Amen! Amen!', 8, 427),
(1818, 'Strophe', 5, 'L\'offrande est pour la gloire de Dieu, \n\r Amen! Amen!', 9, 427),
(1819, 'Refrain', 1, 'Tout dépend de ce que tu en fais. \n\r Amen! Amen! Amen! Amen! Amen! \n\r Tout est fait pour la gloire de Dieu, \n\r Amen! Amen!', 10, 427),
(1820, 'Strophe', 1, 'Dieu bienveillant qui m\'accorde sa grâce, \n\r Je te bénis, je t\'adore avec foi. \n\r Quand mes péchés m\'éloignent de ta face, \n\r Ton grand amour me ramène vers toi. \n\r Quand mes péchés m\'éloignent de ta face, \n\r Ton grand amour me ramène vers toi.', 1, 428),
(1821, 'Strophe', 2, 'Je ne crains point, j\'ai cette confiance \n\r Que mon salut, c\'est toi-même, ô mon Dieu! \n\r L\'hymne joyeux de ma reconnaissance \n\r S\'élève à toi jusque dans le saint lieu. \n\r L\'hymne joyeux de ma reconnaissance \n\r S\'élève à toi jusque dans le saint lieu.', 2, 428),
(1822, 'Strophe', 3, 'Venez puiser aux sources de sa grâce, \n\r Cœurs altérés de justice et de paix. \n\r Là, le péché disparaît et s\'efface: \n\r C\'est le pardon, c\'est la vie à jamais. \n\r Là, le péché disparaît et s\'efface: \n\r C\'est le pardon, c\'est la vie à jamais.', 3, 428),
(1823, 'Strophe', 4, 'Louez son nom, célébrez sa mémoire; \n\r Car il est grand, le Dieu libérateur. \n\r Dites partout sa clémence et sa gloire; \n\r Magnifiez Jésus notre Seigneur. \n\r Dites partout sa clémence et sa gloire; \n\r Magnifiez Jésus notre Seigneur.', 4, 428),
(1824, 'Strophe', 1, 'De toi vient la vie qui te glorifie, \n\r Oui, nous te louons, Créateur, Roi des cieux. \n\r Car tu nous façonnes, nous guides, \n\r Nous donnes la paix, l\'assurance pour vivre joyeux…', 1, 429),
(1825, 'Strophe', 1, '…Et sous ta bannière, voyant la lumière, \n\r Le soir nous suivons ta colonne de feu, \n\r Jusqu\'à ce que vienne la fin de nos peines, \n\r Que tu réalises nos rêves, nos vœux.', 2, 429),
(1826, 'Strophe', 2, 'Fidèle à sa place, tournant dans l\'espace, \n\r L\'étoile proclame ta loi, ton amour. \n\r Les monts et les plaines, les belles fontaines, \n\r La mer et l\'abîme t\'exaltent toujours…', 3, 429),
(1827, 'Strophe', 2, '…Nos voix font de même: ta grâce suprême, \n\r Tes dons, tes bienfaits nous chantons tour à tour. \n\r Au chœur des archanges, bientôt les louanges \n\r De tout l\'univers s\'uniront chaque jour.', 4, 429),
(1828, 'Strophe', 1, 'Louons, chantons en chœur le Créateur. \n\r Adorons le Seigneur, le bienfaiteur. \n\r Tous ensemble d\'un même cœur.', 1, 430),
(1829, 'Strophe', 1, 'Pour la terre et sa beauté, \n\r Pour la majesté des cieux, \n\r Pour l\'amour illimité \n\r Que tu répands en tous lieux,', 1, 431),
(1830, 'Refrain', 1, 'Père, nous t\'offrons ce chant \n\r D\'un accent reconnaissant.', 2, 431),
(1831, 'Strophe', 2, 'Pour l\'amour des bien-aimés, \n\r Les amis chers à nos cœurs, \n\r Le partage et la bonté, \n\r Avant-goût du vrai bonheur,', 3, 431),
(1832, 'Refrain', 1, 'Père, nous t\'offrons ce chant \n\r D\'un accent reconnaissant.', 4, 431),
(1833, 'Strophe', 3, 'Pour l\'espoir, en Jésus-Christ, \n\r D\'une éternité sans pleurs, \n\r Pour le fruit du Saint-Esprit \n\r Par qui nous serons vainqueurs.', 5, 431),
(1834, 'Refrain', 1, 'Père, nous t\'offrons ce chant \n\r D\'un accent reconnaissant.', 6, 431),
(1835, 'Strophe', 1, 'Pour tout l\'espoir du clair matin, \n\r Et le repos du soir; \n\r Pour tant d\'amis, de vrais liens, \n\r Et pour ton doux regard; \n\r Pour ta lumière dans la nuit, \n\r La paix, la guérison; \n\r Pour l\'œuvre à faire et ton appui, \n\r Seigneur, nous te louons.', 1, 432),
(1836, 'Strophe', 2, 'Oh! viens, Seigneur, oui, hâte-toi, \n\r Tes dons sont merveilleux! \n\r Mets dans nos cœurs l\'amour, la joie, \n\r Et comble tous nos vœux. \n\r Étends sur nous, du haut des cieux, \n\r Ta grâce, ton pouvoir. \n\r Quel jour de gloire quand nos yeux \n\r Pourront, enfin, te voir!', 2, 432),
(1837, 'Strophe', 1, 'Rendons gloire au Père tout-puissant, \n\r À son Fils Jésus-Christ, le Seigneur, \n\r À l\'Esprit qui habite en nos cœurs, \n\r Au Dieu qui est, qui était et qui vient.', 1, 433),
(1838, 'Strophe', 1, 'Sans fin, Seigneur, Dieu, notre Père, sans fin, Seigneur, nous te louons. \n\r La terre exulte d\'allégresse, béni sois-tu, Dieu des vivants.', 1, 434),
(1839, 'Strophe', 2, 'L\'oiseau reçoit sa nourriture, la fleur se pare de beauté. \n\r Tu aimes toute créature, tu sais le prix de nos années.', 2, 434),
(1840, 'Strophe', 3, 'Tu es, Seigneur, notre lumière, toi seul nous sauves de la mort. \n\r Ton fils offert à tous les peuples est pour chacun le pain vivant.', 3, 434),
(1841, 'Strophe', 4, 'Heureux les hommes qui t\'adorent, le monde ouvert à ton amour! \n\r L\'Esprit déjà te nomme Père; un jour, Seigneur, nous te verrons.', 4, 434),
(1842, 'Strophe', 1, 'Toi qui disposes \n\r De toutes choses \n\r Et nous les donnes chaque jour \n\r Reçois ô Père! \n\r Notre prière \n\r De reconnaissance et d\'amour.', 1, 435),
(1843, 'Strophe', 2, 'Le don suprême \n\r Que ta main sème \n\r C\'est notre pardon, c\'est ta paix \n\r Et ta clémence, \n\r Trésor immense \n\r Est le plus grand de tes bienfaits.', 2, 435),
(1844, 'Strophe', 3, 'Que par ta grâce, \n\r L\'instant qui passe \n\r Serve à nous rapprocher de toi! \n\r Et qu\'à chaque heure, \n\r Vers ta demeure, \n\r Nos cœurs s\'élèvent par la foi!', 3, 435),
(1845, 'Strophe', 1, 'Abandonne ta vie \n\r Tes craintes et tes vœux, \n\r À la grâce infinie \n\r Du souverain des cieux. \n\r Lui qui trace la route \n\r Aux mondes comme aux vents, \n\r Guidera sans nul doute \n\r Les pas de ses enfants.', 1, 436),
(1846, 'Strophe', 2, 'Il voit dans sa tendresse, \n\r Ainsi qu\'un Père aimant, \n\r Ce qui guérit ou blesse \n\r Le cœur de son enfant; \n\r Et sa toute-puissance \n\r Transforme en ta faveur \n\r Le deuil en délivrance, \n\r La tristesse en bonheur.', 2, 436),
(1847, 'Strophe', 3, 'Espère, âme chargée! \n\r Espère et ne crains pas! \n\r Car la route est tracée \n\r Devant tes faibles pas. \n\r Ton Dieu viendra lui-même \n\r Te prendre par la main, \n\r Et sa grâce suprême \n\r Changera tout en bien.', 3, 436),
(1848, 'Strophe', 4, 'Sans trop d\'impatience, \n\r Attends le jour marqué, \n\r Le jour de délivrance \n\r Que Dieu t\'a préparé. \n\r Alors, plus de tristesse, \n\r De deuil, d\'obscurité; \n\r Tu verras l\'allégresse, \n\r Le repos, la clarté.', 4, 436),
(1849, 'Strophe', 1, 'À Dieu seul j\'abandonne \n\r Ma vie et ma personne, \n\r Mes projets et mes vœux. \n\r Sans lui rien ne prospère, \n\r Sans mon céleste Père, \n\r Rien ne saurait me rendre heureux.', 1, 437),
(1850, 'Strophe', 2, 'Oui, de sa providence, \n\r Avec reconnaissance \n\r Je veux tout accepter. \n\r Ce qu\'il lui plaît de faire \n\r M\'est toujours salutaire. \n\r Cesse, ô mon cœur! de t\'agiter.', 2, 437),
(1851, 'Strophe', 3, 'J\'attends tout de sa grâce, \n\r Qui jamais ne se lasse, \n\r Et je regarde à lui; \n\r Quand le péril me presse, \n\r Il connaît ma détresse, \n\r Et son amour est mon appui.', 3, 437),
(1852, 'Strophe', 4, 'Oui, mon âme est tranquille; \n\r Je trouve un sûr asile, \n\r Ô Seigneur! dans ton sein, \n\r Et pour moi cette vie \n\r Alors sera suivie \n\r D\'un bonheur sans borne et sans fin.', 4, 437),
(1853, 'Strophe', 1, 'Chaque instant de chaque jour qui passe, \n\r En toi seul je puis me confier; \n\r Ô Jésus que jamais rien ne lasse, \n\r Soutiens-moi, viens me vivifier.', 1, 438),
(1854, 'Refrain', 1, 'Et ton cœur si patient, si tendre \n\r Sait pourvoir aux besoins de ma foi; \n\r À toi seul j\'ai appris à m\'attendre. \n\r Car c\'est toi qui t\'occupes de moi.', 2, 438),
(1855, 'Strophe', 2, 'Aide-moi à n\'avoir confiance \n\r Qu\'en toi seul, mon maître, mon Seigneur. \n\r Tu connais toutes mes défaillances; \n\r Mon salut est seul en ta faveur.', 3, 438),
(1856, 'Refrain', 1, 'Et ton cœur si patient, si tendre \n\r Sait pourvoir aux besoins de ma foi; \n\r À toi seul j\'ai appris à m\'attendre. \n\r Car c\'est toi qui t\'occupes de moi.', 4, 438),
(1857, 'Strophe', 3, 'Prends ma main, tiens-moi quand je chancelle; \n\r Le chemin est sombre et rocailleux. \n\r Oh! permets que je te sois fidèle, \n\r Conduis-moi jusqu\'au séjour des cieux.', 5, 438),
(1858, 'Refrain', 1, 'Et ton cœur si patient, si tendre \n\r Sait pourvoir aux besoins de ma foi; \n\r À toi seul j\'ai appris à m\'attendre. \n\r Car c\'est toi qui t\'occupes de moi.', 6, 438),
(1859, 'Strophe', 1, 'Christ est mon rocher, ma lumière et mon Sauveur, \n\r Je ne crains rien, \n\r Il est mon rempart et mon ferme soutien, \n\r De qui pourrais-je avoir peur? \n\r Même si la tempête fait rage,…', 1, 439),
(1860, 'Strophe', 2, '…Au plus fort de l\'orage, \n\r Si mon cœur est troublé, \n\r S\'il venait à douter, accablé \n\r Dieu ranime toujours mon courage \n\r Et je suis consolé.', 2, 439),
(1861, 'Strophe', 3, 'Mon âme espère en l\'Éternel! \n\r Qu\'un chant s\'élève au Dieu du ciel! \n\r Mon âme espère en l\'Éternel!', 3, 439),
(1862, 'Strophe', 4, 'Mon âme espère en l\'Éternel! \n\r Qu\'un chant s\'élève au Dieu du ciel! \n\r Mon âme espère en l\'Éternel! \n\r Amen!', 4, 439),
(1863, 'Strophe', 1, 'Brillante Étoile, Étoile du matin, \n\r Toi, conduis-moi! \n\r Quand tout est noir, obscur sur mon chemin, \n\r Toi, conduis-moi! \n\r Pourquoi vouloir sonder comme autrefois \n\r L\'avenir? non: un seul pas à la fois.', 1, 440),
(1864, 'Strophe', 2, 'Dans la souffrance, au sein de la douleur, \n\r Toi, conduis-moi! \n\r Au jour de deuil, tu consoles mon cœur: \n\r Toi, conduis-moi! \n\r Du haut du ciel, je sais que tu me vois; \n\r Il me suffit d\'un seul pas à la fois.', 2, 440),
(1865, 'Strophe', 3, 'Sur ton amour, oh! j\'aime à m\'appuyer; \n\r Toi, conduis-moi! \n\r Ma seule étude est de me confier: \n\r Toi, conduis-moi! \n\r Tout est repos, Jésus, près de ta croix; \n\r Il me suffit d\'un seul pas à la fois.', 3, 440),
(1866, 'Refrain', 1, 'Comme brame le cerf après les courants d\'eau, je crie à toi. \n\r Je soupire après toi, mon âme a soif du Dieu vivant.', 1, 441),
(1867, 'Strophe', 1, 'Tu es, Jésus mon seul soutien, \n\r Je veux unir mon cœur au tien. \n\r Comme brame le cerf après les courants d\'eau, je crie à toi.', 2, 441),
(1868, 'Refrain', 1, 'Comme brame le cerf après les courants d\'eau, je crie à toi. \n\r Je soupire après toi, mon âme a soif du Dieu vivant.', 3, 441),
(1869, 'Strophe', 2, 'À quoi bon se désoler? \n\r À quoi bon se lamenter? \n\r Je veux te célébrer, toi seul es mon Sauveur, tu es mon Dieu.', 4, 441),
(1870, 'Refrain', 1, 'Comme brame le cerf après les courants d\'eau, je crie à toi. \n\r Je soupire après toi, mon âme a soif du Dieu vivant.', 5, 441),
(1871, 'Strophe', 1, 'Comme un phare sur la plage \n\r Perçant l\'ombre de la nuit, \n\r L\'amour de Dieu, dans l\'orage, \n\r Cherche l\'homme et le conduit.', 1, 442),
(1872, 'Refrain', 1, 'Ô Sauveur! Que ta lumière \n\r Resplendisse sur les flots, \n\r Et, vers le ciel, qu\'elle éclaire \n\r Et sauve les matelots.', 2, 442),
(1873, 'Strophe', 2, 'Nulle étoile n\'étincelle \n\r Pour guider le voyageur. \n\r Qui gardera la nacelle \n\r Des dangers et des frayeurs?', 3, 442),
(1874, 'Refrain', 1, 'Ô Sauveur! Que ta lumière \n\r Resplendisse sur les flots, \n\r Et, vers le ciel, qu\'elle éclaire \n\r Et sauve les matelots.', 4, 442),
(1875, 'Strophe', 3, 'Dans la nuit qui m\'environne \n\r De ton amour, ô Jésus! \n\r Que par moi l\'éclat rayonne \n\r Aux yeux des marins perdus!', 5, 442),
(1876, 'Refrain', 1, 'Ô Sauveur! Que ta lumière \n\r Resplendisse sur les flots, \n\r Et, vers le ciel, qu\'elle éclaire \n\r Et sauve les matelots.', 6, 442),
(1877, 'Strophe', 1, 'Comme un fleuve immense \n\r Est la paix de Dieu. \n\r Parfaite elle avance, \n\r Vainqueur en tout lieu; \n\r Parfaite elle augmente \n\r Constamment son cours; \n\r Parfaite sa pente \n\r S\'abaisse toujours.', 1, 443),
(1878, 'Strophe', 2, 'Sous ta main meurtrie, \n\r Se brise, ô mon Roi! \n\r Toute arme ennemie \n\r Faite contre moi! \n\r Rien ne peut me nuire \n\r Ni troubler mon cœur, \n\r Tu veux me conduire, \n\r Tu seras vainqueur.', 2, 443),
(1879, 'Strophe', 3, 'Tu traces ma voie: \n\r J\'y marche avec foi; \n\r L\'épreuve et la joie \n\r Me viennent de toi! \n\r Cadran de nos vies, \n\r Marque, chaque jour, \n\r Les heures bénies \n\r Du Soleil d\'amour!', 3, 443),
(1880, 'Strophe', 4, 'Océan de gloire, \n\r Paix de mon Sauveur! \n\r Gage de victoire! \n\r Trésor de bonheur! \n\r Ta grâce infinie, \n\r Coulant nuit et jour, \n\r Inonde ma vie \n\r De vagues d\'amour!', 4, 443),
(1881, 'Strophe', 1, 'Dans le jardin où j\'aime entrer, \n\r À l\'heure douce de l\'aurore, \n\r Je me rends seul pour rencontrer \n\r Celui que mon âme adore.', 1, 444),
(1882, 'Refrain', 1, 'Il marche avec moi, \n\r Mon Sauveur, mon Roi. \n\r Il me dit que je suis à Lui; \n\r Il est mon soutien, \n\r Il est tout mon bien, \n\r Mon salut, mon divin appui.', 2, 444),
(1883, 'Strophe', 2, 'Il parle, et dans mon cœur, sa voix \n\r Fait naître une joie infinie, \n\r Les oiseaux même, au fond des bois, \n\r Suspendent leur mélodie.', 3, 444),
(1884, 'Refrain', 1, 'Il marche avec moi, \n\r Mon Sauveur, mon Roi. \n\r Il me dit que je suis à Lui; \n\r Il est mon soutien, \n\r Il est tout mon bien, \n\r Mon salut, mon divin appui.', 4, 444),
(1885, 'Strophe', 3, 'Dans les parterres du jardin, \n\r Les fleurs humides de rosée \n\r Offrent aux visiteurs divins, \n\r Leur corbeille parfumée.', 5, 444),
(1886, 'Refrain', 1, 'Il marche avec moi, \n\r Mon Sauveur, mon Roi. \n\r Il me dit que je suis à Lui; \n\r Il est mon soutien, \n\r Il est tout mon bien, \n\r Mon salut, mon divin appui.', 6, 444),
(1887, 'Strophe', 4, 'Le don que tu cherches, Seigneur, \n\r Meilleur que les fruits de la terre, \n\r C\'est l\'offrande de tout mon cœur; \n\r Elle seule peut te plaire.', 7, 444),
(1888, 'Refrain', 1, 'Il marche avec moi, \n\r Mon Sauveur, mon Roi. \n\r Il me dit que je suis à Lui; \n\r Il est mon soutien, \n\r Il est tout mon bien, \n\r Mon salut, mon divin appui.', 8, 444),
(1889, 'Strophe', 1, 'Entends notre prière, \n\r Qu\'elle monte jusqu\'à toi! \n\r Ô toi notre lumière, \n\r Notre paix et notre joie!', 1, 445),
(1890, 'Refrain', 1, 'Ô toi notre lumière, \n\r Notre paix et notre joie.', 2, 445),
(1891, 'Strophe', 2, 'Illumine la route \n\r De ceux qui sont révoltés; \n\r Délivre-les du doute, \n\r Qu\'ils acceptent tes bontés.', 3, 445),
(1892, 'Refrain', 1, 'Ô toi notre lumière, \n\r Notre paix et notre joie.', 4, 445),
(1893, 'Strophe', 3, 'Donne à celui qui passe \n\r La force de ton amour, \n\r Et qu\'en voyant ta face, \n\r Il soit en paix chaque jour.', 5, 445),
(1894, 'Refrain', 1, 'Ô toi notre lumière, \n\r Notre paix et notre joie.', 6, 445),
(1895, 'Strophe', 4, 'Ô Seigneur, notre Père, \n\r Que ton nom soit glorifié \n\r Tu es notre lumière en \n\r Jésus ressuscité!', 7, 445),
(1896, 'Refrain', 1, 'Ô toi notre lumière, \n\r Notre paix et notre joie.', 8, 445),
(1897, 'Strophe', 1, 'De quoi t\'alarmes-tu, mon cœur? \n\r Reviens à l\'espérance. \n\r À ton Seigneur, à ton Sauveur, \n\r Remets ton existence. \n\r Car nuit et jour, \n\r Dans son amour, \n\r Il veille sur ta vie: \n\r Jamais il ne t\'oublie.', 1, 446),
(1898, 'Strophe', 2, 'Regarde au Dieu de l\'univers: \n\r Il connaît tout d\'avance. \n\r Il peut sonder le ciel, la mer, \n\r Le temps et la distance. \n\r Il voit aussi \n\r Le plus petit, \n\r Le fond caché d\'un être. \n\r Son œil en toi pénètre.', 2, 446),
(1899, 'Strophe', 3, 'Ouvrez vos cœurs, ouvrez vos yeux! \n\r Qui donc hésite à croire? \n\r Voyez par tout l\'amour de Dieu \n\r Et contemplez sa gloire. \n\r Tout est gardé \n\r Par sa bonté. \n\r Sa grâce est infinie. \n\r Il veille sur nos vies.', 3, 446),
(1900, 'Strophe', 1, 'Divin Père éternel, je viens. \n\r Que mon esprit, mon cœur soient tiens. \n\r Ma vie entière t\'appartient: \n\r Prends tout, je ne garde plus rien. \n\r Amen, amen, amen.', 1, 447),
(1901, 'Strophe', 2, 'De mon âme sois le gardien; \n\r Console-moi, sois mon soutien, \n\r Mon protecteur, mon plus grand bien. \n\r C\'est là le sort qui me convient. \n\r Amen, amen, amen.', 2, 447),
(1902, 'Strophe', 3, 'Tout est à moi: je suis chrétien, \n\r Bien à l\'abri, car je suis tien. \n\r Je suis heureux, quand il revient, \n\r Le temps de nos doux entretiens. \n\r Amen, amen, amen.', 3, 447),
(1903, 'Strophe', 4, 'Depuis les jours les plus anciens, \n\r Dans ton amour, tu me maintiens. \n\r Tous mes désirs, tu les préviens. \n\r Oui, tu es mon souverain bien. \n\r Amen, amen, amen.', 4, 447),
(1904, 'Strophe', 1, 'En toi, Seigneur, j\'ai mis ma confiance, (2×) \n\r Ton puissant secours \n\r M\'assiste tous les jours; \n\r Seul tu fais mon espérance. \n\r En toi, Seigneur! en toi, Seigneur, \n\r J\'ai mis ma confiance, j\'ai mis ma confiance, \n\r Et c\'est pour toujours, et c\'est pour toujours.', 1, 448),
(1905, 'Strophe', 2, 'Comme un enfant regarde vers son père, (2×) \n\r Moi, levant les yeux, \n\r Je cherche un Père aux cieux \n\r D\'où me vient force et lumière. \n\r Comme un enfant, comme un enfant \n\r Je regarde à mon Père, je regarde à mon Père, \n\r Paisible et joyeux, paisible et joyeux.', 2, 448),
(1906, 'Strophe', 3, 'Réponds, Seigneur, aux soupirs de mon âme, (2×) \n\r Seul, tu peux remplir \n\r Mes vœux et mon désir: \n\r C\'est toi seul que je réclame \n\r Réponds, Seigneur! réponds, Seigneur \n\r Aux soupirs de mon âme, aux soupirs de mon âme, \n\r Et viens me bénir, et viens me bénir.', 3, 448),
(1907, 'Strophe', 1, 'Flots mugissants, flots en furie, \n\r Entourez-moi, je n\'ai pas peur: \n\r Quoi qu\'il en soit, paix infinie, \n\r Puisqu\'à la barre est mon Sauveur.', 1, 449),
(1908, 'Refrain', 1, 'Le gouvernail, le gouvernail de ma nacelle, \n\r Oh! quel repos, quel repos, Jésus le tient, \n\r Si dans la nuit, dans la nuit mon cœur chancelle, \n\r Avec Jésus, avec Jésus, oui tout est bien.', 2, 449),
(1909, 'Strophe', 2, 'Et vous, récifs, écueils, menaces, \n\r Qui présagez plus d\'un malheur, \n\r Calme, je puis vous voir en face, \n\r Puisqu\'à la barre est mon Sauveur.', 3, 449),
(1910, 'Refrain', 1, 'Le gouvernail, le gouvernail de ma nacelle, \n\r Oh! quel repos, quel repos, Jésus le tient, \n\r Si dans la nuit, dans la nuit mon cœur chancelle, \n\r Avec Jésus, avec Jésus, oui tout est bien.', 4, 449),
(1911, 'Strophe', 3, 'Jour après jour, nouveaux orages, \n\r Nouveaux périls, point de frayeur! \n\r Bientôt j\'atteins le grand rivage, \n\r Puisqu\'à la barre est mon Sauveur.', 5, 449),
(1912, 'Refrain', 1, 'Le gouvernail, le gouvernail de ma nacelle, \n\r Oh! quel repos, quel repos, Jésus le tient, \n\r Si dans la nuit, dans la nuit mon cœur chancelle, \n\r Avec Jésus, avec Jésus, oui tout est bien.', 6, 449),
(1913, 'Strophe', 4, 'Avec Jésus pour mon Pilote, \n\r Pour moi tout est paix et bonheur. \n\r En sûreté mon esquif flotte, \n\r Puisqu\'à la barre est mon Sauveur.', 7, 449),
(1914, 'Refrain', 1, 'Le gouvernail, le gouvernail de ma nacelle, \n\r Oh! quel repos, quel repos, Jésus le tient, \n\r Si dans la nuit, dans la nuit mon cœur chancelle, \n\r Avec Jésus, avec Jésus, oui tout est bien.', 8, 449),
(1915, 'Strophe', 1, 'En quelque lieu que tu me mènes, \n\r Seigneur, que je sois avec toi. \n\r Sentier de bonheur ou de peines, \n\r Qu\'il soit toujours sentier de foi.', 1, 450),
(1916, 'Strophe', 2, 'Que je fasse ma nourriture \n\r De ta Parole, ô Jésus-Christ, \n\r Et que sans plainte, sans murmure, \n\r Je marche selon ton Esprit.', 2, 450),
(1917, 'Strophe', 3, 'Dieu tout-puissant, qui m\'es propice, \n\r Je veux t\'invoquer chaque jour, \n\r Chercher ton règne et ta justice; \n\r J\'obtiendrai tout de ton amour.', 3, 450),
(1918, 'Strophe', 1, 'Habiter sous l\'abri du Très-Haut \n\r C\'est jouir de tout ce qui est beau, \n\r Découvrir avec joie des lieux délicieux \n\r Et chanter ses exploits merveilleux.', 1, 451),
(1919, 'Refrain', 1, 'Dieu pourvoit au retour des feux brillants du jour, \n\r Car la crainte du Seigneur assure le bonheur. \n\r Dieu pourvoit au retour des feux brillants du jour. \n\r Il protège et rend le cœur joyeux.', 2, 451),
(1920, 'Strophe', 2, 'Habiter au sommet de Sion, \n\r C\'est échapper à la tentation, \n\r Au malheur, au chagrin et à la douleur, \n\r Car il est mon rocher, mon Sauveur.', 3, 451),
(1921, 'Refrain', 1, 'Dieu pourvoit au retour des feux brillants du jour, \n\r Car la crainte du Seigneur assure le bonheur. \n\r Dieu pourvoit au retour des feux brillants du jour. \n\r Il protège et rend le cœur joyeux.', 4, 451),
(1922, 'Strophe', 3, 'Reposer à l\'ombre du Seigneur \n\r C\'est goûter à l\'éternel bonheur, \n\r Profiter de l\'abri du Dieu protecteur, \n\r Dans un ciel toujours garni de fleurs.', 5, 451),
(1923, 'Refrain', 1, 'Dieu pourvoit au retour des feux brillants du jour, \n\r Car la crainte du Seigneur assure le bonheur. \n\r Dieu pourvoit au retour des feux brillants du jour. \n\r Il protège et rend le cœur joyeux.', 6, 451),
(1924, 'Strophe', 1, 'Garde-moi près de la croix, \n\r Source radieuse \n\r De vie, de force et de joie, \n\r De foi précieuse.', 1, 452),
(1925, 'Refrain', 1, 'Sainte croix, don divin, \n\r À jamais tu brilles \n\r Comme l\'astre du matin, \n\r Qui toujours scintille.', 2, 452),
(1926, 'Strophe', 2, 'Ta croix rayonne pour moi, \n\r Étoile brillante! \n\r Alors, mon âme en émoi \n\r S\'ouvre à toi, confiante.', 3, 452),
(1927, 'Refrain', 1, 'Sainte croix, don divin, \n\r À jamais tu brilles \n\r Comme l\'astre du matin, \n\r Qui toujours scintille.', 4, 452),
(1928, 'Strophe', 3, 'Agneau de Dieu sur la croix, \n\r Dispense la grâce. \n\r Tu me conduis par la foi \n\r Tout près de ta face.', 5, 452),
(1929, 'Refrain', 1, 'Sainte croix, don divin, \n\r À jamais tu brilles \n\r Comme l\'astre du matin, \n\r Qui toujours scintille.', 6, 452),
(1930, 'Strophe', 4, 'Je me tiens près de ta croix, \n\r Esprit d\'espérance, \n\r Le regard fixé sur toi \n\r Avec assurance.', 7, 452),
(1931, 'Refrain', 1, 'Sainte croix, don divin, \n\r À jamais tu brilles \n\r Comme l\'astre du matin, \n\r Qui toujours scintille.', 8, 452),
(1932, 'Strophe', 1, 'J\'ai soif de ta présence, \n\r Divin chef de ma foi; \n\r Dans ma faiblesse immense \n\r Que ferais-je sans toi?', 1, 453),
(1933, 'Refrain', 1, 'Chaque jour, à chaque heure, \n\r Oh! j\'ai besoin de toi; \n\r Viens, Jésus, et demeure \n\r Auprès de moi.', 2, 453),
(1934, 'Strophe', 2, 'Des ennemis, dans l\'ombre, \n\r Rôdent autour de moi; \n\r Accablé par le nombre, \n\r Que ferais-je sans toi?', 3, 453),
(1935, 'Refrain', 1, 'Chaque jour, à chaque heure, \n\r Oh! j\'ai besoin de toi; \n\r Viens, Jésus, et demeure \n\r Auprès de moi.', 4, 453),
(1936, 'Strophe', 3, 'Pendant les jours d\'orage, \n\r D\'obscurité, d\'effroi, \n\r Quand faiblit mon courage, \n\r Que ferais-je sans toi?', 5, 453),
(1937, 'Refrain', 1, 'Chaque jour, à chaque heure, \n\r Oh! j\'ai besoin de toi; \n\r Viens, Jésus, et demeure \n\r Auprès de moi.', 6, 453),
(1938, 'Strophe', 4, 'Ô Jésus, ta présence, \n\r C\'est la vie et la paix; \n\r La paix dans la souffrance, \n\r Et la vie à jamais.', 7, 453),
(1939, 'Refrain', 1, 'Chaque jour, à chaque heure, \n\r Oh! j\'ai besoin de toi; \n\r Viens, Jésus, et demeure \n\r Auprès de moi.', 8, 453),
(1940, 'Strophe', 1, 'Jésus, ma lumière \n\r Dans la sombre nuit, \n\r Sois ma joie suprême, \n\r Quand le matin luit! \n\r Éclaire mon âme, \n\r Guide mon chemin; \n\r Garde-moi du doute, \n\r Tiens-moi par la main!', 1, 454),
(1941, 'Strophe', 2, 'Sois ma délivrance \n\r Dans la tentation, \n\r Et mon assurance \n\r Dans la confusion! \n\r Ta parole sainte \n\r Est mon conseiller, \n\r Ta chère présence, \n\r Mon sûr bouclier.', 2, 454),
(1942, 'Strophe', 3, 'Conduit par ta grâce, \n\r Je vis chaque jour; \n\r J\'ai déjà ma place \n\r Au divin séjour. \n\r Et plein d\'allégresse, \n\r Je chante l\'amour \n\r Dont la douce flamme \n\r Brûlera toujours.', 3, 454),
(1943, 'Strophe', 1, 'Jésus, mon espérance, \n\r Jésus, mon seul trésor, \n\r Je mets ma confiance \n\r En toi jusqu\'à la mort. \n\r De toi me vient la vie, \n\r De toi descend la paix, \n\r En toi, joie infinie, \n\r Je vis et pour jamais.', 1, 455),
(1944, 'Strophe', 2, 'Rends-moi toujours fidèle, \n\r Aimant tes saintes lois, \n\r Jésus, divin modèle, \n\r Inspire et conduis-moi. \n\r Jamais tu ne commandes \n\r Ce qui serait trop lourd. \n\r Toujours, quand tu demandes, \n\r Ta main donne en retour.', 2, 455),
(1945, 'Strophe', 3, 'Je veux, dans ta lumière, \n\r Marcher le cœur joyeux, \n\r Montant de la poussière \n\r Vers toi, dans les hauts cieux. \n\r C\'est là qu\'est ma patrie, \n\r Pays de mes souhaits. \n\r C\'est là ma bourgeoisie, \n\r Jésus, dans ton palais.', 3, 455),
(1946, 'Strophe', 1, 'Marcher en ta présence, \n\r Fidèle et doux Sauveur! \n\r Dans une humble assurance \n\r En ton bras, en ton cœur; \n\r Ne chercher qu\'à te plaire, \n\r Dans tout ce que l\'on fait, \n\r C\'est le ciel sur la terre, \n\r C\'est le bonheur parfait, \n\r C\'est le bonheur parfait.', 1, 456),
(1947, 'Strophe', 2, 'Ainsi, devant ta face, \n\r Conduis-nous chaque jour, \n\r Et que l\'Esprit de grâce \n\r Verse en nous ton amour. \n\r Si le péril augmente, \n\r Augmente-nous la foi. \n\r Tu restes notre attente, \n\r Ô Jésus, notre roi! \n\r Ô Jésus, notre roi!', 2, 456),
(1948, 'Strophe', 1, 'Mon cœur joyeux, plein d\'espérance, \n\r S\'élève à toi, mon Rédempteur! \n\r Daigne écouter avec clémence \n\r Un pauvre humain faible et pécheur. \n\r En toi seul est ma confiance, \n\r En toi seul est tout mon bonheur.', 1, 457),
(1949, 'Strophe', 2, 'C\'est vers ton ciel que, dans ma course, \n\r Je vois aboutir tous mes pas. \n\r De ton Esprit la vive source \n\r Me rafraîchit quand je suis las; \n\r Et, dans le danger, ma ressource \n\r Est dans la force de ton bras.', 2, 457),
(1950, 'Strophe', 3, 'Le jour, je suis sous ta lumière; \n\r La nuit, je repose en ton sein. \n\r Au matin, ton regard m\'éclaire \n\r Et m\'ouvre un facile chemin; \n\r Et chaque soir, ô mon bon Père! \n\r Tu prépares mon lendemain.', 3, 457),
(1951, 'Strophe', 1, 'Le Fils de Dieu vécut sur terre, \n\r Pour nous sauver et nous guérir. \n\r Il s\'est offert et dans sa grâce, \n\r Il a vaincu la mort pour nous donner la vie. \n\r Il est vivant! L\'avenir s\'éclaire. \n\r Il est vivant! Plus de frayeur! \n\r Il est vivant et nous protège, \n\r Car avec lui la vie est belle. \n\r Quel bonheur!', 1, 458),
(1952, 'Strophe', 1, 'À toi je veux être attaché, \n\r Jésus, mon seul Sauveur. \n\r Vers ton séjour je veux marcher \n\r Par ton Esprit, vainqueur.', 1, 459),
(1953, 'Strophe', 2, 'T\'avoir, Jésus, et près de toi \n\r Vivre jusqu\'à la fin, \n\r Sentir toujours grandir ma foi; \n\r C\'est le bonheur divin.', 2, 459),
(1954, 'Strophe', 3, 'Espoir de tous les cœurs brisés, \n\r Calme-les à jamais, \n\r Dépose sur les fronts lassés \n\r Ta paix, ta douce paix!', 3, 459),
(1955, 'Strophe', 4, 'Reste ma joie et mon soutien, \n\r Mon éternel trésor! \n\r Comptant sur toi, je ne crains rien \n\r Dans la vie et la mort.', 4, 459),
(1956, 'Refrain', 1, 'Mon cœur est comme une flûte de roseau \n\r Que Jésus remplit de musique, \n\r Mon cœur est comme une flûte de roseau \n\r Qu\'il fait chanter, Alléluia! \n\r Qu\'il fait chanter, Alléluia!', 1, 460),
(1957, 'Strophe', 1, 'Un jour, je rencontre Jésus, \n\r Il me regarde, il me sourit, \n\r Tel que je suis, je sais qu\'il m\'aime, \n\r Je suis joyeux et libre.', 2, 460),
(1958, 'Refrain', 1, 'Mon cœur est comme une flûte de roseau \n\r Que Jésus remplit de musique, \n\r Mon cœur est comme une flûte de roseau \n\r Qu\'il fait chanter, Alléluia! \n\r Qu\'il fait chanter, Alléluia!', 3, 460),
(1959, 'Strophe', 2, 'Il me guide, il est mon berger. \n\r Dans le danger il me conduit, \n\r Il dit mon nom quand il m\'appelle: \n\r J\'avance avec confiance.', 4, 460),
(1960, 'Refrain', 1, 'Mon cœur est comme une flûte de roseau \n\r Que Jésus remplit de musique, \n\r Mon cœur est comme une flûte de roseau \n\r Qu\'il fait chanter, Alléluia! \n\r Qu\'il fait chanter, Alléluia!', 5, 460),
(1961, 'Strophe', 3, 'Nous marchons vers un beau pays, \n\r Vers le repos et vers la paix, \n\r Vers le repas où il se donne \n\r Lui-même en nourriture.', 6, 460),
(1962, 'Refrain', 1, 'Mon cœur est comme une flûte de roseau \n\r Que Jésus remplit de musique, \n\r Mon cœur est comme une flûte de roseau \n\r Qu\'il fait chanter, Alléluia! \n\r Qu\'il fait chanter, Alléluia!', 7, 460),
(1963, 'Strophe', 4, 'Il est vraiment le bon berger, \n\r C\'est par amour que pour sauver \n\r Chaque brebis de son troupeau, \n\r Il a donné sa vie.', 8, 460),
(1964, 'Refrain', 1, 'Mon cœur est comme une flûte de roseau \n\r Que Jésus remplit de musique, \n\r Mon cœur est comme une flûte de roseau \n\r Qu\'il fait chanter, Alléluia! \n\r Qu\'il fait chanter, Alléluia!', 9, 460),
(1965, 'Strophe', 1, 'Ô toi qui donnes la vie, \n\r À tes pieds je veux m\'asseoir, \n\r Comme s\'asseyait Marie \n\r À l\'heure douce du soir.', 1, 461),
(1966, 'Strophe', 2, 'Mon être entier te réclame; \n\r Tout n\'est que faiblesse en moi, \n\r Viens te pencher sur mon âme, \n\r Elle a tant besoin de toi!', 2, 461),
(1967, 'Strophe', 3, 'Maître, à tes pieds je m\'incline, \n\r Je t\'appartiens sans retour, \n\r Verse en moi ta paix divine, \n\r Répands en moi ton amour.', 3, 461),
(1968, 'Strophe', 1, 'Ne crains point, crois seulement. \n\r Ne crains point, crois seulement. \n\r Toutes choses sont possibles à celui qui croit. \n\r Regarde à ton Sauveur, il est toujours vainqueur. \n\r Toutes choses sont possibles à celui qui croit.', 1, 462),
(1969, 'Strophe', 1, 'Oh! Que ton joug est facile! \n\r Oh! Combien j\'aime ta loi! \n\r Dieu saint, Dieu de l\'Évangile! \n\r Elle est toujours devant moi. \n\r De mes pas, c\'est la lumière, \n\r C\'est le repos de mon cœur: \n\r Mais pour la voir toute entière, \n\r Ouvre mes yeux, bon Sauveur!', 1, 463),
(1970, 'Strophe', 2, 'Non, ta loi n\'est point pénible \n\r Pour quiconque est né de toi; \n\r Toute victoire est possible \n\r À qui combat avec foi. \n\r Seigneur, dans ta forteresse \n\r Aucun mal ne m\'atteindra; \n\r Si je tremble, en ma faiblesse, \n\r Ta droite me soutiendra!', 2, 463),
(1971, 'Strophe', 3, 'D\'un triste et rude esclavage \n\r Affranchi par Jésus-Christ, \n\r J\'ai part à ton héritage, \n\r Aux secours de ton Esprit. \n\r Au lieu d\'un maître sévère, \n\r Prêt à juger, à punir, \n\r Je sers le plus tendre Père, \n\r Toujours prêt à me bénir.', 3, 463),
(1972, 'Strophe', 4, 'Dieu qui guides, qui consoles! \n\r J\'ai connu que le bonheur \n\r Est de garder tes paroles, \n\r Et je les serre en mon cœur. \n\r Fais-moi marcher dans ta voie \n\r Et me plaire en tes statuts. \n\r Si je cherche en toi ma joie, \n\r Je ne serai pas confus.', 4, 463),
(1973, 'Strophe', 1, 'Ô mon Sauveur, ô source intarissable \n\r De tout vrai bien, de douceur, de bonté! \n\r Tu réunis dans ton être admirable \n\r Tous les trésors de la divinité.', 1, 464),
(1974, 'Strophe', 2, 'Seigneur Jésus, sois à jamais ma gloire; \n\r Sois mon amour, ma joie et ma douceur; \n\r Sois mon rempart, ma force, ma victoire, \n\r Ma paix, mon bien, ma vie et mon bonheur.', 2, 464),
(1975, 'Strophe', 3, 'Sois à jamais toute mon espérance, \n\r Sois mon secours, mon guide, mon Sauveur; \n\r Sois mon trésor, ma fin, ma récompense, \n\r Mon seul partage et le tout de mon cœur.', 3, 464),
(1976, 'Strophe', 1, 'Ô mon Sauveur, seul guide en qui j\'espère, \n\r Protège-moi, je n\'ai d\'autre secours. \n\r Pendant la nuit, que ta clarté m\'éclaire \n\r Et garde-moi sans cesse en ton amour. \n\r Divin rocher, que tes eaux salutaires \n\r Coulent vers moi tout au long de mes jours.', 1, 465),
(1977, 'Strophe', 2, 'De ton Esprit, que la grande sagesse \n\r Garde mon cœur de jamais murmurer! \n\r Ô mon pays, terre de la promesse, \n\r Mon cœur ému de loin t\'a salué. \n\r Dans les accents d\'une sainte allégresse, \n\r Ton nom, Seigneur, soit à jamais loué!', 2, 465),
(1978, 'Strophe', 1, 'Près de Jésus, je trouve un sûr asile. \n\r Et si mon ciel est parfois menaçant, \n\r Il me rassure, en lui je suis tranquille: \n\r Dans ma faiblesse agit son bras puissant.', 1, 466),
(1979, 'Refrain', 1, 'Près de Jésus, nulle frayeur, \n\r La joie est indicible. \n\r Près de Jésus, je suis vainqueur, \n\r L\'âme heureuse et paisible.', 2, 466),
(1980, 'Strophe', 2, 'Près de Jésus, je suis béni sans cesse, \n\r Allègrement, je porte mes fardeaux. \n\r Un mot de lui dissipe ma tristesse \n\r Et son amour me donne le repos.', 3, 466),
(1981, 'Refrain', 1, 'Près de Jésus, nulle frayeur, \n\r La joie est indicible. \n\r Près de Jésus, je suis vainqueur, \n\r L\'âme heureuse et paisible.', 4, 466),
(1982, 'Strophe', 3, 'Près de Jésus, ô douceur infinie, \n\r Je veux marcher heureux jour après jour; \n\r Jusqu\'à la fin du combat de la vie, \n\r En lui goûter un éternel amour.', 5, 466),
(1983, 'Refrain', 1, 'Près de Jésus, nulle frayeur, \n\r La joie est indicible. \n\r Près de Jésus, je suis vainqueur, \n\r L\'âme heureuse et paisible.', 6, 466),
(1984, 'Strophe', 1, 'Quel grand secours \n\r D\'être toujours \n\r Protégé par Dieu même! \n\r Ne craignons rien, \n\r Il fait tout bien; \n\r Il nous garde, il nous aime! \n\r Ne craignons rien, \n\r Il fait tout bien; \n\r Il nous garde, il nous aime!', 1, 467),
(1985, 'Strophe', 2, 'Il dit: «Je veux \n\r Vous rendre heureux; \n\r Laissez-moi tout conduire. \n\r Si je vous prends \n\r Pour mes enfants, \n\r Rien ne pourra vous nuire. \n\r Si je vous prends \n\r Pour mes enfants, \n\r Rien ne pourra vous nuire.»', 2, 467),
(1986, 'Strophe', 3, 'Pour aujourd\'hui \n\r Comptons sur lui; \n\r Il nous garde, il nous aime. \n\r Quant à demain, \n\r Sa bonne main \n\r Y pourvoira de même. \n\r Quant à demain, \n\r Sa bonne main \n\r Y pourvoira de même.', 3, 467),
(1987, 'Strophe', 4, 'Quel grand secours \n\r D\'être toujours \n\r Sous l\'œil du Dieu suprême! \n\r Ne craignons rien \n\r Notre soutien, \n\r C\'est l\'Éternel lui-même. \n\r Ne craignons rien \n\r Notre soutien, \n\r C\'est l\'Éternel lui-même.', 4, 467),
(1988, 'Strophe', 1, 'Quel repos céleste, \n\r Jésus, d\'être à Toi! \n\r À Toi pour la mort et la vie, \n\r Dans les jours mauvais \n\r De chanter avec foi: \n\r Tout est bien, ma paix est infinie!', 1, 468),
(1989, 'Refrain', 1, 'Quel repos, quel repos, quel repos, quel céleste repos!', 2, 468),
(1990, 'Strophe', 2, 'Quel repos céleste, \n\r Mon fardeau n\'est plus! \n\r Libre par le sang du calvaire, \n\r Tous mes ennemis, \n\r Jésus les a vaincus, \n\r Gloire et louange à Dieu, notre Père!', 3, 468),
(1991, 'Refrain', 1, 'Quel repos, quel repos, quel repos, quel céleste repos!', 4, 468),
(1992, 'Strophe', 3, 'Quel repos céleste! \n\r Tu conduis mes pas, \n\r Tu me combles de tes richesses, \n\r Dans ton grand amour, \n\r Chaque jour tu sauras \n\r Déployer envers moi tes tendresses.', 5, 468),
(1993, 'Refrain', 1, 'Quel repos, quel repos, quel repos, quel céleste repos!', 6, 468),
(1994, 'Strophe', 4, 'Quel repos céleste, \n\r Quand enfin, Seigneur, \n\r Auprès de Toi j\'aurais ma place, \n\r Après les travaux, \n\r Les combats, la douleur, \n\r À jamais je pourrais voir ta face.', 7, 468),
(1995, 'Refrain', 1, 'Quel repos, quel repos, quel repos, quel céleste repos!', 8, 468),
(1996, 'Strophe', 1, 'Sur toi, je me repose, \n\r Ô Jésus mon Sauveur! \n\r Faut-il donc autre chose \n\r Pour un pauvre pécheur? \n\r Conduit par ta lumière, \n\r Gardé par ton amour, \n\r Vers la maison du Père \n\r Marchant de jour en jour.', 1, 469),
(1997, 'Refrain', 1, 'Sur toi, je me repose \n\r O Jésus mon Sauveur! \n\r Faut-il donc autre chose \n\r Pour un pauvre pécheur?', 2, 469),
(1998, 'Strophe', 2, 'Ah! ma misère est grande! \n\r Mais tu m\'as pardonné; \n\r Sainte et vivante offrande, \n\r Pour moi tu t\'es donné; \n\r Et de toute souillure, \n\r Par le sang de la croix, \n\r Mon âme devient pure: \n\r Tu l\'as dit, je le crois.', 3, 469),
(1999, 'Refrain', 1, 'Sur toi, je me repose \n\r O Jésus mon Sauveur! \n\r Faut-il donc autre chose \n\r Pour un pauvre pécheur?', 4, 469),
(2000, 'Strophe', 3, 'Au plus fort de l\'orage, \n\r Tu te tiens près de moi, \n\r Ranimant mon courage \n\r Et soutenant ma foi; \n\r C\'est dans ton cœur qui m\'aime \n\r Que tu sais me cacher; \n\r En vain Satan lui-même \n\r Voudrait m\'en arracher.', 5, 469),
(2001, 'Refrain', 1, 'Sur toi, je me repose \n\r O Jésus mon Sauveur! \n\r Faut-il donc autre chose \n\r Pour un pauvre pécheur?', 6, 469),
(2002, 'Strophe', 4, 'En toi, j\'ai la victoire, \n\r La paix, la liberté; \n\r À toi je rendrai gloire \n\r Durant l\'éternité; \n\r Si du bonheur qui passe \n\r La source doit tarir, \n\r C\'est assez de ta grâce \n\r Pour vivre et pour mourir.', 7, 469),
(2003, 'Refrain', 1, 'Sur toi, je me repose \n\r O Jésus mon Sauveur! \n\r Faut-il donc autre chose \n\r Pour un pauvre pécheur?', 8, 469),
(2004, 'Strophe', 1, 'T\'aimer, Jésus, te connaître, \n\r Se reposer sur ton sein; \n\r T\'avoir pour son roi, son maître, \n\r Pour son breuvage et son pain; \n\r Savourer en paix ta grâce; \n\r De ta mort, puissant Sauveur! \n\r Goûter la sainte efficace, \n\r Quelle ineffable douceur!', 1, 470),
(2005, 'Strophe', 2, 'Ô bonheur inexprimable! \n\r J\'ai l\'Éternel pour berger; \n\r Toujours tendre et secourable, \n\r Son cœur ne saurait changer. \n\r Dans sa charité suprême, \n\r Il descendit ici-bas \n\r Chercher sa brebis qu\'il aime, \n\r Et la prendre dans ses bras.', 2, 470),
(2006, 'Strophe', 3, 'Il donna pour moi sa vie; \n\r Il me connaît par mon nom; \n\r À sa table il me convie; \n\r J\'ai ma place en sa maison. \n\r Il veut bien de ma faiblesse, \n\r De tous mes maux s\'enquérir. \n\r Qu\'il est bon! il veut sans cesse \n\r Me pardonner, me guérir.', 3, 470),
(2007, 'Strophe', 1, 'Tiens dans ta main, ta main puissante et forte, \n\r Ton faible enfant qui seul ne peut marcher. \n\r Vers toi, Seigneur, tout mon désir se porte, \n\r Sur mon chemin garde-moi de broncher!', 1, 471),
(2008, 'Strophe', 2, 'Tiens dans ta main tous les jours de ma vie, \n\r Qu\'ils soient des jours de peine ou de bonheur; \n\r Que de t\'aimer soit toute mon envie, \n\r Que je sois à toi, Jésus mon Sauveur!', 2, 471),
(2009, 'Strophe', 3, 'Tiens dans ta main mon cœur plein de faiblesse, \n\r Sans ton secours je ne puis faire un pas. \n\r Écoute-moi, regarde ma tristesse, \n\r Viens, secours-moi, ne m\'abandonne pas!', 3, 471),
(2010, 'Strophe', 4, 'Tiens dans ta main ma main parfois tremblante, \n\r Quand vient l\'épreuve et ses jours de douleur. \n\r Donne à ma foi, souvent si chancelante, \n\r Plus de douceur, de paix et de ferveur!', 4, 471),
(2011, 'Strophe', 5, 'Tiens dans ta main mon âme toute entière; \n\r Révèle-moi, Jésus, tout ton amour; \n\r Sois mon rocher, mon ancre, ma lumière, \n\r Mon Roi, mon Maître, ô Jésus, chaque jour!', 5, 471),
(2012, 'Refrain', 1, 'Toi, ne prends rien pour le voyage, \n\r J\'ai tout prévu, ne t\'en fais pas, \n\r Toi, ne prends rien, mais prends courage, \n\r C\'est seulement ce qu\'il faudra.', 1, 472),
(2013, 'Strophe', 1, 'Moi, je me charge des bagages, \n\r Des lourdes pierres et de tes cris, \n\r Moi, je me charge de tes nuages, \n\r Des vents contraires et de ta pluie. \n\r Porte des fruits, laisse les feuilles, \n\r Garde la foi, sème la vie, \n\r Ouvre les yeux, moi, je t\'accueille, \n\r Lève la tête et puis souris.', 2, 472),
(2014, 'Refrain', 1, 'Toi, ne prends rien pour le voyage, \n\r J\'ai tout prévu, ne t\'en fais pas, \n\r Toi, ne prends rien, mais prends courage, \n\r C\'est seulement ce qu\'il faudra.', 3, 472),
(2015, 'Strophe', 2, 'Rejette au loin les apparences \n\r Et dans la mer tous tes «pourquoi», \n\r Chante à nouveau ton espérance \n\r Tu sais, je connais bien ta voix. \n\r Détends les rides de ton front \n\r Qui se mélangent au fil des heures, \n\r Alors les rires te reviendront \n\r Comme le soleil, comme une fleur.', 4, 472),
(2016, 'Refrain', 1, 'Toi, ne prends rien pour le voyage, \n\r J\'ai tout prévu, ne t\'en fais pas, \n\r Toi, ne prends rien, mais prends courage, \n\r C\'est seulement ce qu\'il faudra.', 5, 472),
(2017, 'Strophe', 3, 'Mais je voudrais aussi te dire… \n\r Et pourtant tu le sais déjà, \n\r Que si tu me laisses conduire \n\r Tu ne le regretteras pas. \n\r Efface tout et tu verras \n\r La nuit disparaître au grand jour \n\r Respire à fond, rappelle-toi, \n\r Les signes de mon prochain retour.', 6, 472),
(2018, 'Refrain', 1, 'Toi, ne prends rien pour le voyage, \n\r J\'ai tout prévu, ne t\'en fais pas, \n\r Toi, ne prends rien, mais prends courage, \n\r C\'est seulement ce qu\'il faudra.', 7, 472),
(2019, 'Strophe', 1, 'Trop souvent je murmure \n\r Quand la route est obscure: \n\r «Oh! Ma foi faiblira, \n\r Oh! Ma foi faiblira.» \n\r Et pourtant, la promesse \n\r Est là pour ma faiblesse: \n\r «L\'Éternel pourvoira, \n\r L\'Éternel pourvoira.»', 1, 473),
(2020, 'Strophe', 2, 'Si mon impatience \n\r Demande avec instance \n\r D\'où le secours viendra, \n\r D\'où le secours viendra? \n\r Par ta Parole sainte \n\r Tu réponds à ma plainte: \n\r «L\'Éternel pourvoira, \n\r L\'Éternel pourvoira.»', 2, 473),
(2021, 'Strophe', 3, 'Je ne veux plus me plaindre, \n\r Je ne veux plus rien craindre: \n\r Contre moi qui sera? \n\r Contre moi qui sera? \n\r En avant! Et courage, \n\r Jusqu\'au bout du voyage: \n\r «L\'Éternel pourvoira, \n\r L\'Éternel pourvoira.»', 3, 473),
(2022, 'Strophe', 4, 'Et la mer mugissante, \n\r Soudain obéissante, \n\r Devant moi s\'ouvrira, \n\r Devant moi s\'ouvrira. \n\r Plus le danger menace, \n\r Plus puissante est la grâce: \n\r «L\'Éternel pourvoira, \n\r L\'Éternel pourvoira.»', 4, 473),
(2023, 'Refrain', 1, 'Trouver dans ma vie ta présence, \n\r Tenir une lampe allumée, \n\r Choisir avec toi la confiance, \n\r Aimer et se savoir aimé.', 1, 474),
(2024, 'Strophe', 1, 'Croiser ton regard dans le doute, \n\r Brûler à l\'écho de ta voix, \n\r Rester pour le pain de la route, \n\r Savoir reconnaître ton pas.', 2, 474),
(2025, 'Refrain', 1, 'Trouver dans ma vie ta présence, \n\r Tenir une lampe allumée, \n\r Choisir avec toi la confiance, \n\r Aimer et se savoir aimé.', 3, 474),
(2026, 'Strophe', 2, 'Brûler quand le feu devient cendre, \n\r Partir vers celui qui attend, \n\r Choisir de donner sans reprendre, \n\r Fêter le retour d\'un enfant.', 4, 474),
(2027, 'Refrain', 1, 'Trouver dans ma vie ta présence, \n\r Tenir une lampe allumée, \n\r Choisir avec toi la confiance, \n\r Aimer et se savoir aimé.', 5, 474),
(2028, 'Strophe', 3, 'Ouvrir quand tu frappes à ma porte, \n\r Briser les verrous de la peur, \n\r Savoir tout ce que tu m\'apportes, \n\r Rester et devenir veilleur.', 6, 474),
(2029, 'Refrain', 1, 'Trouver dans ma vie ta présence, \n\r Tenir une lampe allumée, \n\r Choisir avec toi la confiance, \n\r Aimer et se savoir aimé.', 7, 474),
(2030, 'Strophe', 1, 'Gloire à mon Dieu, c\'est lui qui me console; \n\r Je sais qu\'il m\'aime et qu\'il veut mon bonheur. \n\r Que de beautés je trouve en sa Parole! \n\r Mais la plus grande est l\'amour du Sauveur.', 1, 475),
(2031, 'Refrain', 1, 'Son grand amour, c\'est le bonheur, \n\r Oh! je voudrais qu\'il remplit tout mon cœur!', 2, 475),
(2032, 'Strophe', 2, 'Quand je m\'en vais sur les chemins du monde \n\r Et que je fais ce que je ne veux pas, \n\r Je me souviens que ton pardon abonde, \n\r Et je reviens te suivre pas à pas.', 3, 475),
(2033, 'Refrain', 1, 'Son grand amour, c\'est le bonheur, \n\r Oh! je voudrais qu\'il remplit tout mon cœur!', 4, 475),
(2034, 'Strophe', 3, 'Oh! que vers Dieu monte mon chant suprême, \n\r Pour le louer, le bénir chaque jour. \n\r Que l\'univers entier, le méchant même, \n\r Chante aujourd\'hui son merveilleux amour!', 5, 475),
(2035, 'Refrain', 1, 'Son grand amour, c\'est le bonheur, \n\r Oh! je voudrais qu\'il remplit tout mon cœur!', 6, 475),
(2036, 'Strophe', 1, 'Prends ma main dans la tienne, \n\r Et qu\'en tout lieu \n\r Ta droite me soutienne, \n\r Seigneur, mon Dieu. \n\r Comment donc sans ton aide \n\r Me diriger \n\r Si je ne te possède \n\r Dans le danger?', 1, 476),
(2037, 'Strophe', 2, 'Dans les jours d\'allégresse \n\r Ou de douleur, \n\r Seigneur, dans ta tendresse, \n\r Garde mon cœur. \n\r Ta divine clémence, \n\r Jour après jour, \n\r Me donne l\'assurance \n\r De ton amour.', 2, 476),
(2038, 'Strophe', 3, 'Si quelque noir nuage \n\r Voile à mon cœur \n\r Ton adorable image, \n\r Ô mon Sauveur, \n\r Ma prière fervente \n\r Monte vers toi \n\r Et ta voix consolante \n\r Me dit: «C\'est moi!»', 3, 476),
(2039, 'Strophe', 1, 'Gloire à mon Dieu, c\'est lui qui me console; \n\r Je sais qu\'il m\'aime et qu\'il veut mon bonheur. \n\r Que de beautés je trouve en sa Parole! \n\r Mais la plus grande est l\'amour du Sauveur.', 1, 477),
(2040, 'Refrain', 1, 'Son grand amour, c\'est le bonheur, \n\r Oh! je voudrais qu\'il remplit tout mon cœur!', 2, 477),
(2041, 'Strophe', 2, 'Quand je m\'en vais sur les chemins du monde \n\r Et que je fais ce que je ne veux pas, \n\r Je me souviens que ton pardon abonde, \n\r Et je reviens te suivre pas à pas.', 3, 477),
(2042, 'Refrain', 1, 'Son grand amour, c\'est le bonheur, \n\r Oh! je voudrais qu\'il remplit tout mon cœur!', 4, 477),
(2043, 'Strophe', 3, 'Oh! que vers Dieu monte mon chant suprême, \n\r Pour le louer, le bénir chaque jour. \n\r Que l\'univers entier, le méchant même, \n\r Chante aujourd\'hui son merveilleux amour!', 5, 477),
(2044, 'Refrain', 1, 'Son grand amour, c\'est le bonheur, \n\r Oh! je voudrais qu\'il remplit tout mon cœur!', 6, 477),
(2045, 'Strophe', 1, 'Mon Dieu, plus près de toi, \n\r Plus près de toi. \n\r C\'est le mot de ma foi: \n\r Plus près de toi. \n\r Dans le jour où l\'épreuve \n\r Déborde comme un fleuve, \n\r Garde-moi près de toi, \n\r Plus près de toi.', 1, 478),
(2046, 'Strophe', 2, 'Plus près de toi, Seigneur \n\r Plus près de toi. \n\r Tiens-moi dans ma douleur \n\r Tout près de toi. \n\r Alors que la souffrance \n\r Fait son œuvre en silence \n\r Toujours plus près de toi, \n\r Seigneur, tiens-moi.', 2, 478),
(2047, 'Strophe', 3, 'Plus près de toi, toujours \n\r Plus près de toi! \n\r Donne-moi ton secours \n\r Soutiens ma foi. \n\r Que Satan se déchaîne, \n\r Ton amour me ramène \n\r Toujours plus près de toi, \n\r Plus près de toi.', 3, 478),
(2048, 'Strophe', 4, 'Mon Dieu, plus près de toi! \n\r Dans le désert \n\r J\'ai vu, plus près de toi, \n\r Ton ciel ouvert. \n\r Pèlerin, bon courage! \n\r Ton chant brave l\'orage. \n\r Mon Dieu, plus près de toi, \n\r Plus près de toi.', 4, 478),
(2049, 'Strophe', 1, 'Ô Jésus, mon Sauveur, par ta douce présence, \n\r Viens consoler mon cœur et diriger mes pas; \n\r Ranime dans ce jour ma céleste espérance, \n\r Relève mon courage et ne me quitte pas.', 1, 479),
(2050, 'Strophe', 2, 'Je ne méprise pas ta main qui me châtie. \n\r Je crois en ton amour, ô Dieu d\'éternité. \n\r Je me soumets à toi pour vivre de ta vie, \n\r Accorde à ton enfant ta paix, ta sainteté.', 2, 479),
(2051, 'Strophe', 3, 'Et lorsque ton enfant, cédant à la tristesse, \n\r Se nourrit de douleur, sans chercher le secours, \n\r Oh! viens lui rappeler ta fidèle promesse \n\r Qui soutient, qui guérit, qui console toujours.', 3, 479),
(2052, 'Strophe', 1, 'Nous t\'adorons, Dieu vivant et fidèle, \n\r Qui nous sauvas d\'une mort éternelle, \n\r En nous scellant pour ta gloire, à jamais, \n\r Par la vertu de ton Esprit de paix. \n\r En nous scellant pour ta gloire, à jamais, \n\r Par la vertu de ton Esprit de paix.', 1, 480),
(2053, 'Strophe', 2, 'Tu vins briser notre dur esclavage, \n\r Et ton Esprit nous rend le témoignage \n\r Qu\'en Jésus-Christ nous sommes adoptés \n\r Pour tes enfants et pour tes rachetés. \n\r Qu\'en Jésus-Christ nous sommes adoptés \n\r Pour tes enfants et pour tes rachetés.', 2, 480),
(2054, 'Strophe', 3, 'Le malheureux qui, dans son infortune, \n\r S\'adresse à toi, jamais ne t\'importune. \n\r Allez à lui, fatigués et chargés, \n\r Vous reviendrez guéris ou soulagés. \n\r Allez à lui, fatigués et chargés, \n\r Vous reviendrez guéris ou soulagés.', 3, 480),
(2055, 'Strophe', 4, 'Nous voulons donc marcher à ta lumière, \n\r Guide céleste, Esprit de notre Père. \n\r Oui, nous voulons, docile à ta voix, \n\r Suivre joyeux le chemin de la croix. \n\r Oui, nous voulons, docile à ta voix, \n\r Suivre joyeux le chemin de la croix.', 4, 480),
(2056, 'Strophe', 1, 'Son nom est Jésus, Jésus. \n\r Il vient pour te consoler. \n\r Il guérit les cœurs brisés, soulage les opprimés. \n\r Il est toujours capable de délivrer.', 1, 481),
(2057, 'Strophe', 1, 'Quand écrasé, courbant la tête, \n\r Tu te nourris de tes malheurs, \n\r Ou que le désespoir te guette, \n\r Te livrant aux pires frayeurs:', 1, 482);
INSERT INTO `strophe` (`idStrophe`, `type`, `identifiant`, `texte`, `position`, `idChant`) VALUES
(2058, 'Refrain', 1, 'Fixe les yeux sur le Maître \n\r Viens sur son merveilleux regard, \n\r Et tu seras dans tout ton être. \n\r Vivifié et pour lui mis à part.', 2, 482),
(2059, 'Strophe', 2, 'Quand sur l\'océan ta nacelle \n\r Se remplit de flots écumants, \n\r Lorsqu\'autour de toi tout chancelle, \n\r Que tout te manque en un instant:', 3, 482),
(2060, 'Refrain', 1, 'Fixe les yeux sur le Maître \n\r Viens sur son merveilleux regard, \n\r Et tu seras dans tout ton être. \n\r Vivifié et pour lui mis à part.', 4, 482),
(2061, 'Strophe', 3, 'Quand ton cœur même devient tiède, \n\r En perdant ton premier amour; \n\r Quand le péché caché t\'obsède, \n\r Te tourmentant de nuit, de jour:', 5, 482),
(2062, 'Refrain', 1, 'Fixe les yeux sur le Maître \n\r Viens sur son merveilleux regard, \n\r Et tu seras dans tout ton être. \n\r Vivifié et pour lui mis à part.', 6, 482),
(2063, 'Strophe', 4, 'Quand, affaibli, tu perds courage \n\r Dans ton service, pour Jésus; \n\r Quand lassé du pèlerinage, \n\r Ton œil ne le discerne plus:', 7, 482),
(2064, 'Refrain', 1, 'Fixe les yeux sur le Maître \n\r Viens sur son merveilleux regard, \n\r Et tu seras dans tout ton être. \n\r Vivifié et pour lui mis à part.', 8, 482),
(2065, 'Strophe', 1, 'Quand le vol de la tempête \n\r Viens assombrir ton ciel bleu, \n\r Au lieu de baisser la tête, \n\r Compte les bienfaits de Dieu.', 1, 483),
(2066, 'Refrain', 1, 'Compte les bienfaits de Dieu, \n\r Mets-les tous devant tes yeux, \n\r Tu verras, en adorant, \n\r Combien le nombre en est grand.', 2, 483),
(2067, 'Strophe', 2, 'Quand sur la route glissante \n\r Tu chancèles sous la croix, \n\r Pense à cette main puissante \n\r Qui t\'a béni tant de fois.', 3, 483),
(2068, 'Refrain', 1, 'Compte les bienfaits de Dieu, \n\r Mets-les tous devant tes yeux, \n\r Tu verras, en adorant, \n\r Combien le nombre en est grand.', 4, 483),
(2069, 'Strophe', 3, 'Si tu perds dans le voyage \n\r Plus d\'un cher et doux trésor, \n\r Pense au divin héritage \n\r Qui là-haut te reste encor.', 5, 483),
(2070, 'Refrain', 1, 'Compte les bienfaits de Dieu, \n\r Mets-les tous devant tes yeux, \n\r Tu verras, en adorant, \n\r Combien le nombre en est grand.', 6, 483),
(2071, 'Strophe', 4, 'Bénis donc, bénis sans cesse \n\r Ce Père qui chaque jour \n\r Répand sur toi la richesse \n\r De son merveilleux amour.', 7, 483),
(2072, 'Refrain', 1, 'Compte les bienfaits de Dieu, \n\r Mets-les tous devant tes yeux, \n\r Tu verras, en adorant, \n\r Combien le nombre en est grand.', 8, 483),
(2073, 'Strophe', 1, 'Saint envoyé du Père, \n\r Comment te recevoir, \n\r Désiré de la terre, \n\r Son salut, son espoir? \n\r Dans mon âme ravie \n\r Viens resplendir, Seigneur, \n\r Et faire de ma vie \n\r Un hymne à ton honneur.', 1, 484),
(2074, 'Strophe', 2, 'J\'étais sans espérance, \n\r Le ciel m\'était fermé! \n\r J\'errais dans la souffrance \n\r De remords consumé; \n\r Mais dans ma sombre voie \n\r Tu m\'apparais, Seigneur! \n\r Une ineffable joie \n\r Succède à ma douleur.', 2, 484),
(2075, 'Strophe', 3, 'C\'est ta seule tendresse \n\r Qui dirige tes pas \n\r Vers l\'âme pécheresse \n\r Qui gémit ici-bas. \n\r Touché de sa misère, \n\r Tu viens et dans ce jour \n\r Tu quittes pour la terre \n\r Ton glorieux séjour.', 3, 484),
(2076, 'Strophe', 4, 'Bannis l\'inquiétude, \n\r Pauvre cœur désolé! \n\r Dans sa sollicitude, \n\r Jésus t\'a consolé. \n\r Tu ne peux rien toi-même; \n\r Remets ton avenir \n\r À ce Sauveur qui t\'aime \n\r Et qui veut te bénir.', 4, 484),
(2077, 'Strophe', 1, 'Comme un enfant qui sert son père \n\r Avec amour, avec bonheur, \n\r Je veux t\'aimer, je veux te plaire, \n\r Te servir, ô Dieu, mon Sauveur! \n\r Je veux t\'aimer, je veux te plaire, \n\r Te servir, ô Dieu, mon Sauveur!', 1, 485),
(2078, 'Strophe', 2, 'Comme un enfant, je veux sans crainte \n\r Te livrer mon cœur à toujours, \n\r Accomplir ta volonté sainte, \n\r Compter sur ton divin secours. \n\r Accomplir ta volonté sainte, \n\r Compter sur ton divin secours.', 2, 485),
(2079, 'Strophe', 3, 'Ô viens, par ta puissante grâce, \n\r Affermir en moi ce désir! \n\r Fais-moi marcher devant ta face, \n\r Humble et joyeux de t\'obéir. \n\r Fais-moi marcher devant ta face, \n\r Humble et joyeux de t\'obéir.', 3, 485),
(2080, 'Strophe', 4, 'De ton Esprit remplis mon âme, \n\r De ton amour remplis mon cœur; \n\r Qu\'en moi toujours brûle la flamme \n\r Du saint zèle et de la ferveur. \n\r Qu\'en moi toujours brûle la flamme \n\r Du saint zèle et de la ferveur.', 4, 485),
(2081, 'Strophe', 1, 'Ayez en vous la pensée de Jésus; \n\r Ainsi vivez entre vous. \n\r Lui qui était de condition divine, \n\r De toute éternité, \n\r Il s\'est dépouillé en serviteur. \n\r Il n\'a pas cherché à conserver sa divinité. \n\r Il a pris sur lui notre humanité. \n\r Quelle humanité, quelle humilité!', 1, 486),
(2082, 'Strophe', 2, 'Semblable à nous, il le fut en tout point; \n\r Il était bien un homme. \n\r Il s\'abaissa jusqu\'à subir la mort \n\r Sur le bois de la croix! \n\r C\'est ainsi que Dieu l\'a élevé \n\r Au plus haut du royaume des cieux. \n\r Il lui a donné le plus beau des noms: \n\r Le nom de Jésus. \n\r Venez, adorons. Jésus est Seigneur.', 2, 486),
(2083, 'Refrain', 1, 'Éternel! fais-moi connaître tes voies, \n\r Enseigne-moi tes sentiers. \n\r Conduis-moi dans ta vérité, \n\r Et instruis-moi, et instruis-moi.', 1, 487),
(2084, 'Strophe', 1, 'Car tu es le Dieu de mon salut, \n\r Tu es le Dieu de mon salut. \n\r Tu es toujours mon espérance, \n\r Tu es toujours mon espérance.', 2, 487),
(2085, 'Refrain', 1, 'Éternel! fais-moi connaître tes voies, \n\r Enseigne-moi tes sentiers. \n\r Conduis-moi dans ta vérité, \n\r Et instruis-moi, et instruis-moi.', 3, 487),
(2086, 'Strophe', 2, 'Tous ceux qui lui font confiance \n\r Et sont dans son alliance \n\r Verront toujours sa bienveillance, \n\r Son grand amour et sa patience.', 4, 487),
(2087, 'Refrain', 1, 'Éternel! fais-moi connaître tes voies, \n\r Enseigne-moi tes sentiers. \n\r Conduis-moi dans ta vérité, \n\r Et instruis-moi, et instruis-moi.', 5, 487),
(2088, 'Strophe', 1, 'Jour après jour, conduis-moi, \n\r Toi, mon bon Père et mon roi. \n\r Enseigne-moi le sentier \n\r Montre-moi ta Vérité!', 1, 488),
(2089, 'Strophe', 2, 'Face aux dangers, donne-moi \n\r Cette paix qui est en toi. \n\r Guide-moi dans ton amour; \n\r Qu\'en toi je sois chaque jour.', 2, 488),
(2090, 'Strophe', 3, 'Lorsque vient le tentateur, \n\r Près de toi je n\'ai pas peur. \n\r Dans ta main, je me blottis, \n\r Je trouve mon sûr abri.', 3, 488),
(2091, 'Refrain', 1, 'Le vent souffle où il veut et, toi, \n\r tu entends sa voix \n\r mais tu ne sais pas d\'où il vient \n\r et tu ne sais pas où il va, le vent (le vent).', 1, 489),
(2092, 'Strophe', 1, 'As-tu compté les grains de sable \n\r Sur les bords de la mer? \n\r As-tu compris le chant des vagues \n\r Aux pays des matins clairs?', 2, 489),
(2093, 'Refrain', 1, 'Le vent souffle où il veut et, toi, \n\r tu entends sa voix \n\r mais tu ne sais pas d\'où il vient \n\r et tu ne sais pas où il va, le vent (le vent).', 3, 489),
(2094, 'Strophe', 2, 'Quand tu regardes les étoiles \n\r Au manteau de la nuit, \n\r Tu voudrais bien lever le voile \n\r Qui te masque l\'infini.', 4, 489),
(2095, 'Refrain', 1, 'Le vent souffle où il veut et, toi, \n\r tu entends sa voix \n\r mais tu ne sais pas d\'où il vient \n\r et tu ne sais pas où il va, le vent (le vent).', 5, 489),
(2096, 'Strophe', 3, 'Il est si long le long voyage \n\r Sur un sol inconnu. \n\r Il est si loin l\'autre rivage \n\r Que tu cherches les pieds nus.', 6, 489),
(2097, 'Refrain', 1, 'Le vent souffle où il veut et, toi, \n\r tu entends sa voix \n\r mais tu ne sais pas d\'où il vient \n\r et tu ne sais pas où il va, le vent (le vent).', 7, 489),
(2098, 'Strophe', 4, 'Comme l\'oiseau, loin de la terre, \n\r Tu voudrais t\'envoler \n\r Vers le soleil, vers la lumière, \n\r Dans un ciel de liberté.', 8, 489),
(2099, 'Refrain', 1, 'Le vent souffle où il veut et, toi, \n\r tu entends sa voix \n\r mais tu ne sais pas d\'où il vient \n\r et tu ne sais pas où il va, le vent (le vent).', 9, 489),
(2100, 'Strophe', 5, 'As-tu sondé le cœur de l\'homme \n\r Au secret de sa soif? \n\r As-tu creusé jusqu\'aux racines, \n\r Jusqu\'aux sources de sa vie?', 10, 489),
(2101, 'Refrain', 1, 'Le vent souffle où il veut et, toi, \n\r tu entends sa voix \n\r mais tu ne sais pas d\'où il vient \n\r et tu ne sais pas où il va, le vent (le vent).', 11, 489),
(2102, 'Strophe', 1, 'Ô Dieu de grâce, \n\r Je te fais place \n\r En mon cœur maintenant. \n\r Vois, je t\'attends. \n\r Remplis mon âme \n\r Et que ta flamme \n\r Purifie à l\'instant \n\r Ton faible enfant.', 1, 490),
(2103, 'Strophe', 1, 'Qu\'en toi je vive, Seigneur bien-aimé, \n\r Ô source vive de félicité, \n\r Qu\'à toi je pense, le jour et la nuit, \n\r Car ta clémence toujours me bénit.', 1, 491),
(2104, 'Strophe', 2, 'Dieu, dans ta sagesse rayonne sur moi: \n\r Le mal me presse, toujours garde-moi; \n\r Père, fais grâce, pardonne à ton fils, \n\r Montre ta face, vis en mon esprit.', 2, 491),
(2105, 'Strophe', 3, 'Lorsque je ploie sois mon ferme appui, \n\r Donne ta joie, sois mon sûr abri, \n\r Ma forteresse, mon seul bouclier, \n\r De la détresse tu sais me garder.', 3, 491),
(2106, 'Strophe', 4, 'Fuir la richesse les propos flatteurs, \n\r Dans l\'allégresse servir le Seigneur: \n\r Mon but suprême c\'est lui, mon Sauveur, \n\r Car si je l\'aime, il vit dans mon cœur.', 4, 491),
(2107, 'Strophe', 5, 'À toi la gloire, Seigneur notre Dieu, \n\r Car ta victoire nous ouvre les cieux. \n\r Fais, ô bon Maître, toujours, en tous lieux, \n\r À tous connaître ton nom glorieux.', 5, 491),
(2108, 'Strophe', 1, 'Seigneur, attire mon cœur à toi; \n\r Je te désire tout près de moi. \n\r Ma délivrance, dans le danger, \n\r C\'est ta présence, divin Berger, \n\r C\'est ta présence, divin Berger.', 1, 492),
(2109, 'Strophe', 2, 'Quand je contemple, près de ta croix, \n\r Ton grand exemple, Jésus, mon Roi. \n\r Ah! je n\'aspire qu\'à tout donner, \n\r Tant je désire te ressembler, \n\r Tant je désire te ressembler.', 2, 492),
(2110, 'Strophe', 3, 'Prompt, je m\'élance pour t\'obéir, \n\r Prompt, je m\'avance pour te servir \n\r Mais, sous ton aile, loin du péché, \n\r Sauveur fidèle, tiens-moi caché, \n\r Sauveur fidèle, tiens-moi caché.', 3, 492),
(2111, 'Strophe', 1, 'Seigneur, mon âme soupire \n\r Après tes divins parvis, \n\r Pour t\'écouter et m\'instruire, \n\r À tes pieds toujours assis.', 1, 493),
(2112, 'Refrain', 1, 'Forme-moi pour ton service, \n\r À ton école, ô Jésus! \n\r Que par ton Esprit je puisse \n\r Prêcher ton nom aux perdus.', 2, 493),
(2113, 'Strophe', 2, 'Seigneur, répands dans mon âme \n\r Flot sur flot de ton Esprit; \n\r Qu\'ainsi, brûlant de ta flamme, \n\r En moi, tout mal soit détruit.', 3, 493),
(2114, 'Refrain', 1, 'Forme-moi pour ton service, \n\r À ton école, ô Jésus! \n\r Que par ton Esprit je puisse \n\r Prêcher ton nom aux perdus.', 4, 493),
(2115, 'Strophe', 3, 'Dans tes parcs herbeux, fertiles \n\r Nourris mon cœur affamé. \n\r Qu\'assis près des eaux tranquilles, \n\r Par toi, je sois transformé!', 5, 493),
(2116, 'Refrain', 1, 'Forme-moi pour ton service, \n\r À ton école, ô Jésus! \n\r Que par ton Esprit je puisse \n\r Prêcher ton nom aux perdus.', 6, 493),
(2117, 'Strophe', 4, 'Seigneur, tu sais toute chose, \n\r Je t\'aime et je suis à toi; \n\r De moi tout entier dispose, \n\r Je suis l\'esclave du Roi!', 7, 493),
(2118, 'Refrain', 1, 'Forme-moi pour ton service, \n\r À ton école, ô Jésus! \n\r Que par ton Esprit je puisse \n\r Prêcher ton nom aux perdus.', 8, 493),
(2119, 'Strophe', 1, 'Tendre Sauveur, je viens à toi, \n\r Je voudrais te servir \n\r Comme un disciple d\'autrefois, \n\r Je veux me soumettre à ta loi, \n\r Te croire et t\'obéir.', 1, 494),
(2120, 'Strophe', 2, 'Je t\'apporte mon pauvre cœur, \n\r Remplis-le de ta paix. \n\r Pour qu\'il t\'adore avec ferveur, \n\r Rends-le plus pur et viens, Seigneur, \n\r Y régner désormais.', 2, 494),
(2121, 'Strophe', 3, 'Maintenant, au monde bruyant \n\r Fais que je reste sourd, \n\r Pour entendre à travers le vent, \n\r Le feu, l\'orage et l\'ouragan \n\r Ta tendre voix d\'amour.', 3, 494),
(2122, 'Strophe', 4, 'Seigneur, à tes pieds je me tiens, \n\r Calme et silencieux. \n\r Je sais que je suis l\'un des tiens, \n\r Et pour jamais que j\'appartiens \n\r Au royaume des cieux.', 4, 494),
(2123, 'Strophe', 1, 'Céleste Père, Dieu d\'amour, fidèle et tendre ami, \n\r Nos voix s\'élèvent jusqu\'à toi, conduis nos pas, nos vies. \n\r Mais tu n\'as pas besoin des mots que dicte notre cœur, \n\r Car tous nos faits, nos simples vœux, engagent tes faveurs.', 1, 495),
(2124, 'Strophe', 2, 'Le bien, le mal, tu peux les voir au fond de notre cœur, \n\r Nos plans secrets, nos fausses voies, nos rêves fous, nos peurs. \n\r De nos desseins, oh! daigne ôter la marque du péché; \n\r Vers toi, toujours, nous t\'en prions, dirige nos pensées.', 2, 495),
(2125, 'Strophe', 3, 'Notre âme aspire chaque jour à vivre auprès de toi, \n\r Pour bien connaître ton amour, ouïr ta douce voix; \n\r En toi placer tous nos désirs, guérir nos cœurs mauvais; \n\r Trouver, enfin, le vrai repos, et vivre dans ta paix.', 3, 495),
(2126, 'Refrain', 1, 'Je ne suis qu\'un vase entre tes mains, \n\r Vase d\'argile que tu as façonné. \n\r Je ne suis qu\'un vase entre tes mains, \n\r Vase fragile entre les mains du potier.', 1, 496),
(2127, 'Strophe', 1, 'Déverse en-moi ton huile sainte, \n\r Déverse en-moi ton huile pure. \n\r Déverse en-moi ton huile sainte, \n\r Et que tu sois toujours glorifié.', 2, 496),
(2128, 'Refrain', 1, 'Je ne suis qu\'un vase entre tes mains, \n\r Vase d\'argile que tu as façonné. \n\r Je ne suis qu\'un vase entre tes mains, \n\r Vase fragile entre les mains du potier.', 3, 496),
(2129, 'Strophe', 1, 'C\'est en toi, Père Tout-Puissant, \n\r Que nous mettons notre espérance. \n\r Amen.', 1, 497),
(2130, 'Strophe', 1, 'Jésus m\'entend lorsque par la prière \n\r Je vais à lui sans crainte et sans effroi; \n\r Il veut toujours plaider devant son Père…', 1, 498),
(2131, 'Strophe', 1, '…Pour un pécheur repentant tel que moi. \n\r Et si parfois le ciel paraît d\'airain, \n\r Je sais que mon Sauveur divin, \n\r Au temps voulu me répondra, me répondra.', 2, 498),
(2132, 'Strophe', 2, 'Jésus m\'entend lorsque dans ma détresse, \n\r Avec ferveur j\'implore son secours; \n\r Je sais qu\'il a pitié de ma faiblesse \n\r Et que mon cri parvient à lui toujours…', 3, 498),
(2133, 'Strophe', 2, '…S\'il semble sourd aux appels de ma foi, \n\r Je prends courage, car mon Roi, \n\r Au temps voulu me répondra, me répondra.', 4, 498),
(2134, 'Strophe', 3, 'Jésus m\'entend quand j\'implore sa grâce, \n\r Tout près de moi je sens battre son cœur. \n\r Il ne saurait longtemps cacher sa face…', 5, 498),
(2135, 'Strophe', 3, '…Celui qui fait ma joie et mon bonheur. \n\r S\'il tarde encore, il viendra sûrement; \n\r Et lui qui m\'aime tendrement, \n\r Au temps voulu me répondra, me répondra.', 6, 498),
(2136, 'Strophe', 4, 'Jésus m\'entend; oui, j\'en ai l\'assurance, \n\r Il a perçu les accents de ma voix. \n\r J\'avance en paix, guidé par l\'espérance,…', 7, 498),
(2137, 'Strophe', 4, '…Sur le sentier de ses divines lois. \n\r Et je l\'attends toujours avec bonheur, \n\r Car Jésus, mon divin Sauveur, \n\r Au temps voulu me répondra, me répondra.', 8, 498),
(2138, 'Strophe', 1, 'Moment si doux de la prière, \n\r Où Dieu, m\'élevant jusqu\'à lui, \n\r Se révèle à moi comme un Père, \n\r Comme un Sauveur, comme un appui.', 1, 499),
(2139, 'Strophe', 2, 'Oh! oui, je t\'aime, heure bénie, \n\r Je te désire avec ardeur, \n\r Car déjà souvent dans la vie, \n\r Tu m\'as sauvé du tentateur.', 2, 499),
(2140, 'Strophe', 3, 'Doux moment de paix, heure sainte \n\r Où, sur les ailes de la foi, \n\r Mon cœur s\'élève sans contrainte. \n\r Je ne saurais vivre sans toi.', 3, 499),
(2141, 'Strophe', 4, 'Sois donc toujours toute ma joie, \n\r Tout mon refuge et mon secours, \n\r Et que jamais Dieu ne me voie \n\r Passer sans toi l\'un de mes jours!', 4, 499),
(2142, 'Strophe', 1, 'Mon cœur se tourne, ô Dieu, vers toi, \n\r À ton appel répond ma foi; \n\r Et près du trône je te dis \n\r Tout mon espoir, tous mes soucis. \n\r En temps d\'épreuve et de douleur, \n\r Tu es mon ferme appui, Seigneur, \n\r Car tous les pièges du malin \n\r Se brisent dans ta forte main.', 1, 500),
(2143, 'Strophe', 2, 'Celui qui cherche ton secours \n\r Revient comblé de ton amour. \n\r C\'est là ta force, ton plaisir: \n\r Tu veux sans cesse nous bénir. \n\r Oui, dans ta grâce tu promets \n\r D\'emplir mon âme de ta paix. \n\r Je crois en toi, ma peur n\'est plus, \n\r Mon doute est vite disparu.', 2, 500),
(2144, 'Strophe', 3, 'Je veux répandre autour de moi \n\r La douce onction de cette joie. \n\r Bientôt, je sais, mes yeux verront \n\r Le pur éclat de ta maison. \n\r Alors j\'attends, le cœur soumis, \n\r De recevoir enfin ce prix, \n\r De vivre heureux à tout jamais \n\r Dans tes parvis, dans tes palais.', 3, 500),
(2145, 'Strophe', 1, 'Notre Père qui es aux cieux, \n\r Que ton nom soit sanctifié, \n\r Que ton règne vienne, \n\r Que ta volonté soit faite \n\r Sur la terre comme au ciel…', 1, 501),
(2146, 'Strophe', 2, '…Donne-nous aujourd\'hui \n\r Notre pain de ce jour. \n\r Pardonne-nous nos offenses, \n\r Comme nous pardonnons aussi \n\r À ceux qui nous ont offensés; \n\r Et ne nous soumets pas à la tentation,…', 2, 501),
(2147, 'Strophe', 3, '…Mais délivre-nous du mal, \n\r Car c\'est à toi qu\'appartiennent \n\r Le règne, la puissance et la gloire, \n\r Pour les siècles des siècles. \n\r Amen.', 3, 501),
(2148, 'Strophe', 1, 'Veille et prie et sois fervent, \n\r Combats sans relâche, \n\r Animé d\'un zèle ardent \n\r Pour ta sainte tâche! \n\r Ne crains rien, Jésus vient, \n\r Quand le mal menace, \n\r T\'armer de sa grâce.', 1, 502),
(2149, 'Strophe', 2, 'Veille et prie et sois aimant, \n\r Marche à sa lumière, \n\r Et poursuis fidèlement, \n\r Ta noble carrière. \n\r Son amour, chaque jour, \n\r Sera ta défense \n\r Et ta récompense.', 2, 502),
(2150, 'Strophe', 3, 'Veille et prie et, confiant, \n\r Relève la tête, \n\r Va, demande au Tout-Puissant \n\r Une paix parfaite. \n\r Avec lui pour appui, \n\r Sûre est la victoire. \n\r À lui soit la gloire!', 3, 502),
(2151, 'Strophe', 1, 'Debout, sainte cohorte, \n\r Soldats du Roi des rois! \n\r Tenez d\'une main forte \n\r L\'étendard de la croix! \n\r Au sentier de la gloire, \n\r Jésus-Christ vous conduit; \n\r De victoire en victoire \n\r Il mène qui le suit.', 1, 503),
(2152, 'Strophe', 2, 'La trompette résonne; \n\r Debout! Vaillants soldats! \n\r L\'immortelle couronne \n\r Est le prix des combats. \n\r Si l\'ennemi fait rage, \n\r Soyez fermes et forts; \n\r Redoublez de courage, \n\r S\'il redouble d\'efforts.', 2, 503),
(2153, 'Strophe', 3, 'Debout pour la bataille, \n\r Partez, n\'hésitez plus! \n\r Pour que nul ne défaille, \n\r Regardez à Jésus! \n\r De l\'armure invincible, \n\r Soldats, revêtez-vous! \n\r Le triomphe est possible \n\r Pour qui lutte à genoux.', 3, 503),
(2154, 'Strophe', 4, 'Debout, debout encore! \n\r Luttez jusqu\'au matin; \n\r Déjà brille l\'aurore \n\r À l\'horizon lointain. \n\r Bientôt, jetant nos armes \n\r Aux pieds du Roi des rois! \n\r Les chants après les larmes, \n\r Le trône après la croix!', 4, 503),
(2155, 'Strophe', 1, 'À de nouveaux combats \n\r Jésus, tu nous appelles, \n\r Et nous voici fidèles, \n\r Prêts à suivre tes pas. \n\r Conduis-nous aux combats: \n\r Nous sommes tes soldats.', 1, 504),
(2156, 'Refrain', 1, 'Arme nos bras, arme nos cœurs, \n\r Et nous serons plus que vainqueurs, \n\r Et nous serons plus que vainqueurs; \n\r Arme nos bras, arme nos cœurs.', 2, 504),
(2157, 'Strophe', 2, 'Arme-nous ô Seigneur! \n\r De foi, de hardiesse, \n\r De force, de sagesse, \n\r D\'amour et de ferveur; \n\r Embrase-nous, Seigneur, \n\r D\'une invincible ardeur.', 3, 504),
(2158, 'Refrain', 1, 'Arme nos bras, arme nos cœurs, \n\r Et nous serons plus que vainqueurs, \n\r Et nous serons plus que vainqueurs; \n\r Arme nos bras, arme nos cœurs.', 4, 504),
(2159, 'Strophe', 3, 'Luttons, prions, souffrons! \n\r Nous aurons la victoire; \n\r La couronne de gloire \n\r Un jour ceindra nos fronts. \n\r Luttons, prions, souffrons! \n\r Bientôt nous règnerons.', 5, 504),
(2160, 'Refrain', 1, 'Arme nos bras, arme nos cœurs, \n\r Et nous serons plus que vainqueurs, \n\r Et nous serons plus que vainqueurs; \n\r Arme nos bras, arme nos cœurs.', 6, 504),
(2161, 'Strophe', 1, 'À Jésus, je m\'abandonne; \n\r Ce qu\'il me dit, Je le crois, \n\r Et je prends ce qu\'il me donne, \n\r La couronne avec la croix.', 1, 505),
(2162, 'Refrain', 1, 'Compter sur lui d\'heure en heure, \n\r Tant que dure le combat. \n\r Que l\'on vive ou que l\'on meure, \n\r Compter sur lui, tout est là. \n\r Que l\'on vive ou que l\'on meure, \n\r Compter sur lui, tout est là.', 2, 505),
(2163, 'Strophe', 2, 'Que si l\'ennemi se montre, \n\r Mon cœur n\'en n\'est point troublé \n\r Avec Christ à sa rencontre \n\r Je puis aller sans trembler.', 3, 505),
(2164, 'Refrain', 1, 'Compter sur lui d\'heure en heure, \n\r Tant que dure le combat. \n\r Que l\'on vive ou que l\'on meure, \n\r Compter sur lui, tout est là. \n\r Que l\'on vive ou que l\'on meure, \n\r Compter sur lui, tout est là.', 4, 505),
(2165, 'Strophe', 3, 'Suis-je en paix, vers la lumière \n\r Mon chant s\'élève attendri, \n\r Pour se changer en prière \n\r Si l\'horizon s\'assombrit.', 5, 505),
(2166, 'Refrain', 1, 'Compter sur lui d\'heure en heure, \n\r Tant que dure le combat. \n\r Que l\'on vive ou que l\'on meure, \n\r Compter sur lui, tout est là. \n\r Que l\'on vive ou que l\'on meure, \n\r Compter sur lui, tout est là.', 6, 505),
(2167, 'Strophe', 4, 'Qu\'on m\'approuve ou qu\'on me blâme, \n\r Et demain comme aujourd\'hui, \n\r Je ne veux, quoi qu\'on réclame, \n\r Jamais compter que sur lui.', 7, 505),
(2168, 'Refrain', 1, 'Compter sur lui d\'heure en heure, \n\r Tant que dure le combat. \n\r Que l\'on vive ou que l\'on meure, \n\r Compter sur lui, tout est là. \n\r Que l\'on vive ou que l\'on meure, \n\r Compter sur lui, tout est là.', 8, 505),
(2169, 'Strophe', 1, 'C\'est par la foi que, voyant l\'invisible, \n\r Ils se sont levés à l\'appel du Seigneur, \n\r Rien avec lui ne semblait impossible, \n\r Tant sa promesse enflammait leur ardeur.', 1, 506),
(2170, 'Refrain', 1, 'Brûle en nos âmes, flamme sacrée! \n\r Et nous suivrons les témoins d\'autrefois, \n\r Environnés de leur grande nuée, \n\r Les regards sur Jésus, chef de la foi!', 2, 506),
(2171, 'Strophe', 2, 'C\'est par la foi qu\'ils fuyaient l\'esclavage, \n\r Qu\'ils passaient la mer, qu\'ils étaient valeureux; \n\r Grâce au Seigneur se frayant un passage, \n\r Vers la cité préparée pour eux.', 3, 506),
(2172, 'Refrain', 1, 'Brûle en nos âmes, flamme sacrée! \n\r Et nous suivrons les témoins d\'autrefois, \n\r Environnés de leur grande nuée, \n\r Les regards sur Jésus, chef de la foi!', 4, 506),
(2173, 'Strophe', 3, 'C\'est par la foi que, dans l\'ignominie, \n\r Ils ont enduré des épreuves sans fin; \n\r Sans obtenir ici-bas de patrie, \n\r Mais saluant le triomphe lointain.', 5, 506),
(2174, 'Refrain', 1, 'Brûle en nos âmes, flamme sacrée! \n\r Et nous suivrons les témoins d\'autrefois, \n\r Environnés de leur grande nuée, \n\r Les regards sur Jésus, chef de la foi!', 6, 506),
(2175, 'Strophe', 4, 'Foi des héros, qui combat sous ce signe, \n\r Suis leur saint exemple et partage leur sort, \n\r Eux dont le monde ne fut jamais digne, \n\r Eux, les témoins qui nous parlent encore.', 7, 506),
(2176, 'Refrain', 1, 'Brûle en nos âmes, flamme sacrée! \n\r Et nous suivrons les témoins d\'autrefois, \n\r Environnés de leur grande nuée, \n\r Les regards sur Jésus, chef de la foi!', 8, 506),
(2177, 'Strophe', 1, 'Du Seigneur la grâce baigne l\'univers \n\r Et sur tout l\'espace son œil est ouvert. \n\r Glorieux, immense, ainsi que les cieux, \n\r Éternel, intense est l\'amour de Dieu.', 1, 507),
(2178, 'Strophe', 2, 'Dans la transparence des jours sans danger, \n\r La reconnaissance de nos cœurs légers, \n\r Monte, spontanée, au plus haut des cieux \n\r Et chante, inspirée, la bonté de Dieu.', 2, 507),
(2179, 'Strophe', 3, 'Père, les nuages se font menaçants. \n\r Qu\'à travers l\'orage, ton regard aimant \n\r Donne l\'assurance et le réconfort. \n\r Que cette espérance nous rende plus forts.', 3, 507),
(2180, 'Strophe', 4, 'Ô Dieu, si le doute effleure nos cœurs, \n\r Montre-nous la route et réduis nos peurs. \n\r Mets-nous en mémoire que ton Fils mourut \n\r Pour mener l\'histoire au terme voulu.', 4, 507),
(2181, 'Strophe', 1, 'C\'est un rempart que notre Dieu, \n\r Une invincible armure, \n\r Notre délivrance en tout lieu, \n\r Notre défense sûre. \n\r L\'ennemi contre nous \n\r Redouble de courroux, \n\r Vaine colère! \n\r Que pourrait l\'adversaire? \n\r L\'Éternel détourne ses coups.', 1, 508),
(2182, 'Strophe', 2, 'Seul, nous brochons à chaque pas, \n\r Notre force est faiblesse; \n\r Mais un héros, dans les combats, \n\r Pour nous lutte sans cesse. \n\r Quel est ce défenseur? \n\r C\'est toi, divin Sauveur! \n\r Dieu des armées! \n\r Tes tribus opprimées \n\r Connaissent leur Libérateur.', 2, 508),
(2183, 'Strophe', 3, 'Que les démons forgent des fers \n\r Pour accabler l\'Église; \n\r Ta Sion brave les enfers, \n\r Sur ton rocher, assise. \n\r Constant dans son effort, \n\r En vain avec la mort \n\r Satan conspire; \n\r Pour ruiner son empire, \n\r Il suffit d\'un mot du Dieu fort.', 3, 508),
(2184, 'Strophe', 4, 'Étends ton bras victorieux, \n\r Dans toutes nos détresses, \n\r Et donnes- nous, du haut des cieux, \n\r Ta force et ta sagesse. \n\r Qu\'on nous ôte nos biens, \n\r Qu\'on serre nos liens, \n\r Que nous importe! \n\r Ta grâce est la plus forte, \n\r Et ton royaume est pour les tiens.', 4, 508),
(2185, 'Strophe', 1, 'C\'est un rempart que notre Dieu, \n\r Une invincible armure. \n\r Un défenseur victorieux, \n\r Une aide prompte et sûre. \n\r Satan contre nous redouble ses coups; \n\r En vain sa fureur \n\r Veut ébranler nos cœurs: \n\r Dieu rit de ses injures.', 1, 509),
(2186, 'Strophe', 2, 'Seuls, nous bronchons à chaque pas \n\r Quand l\'ennemi nous presse. \n\r Mais un héros pour nous combat \n\r Et nous soutient sans cesse. \n\r Quel est ce Sauveur, ce libérateur? \n\r C\'est toi, Jésus-Christ, \n\r Uni au Saint-Esprit, \n\r Vainqueur de nos faiblesses.', 2, 509),
(2187, 'Strophe', 3, 'Que les démons forgent des fers, \n\r Menacent ton Église! \n\r Ta Sion brave les enfers, \n\r Sur le rocher assise. \n\r En vain pour sa mort, Satan fait effort: \n\r Tu hausses la voix, \n\r Il tremble devant toi, \n\r Et son pouvoir se brise.', 3, 509),
(2188, 'Strophe', 4, 'Étends ton bras victorieux \n\r Dans toutes nos détresses, \n\r Et donne-nous du haut des cieux \n\r Ta force et ta sagesse. \n\r Qu\'on ôte nos biens, qu\'on serre nos liens, \n\r Nous résisterons, \n\r Et nous hériterons \n\r Ton règne et ses promesses.', 4, 509),
(2189, 'Strophe', 1, 'Chantons tous un chant qui nous réjouira le cœur: \n\r Très bientôt, nous monterons aux cieux. \n\r Car la nuit s\'achève en une aube de bonheur; \n\r Très bientôt, nous monterons aux cieux.', 1, 510),
(2190, 'Refrain', 1, 'Dans très peu de temps, dans très peu de temps, \n\r Nous vaincrons les flots, les vents. \n\r Nous serons unis à ceux que la mort a pris; \n\r Très bientôt, nous monterons aux cieux.', 2, 510),
(2191, 'Strophe', 2, 'Travaillons, amis, tant que le jour dure encor\'. \n\r Très bientôt, nous monterons aux cieux. \n\r Dieu, dans son amour, nous soutient et nous rend forts. \n\r Très bientôt, nous monterons aux cieux.', 3, 510),
(2192, 'Refrain', 1, 'Dans très peu de temps, dans très peu de temps, \n\r Nous vaincrons les flots, les vents. \n\r Nous serons unis à ceux que la mort a pris; \n\r Très bientôt, nous monterons aux cieux.', 4, 510),
(2193, 'Strophe', 3, 'Jésus nous invite à goûter le vrai repos. \n\r Très bientôt, nous monterons aux cieux. \n\r La douleur, les pleurs seront oubliés là-haut! \n\r Très bientôt, nous monterons aux cieux.', 5, 510),
(2194, 'Refrain', 1, 'Dans très peu de temps, dans très peu de temps, \n\r Nous vaincrons les flots, les vents. \n\r Nous serons unis à ceux que la mort a pris; \n\r Très bientôt, nous monterons aux cieux.', 6, 510),
(2195, 'Strophe', 1, 'Debout, resplendis, car voici ta lumière \n\r Et sur toi la gloire du Seigneur! \n\r Lève les yeux et regarde au loin, \n\r Que ton cœur tressaille d\'allégresse! \n\r Voici tes fils qui reviennent à toi, \n\r Et tes filles portées sur la hanche.', 1, 511),
(2196, 'Refrain', 1, 'Jérusalem, Jérusalem, quitte ta robe de tristesse! \n\r Jérusalem, Jérusalem, chante et danse pour ton Dieu!', 2, 511),
(2197, 'Strophe', 2, 'Toutes les nations marcheront vers ta lumière, \n\r Et les rois à ta clarté naissante. \n\r Des multitudes de chameaux te couvriront, \n\r Tous les troupeaux de Quédar et d\'Epha, \n\r Venant de Saba, ils porteront l\'or et l\'encens, \n\r Publiant du Seigneur la louange.', 3, 511),
(2198, 'Refrain', 1, 'Jérusalem, Jérusalem, quitte ta robe de tristesse! \n\r Jérusalem, Jérusalem, chante et danse pour ton Dieu!', 4, 511),
(2199, 'Strophe', 3, 'Les fils d\'étrangers rebâtiront tes remparts, \n\r Et leurs princes franchiront tes portes. \n\r La gloire du Liban viendra sur toi, \n\r On t\'appellera ville du Seigneur. \n\r Les jours de ton deuil seront bientôt tous accomplis, \n\r Le salut sera dans tes murailles.', 5, 511),
(2200, 'Refrain', 1, 'Jérusalem, Jérusalem, quitte ta robe de tristesse! \n\r Jérusalem, Jérusalem, chante et danse pour ton Dieu!', 6, 511),
(2201, 'Strophe', 1, 'Guide-moi, berger fidèle, \n\r En ce monde pèlerin, \n\r Prends pour toi mon cœur rebelle, \n\r Guide-moi, sois mon soutien, \n\r Pain de vie, pain de vie, \n\r De ta grâce nourris-moi, \n\r De ta grâce nourris-moi.', 1, 512),
(2202, 'Strophe', 2, 'Sois pour moi la source vive \n\r Des biens les plus précieux, \n\r Fais que pour toi seul je vive, \n\r Guide-moi du haut des cieux. \n\r Viens, protège, viens, protège, \n\r Sois mon roc, mon bouclier, \n\r Sois mon roc, mon bouclier.', 2, 512),
(2203, 'Strophe', 3, 'Du Jourdain je suis les rives; \n\r Quand j\'ai peur, rassure-moi. \n\r À Sion, qu\'enfin j\'arrive, \n\r Affermis ma faible foi; \n\r Tes louanges, tes louanges, \n\r À jamais, je chanterai, \n\r À jamais, je chanterai.', 3, 512),
(2204, 'Strophe', 1, 'Sur ce rocher de promesses \n\r Que rien n\'ébranlera, \n\r Veillez et priez sans cesse, \n\r L\'Esprit saint vous conduira.', 1, 513),
(2205, 'Refrain', 1, 'Heureux serviteurs fidèles \n\r Qui gardez les yeux fixés \n\r Vers la patrie éternelle, \n\r La demeure des rachetés.', 2, 513),
(2206, 'Strophe', 2, 'Accrochez-vous bien aux branches \n\r Celles de l\'arbre de Vie, \n\r Surtout même si ça penche \n\r Et quand les vents vous ennuient.', 3, 513),
(2207, 'Refrain', 1, 'Heureux serviteurs fidèles \n\r Qui gardez les yeux fixés \n\r Vers la patrie éternelle, \n\r La demeure des rachetés.', 4, 513),
(2208, 'Strophe', 3, 'Recevez en cet instant \n\r Bénédictions et richesses, \n\r Vous pourrez par tous les temps \n\r Naviguer avec sagesse.', 5, 513),
(2209, 'Refrain', 1, 'Heureux serviteurs fidèles \n\r Qui gardez les yeux fixés \n\r Vers la patrie éternelle, \n\r La demeure des rachetés.', 6, 513),
(2210, 'Strophe', 1, 'Ô Jésus, j\'ai besoin de toi, \n\r Ô Jésus, j\'ai besoin de toi. \n\r Quand je suis lassé du voyage, \n\r Ô Jésus, viens, conduis mes pas.', 1, 514),
(2211, 'Strophe', 2, 'Dans mes luttes soutiens ma foi, \n\r Dans mes luttes soutiens ma foi. \n\r Quand sur moi descend la nuit sombre \n\r Ô Jésus, viens, protège-moi.', 2, 514),
(2212, 'Strophe', 3, 'Dans ma peine révèle-toi, \n\r Dans ma peine révèle-toi. \n\r Quand mon cœur attend ta présence, \n\r Ô Jésus, viens, demeure en moi.', 3, 514),
(2213, 'Strophe', 1, 'Lève-toi, lève-toi, \n\r À l\'Appel du divin Roi! \n\r C\'est aux cimes de lumière \n\r Qu\'il te mène par la foi; \n\r De ses pas sur cette terre \n\r Suis les traces sans effroi. \n\r Lève-toi, lève-toi!', 1, 515),
(2214, 'Strophe', 2, 'Le front haut, le front haut, \n\r Brave le plus dur assaut! \n\r L\'ennemi t\'attend peut-être, \n\r Mais il s\'enfuira bientôt \n\r S\'il te voit, du Christ, ton Maître, \n\r Élever le saint drapeau. \n\r Le front haut, le front haut.', 2, 515),
(2215, 'Strophe', 3, 'En avant, en avant! \n\r Garde en toi l\'espoir vivant! \n\r Qu\'on entende sur la route \n\r Malgré tout monter ton chant; \n\r Chasse au loin l\'ombre du doute, \n\r Va d\'un cœur simple et fervent, \n\r En avant, en avant!', 3, 515),
(2216, 'Strophe', 1, 'Oui, Jésus, c\'est vers toi, \n\r Toujours plus haut! \n\r Que s\'élève ma foi, \n\r Toujours plus haut! \n\r Libre de tout lien, \n\r Poursuivant le seul bien, \n\r Mon regard sur le tien, \n\r Toujours plus haut!', 1, 516),
(2217, 'Strophe', 2, 'Tu calmes la douleur, \n\r Toujours plus haut! \n\r Tu satisfais le cœur, \n\r Toujours plus haut! \n\r Das les rudes combats, \n\r Les clameurs d\'ici-bas, \n\r Tu diriges mes pas \n\r Toujours plus haut!', 2, 516),
(2218, 'Strophe', 3, 'Oui, c\'est là mon soupir, \n\r Toujours plus haut! \n\r Tu connais mon désir, \n\r Toujours plus haut! \n\r Perçant l\'obscurité, \n\r Montre-moi ta clarté, \n\r Ta divine beauté, \n\r Toujours plus haut!', 3, 516),
(2219, 'Strophe', 4, 'Marche, me dit Jésus, \n\r Toujours plus haut! \n\r Oh! ne t\'arrête plus, \n\r Toujours plus haut! \n\r Bientôt je reviendrai \n\r Et te délivrerai. \n\r Oui, je t\'introduirai \n\r Chez moi, là-haut!', 4, 516),
(2220, 'Strophe', 1, 'Seigneur dirige tous mes pas \n\r Vers le ciel, ma patrie! \n\r Mon Dieu! Ne me délaisse pas, \n\r Dans ta grâce infinie. \n\r Remplis-moi d\'une sainte ardeur \n\r Pour toi, mon Chef et mon Sauveur; \n\r Toi, mon trésor, et ma vie.', 1, 517),
(2221, 'Strophe', 2, 'Tu sais bien que souvent ma foi \n\r Est faible et languissante. \n\r Saint d\'Israël, protège-moi \n\r Par ta vertu puissante. \n\r Sois mon asile et mon secours: \n\r Daigne en moi signaler toujours \n\r Ta faveur éclatante.', 2, 517),
(2222, 'Strophe', 3, 'Jusqu\'au jour où je te verrai \n\r Dans l\'éternelle gloire, \n\r Où, près de toi, j\'exalterai \n\r Ta mort expiatoire. \n\r Sois ma seule part et mon fort, \n\r Mon gain dans la vie et la mort, \n\r Ma joie et ma victoire.', 3, 517),
(2223, 'Strophe', 1, 'Seigneur, tu cherches tes enfants, \n\r Car tu es l\'amour. \n\r Tu veux unir tous les vivants \n\r Grâce à ton amour!', 1, 518),
(2224, 'Refrain', 1, 'Seigneur, Seigneur, \n\r Oh! Prends en ton Église \n\r Tous nos frères de la terre \n\r Dans un même amour!', 2, 518),
(2225, 'Strophe', 2, 'Seigneur, tu sauves par ta mort, \n\r Car tu es l\'amour. \n\r Fais-nous les membres de ton corps \n\r Grâce à ton amour!', 3, 518),
(2226, 'Refrain', 1, 'Seigneur, Seigneur, \n\r Oh! Prends en ton Église \n\r Tous nos frères de la terre \n\r Dans un même amour!', 4, 518),
(2227, 'Strophe', 3, 'Seigneur, tu calmes notre faim, \n\r Car tu es l\'amour. \n\r Partage à tous le même pain \n\r Grâce à ton amour!', 5, 518),
