(6201, 'Refrain', 1, 'Tu es l\'oasis au milieu du désert, \n\r l\'eau vive qui coule dans les sources amères, \n\r le soleil qui brille au milieu de la nuit, \n\r l\'aurore qui se lève sur nos vies.', 3, 1399),
(6202, 'Strophe', 3, 'Nous sommes une maison pour toi, \n\r un autel pour prier ton nom.', 4, 1399),
(6203, 'Strophe', 1, 'Nous voulons voir Jésus, car on nous a dit \n\r que des millions d\'hommes vivent grâce à lui, \n\r et que même des morts reviennent à la vie. \n\r On entend des chants, des danses, \n\r des échos de l\'espérance. \n\r Nous voulons voir Jésus.', 1, 1400),
(6204, 'Strophe', 2, 'Nous voulons voir Jésus, l\'homme de douleur \n\r qui voyait sa mort sans céder à la peur, \n\r et qui s\'est donné jusqu\'au dernier souffle, \n\r et par son sang versé fait de nous les pardonnés. \n\r Nous voulons voir Jésus.', 2, 1400),
(6205, 'Strophe', 3, 'Nous voulons voir Jésus dans sa sainteté, \n\r lui dont les prophètes nous avaient parlé, \n\r la parole de Dieu, la lumière des hommes. \n\r Il est le témoin fidèle, son amour nous interpelle. \n\r Nous voulons voir Jésus.', 3, 1400),
(6206, 'Refrain', 1, 'Nous avons soif, nous avons faim, \n\r et les autres nous ont tous déçus. \n\r Nous voulons voir Jésus. \n\r Nous voulons voir Jésus.', 4, 1400),
(6207, 'Strophe', 4, 'Nous voulons voir Jésus, \n\r celui qu\'on appelle Prince de la Paix, \n\r le Père éternel. \n\r Il est l\'Admirable, Il étend son règne \n\r car Il est le souverain, sa justice sera sans fin. \n\r Nous voulons voir Jésus.', 5, 1400),
(6208, 'Refrain', 1, 'Nous avons soif, nous avons faim, \n\r et les autres nous ont tous déçus. \n\r Nous voulons voir Jésus. \n\r Nous voulons voir Jésus.', 6, 1400),
(6209, 'Strophe', 5, 'Nous voulons voir Jésus, \n\r car Il nous rappelle que son sacrifice n\'est pas virtuel, \n\r Mais Il vit toujours, réel et tangible, \n\r et l\'espace autour de lui résonne de l\'infini. \n\r Nous voulons voir Jésus.', 7, 1400),
(6210, 'Refrain', 1, 'Nous avons soif, nous avons faim, \n\r et les autres nous ont tous déçus. \n\r Nous voulons voir Jésus. \n\r Nous voulons voir Jésus.', 8, 1400),
