(3358, 'Strophe', 1, 'Louange et gloire à ton nom, alléluia, alléluia! \n\r Seigneur, Dieu de l\'univers, alléluia, alléluia!', 1, 823),
(3359, 'Refrain', 1, 'Gloire à Dieu, gloire à Dieu, \n\r Au plus haut des cieux. \n\r Gloire à Dieu, gloire à Dieu, \n\r Au plus haut des cieux.', 2, 823),
(3360, 'Strophe', 2, 'Venez, chantons notre Dieu, alléluia, alléluia! \n\r C\'est lui notre créateur, alléluia, alléluia!', 3, 823),
(3361, 'Refrain', 1, 'Gloire à Dieu, gloire à Dieu, \n\r Au plus haut des cieux. \n\r Gloire à Dieu, gloire à Dieu, \n\r Au plus haut des cieux.', 4, 823),
(3362, 'Strophe', 3, 'Pour nous, il fit des merveilles, alléluia, alléluia! \n\r Éternel est son amour, alléluia, alléluia!', 5, 823),
(3363, 'Refrain', 1, 'Gloire à Dieu, gloire à Dieu, \n\r Au plus haut des cieux. \n\r Gloire à Dieu, gloire à Dieu, \n\r Au plus haut des cieux.', 6, 823),
