(3760, 'Refrain', 1, 'Celui qui a changé ma vie, oui, c\'est Jésus, Jésus. \n\r Celui qui a changé ma vie, oui, c\'est Jésus, Jésus.', 1, 908),
(3761, 'Strophe', 1, 'Je l\'ai rencontré, lui le ressuscité. \n\r Il a changé ma vie, Il est vivant aujourd\'hui.', 2, 908),
(3762, 'Refrain', 1, 'Celui qui a changé ma vie, oui, c\'est Jésus, Jésus. \n\r Celui qui a changé ma vie, oui, c\'est Jésus, Jésus.', 3, 908),
(3763, 'Strophe', 2, 'Lorsqu\'il reviendra, tout genou fléchira. \n\r Chacun reconnaîtra qu\'il est vivant, le Roi des rois.', 4, 908),
(3764, 'Refrain', 1, 'Celui qui a changé ma vie, oui, c\'est Jésus, Jésus. \n\r Celui qui a changé ma vie, oui, c\'est Jésus, Jésus.', 5, 908),
(3765, 'Strophe', 3, 'Si tu entends sa voix, n\'endurcis pas ton cœur. \n\r Laisse-le te saisir, te pardonner, te guérir.', 6, 908),
(3766, 'Refrain', 1, 'Celui qui a changé ma vie, oui, c\'est Jésus, Jésus. \n\r Celui qui a changé ma vie, oui, c\'est Jésus, Jésus.', 7, 908),
(3767, 'Strophe', 1, 'Tu es mon Roi, Seigneur, \n\r Ta joie remplit mon cœur. \n\r Chaque jour, je te louerai; \n\r Tes desseins sont parfaits.', 1, 909),
(3768, 'Strophe', 2, 'Tu es le seul Seigneur, \n\r Ta loi fait mon bonheur. \n\r Que ton règne s\'établisse, \n\r Ta volonté s\'accomplisse.', 2, 909),
(3769, 'Strophe', 3, 'Gloire, gloire à toi l\'Agneau, \n\r Tu traces un chemin nouveau. \n\r Nous y marchons en vainqueurs, \n\r Nous proclamons ta grandeur.', 3, 909),
(3770, 'Strophe', 4, 'Victoire au Lion de Juda! \n\r Tu es le Tout-puissant. \n\r Victoire au Lion de Juda! \n\r Tu es si merveilleux, \n\r Tu es si merveilleux, \n\r Tu es si merveilleux.', 4, 909),
(3771, 'Refrain', 1, 'Christ en vous l\'espoir de gloire. \n\r Christ en vous l\'espoir de gloire. \n\r Christ en vous l\'espoir de gloire, \n\r Pour l\'éternité.', 1, 910),
(3772, 'Refrain', 1, 'Christ en vous l\'espoir de gloire. \n\r Christ en vous l\'espoir de gloire. \n\r Christ en vous l\'espoir de gloire, \n\r Pour l\'éternité.', 2, 910),
(3773, 'Strophe', 1, 'Jésus, image du Père, \n\r En toi Dieu créa tout l\'univers. \n\r Par toi, il subsiste encore, \n\r Toi, le chef de ce corps que nous formons.', 3, 910),
(3774, 'Refrain', 1, 'Christ en vous l\'espoir de gloire. \n\r Christ en vous l\'espoir de gloire. \n\r Christ en vous l\'espoir de gloire, \n\r Pour l\'éternité.', 4, 910);
INSERT INTO `strophe` (`idStrophe`, `type`, `identifiant`, `texte`, `position`, `idChant`) VALUES
(3775, 'Refrain', 1, 'Christ en vous l\'espoir de gloire. \n\r Christ en vous l\'espoir de gloire. \n\r Christ en vous l\'espoir de gloire, \n\r Pour l\'éternité.', 5, 910),
(3776, 'Strophe', 1, 'Sans ta lumière, Seigneur, que ferions-nous dans ce monde? \n\r Perdus au large, dans le brouillard, au gré des vents.', 1, 911),
