(4664, 'Strophe', 1, 'Sortant de la souche de Jessé, \n\r un rejeton viendra à paraître. \n\r L\'Esprit du Seigneur sera sur lui, \n\r Esprit de vérité, de sagesse.', 1, 1094),
(4665, 'Refrain', 1, 'Il régnera sur toute la terre. \n\r (4x)', 2, 1094),
(4666, 'Strophe', 2, 'Il rassemblera Israël, \n\r les dispersés de Juda. \n\r Il sera un signe pour les nations \n\r qui verront Jésus dans sa gloire.', 3, 1094),
(4667, 'Refrain', 1, 'Il régnera sur toute la terre. \n\r (4x)', 4, 1094),
(4668, 'Strophe', 1, 'Je connais le Créateur du monde. \n\r Ses merveilles et ses bienfaits m\'inondent. \n\r Celui qui sait compter les étoiles, \n\r sait aussi se pencher sur mes pas, \n\r sait aussi se pencher sur mes pas.', 1, 1095),
(4669, 'Strophe', 2, 'Je connais le Rédempteur du monde. \n\r Sa douceur et son amour abondent. \n\r Celui qui se charge de nos peines, \n\r sait aussi nous partager sa joie, \n\r sait aussi nous partager sa joie.', 2, 1095),
(4670, 'Strophe', 3, 'Je connais aussi l\'esprit du monde, \n\r celui qui détruit ce que Dieu sonde. \n\r Mais devant sa force je tiens ferme, \n\r car je sais qu\'il a perdu déjà, \n\r Jésus-Christ a triomphé pour moi.', 3, 1095),
(4671, 'Strophe', 1, 'Je loue ton nom, Éternel; \n\r Seigneur, je célèbre ta bonté. \n\r Quelle joie, tu vis en moi! \n\r Quelle joie, tu viens pour nous sauver.', 1, 1096),
(4672, 'Strophe', 1, 'Je loue ton nom, Éternel; \n\r Seigneur, je célèbre ta bonté. \n\r Quelle joie, tu vis en moi! \n\r Quelle joie, tu viens pour nous sauver.', 2, 1096),
(4673, 'Refrain', 1, 'Tu viens du ciel sur la terre \n\r montrer la voie, \n\r de la terre à la croix \n\r payer pour moi, \n\r de la croix jusqu\'au tombeau, \n\r et du tombeau jusqu\'au ciel. \n\r Je loue ton nom, Éternel.', 3, 1096),
(4674, 'Strophe', 1, 'Je loue ton nom, Éternel; \n\r Seigneur, je célèbre ta bonté. \n\r Quelle joie, tu vis en moi! \n\r Quelle joie, tu viens pour nous sauver.', 4, 1096),
(4675, 'Strophe', 1, 'Je loue ton nom, Éternel; \n\r Seigneur, je célèbre ta bonté. \n\r Quelle joie, tu vis en moi! \n\r Quelle joie, tu viens pour nous sauver.', 5, 1096),
(4676, 'Refrain', 1, 'Tu viens du ciel sur la terre \n\r montrer la voie, \n\r de la terre à la croix \n\r payer pour moi, \n\r de la croix jusqu\'au tombeau, \n\r et du tombeau jusqu\'au ciel. \n\r Je loue ton nom, Éternel.', 6, 1096),
(4677, 'Strophe', 1, 'Seigneur, je sais tant de toi. \n\r J\'ai connu ta force et ta splendeur. \n\r Seigneur, j\'en appelle à toi; \n\r reviens manifester ta grandeur.', 1, 1097),
(4678, 'Refrain', 1, 'Et nous prions, oh nous prions, \n\r reviens toucher nos cœurs. \n\r Viens au fil de nos jours \n\r manifester ta gloire. \n\r Viens au fil de nos jours, \n\r nous révéler ta grâce. \n\r Dans ton amour, \n\r reviens bouleverser nos vies.', 2, 1097),
(4679, 'Refrain', 1, 'Le Dieu de victoire habite en moi. \n\r Le Dieu de victoire habite en moi. \n\r Je marche non par la vue, \n\r mais par la foi. \n\r Le Dieu de victoire habite en moi.', 1, 1098),
(4680, 'Refrain', 1, 'Le Dieu de victoire habite en moi. \n\r Le Dieu de victoire habite en moi. \n\r Je marche non par la vue, \n\r mais par la foi. \n\r Le Dieu de victoire habite en moi.', 2, 1098),
(4681, 'Strophe', 1, 'Je chanterai, et danserai, \n\r car le combat est celui du Seigneur. \n\r La victoire est assurée, \n\r car notre Dieu est vainqueur.', 3, 1098),
(4682, 'Refrain', 1, 'Le Dieu de victoire habite en moi. \n\r Le Dieu de victoire habite en moi. \n\r Je marche non par la vue, \n\r mais par la foi. \n\r Le Dieu de victoire habite en moi.', 4, 1098),
(4683, 'Strophe', 1, 'Merveilleux Seigneur, \n\r merveilleux Sauveur, \n\r nous t\'adorons, \n\r nous t\'adorons, \n\r nous t\'adorons, \n\r merveilleux Seigneur.', 1, 1099),
(4684, 'Strophe', 1, 'Merveilleux Seigneur, \n\r merveilleux Sauveur, \n\r nous t\'adorons, \n\r nous t\'adorons, \n\r nous t\'adorons, \n\r merveilleux Seigneur.', 2, 1099),
(4685, 'Strophe', 1, 'Nous t\'adorons, \n\r nous t\'adorons, \n\r nous t\'adorons, \n\r merveilleux Seigneur.', 3, 1099);
INSERT INTO `strophe` (`idStrophe`, `type`, `identifiant`, `texte`, `position`, `idChant`) VALUES
(4686, 'Strophe', 1, 'Nous t\'adorons, \n\r nous t\'adorons, \n\r nous t\'adorons, \n\r merveilleux Seigneur, \n\r merveilleux Seigneur, \n\r merveilleux Seigneur.', 4, 1099),
(4687, 'Strophe', 1, 'Mon cœur s\'incline devant toi \n\r pour te louer, t\'adorer, \n\r tout-puissant Prince de paix. \n\r Tu es mon Roi. \n\r Assis au ciel avec toi, \n\r je te contemple, Éternel, \n\r parfait dans toutes tes voies, \n\r tu es mon Roi, tu es mon Roi.', 1, 1100),
