INSERT INTO `strophe` (`idStrophe`, `type`, `identifiant`, `texte`, `position`, `idChant`) VALUES
(5259, 'Refrain', 1, 'Ouvre les yeux de mon cœur, \n\r ouvre les yeux de mon cœur. \n\r Je désire te voir, \n\r je désire te voir.', 4, 1214),
(5260, 'Refrain', 1, 'Ouvre les yeux de mon cœur, \n\r ouvre les yeux de mon cœur. \n\r Je désire te voir, \n\r je désire te voir \n\r élevé au plus haut des cieux, \n\r rayonnant de l\'éclat de ta gloire.', 5, 1214),
(5261, 'Refrain', 1, 'Répands ton amour, ta force, \n\r alors que nous chantons: «Tu es saint.» \n\r   \n\r Tu es saint, tu es saint, (3x) \n\r   \n\r je désire te voir.', 6, 1214),
(5262, 'Strophe', 1, 'Parcourant les monts et les mers, \n\r ton amour vient comme une rivière \n\r et je t\'ouvre tout grand mon cœur; \n\r viens me guérir, ô mon Sauveur.', 1, 1215),
(5263, 'Strophe', 2, 'Ta vérité me réjouit \n\r et chaque jour je te bénis; \n\r je chanterai sans fin \n\r l\'amour de Dieu pour moi.', 2, 1215),
(5264, 'Refrain', 1, 'Je passerai ma vie à chanter, \n\r oui, chanter ton amour à jamais. \n\r (2x)', 3, 1215),
(5265, 'Strophe', 3, 'J\'ai envie de danser, \n\r c\'est un peu fou, je sais, \n\r mais ceux qui verront la lumière \n\r danseront de joie comme nous sur terre.', 4, 1215),
(5266, 'Refrain', 1, 'Je passerai ma vie à chanter, \n\r oui, chanter ton amour à jamais. \n\r (2x)', 5, 1215),
(5267, 'Strophe', 1, 'Pour l\'amour de Sion, \n\r je ne me tairai point. \n\r Je ne me tairai point \n\r pour l\'amour de Jérusalem. \n\r Je n\'aurai point de repos.', 1, 1216),
(5268, 'Strophe', 1, 'Je n\'aurai point de repos \n\r jusqu\'à ce que son salut \n\r paraisse comme l\'aurore. \n\r Je ne me tairai point \n\r et que sa délivrance \n\r s\'allume comme un flambeau. \n\r Je n\'aurai point de repos.', 2, 1216),
(5269, 'Strophe', 1, 'Préparez pour le Seigneur \n\r un chemin dans le désert. \n\r Il est au-dessus de tous les rois. \n\r Il est vainqueur par sa mort sur la croix.', 1, 1217),
(5270, 'Refrain', 1, 'Ton règne vienne, Seigneur, \n\r sois élevé, Seigneur tout-puissant. \n\r À toi l\'honneur, la gloire et la majesté! \n\r Que ta gloire soit parmi nous.', 2, 1217),
(5271, 'Strophe', 2, 'Marchons pour notre Seigneur, \n\r car il s\'est choisi un peuple \n\r qui régnera toujours avec lui. \n\r Préparons nos cœurs, nos vies pour le suivre.', 3, 1217),
(5272, 'Refrain', 1, 'Ton règne vienne, Seigneur, \n\r sois élevé, Seigneur tout-puissant. \n\r À toi l\'honneur, la gloire et la majesté! \n\r Que ta gloire soit parmi nous.', 4, 1217),
(5273, 'Refrain', 1, 'Que Dieu se lève, \n\r que Dieu se lève. \n\r (2x)', 1, 1218),
(5274, 'Strophe', 1, 'Ses ennemis sont dispersés, \n\r les justes exultent de joie. \n\r Qu\'ils se réjouissent avec toi, \n\r Dieu a triomphé puissamment.', 2, 1218),
(5275, 'Refrain', 1, 'Que Dieu se lève, \n\r que Dieu se lève. \n\r (2x)', 3, 1218),
(5276, 'Strophe', 1, 'Que ferais-je pour l\'Éternel \n\r en retour de toutes ses bontés? \n\r Que ferais-je pour l\'Éternel \n\r en retour de toutes ses bontés?', 1, 1219),
(5277, 'Refrain', 1, 'J\'élèverai la coupe du salut, \n\r j\'invoquerai le nom d\'Adonaï. \n\r J\'élèverai la coupe du salut, \n\r je bénirai le nom du Seigneur.', 2, 1219),
(5278, 'Refrain', 1, 'Que ta grâce coule sur moi, \n\r que ton onction m\'envahisse. \n\r Jésus, mon Roi, \n\r je viens à toi, j\'ai besoin de toi. \n\r (2x)', 1, 1220),
(5279, 'Strophe', 1, 'Envoie ton réveil dans ma vie, \n\r envoie ton réveil dans ma vie. \n\r Le feu de ton Esprit me purifie, \n\r le feu de ton Esprit me purifie. \n\r (2x)', 2, 1220),
(5280, 'Strophe', 1, 'Envoie ton réveil, \n\r envoie ton réveil, \n\r envoie ton réveil, \n\r envoie ton réveil dans ma vie, \n\r dans ma vie, dans ma vie.', 3, 1220),
(5281, 'Strophe', 1, 'Dans ta douceur, viens guérir \n\r nos cœurs étouffés par l\'amertume. \n\r Dans ta bonté, viens réveiller \n\r l\'amour éteint par l\'habitude. \n\r Par ton Esprit, reconstruis \n\r un peuple debout pour la vie.', 1, 1221),
(5282, 'Refrain', 1, 'Que nous soyons ensemble un signe, \n\r pour que ton règne s\'établisse. \n\r Que tous soient un comme tu es un, \n\r pour que le monde voie le chemin \n\r qui mène au Père.', 2, 1221),
(5283, 'Strophe', 2, 'Dans nos pensées, viens déposer \n\r la passion de la vérité, \n\r et que ta grâce, dans nos regards, \n\r renforce les liens étiolés. \n\r Par ton amour, rassemble-nous \n\r pour hâter ton retour.', 3, 1221),
(5284, 'Refrain', 1, 'Que nous soyons ensemble un signe, \n\r pour que ton règne s\'établisse. \n\r Que tous soient un comme tu es un, \n\r pour que le monde voie le chemin \n\r qui mène au Père.', 4, 1221),
(5285, 'Strophe', 3, 'De ton Esprit, viens nous couvrir, \n\r en chacun te révéler. \n\r Dans ta compassion, viens et réponds \n\r aux soupirs de ta bien-aimée. \n\r Écoute-nous, nous voulons \n\r ta gloire au milieu des nations.', 5, 1221),
(5286, 'Refrain', 1, 'Que nous soyons ensemble un signe, \n\r pour que ton règne s\'établisse. \n\r Que tous soient un comme tu es un, \n\r pour que le monde voie le chemin \n\r qui mène au Père.', 6, 1221),
(5287, 'Strophe', 1, 'Que le Seigneur te bénisse et te garde, \n\r qu\'il fasse luire sa face sur toi, \n\r qu\'il fasse briller sa lumière en toi.', 1, 1222),
(5288, 'Strophe', 2, 'Que le Seigneur te bénisse et te garde, \n\r qu\'il t\'accorde sa paix, \n\r qu\'il t\'accorde sa grâce, \n\r lui, le Dieu d\'Israël, \n\r lui, le Dieu d\'Israël.', 2, 1222),
(5289, 'Strophe', 1, 'Que le Seigneur te bénisse et te garde, \n\r qu\'il fasse luire sa face sur toi, \n\r qu\'il fasse briller sa lumière en toi.', 3, 1222),
(5290, 'Strophe', 2, 'Que le Seigneur te bénisse et te garde, \n\r qu\'il t\'accorde sa paix, \n\r qu\'il t\'accorde sa grâce, \n\r lui, le Dieu d\'Israël, \n\r lui, le grand Dieu d\'Israël.', 4, 1222),
(5291, 'Strophe', 1, 'Qu\'il soit béni, celui qui vient \n\r au nom du Seigneur. \n\r (4x) \n\r   \n\r Alléluia.', 1, 1223),
(5292, 'Strophe', 2, 'Baruch haba, beshem Adonaï. \n\r Haleluyah. \n\r (2x) \n\r   \n\r Baruch haba, beshem Adonaï. \n\r (2x) \n\r   \n\r Haleluyah.', 2, 1223),
(5293, 'Strophe', 3, 'Il est béni, celui qui vient \n\r au nom du Seigneur. \n\r (4x) \n\r   \n\r Alléluia.', 3, 1223),
(5294, 'Refrain', 1, 'Qui est comparable à l\'Éternel, notre Dieu? \n\r Il a sa demeure dans les lieux très hauts. \n\r Qui est comparable à l\'Éternel, notre Dieu? \n\r Il a sa demeure dans les lieux très hauts, \n\r mais il s\'abaisse pour voir le ciel et la terre.', 1, 1224),
(5295, 'Strophe', 1, 'Il arrache à la poussière l\'homme misérable. \n\r Il relève l\'indigent pour le faire asseoir \n\r parmi les notables.', 2, 1224),
(5296, 'Refrain', 1, 'Qui est comparable à l\'Éternel, notre Dieu? \n\r Il a sa demeure dans les lieux très hauts. \n\r Qui est comparable à l\'Éternel, notre Dieu? \n\r Il a sa demeure dans les lieux très hauts, \n\r mais il s\'abaisse pour voir le ciel et la terre.', 3, 1224),
(5297, 'Strophe', 2, 'Il installe en sa maison la femme stérile, \n\r afin qu\'elle puisse y connaître \n\r la joie d\'être mère de nombreux enfants.', 4, 1224),
(5298, 'Refrain', 1, 'Qui est comparable à l\'Éternel, notre Dieu? \n\r Il a sa demeure dans les lieux très hauts. \n\r Qui est comparable à l\'Éternel, notre Dieu? \n\r Il a sa demeure dans les lieux très hauts, \n\r mais il s\'abaisse pour voir le ciel et la terre.', 5, 1224),
(5299, 'Refrain', 1, 'Reçois ma vie comme une adoration, \n\r reçois mon cœur comme un cadeau d\'amour; \n\r je n\'ai rien d\'autre à t\'offrir \n\r que ce sacrifice vivant. \n\r Je te donne ma vie pour toujours.', 1, 1225),
(5300, 'Strophe', 1, 'J\'abandonne sur ton autel, \n\r en réponse à ton appel, \n\r mes visions, mes ambitions, \n\r car tu es ma vie, ma passion. \n\r À tes pieds, émerveillé, \n\r je contemple ta majesté; \n\r je te donne sans compromis \n\r ce parfum de très grand prix.', 2, 1225),
(5301, 'Refrain', 1, 'Reçois ma vie comme une adoration, \n\r reçois mon cœur comme un cadeau d\'amour; \n\r je n\'ai rien d\'autre à t\'offrir \n\r que ce sacrifice vivant. \n\r Je te donne ma vie pour toujours.', 3, 1225),
(5302, 'Refrain', 1, 'Shalom, shalom, paix pour toi, \n\r shalom, shalom, paix sur toi, \n\r shalom, shalom, paix en toi, \n\r la paix de Jésus avec toi.', 1, 1226),
(5303, 'Refrain', 1, 'Shalom, shalom, paix pour toi, \n\r shalom, shalom, paix sur toi, \n\r shalom, shalom, paix en toi, \n\r la paix de Jésus avec toi.', 2, 1226),
(5304, 'Strophe', 1, 'Et qu\'en tous lieux, toutes circonstances, \n\r oui, qu\'en tous temps, à chaque instant, \n\r dans la souffrance, dans la joie, \n\r que la paix soit avec toi!', 3, 1226),
(5305, 'Refrain', 1, 'Shalom, shalom, paix pour toi, \n\r shalom, shalom, paix sur toi, \n\r shalom, shalom, paix en toi, \n\r la paix de Jésus avec toi.', 4, 1226),
(5306, 'Refrain', 1, 'Shalom, shalom, paix pour toi, \n\r shalom, shalom, paix sur toi, \n\r shalom, shalom, paix en toi, \n\r la paix de Jésus avec toi.', 5, 1226),
(5307, 'Refrain', 1, '«Saint, saint, saint est l\'Éternel des armées. \n\r Toute la terre est remplie de sa gloire.» \n\r (2x)', 1, 1227),
(5308, 'Strophe', 1, 'L\'Éternel se tient sur un trône élevé, \n\r son manteau de gloire \n\r remplit sa maison, \n\r des séraphins ne cessent de crier:', 2, 1227),
(5309, 'Refrain', 1, '«Saint, saint, saint est l\'Éternel des armées. \n\r Toute la terre est remplie de sa gloire.» \n\r (2x)', 3, 1227),
(5310, 'Strophe', 2, 'Et les fondations tremblent \n\r au son de cette voix, \n\r toute la maison se remplit de fumée, \n\r je tombe à genoux, \n\r je ne peux que crier:', 4, 1227),
(5311, 'Refrain', 1, '«Saint, saint, saint est l\'Éternel des armées. \n\r Toute la terre est remplie de sa gloire.» \n\r (2x)', 5, 1227),
(5312, 'Strophe', 3, 'L\'Éternel me dit: \n\r «Qui pourrai-je envoyer? \n\r Qui se lèvera et marchera pour nous?» \n\r Je dis: «Me voici, partout je chanterai:»', 6, 1227),
(5313, 'Refrain', 1, '«Saint, saint, saint est l\'Éternel des armées. \n\r Toute la terre est remplie de sa gloire.» \n\r (2x)', 7, 1227),
(5314, 'Refrain', 1, 'O Herr, giesse Ströme des lebendigen Wassers aus, \n\r o Herr, über uns. \n\r O Herr, giesse neu die Kraft des Heiligen Geistes aus, \n\r o Herr, über uns.', 1, 1228),
(5315, 'Strophe', 1, 'Sieh\' unser dürres Land, hör\' unser Schrei\'n. \n\r Nur deine starke Hand kann uns befrei\'n. \n\r Lass Wasser fliessen Herr, von Deinem Thron, \n\r giess\'aus in unser Land den Lebenstrom.', 2, 1228),
(5316, 'Refrain', 1, 'Seigneur, fais couler tes fleuves d\'eau \n\r sur tous tes enfants, \n\r Seigneur, sur nous. \n\r Seigneur, fais couler en nous \n\r la force de ton Esprit saint, \n\r Seigneur, sur nous.', 3, 1228),
(5317, 'Strophe', 2, 'Vois ce pays aride, écoute nos cris. \n\r Ta main seule, ô Seigneur, peut nous libérer. \n\r Que les sources d\'eau limpides, \n\r coulant de ton trône, \n\r inondent ce pays des flots de ta vie.', 4, 1228),
(5318, 'Refrain', 1, 'Seigneur, fais couler tes fleuves d\'eau \n\r sur tous tes enfants, \n\r Seigneur, sur nous. \n\r Seigneur, fais couler en nous \n\r la force de ton Esprit saint, \n\r Seigneur, sur nous.', 5, 1228),
(5319, 'Strophe', 1, 'Seigneur, ce dont j\'ai besoin, \n\r c\'est d\'un cœur brisé. \n\r Seigneur, ce dont j\'ai besoin, \n\r c\'est d\'un cœur brisé et contrit.', 1, 1229),
(5320, 'Strophe', 2, 'Donne-moi un cœur tout simple \n\r qui ne soit pas divisé, \n\r qui ne s\'enfle pas de connaissance, \n\r mais s\'attache à ta présence.', 2, 1229),
(5321, 'Strophe', 3, 'Donne-moi un cœur tout simple \n\r qui ne soit pas divisé, \n\r qui ne s\'enfle pas d\'orgueil, \n\r mais qui vit simplement près de toi.', 3, 1229),
(5322, 'Refrain', 1, 'Si le Seigneur combat pour nous, \n\r qui se lèvera contre nous? \n\r Nos vies entières sont dans sa main. \n\r Ne soyons pas découragés, \n\r Il est là pour nous protéger, \n\r pour nous conduire sur le chemin.', 1, 1230),
(5323, 'Strophe', 1, 'Parce que les temps sont mauvais, \n\r nous devons vivre désormais \n\r en nous plaçant sous le sang de Jésus. \n\r C\'est Yéshua notre salut.', 2, 1230),
(5324, 'Refrain', 1, 'Si le Seigneur combat pour nous, \n\r qui se lèvera contre nous? \n\r Nos vies entières sont dans sa main. \n\r Ne soyons pas découragés, \n\r Il est là pour nous protéger, \n\r pour nous conduire sur le chemin.', 3, 1230),
(5325, 'Strophe', 2, 'Le Seigneur nous rassemblera, \n\r à ses bien-aimés il dira: \n\r «Recevez le royaume en héritage, \n\r préparé pour les vierges sages.»', 4, 1230),
(5326, 'Refrain', 1, 'Si le Seigneur combat pour nous, \n\r qui se lèvera contre nous? \n\r Nos vies entières sont dans sa main. \n\r Ne soyons pas découragés, \n\r Il est là pour nous protéger, \n\r pour nous conduire sur le chemin.', 5, 1230),
(5327, 'Strophe', 1, 'Louez Dieu, car il est bon, \n\r son amour dure à toujours. \n\r Louez le Dieu du pardon, \n\r son amour dure à toujours.', 1, 1231),
(5328, 'Strophe', 1, 'Louez le Dieu juste et saint, \n\r son amour dure à toujours. \n\r Louez le Dieu souverain, \n\r son amour dure à toujours.', 2, 1231),
(5329, 'Refrain', 1, 'Son amour calme la frayeur, \n\r son amour guérit la douleur. \n\r Oui, son amour chasse nos erreurs. \n\r Dieu nous offre la grâce de son amour.', 3, 1231),
(5330, 'Refrain', 1, 'Son amour réveille en douceur, \n\r son amour touche avec ardeur. \n\r Oui, son amour embrase nos cœurs. \n\r Dieu nous tient sous les ailes de son amour.', 4, 1231),
(5331, 'Strophe', 2, 'Louez le Père éternel, \n\r son amour dure à toujours. \n\r Créateur de l\'univers, \n\r son amour dure à toujours.', 5, 1231),
(5332, 'Strophe', 2, 'Louez ce sublime artiste, \n\r son amour dure à toujours. \n\r Il est le Dieu des prodiges, \n\r son amour dure à toujours.', 6, 1231),
(5333, 'Refrain', 1, 'Son amour calme la frayeur, \n\r son amour guérit la douleur. \n\r Oui, son amour chasse nos erreurs. \n\r Dieu nous offre la grâce de son amour.', 7, 1231),
(5334, 'Refrain', 1, 'Son amour réveille en douceur, \n\r son amour touche avec ardeur. \n\r Oui, son amour embrase nos cœurs. \n\r Dieu nous tient sous les ailes de son amour.', 8, 1231),
(5335, 'Strophe', 3, 'Louez le Dieu tout-puissant, \n\r son amour dure à toujours. \n\r Il prend soin de ses enfants, \n\r son amour dure à toujours.', 9, 1231),
(5336, 'Strophe', 3, 'Louez le Dieu de victoire, \n\r son amour dure à toujours. \n\r À lui seul rendons la gloire, \n\r son amour dure à toujours.', 10, 1231),
(5337, 'Refrain', 1, 'Son amour calme la frayeur, \n\r son amour guérit la douleur. \n\r Oui, son amour chasse nos erreurs. \n\r Dieu nous offre la grâce de son amour.', 11, 1231),
(5338, 'Refrain', 1, 'Son amour réveille en douceur, \n\r son amour touche avec ardeur. \n\r Oui, son amour embrase nos cœurs. \n\r Dieu nous tient sous les ailes de son amour.', 12, 1231),
(5339, 'Refrain', 1, 'Dieu nous offre la grâce de son amour. \n\r Son Esprit nous remplit de son amour. \n\r Louez-le!', 13, 1231),
(5340, 'Refrain', 1, 'Ton Dieu ordonne que tu sois puissant et fort. \n\r Ton Dieu te donne autorité, \n\r autorité sur les puissances de la mort. \n\r Ton Dieu te donne sa victoire. \n\r Ton Dieu ordonne que tu sois puissant et fort. \n\r Ton Dieu te donne son onction, \n\r ton Dieu te donne sa force.', 1, 1232),
(5341, 'Strophe', 1, 'Le Seigneur est présent, \n\r Il s\'avance au combat; \n\r son armée, fortifiée, \n\r lance des cris de victoire. \n\r L\'ennemi, terrassé, \n\r restitue ce qu\'il a volé.', 2, 1232),
(5342, 'Refrain', 1, 'Ton Dieu ordonne que tu sois puissant et fort. \n\r Ton Dieu te donne autorité, \n\r autorité sur les puissances de la mort. \n\r Ton Dieu te donne sa victoire. \n\r Ton Dieu ordonne que tu sois puissant et fort. \n\r Ton Dieu te donne son onction, \n\r ton Dieu te donne sa force.', 3, 1232),
(5343, 'Strophe', 2, 'Toute la gloire et l\'honneur \n\r sont pour notre Seigneur, \n\r le Seigneur glorieux, le Dieu majestueux! \n\r Cris et chants victorieux \n\r montent jusqu\'au plus haut des cieux!', 4, 1232),
(5344, 'Strophe', 1, 'Sur tes murs, Jérusalem, \n\r j\'ai placé des sentinelles. \n\r Elles ne se tairont jamais, \n\r ni le jour ni la nuit. \n\r On n\'te nommera plus la délaissée, \n\r ni ta terre la dévastée. \n\r Tu porteras un nom nouveau: \n\r Mon plaisir est en elle!', 1, 1233),
(5345, 'Refrain', 1, 'Pour l\'amour de Sion, je n\'me tairai pas! \n\r Pour l\'amour de Jérusalem, point de repos! \n\r Pour que son salut brille comme l\'aurore \n\r et sa délivrance comme un flambeau.', 2, 1233),
(5346, 'Strophe', 2, 'Toutes les nations de la terre \n\r regarderont ta justice \n\r et les rois contempleront \n\r la gloire de ton nom. \n\r Tu seras comme un diadème, \n\r scintillant dans la main de Dieu, \n\r comme une couronne royale \n\r préparée pour la fête.', 3, 1233),
(5347, 'Refrain', 1, 'Pour l\'amour de Sion, je n\'me tairai pas! \n\r Pour l\'amour de Jérusalem, point de repos! \n\r Pour que son salut brille comme l\'aurore \n\r et sa délivrance comme un flambeau, \n\r comme un jeune homme s\'unit à sa fiancée, \n\r tous tes enfants s\'uniront à leur Dieu.', 4, 1233),
(5348, 'Refrain', 1, 'Pour l\'amour de Sion, je n\'me tairai pas! \n\r Pour l\'amour de Jérusalem, point de repos! \n\r Pour que son salut brille comme l\'aurore \n\r et sa délivrance comme un flambeau.', 5, 1233),
(5349, 'Refrain', 1, 'Toi, mon Seigneur, \n\r tu es mon guide chaque jour. \n\r Toi, mon Jésus, \n\r tu es la raison de ma vie. \n\r Toi, mon Seigneur, \n\r mon ami le plus précieux, \n\r toi, mon Jésus, \n\r tu es la raison de ma vie.', 1, 1234),
(5350, 'Strophe', 1, 'Quand mon chemin s\'éloigne du tien, \n\r je suis perdu, désemparé. \n\r À chaque fois, toi, tu es là, \n\r tu tends les bras pour me relever.', 2, 1234),
(5351, 'Refrain', 1, 'Toi, mon Seigneur, \n\r tu es mon guide chaque jour. \n\r Toi, mon Jésus, \n\r tu es la raison de ma vie. \n\r Toi, mon Seigneur, \n\r mon ami le plus précieux, \n\r toi, mon Jésus, \n\r tu es la raison de ma vie.', 3, 1234),
(5352, 'Strophe', 2, 'Mon cœur troublé se met à douter, \n\r je cherche en toi ma sécurité. \n\r Mes yeux sur toi vont se fixer, \n\r tu tends les bras pour me libérer.', 4, 1234),
(5353, 'Refrain', 1, 'Toi, mon Seigneur, \n\r tu es mon guide chaque jour. \n\r Toi, mon Jésus, \n\r tu es la raison de ma vie. \n\r Toi, mon Seigneur, \n\r mon ami le plus précieux, \n\r toi, mon Jésus, \n\r tu es la raison de ma vie.', 5, 1234),
(5354, 'Strophe', 1, 'Je viens dans ta présence, \n\r Seigneur de l\'univers. \n\r Rien n\'a plus d\'importance, \n\r rien n\'a plus de valeur. \n\r Je viens chercher ton cœur, \n\r trouver ta volonté, \n\r me tenir prosterné \n\r en chantant tes louanges.', 1, 1235),
(5355, 'Refrain', 1, 'Ton nom est grand, ton nom est saint, \n\r ton nom est digne de louange. \n\r À toi l\'honneur, à toi la gloire, \n\r à toi la puissance et la force, \n\r Jésus, Agneau de Dieu.', 2, 1235),
(5356, 'Strophe', 2, 'Je viens dans ta présence \n\r déposer mes fardeaux, \n\r tout ce qui me retient \n\r de m\'approcher de toi. \n\r Tu es mon bouclier, mon abri, mon seul bien; \n\r vers toi, Seigneur mon Dieu, \n\r je veux lever les mains.', 3, 1235),
(5357, 'Refrain', 1, 'Ton nom est grand, ton nom est saint, \n\r ton nom est digne de louange. \n\r À toi l\'honneur, à toi la gloire, \n\r à toi la puissance et la force, \n\r Jésus, Agneau de Dieu.', 4, 1235),
(5358, 'Strophe', 1, 'Toute la terre est dans la joie, \n\r le Seigneur l\'a créée de ses mains; \n\r les arbres et même les montagnes \n\r chantent pour le Roi des rois. \n\r Ses œuvres comblent ma joie, \n\r car il a tout créé de ses mains, \n\r l\'Éternel, Dieu d\'Israël; \n\r pour lui j\'élève ma voix.', 1, 1236),
(5359, 'Refrain', 1, 'Je voudrais te dire: \n\r «Ta grandeur touche mon cœur. \n\r Comment puis-je décrire \n\r tes merveilles et ta splendeur?» \n\r (2x)', 2, 1236),
(5360, 'Strophe', 1, 'Nul n\'est comme toi, Dieu puissant éternel, \n\r tu nous as consolés, \n\r tu nous as relevés. \n\r Ton amour est si fort pour restaurer, sauver.', 1, 1237),
(5361, 'Refrain', 1, 'Tu es merveilleux dans tout ce que tu fais. \n\r (2x)', 2, 1237),
(5362, 'Strophe', 2, 'Nul n\'est comme toi, un père et un ami \n\r si tendre et si patient. \n\r Tu corriges et conduis, \n\r toujours à nos côtés, toujours pour nous aider.', 3, 1237),
(5363, 'Refrain', 1, 'Tu es merveilleux dans tout ce que tu fais. \n\r (2x)', 4, 1237),
(5364, 'Strophe', 3, 'Nul n\'est comme toi, Dieu d\'amour et de paix, \n\r justice et vérité. \n\r En toi tout est parfait, \n\r lumière et sainteté, splendeur et majesté.', 5, 1237),
(5365, 'Refrain', 1, 'Tu es merveilleux dans tout ce que tu fais. \n\r (2x)', 6, 1237),
(5366, 'Strophe', 4, 'Oh! tu es merveilleux dans tout ce que tu es, \n\r mon père et mon Sauveur, \n\r mon ami, mon Seigneur;', 7, 1237),
(5367, 'Refrain', 2, 'Tu es merveilleux dans tout ce que tu es. \n\r (2x)', 8, 1237),
(5368, 'Strophe', 1, 'Tu m\'as choisi, je t\'ai reçu. \n\r Tu m\'as aimé, je t\'ai aimé. \n\r Tu m\'as donné, j\'ai accepté ta vie. \n\r Je suis tombé, tu m\'as relevé, \n\r tu as effacé tout mon passé, \n\r tu m\'as pardonné, tu as transformé ma vie.', 1, 1238),
(5369, 'Refrain', 1, 'Encore et encore, \n\r encore à chaque instant, \n\r tant que je respire, \n\r je te choisirai encore à chaque instant, \n\r tant que je respire, \n\r je t\'adorerai.', 2, 1238),
(5370, 'Strophe', 1, 'Hodu la\'Adonaï ki tov, \n\r ki leolam rasdo. \n\r (2x) \n\r   \n\r Hodu, hodu, hodu, hodu, hodu \n\r la\'Adonaï ki tov. \n\r (2x)', 1, 1239),
(5371, 'Strophe', 2, 'Venez louer le Seigneur, \n\r car sa bonté est grande. \n\r Venez louer le Roi des rois \n\r qui règne sur le monde. \n\r   \n\r Louez son nom, louez son nom, \n\r car sa bonté est grande. \n\r (2x)', 2, 1239),
(5372, 'Refrain', 1, 'Viens, ne tarde plus, adore. \n\r Viens, ne tarde plus, donne ton cœur. \n\r Viens tel que tu es, adore. \n\r Viens tel que tu es devant ton Dieu. \n\r Viens.', 1, 1240),
(5373, 'Strophe', 1, 'Un jour, toute langue dira: «Tu es Dieu.» \n\r Un jour, tout genou fléchira. \n\r Mais le vrai trésor est pour ceux \n\r qui t\'ont choisi dès aujourd\'hui.', 2, 1240),
(5374, 'Refrain', 1, 'Viens, ne tarde plus, adore. \n\r Viens, ne tarde plus, donne ton cœur. \n\r Viens tel que tu es, adore. \n\r Viens tel que tu es devant ton Dieu. \n\r Viens.', 3, 1240),
(5375, 'Strophe', 1, 'Nous avons soif de ta présence, \n\r ô Saint-Esprit. \n\r Viens, souffle sur nos vies! \n\r Embrase nos cœurs, ô Seigneur! \n\r Aide-nous à discerner ta volonté!', 1, 1241),
(5376, 'Refrain', 1, 'Viens, viens Esprit de Dieu, \n\r nous t\'invitons, revêts-nous, \n\r remplis-nous de ta sainte présence! \n\r Qu\'au travers nous tu puisses agir \n\r avec puissance pour confirmer \n\r les paroles que nous allons annoncer!', 2, 1241),
(5377, 'Strophe', 2, 'Nous avons soif de ta présence, \n\r ô Saint-Esprit. \n\r Viens, souffle sur nos vies! \n\r Purifie nos cœurs, ô Seigneur! \n\r Aide-nous à toujours plus te ressembler!', 3, 1241),
(5378, 'Refrain', 1, 'Viens, viens Esprit de Dieu, \n\r nous t\'invitons, revêts-nous, \n\r remplis-nous de ta sainte présence! \n\r Qu\'au travers nous tu puisses agir \n\r avec puissance pour confirmer \n\r les paroles que nous allons annoncer!', 4, 1241),
(5379, 'Strophe', 3, 'Nous avons soif de ton réveil, \n\r ô Saint-Esprit. \n\r Viens sur notre pays! \n\r Nous voulons voir des milliers d\'hommes \n\r chanter ensemble: «Jésus est Seigneur!»', 5, 1241),
(5380, 'Refrain', 1, 'Viens, viens Esprit de Dieu, \n\r nous t\'invitons, revêts-nous, \n\r remplis-nous de ta sainte présence! \n\r Qu\'au travers nous tu puisses agir \n\r avec puissance pour confirmer \n\r les paroles que nous allons annoncer!', 6, 1241),
(5381, 'Refrain', 1, 'Admirable est ton conseil, \n\r si grande est ta sagesse; \n\r tu sièges dans les hauteurs \n\r au-dessus de l\'univers. \n\r Que tes actions sont grandioses \n\r et tes pensées profondes! \n\r Tu es le Dieu tout-puissant, \n\r infiniment grand.', 1, 1242),
(5382, 'Strophe', 1, 'Dans mon esprit, ton nom me remplit. \n\r Père éternel, Roi bienveillant, Dieu qui entend, \n\r toi seul es l\'Éternel Dieu, \n\r Seigneur et maître des cieux.', 2, 1242),
(5383, 'Refrain', 1, 'Admirable est ton conseil, \n\r si grande est ta sagesse; \n\r tu sièges dans les hauteurs \n\r au-dessus de l\'univers. \n\r Que tes actions sont grandioses \n\r et tes pensées profondes! \n\r Tu es le Dieu tout-puissant, \n\r infiniment grand.', 3, 1242),
(5384, 'Strophe', 2, 'Tu es parfait dans tous tes aspects: \n\r Fidélité, miséricorde, \n\r justice et paix, \n\r patience, immense bonté, \n\r Sauveur de l\'humanité.', 4, 1242),
(5385, 'Refrain', 1, 'Admirable est ton conseil, \n\r si grande est ta sagesse; \n\r tu sièges dans les hauteurs \n\r au-dessus de l\'univers. \n\r Que tes actions sont grandioses \n\r et tes pensées profondes! \n\r Tu es le Dieu tout-puissant, \n\r infiniment grand.', 5, 1242),
(5386, 'Refrain', 1, 'Acclamez l\'Éternel, bénissez son nom, \n\r le grand Dieu d\'Israël, puissant en actions! \n\r Que tout ce qui respire loue le Seigneur! \n\r Levons-nous pour offrir l\'élan de nos cœurs!', 1, 1243),
(5387, 'Strophe', 1, 'Chantons un cantique nouveau! \n\r Portes, élevez vos linteaux! \n\r Qu\'apparaisse le Roi de gloire, \n\r Le Seigneur, Dieu de victoire!', 2, 1243),
(5388, 'Refrain', 1, 'Acclamez l\'Éternel, bénissez son nom, \n\r le grand Dieu d\'Israël, puissant en actions! \n\r Que tout ce qui respire loue le Seigneur! \n\r Levons-nous pour offrir l\'élan de nos cœurs!', 3, 1243),
(5389, 'Strophe', 2, 'Qui s\'élèvera vers lui? \n\r Celui qui trouve un abri \n\r dans la force de l\'Éternel, \n\r l\'amour du Dieu d\'Israël.', 4, 1243),
(5390, 'Refrain', 1, 'Acclamez l\'Éternel, bénissez son nom, \n\r le grand Dieu d\'Israël, puissant en actions! \n\r Que tout ce qui respire loue le Seigneur! \n\r Levons-nous pour offrir l\'élan de nos cœurs!', 5, 1243),
(5391, 'Strophe', 3, 'Reconnaissons qu\'Il est Dieu, \n\r Roi de la terre et des cieux! \n\r Bénissons le souverain, \n\r nous, l\'ouvrage de ses mains.', 6, 1243),
(5392, 'Refrain', 1, 'Acclamez l\'Éternel, bénissez son nom, \n\r le grand Dieu d\'Israël, puissant en actions! \n\r Que tout ce qui respire loue le Seigneur! \n\r Levons-nous pour offrir l\'élan de nos cœurs!', 7, 1243),
(5393, 'Strophe', 4, 'Que tout ce qui respire loue le Seigneur! \n\r Levons-nous pour offrir l\'élan de nos cœurs!', 8, 1243),
(5394, 'Strophe', 1, 'Alléluia, alléluia, \n\r Gloire à Toi Eternel, Dieu puissant.', 1, 1244),
(5395, 'Strophe', 2, 'Alléluia, alléluia, \n\r Gloire à Toi Eternel, Dieu puissant.', 2, 1244),
(5396, 'Strophe', 3, 'Allé-luia.  \n\r Tu es saint, trois fois saint, \n\r Dieu tout puissant, Eternel. \n\r Louange à l\'agneau, \n\r Louange à l\'agneau,', 3, 1244),
(5397, 'Strophe', 4, 'Car toi seul est saint, trois fois saint, \n\r Dieu tout puissant, Eternel. \n\r Louange à l\'agneau, \n\r Louange à l\'agneau,', 4, 1244),
(5398, 'Strophe', 5, 'Amen.', 5, 1244),
(5399, 'Refrain', 1, 'Tous les anges louent sa sainteté. \n\r L\'univers crie sa majesté. \n\r Chantons gloire et puissance, \n\r force et louange à l\'Agneau, \n\r à l\'Agneau immolé, \n\r à l\'Agneau, à l\'Agneau immolé.', 1, 1245),
(5400, 'Strophe', 1, 'Les ténèbres ont reculé devant l\'œuvre de la croix, \n\r devant l\'Agneau immolé et le sang versé pour moi.', 2, 1245),
(5401, 'Refrain', 1, 'Tous les anges louent sa sainteté. \n\r L\'univers crie sa majesté. \n\r Chantons gloire et puissance, \n\r force et louange à l\'Agneau, \n\r à l\'Agneau immolé, \n\r à l\'Agneau, à l\'Agneau immolé.', 3, 1245),
(5402, 'Strophe', 2, 'De la vie qu\'Il a donnée à la vie qui brûle en moi, \n\r tant d\'amour et à jamais ma reconnaissance au Roi.', 4, 1245),
(5403, 'Refrain', 1, 'Tous les anges louent sa sainteté. \n\r L\'univers crie sa majesté. \n\r Chantons gloire et puissance, \n\r force et louange à l\'Agneau, \n\r à l\'Agneau immolé, \n\r à l\'Agneau, à l\'Agneau immolé.', 5, 1245),
(5404, 'Refrain', 1, 'Vivant, vivant, puissant, puissant, vivant! \n\r Dieu vivant, puissant, tout-puissant, \n\r Dieu vivant, Dieu tout-puissant, \n\r Dieu vivant, Dieu tout-puissant!', 6, 1245),
(5405, 'Refrain', 1, 'Tous les anges louent sa sainteté. \n\r L\'univers crie sa majesté. \n\r Chantons gloire et puissance, \n\r force et louange à l\'Agneau, \n\r à l\'Agneau immolé, \n\r à l\'Agneau, à l\'Agneau immolé.', 7, 1245),
(5406, 'Refrain', 1, 'À toi l\'honneur, Agneau de Dieu! \n\r Ton corps brisé sur la croix \n\r a porté notre condamnation. \n\r À toi l\'honneur, Agneau de Dieu! \n\r Ton sang versé sur la croix \n\r est le prix de notre rançon.', 1, 1246),
(5407, 'Strophe', 1, 'Roi souffrant pour nos péchés, \n\r tu t\'es chargé de nos fautes. \n\r Qui ne pourrait s\'indigner \n\r de tant de cruauté?', 2, 1246),
(5408, 'Refrain', 1, 'À toi l\'honneur, Agneau de Dieu! \n\r Ton corps brisé sur la croix \n\r a porté notre condamnation. \n\r À toi l\'honneur, Agneau de Dieu! \n\r Ton sang versé sur la croix \n\r est le prix de notre rançon.', 3, 1246),
(5409, 'Strophe', 2, 'Roi méprisé sur la terre, \n\r tu as bu la coupe amère, \n\r comme un brigand transpercé \n\r pour notre liberté.', 4, 1246),
(5410, 'Refrain', 1, 'À toi l\'honneur, Agneau de Dieu! \n\r Ton corps brisé sur la croix \n\r a porté notre condamnation. \n\r À toi l\'honneur, Agneau de Dieu! \n\r Ton sang versé sur la croix \n\r est le prix de notre rançon.', 5, 1246),
(5411, 'Strophe', 3, 'Roi couronné dans les cieux, \n\r tu reviendras victorieux. \n\r Tu as reçu de ton père \n\r les clés de l\'univers.', 6, 1246),
(5412, 'Refrain', 1, 'À toi l\'honneur, Agneau de Dieu! \n\r Ton corps brisé sur la croix \n\r a porté notre condamnation. \n\r À toi l\'honneur, Agneau de Dieu! \n\r Ton sang versé sur la croix \n\r est le prix de notre rançon.', 7, 1246),
(5413, 'Strophe', 1, 'Nos louanges montent vers ta demeure, \n\r Dieu véritable, souverain, roi, libérateur. \n\r Admirable dans tes œuvres, Seigneur, \n\r les cieux racontent la symphonie du Créateur.', 1, 1247),
(5414, 'Refrain', 1, 'Au-dessus de tout tu règnes. \n\r Toutes nos vies t\'appartiennent, \n\r Alpha et Oméga, Roi des rois. \n\r Tous les autres dieux tomberont, \n\r tous les rois se prosterneront \n\r devant ta majesté, ta beauté.', 2, 1247),
(5415, 'Strophe', 2, 'Ta sagesse confond celle des puissants; \n\r par ta parole, tu renouvelles tes enfants. \n\r Tu es maître en tout lieu, en tout temps. \n\r Dieu sans limite, \n\r nous te célébrons par nos chants.', 3, 1247),
(5416, 'Refrain', 1, 'Au-dessus de tout tu règnes. \n\r Toutes nos vies t\'appartiennent, \n\r Alpha et Oméga, Roi des rois. \n\r Tous les autres dieux tomberont, \n\r tous les rois se prosterneront \n\r devant ta majesté, ta beauté.', 4, 1247),
(5417, 'Strophe', 1, 'Au-dessus des puissances, \n\r au-dessus des rois, \n\r au-dessus de la nature et de la création, \n\r au-dessus de tous les plans des hommes sages, \n\r bien avant le monde tu existais.', 1, 1248),
(5418, 'Strophe', 2, 'Au-dessus des royaumes, \n\r au-dessus des trônes, \n\r au-dessus des merveilles que ce monde a connues, \n\r par-dessus tous les trésors de la terre, \n\r rien ne peut mesurer ta valeur.', 2, 1248),
(5419, 'Strophe', 3, 'Crucifié, seul, abandonné, \n\r tu as souffert, méprisé, rejeté, \n\r telle une rose foulée sous nos pieds. \n\r Tu m\'as sauvé, tu m\'as aimé \n\r par dessus tout.', 3, 1248),
(5420, 'Strophe', 1, 'Dieu, dans sa gloire, est puissant; \n\r sa voix retentit sur les eaux; \n\r sa voix puissante fait bondir \n\r les montagnes comme des veaux. \n\r Dans la tempête du combat, \n\r déployez toutes vos bannières \n\r pour célébrer sa victoire, \n\r car c\'est lui qui nous libère.', 1, 1249),
(5421, 'Refrain', 1, 'Aux uns les chars de guerre, \n\r aux autres les chevaux; \n\r nous, notre confiance, \n\r nous la mettons en toi. \n\r Eux, ils fléchissent, ils tombent; \n\r nous, nous restons debout. \n\r Nous tenons fermement, \n\r car tu es devant nous, \n\r Éternel notre Dieu, Éternel notre Dieu!', 2, 1249),
(5422, 'Strophe', 2, 'Nous célébrerons ta vaillance, \n\r nous chanterons tous tes exploits. \n\r En toi, Seigneur, nous nous confions; \n\r que ta puissance se déploie.', 3, 1249),
(5423, 'Refrain', 1, 'La voix de l\'Éternel fait jaillir des éclairs, \n\r la voix de l\'Éternel fait trembler le désert. \n\r Louange et gloire à toi, \n\r ô Seigneur des seigneurs. \n\r Tu nous délivreras, \n\r tu es le grand vainqueur, \n\r Éternel, notre Roi, Éternel, notre Roi!', 4, 1249),
(5424, 'Strophe', 1, 'Baruch hashem Adonaï! \n\r Baruch hashem Adonaï! \n\r Béni soit le nom du Seigneur! \n\r Baruch hashem Adonaï!', 1, 1250),
(5425, 'Strophe', 1, 'Baruch hashem Adonaï! \n\r Baruch hashem Adonaï! \n\r Béni soit le nom du Seigneur! \n\r Baruch hashem Adonaï!', 2, 1250),
(5426, 'Strophe', 1, 'Béni soit le nom du Seigneur. \n\r (4x)', 1, 1251),
(5427, 'Strophe', 1, 'Il est mon rocher, Il est mon rocher, Il est Seigneur. \n\r (2x)', 2, 1251),
(5428, 'Strophe', 2, 'Jésus règne au ciel et sur la terre. \n\r (4x)', 3, 1251),
(5429, 'Strophe', 1, 'Tout l\'univers est dans la main du Seigneur. \n\r (2x)', 4, 1251),
(5430, 'Strophe', 1, 'Béni soit ton nom \n\r là où tu donnes l\'abondance \n\r et déverses ta bienveillance; \n\r béni soit ton nom.', 1, 1252),
(5431, 'Strophe', 1, 'Et béni soit ton nom \n\r quand ma vie traverse un désert, \n\r quand je marche en terre inconnue; \n\r béni soit ton nom.', 2, 1252),
(5432, 'Refrain', 1, 'Tes bienfaits font naître en moi \n\r des chants de louange. \n\r Et, même au cœur \n\r de la nuit, Seigneur, \n\r je redirai:', 3, 1252),
(5433, 'Refrain', 1, '«Béni soit le nom du Seigneur, \n\r béni soit ton nom. \n\r Béni soit le nom du Seigneur, \n\r béni soit ton nom glorieux!»', 4, 1252),
(5434, 'Strophe', 2, 'Béni soit ton nom \n\r quand sur moi brille le soleil. \n\r Quand la vie semble me sourire; \n\r béni soit ton nom.', 5, 1252),
(5435, 'Strophe', 2, 'Et béni soit ton nom \n\r sur la route semée de souffrance. \n\r S\'il m\'en coûte d\'offrir ma louange; \n\r béni soit ton nom.', 6, 1252),
(5436, 'Refrain', 1, 'Tes bienfaits font naître en moi \n\r des chants de louange. \n\r Et, même au cœur \n\r de la nuit, Seigneur, \n\r je redirai:', 7, 1252),
(5437, 'Refrain', 1, '«Béni soit le nom du Seigneur, \n\r béni soit ton nom. \n\r Béni soit le nom du Seigneur, \n\r béni soit ton nom glorieux!»', 8, 1252),
(5438, 'Refrain', 1, 'Tu donnes et tu reprends, \n\r tu donnes et tu reprends. \n\r Mon cœur choisit de dire: \n\r «Ô béni soit ton nom!» \n\r (2x)', 9, 1252),
(5439, 'Refrain', 1, 'Béni soit le nom du Seigneur, \n\r béni soit ton nom. \n\r Béni soit le nom du Seigneur, \n\r béni soit ton nom glorieux!', 10, 1252),
(5440, 'Refrain', 1, 'Célébrer, nous venons célébrer, \n\r nous venons pour louer, adorer Dieu, notre Père. \n\r Célébrer, nous venons célébrer, \n\r nous venons pour louer, adorer.', 1, 1253),
(5441, 'Strophe', 1, 'Célébrons-le avec nos instruments, \n\r chantons le cœur plein de remerciements. \n\r Célébrons-le, car il est juste et bon, \n\r Il nous a comblés de bénédictions.', 2, 1253),
(5442, 'Refrain', 1, 'Célébrer, nous venons célébrer, \n\r nous venons pour louer, adorer Dieu, notre Père. \n\r Célébrer, nous venons célébrer, \n\r nous venons pour louer, adorer.', 3, 1253),
(5443, 'Strophe', 2, 'Célébrons-le, il nous donne la vie \n\r et dans son livre nos noms sont écrits. \n\r Célébrons-le, il nous a introduits \n\r dans sa famille qui nous a accueillis!', 4, 1253),
(5444, 'Refrain', 1, 'Célébrer, nous venons célébrer, \n\r nous venons pour louer, adorer Dieu, notre Père. \n\r Célébrer, nous venons célébrer, \n\r nous venons pour louer, adorer.', 5, 1253),
(5445, 'Strophe', 3, 'Célébrons-le en exerçant nos dons, \n\r sans négliger la sanctification. \n\r Célébrons-le par un comportement \n\r qui soit conforme à son enseignement.', 6, 1253),
(5446, 'Refrain', 1, 'Célébrer, nous venons célébrer, \n\r nous venons pour louer, adorer Dieu, notre Père. \n\r Célébrer, nous venons célébrer, \n\r nous venons pour louer, adorer.', 7, 1253),
(5447, 'Strophe', 1, 'Nous sommes unis dans la famille \n\r car Dieu nous lie à toujours \n\r par une chaîne d\'amour.', 1, 1254),
(5448, 'Strophe', 2, 'Concitoyens, nous sommes les siens \n\r car Dieu nous lie à toujours \n\r par une chaîne d\'amour', 2, 1254),
(5449, 'Refrain', 1, 'Et chacun des maillons \n\r dans l\'épreuve tiendra bon, \n\r lié à toujours \n\r par une chaîne d\'amour.', 3, 1254),
(5450, 'Refrain', 1, 'Oui, chacun des maillons \n\r dans l\'épreuve tiendra bon, \n\r lié à toujours \n\r par une chaîne d\'amour.', 4, 1254),
(5451, 'Strophe', 1, 'C\'est toi qui as reçu les coups que je méritais, \n\r sur toi qu\'est tombée la colère qui me revenait \n\r et c\'est par tes blessures que je suis guéri, \n\r c\'est par ton sang que je suis libre aujourd\'hui.', 1, 1255),
(5452, 'Refrain', 1, 'Ce sont tes larmes qui m\'ont touché, \n\r ta compassion qui m\'a lavé. \n\r À toi la gloire, la majesté \n\r et l\'honneur pour l\'éternité.', 2, 1255),
(5453, 'Strophe', 2, 'C\'est toi qui as couvert ma honte et ma nudité \n\r de ton amour et ton pardon, de ta vérité. \n\r C\'est par ton sacrifice que je suis racheté, \n\r sur la croix tu m\'as offert ta liberté.', 3, 1255),
(5454, 'Refrain', 1, 'Ce sont tes larmes qui m\'ont touché, \n\r ta compassion qui m\'a lavé. \n\r À toi la gloire, la majesté \n\r et l\'honneur pour l\'éternité.', 4, 1255),
(5455, 'Strophe', 3, 'C\'est toi qui as été trahi et abandonné, \n\r c\'est toi qui as été brisé pour tous mes péchés \n\r et ce sont ces mêmes mains que les hommes ont percées \n\r que tu as tendues vers moi pour me relever.', 5, 1255),
(5456, 'Refrain', 1, 'Ce sont tes larmes qui m\'ont touché, \n\r ta compassion qui m\'a lavé. \n\r À toi la gloire, la majesté \n\r et l\'honneur pour l\'éternité.', 6, 1255),
(5457, 'Strophe', 1, 'Comme un doux murmure, \n\r comme une pluie légère, \n\r tu nous arroses de ta douce présence.', 1, 1256),
(5458, 'Strophe', 2, 'Tu déposes sur nous des couronnes d\'or, \n\r tu nous appelles: «Mon plaisir.» \n\r (2x)', 2, 1256),
(5459, 'Refrain', 1, 'Alors nos yeux s\'ouvrent à ta gloire, \n\r nous te contemplons sur ton trône, \n\r revêtu d\'un manteau de gloire, \n\r tu es le Roi.', 3, 1256),
(5460, 'Refrain', 1, 'Sur ta tête repose une couronne \n\r ornée de milliers de diamants, \n\r et ton regard sur nous se pose \n\r et nous transforme.', 4, 1256),
(5461, 'Strophe', 2, 'Et des milliers d\'anges, \n\r dansent autour de toi, \n\r élèvent la bannière \n\r de ta royauté.', 5, 1256),
(5462, 'Strophe', 2, 'Les trompettes sonnent avec puissance, \n\r appelant ton règne sur la terre. \n\r (2x)', 6, 1256),
(5463, 'Refrain', 1, 'Alors nos yeux s\'ouvrent à ta gloire, \n\r nous te contemplons sur ton trône, \n\r revêtu d\'un manteau de gloire, \n\r tu es le Roi.', 7, 1256),
(5464, 'Refrain', 1, 'Sur ta tête repose une couronne \n\r ornée de milliers de diamants, \n\r et ton regard sur nous se pose \n\r et nous transforme.', 8, 1256),
(5465, 'Refrain', 1, 'Rien ni personne ne pourra m\'arracher de ta main, \n\r (3x) \n\r ma vie, mon destin sont dans ta main.', 1, 1257),
(5466, 'Strophe', 1, 'Tu as créé ciel et terre, \n\r tu es la source de vie, \n\r le Père en qui j\'espère, \n\r le Roi dont je suis fier; \n\r rien de secret, de négligé, \n\r nos cheveux mêmes sont comptés. \n\r C\'est vrai, je suis à toi, \n\r tu sais tout de moi.', 2, 1257),
(5467, 'Refrain', 1, 'Rien ni personne ne pourra m\'arracher de ta main, \n\r (3x) \n\r ma vie, mon destin sont dans ta main.', 3, 1257),
(5468, 'Strophe', 2, 'Quand la tempête fait rage, \n\r quand trop sombre est mon chemin, \n\r tu déploies comme un toit \n\r ton amour sur moi. \n\r Ceux qui en voulaient à ma vie, \n\r tu les as tous anéantis; \n\r personne n\'est comme toi, \n\r mon papa, mon Roi!', 4, 1257),
(5469, 'Refrain', 1, 'Rien ni personne ne pourra m\'arracher de ta main, \n\r (3x) \n\r ma vie, mon destin sont dans ta main.', 5, 1257),
(5470, 'Refrain', 1, 'Quand les pensées s\'agitent en foule \n\r au plus profond de moi, \n\r tu viens à mon secours \n\r calmer mon désarroi; \n\r tu consoles mon âme \n\r et tu réjouis mon cœur \n\r par tant de compassion, \n\r de grâce et de douceur.', 6, 1257),
(5471, 'Strophe', 1, 'Je veux, mon Dieu, de tout mon cœur \n\r te remercier et te louer, \n\r et devant les grands de ce monde \n\r te proclamer, te glorifier. \n\r J\'irai, Seigneur, dans ta maison \n\r chanter ton nom, ô Éternel, \n\r pour ton amour, ta vérité \n\r et ta bonté toujours fidèles.', 1, 1258),
(5472, 'Strophe', 2, 'L\'accomplissement de tes promesses \n\r a dépassé mes plus beaux rêves; \n\r ta parole remplie de sagesse \n\r me réconforte et me relève. \n\r Je me prosterne dans ta maison, \n\r devant ton trône pour t\'adorer. \n\r À toi l\'honneur et la puissance, \n\r dans ta présence je veux rester.', 2, 1258),
(5473, 'Strophe', 3, 'Lorsque je passe par la détresse, \n\r tu me préserves de l\'ennemi \n\r et malgré toutes mes faiblesses, \n\r tu renouvelleras ma vie. \n\r Tu agiras en ma faveur, \n\r toi, le Sauveur de l\'univers. \n\r N\'abandonne pas tes créatures, \n\r garde-nous sûrs auprès du Père.', 3, 1258),
(5474, 'Strophe', 1, 'Devant le trône du Très-Haut, \n\r mon défenseur saura plaider. \n\r Cet avocat s\'appelle: «Amour» \n\r Il intercède pour moi toujours.', 1, 1259),
(5475, 'Strophe', 1, 'Mon nom est gravé sur ses mains, \n\r mon nom est inscrit sur son cœur. \n\r Puisqu\'Il défend ma cause au ciel, \n\r nul ne pourra me condamner, \n\r nul ne pourra me condamner.', 2, 1259),
(5476, 'Strophe', 2, 'Et quand Satan me fait douter, \n\r vient me tenter et m\'accuser, \n\r je lève les yeux vers celui \n\r qui a donné pour moi sa vie.', 3, 1259),
(5477, 'Strophe', 2, 'Mon Sauveur était innocent, \n\r sa mort me rend la liberté. \n\r Oui, Dieu le juste est satisfait \n\r et Il pardonne mon péché, \n\r et Il pardonne mon péché.', 4, 1259),
(5478, 'Strophe', 3, 'Voyez l\'Agneau ressuscité, \n\r Il est le Roi plein de bonté. \n\r Non, Il ne changera jamais, \n\r lui, ma justice, mon bien, ma paix.', 5, 1259),
(5479, 'Strophe', 3, 'En lui, j\'ai la vie éternelle, \n\r par son sang, Il m\'a racheté. \n\r Pour toujours, ma vie est cachée \n\r en Jésus, mon Sauveur, mon Dieu, \n\r en Jésus, mon Sauveur, mon Dieu.', 6, 1259),
(5480, 'Strophe', 1, 'Dans ta présence je me sens fort, \n\r auprès de toi je ne crains rien. \n\r J\'ouvre mon cœur à ton Esprit, \n\r tu es celui qui me guérit.', 1, 1260),
(5481, 'Refrain', 1, 'Viens ô Seigneur, remplis-moi de ta présence. \n\r Viens ô Seigneur, revêts-moi de ta puissance! \n\r Viens ô Seigneur, conduis-moi dans ta présence! \n\r Plus près de toi, ah! plus près de toi, \n\r ah! plus près de toi, ah! plus près de toi.', 2, 1260),
(5482, 'Strophe', 2, 'Seigneur mon Dieu qui te ressemble? \n\r Majestueux dans ton saint temple, \n\r je me prosterne devant ton trône \n\r et te célèbre avec les anges.', 3, 1260),
(5483, 'Refrain', 1, 'Viens ô Seigneur, remplis-moi de ta présence. \n\r Viens ô Seigneur, revêts-moi de ta puissance! \n\r Viens ô Seigneur, conduis-moi dans ta présence! \n\r Plus près de toi, ah! plus près de toi, \n\r ah! plus près de toi, ah! plus près de toi.', 4, 1260),
(5484, 'Refrain', 1, 'Fais couler ton Esprit, remplis-moi, \n\r tout à nouveau sur moi, remplis-moi. \n\r Comme la pluie du printemps remplis-moi! \n\r Viens sur moi et descends maintenant!', 5, 1260),
(5485, 'Refrain', 1, 'Des cris de joie, des chants de salut \n\r montent des tentes des fidèles du Seigneur. \n\r (2x)', 1, 1261),
(5486, 'Strophe', 1, 'La main de Dieu agit puissamment pour nous. \n\r Le bras de Dieu est élevé. \n\r La main de Dieu est puissante et glorieuse. \n\r Le bras de Dieu est souverain, \n\r le bras de Dieu est souverain.', 2, 1261),
(5487, 'Refrain', 1, 'Des cris de joie, des chants de salut \n\r montent des tentes des fidèles du Seigneur. \n\r (2x)', 3, 1261),
(5488, 'Strophe', 1, 'Regarde, ô Dieu, notre pays, \n\r son cœur est loin de toi. \n\r Vois, ton Église implore et prie \n\r pour un retour à toi. \n\r Dans ta compassion, souviens-toi de ta bonté \n\r et guéris ton corps blessé par le péché.', 1, 1262),
(5489, 'Refrain', 1, 'Devant ton autel, je viens t\'adorer, \n\r toi, le seul Dieu saint, \n\r seul Dieu trois fois saint, \n\r créateur de la terre et des cieux. \n\r (2x)', 2, 1262),
(5490, 'Strophe', 2, 'Reviens régner sur la nation, \n\r les sources sont bouchées. \n\r Le peuple a rejeté ton nom \n\r en suivant ses pensées. \n\r Dans ta compassion, viens changer nos sentiments \n\r et pardonne le mépris de tes enfants.', 3, 1262),
(5491, 'Refrain', 1, 'Devant ton autel, je viens t\'adorer, \n\r toi, le seul Dieu saint, \n\r seul Dieu trois fois saint, \n\r créateur de la terre et des cieux. \n\r (2x)', 4, 1262),
(5492, 'Strophe', 3, 'Réveille-nous, Dieu éternel, \n\r pour suivre ta parole. \n\r Nous voulons bâtir ton autel \n\r et brûler nos idoles. \n\r Dans ta compassion, revêts-nous de ton Esprit \n\r et conduis-nous sur le chemin de la vie.', 5, 1262),
(5493, 'Refrain', 1, 'Devant ton autel, je viens t\'adorer, \n\r toi, le seul Dieu saint, \n\r seul Dieu trois fois saint, \n\r créateur de la terre et des cieux. \n\r (2x)', 6, 1262),
(5494, 'Strophe', 1, 'Dieu, Créateur de la terre et des cieux, \n\r nous contemplons ton ouvrage. \n\r Tu as voulu que les hommes en tous lieux \n\r découvrent ton vrai visage.', 1, 1263),
(5495, 'Refrain', 1, 'À toi la gloire et la louange, \n\r à toi le règne et l\'autorité. \n\r La révérence et la puissance, \n\r l\'adoration t\'appartiennent à jamais.', 2, 1263),
(5496, 'Strophe', 2, 'Dieu éternel, toi, le tout-suffisant \n\r et l\'être le plus sensible, \n\r tu es parfait et vraiment étonnant; \n\r pour toi, rien n\'est impossible.', 3, 1263),
(5497, 'Refrain', 1, 'À toi la gloire et la louange, \n\r à toi le règne et l\'autorité. \n\r La révérence et la puissance, \n\r l\'adoration t\'appartiennent à jamais.', 4, 1263),
(5498, 'Strophe', 3, 'Dieu souverain, tu n\'es jamais lointain, \n\r tu connais tous nos besoins, \n\r tu as prévu pour chacun un chemin, \n\r nos vies sont entre tes mains.', 5, 1263),
(5499, 'Refrain', 1, 'À toi la gloire et la louange, \n\r à toi le règne et l\'autorité. \n\r La révérence et la puissance, \n\r l\'adoration t\'appartiennent à jamais.', 6, 1263),
(5500, 'Refrain', 1, 'Dieu, chef de l\'armée, \n\r puissant guerrier, \n\r révèle-nous tes stratégies \n\r pour vaincre l\'ennemi! \n\r Lève ton armée, \n\r saint messager, \n\r réveille l\'Église et qu\'elle te suive \n\r pour que ton règne arrive!', 1, 1264),
(5501, 'Strophe', 1, 'Les armes que tu as données \n\r ne sont pas de ce monde. \n\r Nous ne luttons pas en versant le sang. \n\r La prière et la foi \n\r nous mènent au combat. \n\r Quand nous partageons ton amour, \n\r notre ennemi s\'enfuit.', 2, 1264),
(5502, 'Refrain', 1, 'Dieu, chef de l\'armée, \n\r puissant guerrier, \n\r révèle-nous tes stratégies \n\r pour vaincre l\'ennemi! \n\r Lève ton armée, \n\r saint messager, \n\r réveille l\'Église et qu\'elle te suive \n\r pour que ton règne arrive!', 3, 1264),
(5503, 'Strophe', 2, 'Tu nous confies une mission: \n\r porter ton salut \n\r aux millions qui n\'ont jamais entendu. \n\r L\'ardeur de nos louanges \n\r fait lever ta bannière \n\r et l\'ennemi doit reculer, \n\r balayé par l\'Esprit.', 4, 1264),
(5504, 'Refrain', 1, 'Dieu, chef de l\'armée, \n\r puissant guerrier, \n\r révèle-nous tes stratégies \n\r pour vaincre l\'ennemi! \n\r Lève ton armée, \n\r saint messager, \n\r réveille l\'Église et qu\'elle te suive \n\r pour que ton règne arrive!', 5, 1264),
(5505, 'Strophe', 1, 'Dieu de toute grâce, trésor de bonté, \n\r source intarissable d\'infinis bienfaits. \n\r Tout en moi soupire le jour et la nuit \n\r après ta présence qui comble ma vie.', 1, 1265),
(5506, 'Strophe', 1, 'Ta fidélité, refuge de paix, \n\r me couvre et m\'entoure toujours; \n\r toujours je t\'ouvre mon cœur, tu m\'ouvres tes bras; \n\r tu prends soin de moi toujours, toujours, toujours, toujours, toujours, toujours, toujours.', 2, 1265),
(5507, 'Strophe', 2, 'Dieu qui m\'as fait grâce, qui m\'as tout donné, \n\r je cherche ta face et ta volonté. \n\r Ce que je désire, c\'est t\'aimer plus fort, \n\r vivre ton alliance, te servir encore.', 3, 1265),
(5508, 'Strophe', 2, 'Ta fidélité, refuge de paix, \n\r me couvre et m\'entoure toujours; \n\r toujours je t\'offre mon cœur, tu m\'offres ta joie, \n\r tu conduis mes pas toujours, toujours, Abba, Père, Abba, Papa, Abba.', 4, 1265),
(5509, 'Refrain', 1, 'Du lever du soleil jusqu\'à son coucher, \n\r ton nom, Éternel, est exalté. \n\r (2x)', 1, 1266),
(5510, 'Strophe', 1, 'Ta compassion est grande, \n\r elle dure pour mille générations, \n\r pour ceux qui te respectent \n\r et suivent tes commandements.', 2, 1266),
(5511, 'Refrain', 1, 'Du lever du soleil jusqu\'à son coucher, \n\r ton nom, Éternel, est exalté. \n\r (2x)', 3, 1266),
(5512, 'Strophe', 2, 'Ton peuple est comme une vigne \n\r qui donne son fruit en sa saison. \n\r Son cœur est à la fête \n\r comme aux beaux jours de la moisson.', 4, 1266),
(5513, 'Refrain', 1, 'Du lever du soleil jusqu\'à son coucher, \n\r ton nom, Éternel, est exalté. \n\r (2x)', 5, 1266),
(5514, 'Strophe', 3, 'Ton nom est une forteresse \n\r et un refuge pour l\'opprimé. \n\r C\'est dans notre faiblesse \n\r que ta puissance est révélée.', 6, 1266),
(5515, 'Refrain', 1, 'Du lever du soleil jusqu\'à son coucher, \n\r ton nom, Éternel, est exalté. \n\r (2x)', 7, 1266),
(5516, 'Strophe', 1, 'Dieu puissant, ta splendeur remplit la terre. \n\r Rien ne peut expliquer ce grand mystère. \n\r L\'univers acclame ton nom glorieux. \n\r Tu es merveilleux.', 1, 1267),
(5517, 'Strophe', 2, 'Qui pourrait s\'élever plus haut que toi? \n\r Qui pourrait contempler ce que tu vois? \n\r Ton pouvoir infini surpasse tout. \n\r Tu es près de nous.', 2, 1267),
(5518, 'Refrain', 1, 'Souverain sur tout ce qui existe, \n\r tu es maître de toute la terre. \n\r El Elyon, tu es le plus haut. \n\r Nous, ton peuple, chantons ta grandeur.', 3, 1267),
(5519, 'Refrain', 1, 'Élève-toi, ô notre Dieu! \n\r Élève-toi, ô notre Dieu! \n\r Que ton règne vienne sur la terre! \n\r Que ton règne vienne parmi nous! \n\r (2x)', 1, 1268),
(5520, 'Strophe', 1, 'J\'éclaterai en chants de triomphe, \n\r à la tête des grandes nations; \n\r je publierai tes hauts faits, ta gloire, \n\r pour l\'honneur de ton nom.', 2, 1268);
INSERT INTO `strophe` (`idStrophe`, `type`, `identifiant`, `texte`, `position`, `idChant`) VALUES
(5521, 'Refrain', 1, 'Élève-toi, ô notre Dieu! \n\r Élève-toi, ô notre Dieu! \n\r Que ton règne vienne sur la terre! \n\r Que ton règne vienne parmi nous! \n\r (2x)', 3, 1268),
(5522, 'Strophe', 2, 'Je t\'élèverai parmi les peuples, \n\r en proclamant haut ta sainteté. \n\r Races et tribus de toute la terre \n\r te loueront à jamais.', 4, 1268),
(5523, 'Refrain', 1, 'Élève-toi, ô notre Dieu! \n\r Élève-toi, ô notre Dieu! \n\r Que ton règne vienne sur la terre! \n\r Que ton règne vienne parmi nous! \n\r (2x)', 5, 1268),
(5524, 'Strophe', 3, 'Pourquoi les nations se moqueraient-elles \n\r en disant: «mais où est donc leur Dieu?» \n\r Notre Seigneur, qui vit dans le ciel, \n\r règne et fait tout ce qu\'il veut.', 6, 1268),
(5525, 'Refrain', 1, 'Élève-toi, ô notre Dieu! \n\r Élève-toi, ô notre Dieu! \n\r Que ton règne vienne sur la terre! \n\r Que ton règne vienne parmi nous! \n\r (2x)', 7, 1268),
(5526, 'Strophe', 1, 'Tu es jaloux de ton nom, tu es jaloux de ta gloire. \n\r Que le monde puisse te voir tel que tu es, le Vrai. \n\r Tu es l\'époux dévoué, tu es fidèle à jamais, \n\r tu te montres bienveillant pour ceux qui aiment ton nom.', 1, 1269),
(5527, 'Refrain', 1, 'Tu es le Dieu jaloux et devant ta justice \n\r un amour divisé ne pourrait subsister. \n\r Ta passion infinie demeure sans compromis. \n\r Toi, Dieu au cœur de feu, c\'est mon cœur que tu veux.', 2, 1269),
(5528, 'Strophe', 2, 'Tu es jaloux de ton nom, tu es jaloux de ta gloire. \n\r Rien ne peut se comparer à ton immensité. \n\r De ton trône de lumière, tu fais trembler l\'univers. \n\r Toi, le Dieu de sainteté, tu nous veux tout entier.', 3, 1269),
(5529, 'Refrain', 1, 'Tu es le Dieu jaloux et devant ta justice \n\r un amour divisé ne pourrait subsister. \n\r Ta passion infinie demeure sans compromis. \n\r Toi, Dieu au cœur de feu, c\'est mon cœur que tu veux.', 4, 1269),
(5530, 'Refrain', 1, 'Reçois toute notre affection; \n\r tu es le seul Dieu vivant! \n\r Reçois toute notre affection; \n\r tu es le seul Dieu.', 5, 1269),
(5531, 'Strophe', 1, 'Dans les heures sombres de crainte et de doute, \n\r seul dans mon désert, j\'ai perdu la route. \n\r Mais Dieu s\'approche de ma souffrance. \n\r Je n\'suis pas oublié, car Dieu est El Roï.', 1, 1270),
(5532, 'Refrain', 1, 'El Roï, toi qui vois dans le secret de mon cœur. \n\r Tu connais mes pensées, tout de moi. \n\r El Roï, toi qui vois dans ta sagesse infinie, \n\r tu consoles et je suis libre en toi.', 2, 1270),
(5533, 'Strophe', 2, 'Je loue ta sagesse, ta tendre affection. \n\r Tu me relèves de l\'humiliation, \n\r car tu t\'approches de ma souffrance. \n\r Je n\'suis pas oublié, car tu es El Roï.', 3, 1270),
(5534, 'Refrain', 1, 'El Roï, the One who sees is now looking deep in me \n\r for He knows everything in my life. \n\r El Roï, the One who sees, in his wisdom comforts me. \n\r In this safe place of love I am free.', 4, 1270),
(5535, 'Strophe', 1, 'Notre Dieu nous a créés différents, \n\r Il nous aime d\'un amour infini. \n\r Accepter les uns, respecter les autres, \n\r c\'est donner gloire au Roi des rois.', 1, 1271),
(5536, 'Refrain', 1, 'Ensemble nous voulons élever nos voix. \n\r Ensemble nous voulons proclamer le Roi. \n\r (2x)', 2, 1271),
(5537, 'Strophe', 2, 'Notre Dieu nous a sauvés, rachetés, \n\r Il nous aime d\'un amour infini. \n\r Il nous donne à tous de merveilleux dons \n\r pour servir, pour nous faire grandir.', 3, 1271),
(5538, 'Refrain', 1, 'Ensemble nous voulons élever nos voix. \n\r Ensemble nous voulons proclamer le Roi. \n\r (2x)', 4, 1271),
(5539, 'Strophe', 1, 'Entrons dans ce lieu, exaltons le Roi, \n\r élevons les mains, nos cœurs à l\'unisson. \n\r Il est digne, digne de nos louanges, \n\r oui, digne, digne de nos louanges.', 1, 1272),
(5540, 'Strophe', 2, 'Par nos mélodies, chantons notre Roi, \n\r Il nous a donné la louange pour épée. \n\r Il est digne, digne de nos louanges, \n\r oui, digne, digne de nos louanges.', 2, 1272),
(5541, 'Refrain', 1, 'À lui soient l\'honneur, la louange, \n\r la puissance et la gloire! \n\r Fléchis le genou et adore, \n\r exalte son saint nom!', 3, 1272),
(5542, 'Strophe', 1, 'Esprit de Dieu, viens comme un souffle, une flamme; \n\r Esprit de Dieu, viens déployer ta force en nous. (moi)', 1, 1273),
(5543, 'Refrain', 1, 'Viens, Esprit saint. \n\r (4x)', 2, 1273),
(5544, 'Strophe', 2, 'Esprit de dieu, viens, ouvre nos yeux, nos oreilles; \n\r Esprit de Dieu, viens, parle-nous dans ta douceur.', 3, 1273),
(5545, 'Refrain', 1, 'Viens, Esprit saint. \n\r (4x)', 4, 1273),
(5546, 'Refrain', 1, 'Faites résonner un chant de victoire, \n\r le Seigneur est ressuscité! \n\r Il est vivant parmi nous et sa gloire \n\r se voit dans nos vies transformées.', 1, 1274),
(5547, 'Strophe', 1, 'Il est venu partager notre sort, \n\r en être humain, sans jamais faire de tort. \n\r Il est unique par sa vie et sa mort.', 2, 1274),
(5548, 'Refrain', 1, 'Faites résonner un chant de victoire, \n\r le Seigneur est ressuscité! \n\r Il est vivant parmi nous et sa gloire \n\r se voit dans nos vies transformées.', 3, 1274),
(5549, 'Strophe', 2, 'Il a parlé comme personne n\'a parlé, \n\r en enseignant avec autorité. \n\r Puis Il est mort, on l\'a crucifié.', 4, 1274),
(5550, 'Refrain', 1, 'Faites résonner un chant de victoire, \n\r le Seigneur est ressuscité! \n\r Il est vivant parmi nous et sa gloire \n\r se voit dans nos vies transformées.', 5, 1274),
(5551, 'Strophe', 3, 'Mais le tombeau n\'a pas pu le garder. \n\r Le jour de Pâques, Christ est ressuscité. \n\r Il est vainqueur, au ciel Il est monté.', 6, 1274),
(5552, 'Refrain', 1, 'Faites résonner un chant de victoire, \n\r le Seigneur est ressuscité! \n\r Il est vivant parmi nous et sa gloire \n\r se voit dans nos vies transformées.', 7, 1274),
(5553, 'Strophe', 4, 'Devant le Père, Il est notre avocat, \n\r Il intercède et ne fléchira pas. \n\r Pour son Église, bientôt Il reviendra.', 8, 1274),
(5554, 'Refrain', 1, 'Faites résonner un chant de victoire, \n\r le Seigneur est ressuscité! \n\r Il est vivant parmi nous et sa gloire \n\r se voit dans nos vies transformées.', 9, 1274),
(5555, 'Refrain', 1, 'Brûle en nous, feu du réveil! \n\r Embrase-nous, Dieu de vie! \n\r Révèle-nous la gloire du ciel, \n\r la communion de l\'Esprit!', 1, 1275),
(5556, 'Strophe', 1, 'L\'amour du Père a transformé nos cœurs \n\r par la folie de la croix. \n\r Il nous délivre de notre tiédeur \n\r pour enflammer notre foi.', 2, 1275),
(5557, 'Refrain', 1, 'Brûle en nous, feu du réveil! \n\r Embrase-nous, Dieu de vie! \n\r Révèle-nous la gloire du ciel, \n\r la communion de l\'Esprit!', 3, 1275),
(5558, 'Strophe', 2, 'Étends ta main sur ton peuple assemblé \n\r dans la présence de Jésus. \n\r Tu viens guérir celui qui est blessé, \n\r sauver les hommes perdus.', 4, 1275),
(5559, 'Refrain', 1, 'Brûle en nous, feu du réveil! \n\r Embrase-nous, Dieu de vie! \n\r Révèle-nous la gloire du ciel, \n\r la communion de l\'Esprit!', 5, 1275),
(5560, 'Strophe', 3, 'D\'une même voix, nous disons: «Notre Père, \n\r que ton nom soit sanctifié!» \n\r Répands sur nous ton Esprit de prière, \n\r accomplis ta volonté.', 6, 1275),
(5561, 'Refrain', 1, 'Brûle en nous, feu du réveil! \n\r Embrase-nous, Dieu de vie! \n\r Révèle-nous la gloire du ciel, \n\r la communion de l\'Esprit!', 7, 1275),
(5562, 'Strophe', 1, 'Homme de douleur, habitué à la souffrance, \n\r méprisé, abandonné, \n\r tu as déversé ton amour immense \n\r en mourrant pour nos péchés.', 1, 1276),
(5563, 'Refrain', 1, 'Oh! je méritais la mort et c\'est toi qui as payé; \n\r je m\'incline et je t\'adore en esprit et en vérité. \n\r Un si grand amour, je ne l\'ai pas mérité. \n\r Tu me demandes simplement de l\'accepter. \n\r Tu me demandes simplement de l\'accepter.', 2, 1276),
(5564, 'Strophe', 1, 'Hommes de foi, debout, chantez \n\r à la gloire de notre Roi; \n\r vous êtes forts dans la faiblesse, \n\r affermis dans la détresse.', 1, 1277),
(5565, 'Refrain', 1, 'Criez au nord et au sud, \n\r chantez à l\'est et à l\'ouest: \n\r «Jésus veut nous sauver tous, \n\r Il est le seul Seigneur.»', 2, 1277),
(5566, 'Strophe', 2, 'Debout, femmes de vérité, \n\r chantez pour les cœurs brisés, \n\r chantez-leur la guérison \n\r dans le nom du Roi d\'amour.', 3, 1277),
(5567, 'Refrain', 1, 'Criez au nord et au sud, \n\r chantez à l\'est et à l\'ouest: \n\r «Jésus veut nous sauver tous, \n\r Il est le seul Seigneur.»', 4, 1277),
(5568, 'Strophe', 3, 'Viens, Église aux ailes brisées, \n\r qu\'à nouveau ton chant résonne \n\r pour ton Dieu qui règne au ciel; \n\r par sa grâce, reprends ton vol.', 5, 1277),
(5569, 'Refrain', 1, 'Criez au nord et au sud, \n\r chantez à l\'est et à l\'ouest: \n\r «Jésus veut nous sauver tous, \n\r Il est le seul Seigneur.»', 6, 1277),
(5570, 'Strophe', 4, 'Au travers du feu, des grandes eaux, \n\r nous sommes purifiés par ta puissance. \n\r Nos cœurs sont brûlants d\'amour pour toi, \n\r scellés par la vérité.', 7, 1277),
(5571, 'Refrain', 1, 'Criez au nord et au sud, \n\r chantez à l\'est et à l\'ouest: \n\r «Jésus veut nous sauver tous, \n\r Il est le seul Seigneur.»', 8, 1277),
(5572, 'Refrain', 1, 'Tu es le seul Seigneur. \n\r (3x)', 9, 1277),
(5573, 'Strophe', 1, 'L\'heure est arrivée, le moment de vérité \n\r a sonné l\'avènement de sa lumière. \n\r L\'ombre est désarmée, l\'ennemi est terrassé \n\r et le jour de la victoire est arrivé.', 1, 1278),
(5574, 'Refrain', 1, 'Il est Seigneur, Il est vainqueur. \n\r Par la force de son nom, Il a scellé le pardon \n\r de sa vie, de sa paix \n\r et d\'un testament d\'éternité.', 2, 1278),
(5575, 'Strophe', 2, 'Revêtus de joie, à l\'écoute de sa voix, \n\r avançons vers le combat dans sa puissance. \n\r Unis par son sang et portés par notre chant, \n\r nous amènerons les foules à sa louange.', 3, 1278),
(5576, 'Refrain', 1, 'Il est Seigneur, Il est vainqueur. \n\r Par la force du Très-Haut, Il est sorti du tombeau \n\r et il vit à jamais, \n\r couronné de gloire et d\'autorité.', 4, 1278),
(5577, 'Strophe', 3, 'L\'aube est déjà là, de ce jour où l\'on verra \n\r le salut de Jésus-Christ briller sur terre. \n\r Rien n\'est plus certain que l\'étoile du matin \n\r dans le ciel illuminé de nos prières.', 5, 1278),
(5578, 'Refrain', 1, 'Il est Seigneur, Il est vainqueur. \n\r Que le Dieu de l\'univers soit manifesté sur terre, \n\r que son nom soit loué \n\r et que règne enfin sa majesté.', 6, 1278),
(5579, 'Refrain', 1, 'Chante au Seigneur, terre entière, \n\r pousse un cri de joie! \n\r Viens danser dans sa lumière \n\r à pleine voix!', 1, 1279),
(5580, 'Strophe', 1, 'Il est vivant! Jésus est Seigneur! \n\r Il est vivant! Il est l\'Agneau vainqueur! \n\r Il est vivant! Jésus est Roi des rois \n\r et ça change tout pour toi et moi!', 2, 1279),
(5581, 'Refrain', 1, 'Chante au Seigneur, terre entière, \n\r pousse un cri de joie! \n\r Viens danser dans sa lumière \n\r à pleine voix!', 3, 1279),
(5582, 'Strophe', 2, 'Il est vivant! Jésus est Seigneur! \n\r Il est vivant! Il est l\'Agneau vainqueur! \n\r Il est vivant! \n\r Tout-Puissant est son nom \n\r et ça change tout pour les nations.', 4, 1279),
(5583, 'Strophe', 3, 'Il a dépouillé les dominations. \n\r Satan, son armée tremblent à son nom. \n\r Jésus, Dieu qui sauve, Dieu de délivrance, \n\r non, rien ne s\'oppose à ta toute-puissance.', 5, 1279),
(5584, 'Strophe', 1, 'Il est vivant! Jésus est Seigneur! \n\r Il est vivant! Il est l\'Agneau vainqueur! \n\r Il est vivant! Jésus est Roi des rois \n\r et ça change tout pour toi et moi!', 6, 1279),
(5585, 'Strophe', 1, 'Peuple de Dieu réjouis-toi, \n\r lève les yeux et vois ton Roi, \n\r car Il s\'avance devant toi \n\r au milieu des danses et des cris de joie! \n\r L\'armée de l\'Éternel est là, \n\r face à l\'ennemi apte au combat. \n\r Non par la force ou les armes, \n\r mais grâce au Sauveur de nos âmes.', 1, 1280),
(5586, 'Refrain', 1, 'Il est vivant, Jésus est vivant. \n\r (4x) \n\r Il est vivant!', 2, 1280),
(5587, 'Strophe', 2, 'Quel est celui si grand, si fort \n\r Qui, m\'a-t-on dit, ne craint la mort? \n\r Son visage rayonne de beauté, \n\r nous redonnant à tous la paix. \n\r Il est le lion de Juda, \n\r sa voix rugit dans les nations. \n\r Il nous appelle dans ses voies \n\r et, confiants, nous y marcherons.', 3, 1280),
(5588, 'Refrain', 1, 'Il est vivant, Jésus est vivant. \n\r (4x) \n\r Il est vivant!', 4, 1280),
(5589, 'Refrain', 1, 'Il m\'aime, Il m\'a tant donné, \n\r je choisis de suivre celui qui me fait vivre. \n\r Je ne reste pas seul dans la nuit, \n\r je prends sa main et marche, \n\r jamais ne la relâche.', 1, 1281),
(5590, 'Refrain', 1, 'Il m\'aime, Il m\'a tant donné, \n\r je choisis de suivre celui qui me fait vivre. \n\r Je ne reste pas seul dans la nuit, \n\r je prends sa main et marche, \n\r jamais ne la relâche.', 2, 1281),
(5591, 'Strophe', 1, 'Il se tient tout près de moi \n\r quand je dis: «Viens vivre en moi, \n\r prends ma vie entière, \n\r fais-moi vivre à ta lumière.» \n\r Je ne sais plus où aller, \n\r Il est là pour me guider \n\r et dans mes déserts \n\r je connais le cœur du Père.', 3, 1281),
(5592, 'Refrain', 1, 'Il m\'aime, Il m\'a tant donné, \n\r je choisis de suivre celui qui me fait vivre. \n\r Je ne reste pas seul dans la nuit, \n\r je prends sa main et marche, \n\r jamais ne la relâche.', 4, 1281),
(5593, 'Refrain', 1, 'Il m\'aime, Il m\'a tant donné, \n\r je choisis de suivre celui qui me fait vivre. \n\r Je ne reste pas seul dans la nuit, \n\r je prends sa main et marche, \n\r jamais ne la relâche.', 5, 1281),
(5594, 'Strophe', 2, 'Il me dit: «Viens et suis-moi, \n\r je ne te quitterai pas, \n\r ni tes peurs, tes doutes \n\r ne détourneront ta route. \n\r Je connais le fond de toi, \n\r car je vois bien au-delà, \n\r jusqu\'au bout des jours, \n\r rien ne changera mon amour.»', 6, 1281),
(5595, 'Refrain', 1, 'Il m\'aime, Il m\'a tant donné, \n\r je choisis de suivre celui qui me fait vivre. \n\r Je ne reste pas seul dans la nuit, \n\r je prends sa main et marche, \n\r jamais ne la relâche.', 7, 1281),
(5596, 'Refrain', 1, 'Il m\'aime, Il m\'a tant donné, \n\r je choisis de suivre celui qui me fait vivre. \n\r Je ne reste pas seul dans la nuit, \n\r je prends sa main et marche, \n\r jamais ne la relâche. \n\r Il m\'aime.', 8, 1281),
(5597, 'Refrain', 1, 'J\'ai confiance quand, dans ta présence, \n\r je prie, je crie: «Miséricorde!» \n\r (2x)', 1, 1282),
(5598, 'Strophe', 1, 'Grand Dieu d\'amour, ne t\'éloigne pas de moi! \n\r Grand Dieu d\'amour, pose ton regard sur moi! \n\r Souviens-toi de ta miséricorde, \n\r souviens-toi de ta miséricorde!', 2, 1282),
(5599, 'Refrain', 1, 'J\'ai confiance quand, dans ta présence, \n\r je prie, je crie: «Miséricorde!» \n\r (2x)', 3, 1282),
(5600, 'Strophe', 1, 'Je crois en toi, Seigneur Jésus. \n\r Tu nous as donné ton Esprit. \n\r Quand deux ou trois prient en ton nom, \n\r tu as promis d\'être présent; \n\r je crois, oui, je crois!', 1, 1283),
(5601, 'Strophe', 2, 'Je crois que tu es Fils de Dieu, \n\r la seule offrande pour nos péchés. \n\r Je crois que tu es tout-puissant, \n\r tu nous as sauvés par ton sang; \n\r je crois, oui, je crois!', 2, 1283),
(5602, 'Refrain', 1, 'Roi de paix venu du ciel, \n\r tu as quitté ton trône éternel, \n\r revêtu notre humanité \n\r afin de nous libérer.', 3, 1283),
(5603, 'Strophe', 2, 'Je crois que tu reviens bientôt, \n\r comme Roi de gloire sur l\'univers. \n\r Les peuples se rassembleront \n\r pour t\'offrir leur adoration; \n\r je crois, oui, je crois!', 4, 1283),
(5604, 'Strophe', 3, 'Je crois en la résurrection, \n\r la vie éternelle avec toi; \n\r tu nous as préparé une place \n\r et nous te verrons face à face; \n\r je crois, oui, je crois!', 5, 1283),
(5605, 'Refrain', 1, 'Roi de paix venu du ciel, \n\r tu as quitté ton trône éternel, \n\r revêtu notre humanité \n\r afin de nous libérer.', 6, 1283),
(5606, 'Strophe', 1, 'Je déclare au ciel et à la terre que Dieu est mon Père \n\r et qu\'en Jésus-Christ, son Fils, j\'ai tout de lui en moi.', 1, 1284),
(5607, 'Strophe', 1, 'Je déclare aussi que son Esprit crie en moi: «Abba, Père» \n\r et que l\'ennemi n\'a plus qu\'à trembler sous nos pas.', 2, 1284),
(5608, 'Strophe', 2, 'Je déclare au ciel et à la terre que rien ne peut nuire \n\r à l\'amour qu\'il m\'a donné en mourant sur le bois \n\r et qu\'il me donne à jamais, à chaque fois que mon cœur prie \n\r et que son Esprit atteste ses désirs en moi.', 3, 1284),
(5609, 'Refrain', 1, 'À Dieu soient le règne, la puissance et la gloire aussi. \n\r (2x)', 4, 1284),
(5610, 'Strophe', 1, 'Je déclare au ciel et à la terre que Dieu est mon Père \n\r et qu\'en Jésus-Christ, son Fils, j\'ai tout de lui en moi.', 5, 1284),
(5611, 'Strophe', 1, 'Je déclare aussi que son Esprit crie en moi: «Abba, Père» \n\r et que l\'ennemi n\'a plus qu\'à trembler sous nos pas.', 6, 1284),
(5612, 'Strophe', 2, 'Je déclare au ciel et à la terre que rien ne peut nuire \n\r à l\'amour qu\'il m\'a donné en mourant sur le bois \n\r et qu\'il me donne à jamais, à chaque fois que mon cœur prie \n\r et que son Esprit atteste ses désirs en moi.', 7, 1284),
(5613, 'Refrain', 1, 'À Dieu soient le règne, la puissance et la gloire aussi. \n\r (2x) \n\r Et la gloire aussi. (2x)', 8, 1284),
(5614, 'Strophe', 1, 'Je dépose devant toi ma couronne et tous mes droits. \n\r Je fléchis ma volonté devant tant de majesté.', 1, 1285),
(5615, 'Refrain', 1, 'J\'ouvre mon cœur à ton Esprit. \n\r J\'ouvre mon cœur à ton Esprit.', 2, 1285),
(5616, 'Strophe', 2, 'Agneau de Dieu, sois élevé! \n\r Agneau de Dieu, Agneau de Dieu.', 3, 1285),
(5617, 'Strophe', 1, 'Je fléchis le genou devant toi \n\r et je veux que tu règnes sur moi. \n\r Je te donne ma vie et mes droits. \n\r Viens, Jésus, sois mon maître et mon Roi.', 1, 1286),
(5618, 'Strophe', 2, 'Je ne veux pas cacher mon péché, \n\r je ne veux pas tromper ta bonté. \n\r Je n\'ai qu\'un seul désir, qu\'un seul souhait, \n\r celui de te servir, de t\'adorer.', 2, 1286),
(5619, 'Refrain', 1, 'Et j\'abandonne entre tes mains ma personne. \n\r Je ne suis rien et je soumets ma volonté \n\r à ta divine royauté.', 3, 1286),
(5620, 'Strophe', 1, 'Je fléchis le genou, \n\r je fléchis le genou devant toi, \n\r je fléchis le genou. \n\r (2x)', 4, 1286),
(5621, 'Strophe', 3, 'Je m\'humilie devant ta face, \n\r je fléchis le genou.', 5, 1286),
(5622, 'Strophe', 1, 'Je le reconnais, je ne suis rien sans toi, \n\r tu es parfait, tu sais tout de moi. \n\r J\'ai tant besoin de te connaître mieux, \n\r je fais le plein quand tu me rejoins.', 1, 1287),
(5623, 'Refrain', 1, 'Apprends-moi à te servir, dis-moi comment t\'obéir, \n\r marcher dans tes pas, écouter ta voix! \n\r Façonne en moi ta volonté, montre-moi comment marcher, \n\r agir par la foi, accomplir ce que je dois!', 2, 1287),
(5624, 'Strophe', 2, 'Oui, je l\'admets, je ne suis rien sans toi, \n\r pas de secrets entre toi et moi. \n\r J\'ai tant besoin de garder mes pensées, \n\r je suis serein, car tu n\'es pas loin.', 3, 1287),
(5625, 'Refrain', 1, 'Apprends-moi à te servir, dis-moi comment t\'obéir, \n\r marcher dans tes pas, écouter ta voix! \n\r Façonne en moi ta volonté, montre-moi comment marcher, \n\r agir par la foi, accomplir ce que je dois!', 4, 1287),
(5626, 'Strophe', 1, 'Je sais en vérité que l\'Éternel est grand et que notre Seigneur surpasse tous les dieux. \n\r L\'Éternel accomplit tout ce qu\'Il veut au ciel et sur la terre, dans les mers et dans les abîmes.', 1, 1288),
(5627, 'Strophe', 1, 'Des confins de la terre, Il fait monter les brumes, Il lance les éclairs au milieu de la pluie, Il fait lever le vent qu\'Il tenait en réserve.', 2, 1288),
(5628, 'Strophe', 1, 'Je sais en vérité que l\'Éternel est grand et que notre Seigneur surpasse tous les dieux. \n\r L\'Éternel accomplit tout ce qu\'Il veut au ciel et sur la terre, dans les mers et dans les abîmes.', 3, 1288),
(5629, 'Strophe', 1, 'Des confins de la terre, Il fait monter les brumes, Il lance les éclairs au milieu de la pluie, Il fait lever le vent qu\'Il tenait en réserve.', 4, 1288),
(5630, 'Strophe', 1, 'Jésus-Christ, je repense à ton sacrifice; \n\r tu t\'es humilié jusqu\'à la mort.', 1, 1289),
(5631, 'Strophe', 1, 'Bien souvent ce don de vie m\'a bouleversé \n\r et j\'en suis ému à nouveau. \n\r J\'en suis ému à nouveau.', 2, 1289),
(5632, 'Refrain', 1, 'À nouveau je regarde à la croix où tu es mort, \n\r je suis humilié, brisé par ta miséricorde, \n\r à nouveau merci, à nouveau je répands ma vie.', 3, 1289),
(5633, 'Strophe', 2, 'Mais aujourd\'hui, tu règnes dans les lieux très hauts \n\r où, devant mon Roi, je m\'inclinerai.', 4, 1289),
(5634, 'Strophe', 3, 'À présent, émerveillé par ton salut, \n\r oui, mon cœur t\'exalte à nouveau. \n\r Mon cœur t\'exalte à nouveau.', 5, 1289),
(5635, 'Refrain', 1, 'À nouveau je regarde à la croix où tu es mort, \n\r je suis humilié, brisé par ta miséricorde, \n\r à nouveau merci, à nouveau je répands ma vie.', 6, 1289),
(5636, 'Refrain', 2, 'Merci pour la croix, merci pour la croix, \n\r merci pour la croix, Jésus. \n\r (2x)', 7, 1289),
(5637, 'Strophe', 1, 'Pourquoi, parmi les morts, chercher les vivants? \n\r Jésus est ressuscité! \n\r Comme la lumière du soleil levant, \n\r Jésus est ressuscité, alléluia, \n\r Jésus est ressuscité.', 1, 1290),
(5638, 'Strophe', 2, 'Que la terre entière sache la vérité: \n\r Jésus est ressuscité! \n\r Comme la vie nouvelle qu\'il nous a donnée, \n\r Jésus est ressuscité, alléluia, \n\r Jésus est ressuscité.', 2, 1290),
(5639, 'Strophe', 3, 'L\'ennemi est vaincu, il est terrassé, \n\r Jésus est ressuscité! \n\r À lui la victoire, nous sommes libérés, \n\r Jésus est ressuscité, alléluia, \n\r Jésus est ressuscité.', 3, 1290),
(5640, 'Strophe', 4, 'Nous voyons sa gloire dans tout l\'univers. \n\r Jésus est ressuscité! \n\r L\'image parfaite du Dieu notre Père, \n\r Jésus est ressuscité, alléluia, \n\r Jésus est ressuscité.', 4, 1290),
(5641, 'Strophe', 5, 'Acclamons Jésus, Il a vaincu la mort! \n\r Jésus est ressuscité! \n\r Dansons et chantons, car Il est le plus fort, \n\r Jésus est ressuscité, alléluia, \n\r Jésus est ressuscité.', 5, 1290),
(5642, 'Strophe', 6, 'Pourquoi, parmi les morts, chercher les vivants? \n\r Jésus est ressuscité! \n\r Comme la lumière du soleil levant, \n\r Jésus est ressuscité, alléluia, \n\r Jésus est ressuscité.', 6, 1290),
(5643, 'Strophe', 1, 'Jésus, j\'élève ma voix vers toi \n\r car tu as tout pardonné, \n\r tout ce qui m\'accablait. \n\r Jésus, j\'élève ma voix vers toi, \n\r pour moi tu as tout donné, \n\r oh je veux te louer.', 1, 1291),
(5644, 'Refrain', 1, 'Même si tout basculait, \n\r si la peur venait m\'envahir, \n\r rien ne pourrait taire ton amour. \n\r Ni les puissances du ciel, \n\r ni le présent, ni l\'avenir, \n\r rien ne me séparera de ton amour.', 2, 1291),
(5645, 'Refrain', 1, 'Jésus, sois le centre, \n\r sois ma lumière, sois ma source, Jésus!', 1, 1292),
(5646, 'Strophe', 1, 'Jésus, sois le centre, \n\r sois mon espoir, sois mon chant, Jésus!', 2, 1292),
(5647, 'Refrain', 1, 'Sois le feu dans mon cœur, \n\r sois le vent dans mes voiles, \n\r sois la raison de ma vie, \n\r Jésus, Jésus!', 3, 1292),
